import { AnswersQuestions } from '@serenity-js/core';
import { match } from 'tiny-types';
import { Expectation } from '../Expectation';
import { ExpectationNotMet, Outcome } from '../outcomes';

export function and<Actual>(...expectations: Array<Expectation<any, Actual>>): Expectation<any, Actual> {
    return new And(expectations);
}

/**
 * @package
 */
class And<Actual> extends Expectation<any, Actual> {
    constructor(private readonly expectations: Array<Expectation<any, Actual>>) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual) => Promise<Outcome<any, Actual>> {

        return (actual: any) =>
            this.expectations.reduce(
                (previous, current) =>
                    previous.then(outcome =>
                        match(outcome)
                            .when(ExpectationNotMet, o => o)
                            .else(_ => current.answeredBy(actor)(actual)),
                ),
                Promise.resolve(void 0),
            );
    }

    toString(): string {
        return this.expectations.map(assertion => assertion.toString()).join(' and ');
    }
}

import { Answerable } from '@serenity-js/core';
import { equal } from 'tiny-types/lib/objects'; // tslint:disable-line:no-submodule-imports

import { Expectation } from '../Expectation';

export function contain<Item>(expected: Answerable<Item>): Expectation<Item, Item[]> {
    return Expectation.thatActualShould<Item, Item[]>('contain', expected)
        .soThat((actualValue, expectedValue) => !! ~ actualValue.findIndex(av => equal(av, expectedValue)));
}

import { AnswersQuestions } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';
import { Expectation } from '../Expectation';
import { ExpectationMet, ExpectationNotMet, Outcome } from '../outcomes';

export function containAtLeastOneItemThat<Actual>(expectation: Expectation<any, Actual>): Expectation<any, Actual[]> {
    return new ContainAtLeastOneItemThatMeetsExpectation(expectation);
}

/**
 * @package
 */
class ContainAtLeastOneItemThatMeetsExpectation<Expected, Actual> extends Expectation<Expected, Actual[]> {
    constructor(private readonly expectation: Expectation<Expected, Actual>) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual[]) => Promise<Outcome<Expected, Actual[]>> {
        return (actual: Actual[]) =>
            actual.length === 0
                ? Promise.resolve(new ExpectationNotMet(this.toString(), null, actual))
                : Promise.all(actual.map(item => this.expectation.answeredBy(actor)(item)))
                    .then(results => results.some(result => result instanceof ExpectationMet)
                        ? new ExpectationMet(this.toString(), results[0].expected, actual)
                        : new ExpectationNotMet(this.toString(), results[0].expected, actual),
                    );
    }

    toString(): string {
        return formatted `contain at least one item that does ${ this.expectation }`;
    }
}

import { AnswersQuestions } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';
import { Expectation } from '../Expectation';
import { ExpectationMet, ExpectationNotMet, Outcome } from '../outcomes';

export function containItemsWhereEachItem<Actual>(expectation: Expectation<any, Actual>): Expectation<any, Actual[]> {
    return new ContainItemsWhereEachItemMeetsExpectation(expectation);
}

/**
 * @package
 */
class ContainItemsWhereEachItemMeetsExpectation<Expected, Actual> extends Expectation<Expected, Actual[]> {
    constructor(private readonly expectation: Expectation<Expected, Actual>) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual[]) => Promise<Outcome<Expected, Actual[]>> {
        return (actual: Actual[]) =>
            actual.length === 0
                ? Promise.resolve(new ExpectationNotMet(this.toString(), null, actual))
                : Promise.all(actual.map(item => this.expectation.answeredBy(actor)(item)))
                    .then(results => results.every(result => result instanceof ExpectationMet)
                        ? new ExpectationMet(this.toString(), results[0].expected, actual)
                        : new ExpectationNotMet(this.toString(), results[0].expected, actual),
                    );
    }

    toString(): string {
        return formatted `contain items where each item does ${ this.expectation }`;
    }
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function endsWith(expected: Answerable<string>): Expectation<string> {
    return Expectation.thatActualShould<string, string>('end with', expected)
        .soThat((actualValue, expectedValue) => actualValue.endsWith(expectedValue));
}

import { Answerable } from '@serenity-js/core';
import { equal } from 'tiny-types/lib/objects'; // tslint:disable-line:no-submodule-imports

import { Expectation } from '../Expectation';

export function equals<Expected>(expectedValue: Answerable<Expected>): Expectation<Expected> {
    return Expectation.thatActualShould<Expected, Expected>('equal', expectedValue)
        .soThat((actual, expected) => equal(actual, expected));
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function includes(expected: Answerable<string>): Expectation<string> {
    return Expectation.thatActualShould<string, string>('include', expected)
        .soThat((actualValue, expectedValue) => actualValue.includes(expectedValue));
}

export * from './and';
export * from './contain';
export * from './containAtLeastOneItemThat';
export * from './containItemsWhereEachItem';
export * from './endsWith';
export * from './equals';
export * from './includes';
export * from './isAfter';
export * from './isBefore';
export * from './isGreaterThan';
export * from './isLessThan';
export * from './matches';
export * from './not';
export * from './or';
export * from './property';
export * from './startsWith';

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function isAfter(expected: Answerable<Date>): Expectation<Date> {
    return Expectation.thatActualShould<Date, Date>('have value that is after', expected)
        .soThat((actualValue, expectedValue) => actualValue.getTime() > expectedValue.getTime());
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function isBefore(expected: Answerable<Date>): Expectation<Date> {
    return Expectation.thatActualShould<Date, Date>('have value that is before', expected)
        .soThat((actualValue, expectedValue) => actualValue.getTime() < expectedValue.getTime());
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function isGreaterThan(expected: Answerable<number>): Expectation<number> {
    return Expectation.thatActualShould<number, number>('have value greater than', expected)
        .soThat((actualValue, expectedValue) => actualValue > expectedValue);
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function isLessThan(expected: Answerable<number>): Expectation<number> {
    return Expectation.thatActualShould<number, number>(`have value that's less than`, expected)
        .soThat((actualValue, expectedValue) => actualValue < expectedValue);
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function matches(expected: Answerable<RegExp>): Expectation<RegExp, string> {
    return Expectation.thatActualShould<RegExp, string>('match', expected)
        .soThat((actualValue, expectedValue) => expectedValue.test(actualValue));
}

import { AnswersQuestions } from '@serenity-js/core';
import { match } from 'tiny-types';

import { Expectation } from '../Expectation';
import { ExpectationMet, ExpectationNotMet, Outcome } from '../outcomes';

export function not<Expected, Actual>(assertion: Expectation<Expected, Actual>): Expectation<Expected, Actual> {
    return new Not<Expected, Actual>(assertion);
}

/**
 * @package
 */
class Not<Expected, Actual> extends Expectation<Expected, Actual> {
    constructor(private readonly expectation: Expectation<Expected, Actual>) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual) => Promise<Outcome<Expected, Actual>> {

        return (actual: any) =>
            this.expectation.answeredBy(actor)(actual)
                .then((outcome: Outcome<Expected, Actual>) =>
                    match<Outcome<Expected, Actual>, Outcome<Expected, Actual>>(outcome)
                        .when(ExpectationMet, o => new ExpectationNotMet(this.flipped(this.expectation.toString()), o.expected, o.actual))
                        .else(o => new ExpectationMet(this.flipped(this.expectation.toString()), o.expected, o.actual)));
    }

    toString(): string {
        return this.flipped(this.expectation.toString());
    }

    private flipped(message: string): string {
        return message.startsWith('not ')
            ? message.substring(4)
            : `not ${ message }`;
    }
}

import { AnswersQuestions } from '@serenity-js/core';

import { Expectation } from '../Expectation';
import { ExpectationMet, ExpectationNotMet, Outcome } from '../outcomes';

export function or<Actual>(...assertions: Array<Expectation<any, Actual>>): Expectation<any, Actual> {
    return new Or(assertions);
}

/**
 * @package
 */
class Or<Actual> extends Expectation<any, Actual> {
    private static readonly Separator = ' or ';

    constructor(private readonly expectations: Array<Expectation<any, Actual>>) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual) => Promise<Outcome<any, Actual>> {

        return (actual: any) =>
            this.expectations.reduce(
                (previous, current) =>
                    previous.then((outcomesSoFar: Array<Outcome<any, Actual>>) =>
                        current.answeredBy(actor)(actual)
                            .then(outcome => outcomesSoFar.concat(outcome)),        // todo: should stop on the first met expectation
                        ),
                Promise.resolve([]),
            ).
            then((outcomes: Array<Outcome<any, Actual>>) => {

                const
                    unmetExpectations = outcomes.filter(outcome => outcome instanceof ExpectationNotMet),
                    message = outcomes.map(outcome => outcome.message).join(Or.Separator);

                return unmetExpectations.length === this.expectations.length
                    ? new ExpectationNotMet(message, outcomes[0].expected, outcomes[0].actual)
                    : new ExpectationMet(message, outcomes[0].expected, outcomes[0].actual);
            });
    }

    toString(): string {
        return this.expectations
            .map(assertion => assertion.toString())
            .join(Or.Separator);
    }
}

import { AnswersQuestions } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';

import { Expectation } from '../Expectation';
import { ExpectationMet, ExpectationNotMet, Outcome } from '../outcomes';

export function property<Actual, Property extends keyof Actual>(
    propertyName: Property,
    expectation: Expectation<any, Actual[Property]>,
): Expectation<Actual[Property], Actual> {
    return new HasProperty(propertyName, expectation);
}

/**
 * @package
 */
class HasProperty<Property extends keyof Actual, Actual> extends Expectation<Actual[Property], Actual> {
    constructor(
        private readonly propertyName: Property,
        private readonly expectation: Expectation<any, Actual[Property]>,
    ) {
        super();
    }

    answeredBy(actor: AnswersQuestions): (actual: Actual) => Promise<Outcome<Actual[Property], any>> {

        return (actual: Actual) =>
            this.expectation.answeredBy(actor)(actual[this.propertyName])
                .then((outcome: Outcome<any, Actual[Property]>) => {

                    return outcome instanceof ExpectationMet
                        ? new ExpectationMet(this.toString(), outcome.expected, actual[this.propertyName])
                        : new ExpectationNotMet(this.toString(), outcome.expected, actual[this.propertyName]);
                });
    }

    toString(): string {
        return formatted `have property ${ this.propertyName } that does ${ this.expectation }`;
    }
}

import { Answerable } from '@serenity-js/core';

import { Expectation } from '../Expectation';

export function startsWith(expected: Answerable<string>): Expectation<string> {
    return Expectation.thatActualShould<string, string>('start with', expected)
        .soThat((actualValue, expectedValue) => actualValue.startsWith(expectedValue));
}

export * from './Check';
export * from './Expectation';
export * from './expectations';
export * from './Ensure';
export * from './outcomes';

import { TinyType } from 'tiny-types';

export class Outcome<Expected, Actual> extends TinyType {
    constructor(
        public readonly message: string,
        public readonly expected: Expected,
        public readonly actual: Actual,
    ) {
        super();
    }
}

export class ExpectationMet<Expected, Actual> extends Outcome<Expected, Actual> {}

export class ExpectationNotMet<Expected, Actual> extends Outcome<Expected, Actual> {}

# Serenity/JS

[Serenity/JS](https://serenity-js.org) is a Node.js library designed to make acceptance and regression testing
of modern full-stack applications faster, more collaborative and easier to scale.

## Serenity/JS Assertions

An assertion library implementing the Screenplay Pattern.

### Performing verifications using `Ensure`

```typescript
import { actorCalled } from '@serenity-js/core';
import { Ensure, endsWith } from '@serenity-js/assertions';
import { Website } from '@serenity-js/protractor';

const actor = actorCalled('Erica');

actor.attemptsTo(
    Ensure.that(Website.title(), endsWith('Serenity/JS'))
);
```

### Controlling execution flow using `Check`

```typescript
import { actorCalled } from '@serenity-js/core';
import { Check } from '@serenity-js/assertions'; 
import { Click, isVisible } from '@serenity-js/protractor';

actorCalled('Erica').attemptsTo(
    Check.whether(NewsletterModal, isVisible())
        .andIfSo(Click.on(CloseModalButton)),
);
```

### Synchronising the test with the System Under Test using `Wait`

```typescript
import { actorCalled } from '@serenity-js/core';
import { Click, isVisible, Wait } from '@serenity-js/protractor';

actorCalled('Erica').attemptsTo(
    Wait.until(CloseModalButton, isVisible()),
    Click.on(CloseModalButton)
);
```

### Defining custom expectations using `Expectation.thatActualShould`

```typescript
import { actorCalled } from '@serenity-js/core';
import { Expectation, Ensure } from '@serenity-js/assertions';

function isDivisibleBy(expected: Answerable<number>): Expectation<number> {
    return Expectation.thatActualShould<number, number>('have value divisible by', expected)
        .soThat((actualValue, expectedValue) => actualValue % expectedValue === 0);
}

actorCalled('Erica').attemptsTo(
    Ensure.that(4, isDivisibleBy(2)),
);
```

### Composing expectations using `Expectation.to`

```typescript
import { actorCalled } from '@serenity-js/core';
import { Expectation, Ensure, and, or, isGreaterThan, isLessThan, equals  } from '@serenity-js/assertions';

function isWithin(lowerBound: number, upperBound: number) {
    return Expectation
        .to(`have value within ${ lowerBound } and ${ upperBound }`)
        .soThatActual(and(
           or(isGreaterThan(lowerBound), equals(lowerBound)),
           or(isLessThan(upperBound), equals(upperBound)),
        ));
}

actorCalled('Erica').attemptsTo(
    Ensure.that(5, isWithin(3, 6)),
);
```

