import 'mocha';

import { expect } from '@integration/testing-tools';
import { actorCalled, Interaction, Log, Note, Question, Task } from '@serenity-js/core';
import * as sinon from 'sinon';
import { Check, equals, startsWith } from '../src';

/** @test {Check} */
describe('Check', () => {

    const Call = (fn: () => void) => Interaction.where(`#actor calls a function`, actor => fn());

    let spy: sinon.SinonSpy;
    beforeEach(() => spy = sinon.spy());

    describe('(if branch)', () => {

        /** @test {Check.whether} */
        /** @test {Check#andIfSo} */
        it('makes the actor execute the activities when the expectation is met', () =>
            expect(actorCalled('Enrique').attemptsTo(
                Check.whether('Hello World', startsWith('Hello'))
                    .andIfSo(
                        Call(() => spy(true)),
                        Call(() => spy(true)),
                    ),
            )).to.be.fulfilled.
            then(() => {
                expect(spy).to.have.been.calledWith(true).callCount(2);
            }),
        );

        /** @test {Check.whether} */
        /** @test {Check#andIfSo} */
        it('makes the actor ignore the activities when the expectation is not met', () =>
            expect(actorCalled('Enrique').attemptsTo(
                Check.whether('Hello World', startsWith('Hola'))
                    .andIfSo(
                        Call(() => spy(true)),
                    ),
            )).to.be.fulfilled.
            then(() => {
                expect(spy).to.not.have.been.called;    // tslint:disable-line:no-unused-expression
            }),
        );
    });

    describe('(if/else branches)', () => {
        /** @test {Check.whether} */
        /** @test {Check#andIfSo} */
        /** @test {Check#otherwise} */
        it('makes the actor execute the activities when the expectation is met', () =>
            expect(actorCalled('Enrique').attemptsTo(
                Check.whether('Hello World', startsWith('Hello'))
                    .andIfSo(
                        Call(() => spy(true)),
                    )
                    .otherwise(
                        Call(() => spy(false)),
                    ),
            )).to.be.fulfilled.
            then(() => {
                expect(spy).to.have.been.calledWith(true).callCount(1);
            }),
        );

        /** @test {Check.whether} */
        /** @test {Check#andIfSo} */
        /** @test {Check#otherwise} */
        it('makes the actor execute the alternative activities when the expectation is not met', () =>
            expect(actorCalled('Enrique').attemptsTo(
                Check.whether('Hello World', startsWith('Hola'))
                    .andIfSo(
                        Call(() => spy(true)),
                    )
                    .otherwise(
                        Call(() => spy(false)),
                    ),
            )).to.be.fulfilled.
            then(() => {
                expect(spy).to.have.been.calledWith(false).callCount(1);
            }),
        );
    });
});

import 'mocha';

import { EventRecorder, expect, PickEvent, stage } from '@integration/testing-tools';
import { actorCalled, Answerable, AnswersQuestions, AssertionError, configure, LogicError, Question, RuntimeError, TestCompromisedError } from '@serenity-js/core';
import { ActivityRelatedArtifactGenerated } from '@serenity-js/core/lib/events';
import { Name } from '@serenity-js/core/lib/model';
import { given } from 'mocha-testdata';
import { Ensure, equals, Expectation, Outcome } from '../src';
import { isIdenticalTo, p, q } from './fixtures';

/** @test {Ensure} */
describe('Ensure', () => {

    /** @test {Ensure.that} */
    it('allows the actor to make an assertion', () => {
        return expect(actorCalled('Enrique').attemptsTo(
            Ensure.that(4, isIdenticalTo(4)),
        )).to.be.fulfilled;
    });

    /** @test {Ensure.that} */
    it('fails the actor flow when the assertion is not met', () => {
        return expect(actorCalled('Enrique').attemptsTo(
            Ensure.that(4, isIdenticalTo(7)),
        )).to.be.rejectedWith(AssertionError, 'Expected 4 to have value identical to 7');
    });

    /** @test {Ensure.that} */
    it('provides a description of the assertion being made', () => {
        expect(Ensure.that(4, isIdenticalTo(7)).toString()).to.equal(`#actor ensures that 4 does have value identical to 7`);
    });

    /** @test {Ensure.that} */
    it('provides a description of the assertion being made, while correctly cleaning the output from new line characters', () => {
        expect(Ensure.that({ person: { name: 'Jan' }}, equals({
            person: {
                name: 'Jan',
            },
        })).toString()).to.equal(`#actor ensures that { person: { name: 'Jan' } } does equal { person: { name: 'Jan' } }`);
    });

    given<Answerable<number>>(
        42,
        p(42),
        q(42),
        q(p(42)),
    ).
    /** @test {Ensure.that} */
    it('allows for the actual to be a Answerable<T> as it compares its value', (actual: Answerable<number>) => {
        return expect(actorCalled('Enrique').attemptsTo(
            Ensure.that(actual, isIdenticalTo(42)),
        )).to.be.fulfilled;
    });

    /** @test {Ensure.that} */
    it('complains when given an Expectation that doesn\'t conform to the interface', () => {
        class BrokenExpectation<Expected, Actual> extends Expectation<Expected, Actual> {
            answeredBy(actor: AnswersQuestions): (actual: Actual) => Promise<Outcome<any, Actual>> {
                return (actual: Actual) => Promise.resolve(null);
            }

            toString(): string {
                return `broken`;
            }
        }

        return expect(actorCalled('Enrique').attemptsTo(
            Ensure.that(4, new BrokenExpectation()),
        )).to.be.rejectedWith(LogicError, 'An Expectation should return an instance of an Outcome, not null');
    });

    describe('when emitting an artifact', () => {
        let recorder;
        beforeEach(() => {

            recorder = new EventRecorder();

            configure({
                crew: [ recorder ],
            });
        });

        given([{
            description: 'tiny type',
            expected: new Name('Alice'),
            actual: new Name('Bob'),
            artifact: { expected: 'Name(value=Alice)', actual: 'Name(value=Bob)' },
        }, {
            description: 'boolean',
            expected: true,
            actual: false,
            artifact: { expected: 'true', actual: 'false' },
        }, {
            description: 'string',
            expected: 'name',
            actual: 'not-name',
            artifact: { expected: `'name'`, actual: `'not-name'` },
        }, {
            description: 'list',
            expected: [{ name: 'Bob' }, { name: 'Alice' }],
            actual: [{ name: 'Alice' }],
            artifact: { expected: `[\n  { name: 'Bob' },\n  { name: 'Alice' }\n]`, actual: `[\n  { name: 'Alice' }\n]` },
        }, {
            description: 'promise',
            expected: Promise.resolve(true),
            actual: Promise.resolve(false),
            artifact: { expected: `true`, actual: `false` },
        }, {
            description: 'question',
            expected: Question.about('some value', actor => true),
            actual: Question.about('some value', actor => false),
            artifact: { expected: 'true', actual: 'false' },
        }]).
            /** @test {Ensure.that} */
            it('emits an artifact describing the actual and expected values', ({ actual, expected, artifact }) => {

                return expect(actorCalled('Enrique').attemptsTo(
                    Ensure.that(actual, equals(expected)),  // we don't care about the expectation itself in this test
                )).to.be.rejected.then(() =>
                    PickEvent.from(recorder.events)
                        .next(ActivityRelatedArtifactGenerated, e => e.artifact.map(value => {
                            expect(value.expected).to.equal(artifact.expected);
                            expect(value.actual).to.equal(artifact.actual);
                        })),
                );
            });
    });

    describe('custom errors', () => {

        it('allows the actor to fail the flow with a custom RuntimeError, embedding the original error', () => {
            return expect(actorCalled('Enrique').attemptsTo(
                    Ensure.that(503, equals(200)).otherwiseFailWith(TestCompromisedError),
                ))
                .to.be.rejectedWith(TestCompromisedError, 'Expected 503 to equal 200')
                .then((error: RuntimeError) => {
                    expect(error.cause).to.be.instanceOf(AssertionError);
                    expect(error.cause.message).to.be.equal('Expected 503 to equal 200');
                });
        });

        it('allows the actor to fail the flow with a custom RuntimeError with a custom error message', () => {
            return expect(actorCalled('Enrique').attemptsTo(
                Ensure.that(503, equals(200)).otherwiseFailWith(TestCompromisedError, 'The server is down. Please cheer it up.'),
            )).to.be.rejectedWith(TestCompromisedError, 'The server is down. Please cheer it up.');
        });
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { actorCalled, Answerable, AssertionError } from '@serenity-js/core';
import { given } from 'mocha-testdata';
import { and, Ensure, equals, Expectation, isGreaterThan, isLessThan, or } from '../src';
import { isIdenticalTo, p, q } from './fixtures';

/** @test {Expectation} */
describe('Expectation', () => {

    describe('allows to easily define an assertion, which', () => {

        /**
         * @test {Expectation.that}
         * @test {Ensure.that}
         */
        it('allows the actor flow to continue when the assertion passes', () => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that(4, isIdenticalTo(4)),
            )).to.be.fulfilled;
        });

        /**
         * @test {Expectation.that}
         * @test {Ensure.that}
         */
        it('stops the actor flow when the assertion fails', () => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that(4, isIdenticalTo('4' as any)),
            )).to.be.rejectedWith(AssertionError, "Expected 4 to have value identical to '4'");
        });

        given<Answerable<number>>(
            42,
            p(42),
            q(42),
            q(p(42)),
        ).
        it('allows for the expected value to be defined as any Answerable<T>', (expected: Answerable<number>) => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that(42, isIdenticalTo(expected)),
            )).to.be.fulfilled;
        });
    });

    describe('allows to alias an expectation, so that the alias', () => {

        function isWithin(lowerBound: number, upperBound: number) {
            return Expectation
                .to(`have value within ${ lowerBound } and ${ upperBound }`)
                .soThatActual(and(
                   or(isGreaterThan(lowerBound), equals(lowerBound)),
                   or(isLessThan(upperBound), equals(upperBound)),
                ));
        }

        /** @test {Expectation.to} */
        it('contributes to a human-readable description', () => {
            expect(Ensure.that(5, isWithin(3, 6)).toString())
                .to.equal(`#actor ensures that 5 does have value within 3 and 6`);
        });

        /** @test {Expectation.to} */
        it('provides a precise failure message when the expectation is not met', () => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that(9, isWithin(7, 8)),
            )).to.be.rejectedWith(AssertionError, `Expected 9 to have value that's less than 8 or equal 8`);
        });
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { actorCalled, AssertionError } from '@serenity-js/core';
import { and, endsWith, Ensure, startsWith } from '../../src';

describe('and', () => {

    /** @test {and} */
    it('allows for the actor flow to continue when the "actual" meets all the expectations', () => {
        return expect(actorCalled('Astrid').attemptsTo(
            Ensure.that('Hello World!', and(startsWith('Hello'), endsWith('World!'))),
        )).to.be.fulfilled;
    });

    describe('breaks the actor flow when "actual"', () => {

        /** @test {and} */
        it('does not meet the first expectation', () => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that('Hello World!', and(startsWith('Hola'), endsWith('World!'))),
            )).to.be.rejectedWith(AssertionError, `Expected 'Hello World!' to start with 'Hola'`)
                .then((error: AssertionError) => {
                    expect(error.expected.toString()).to.equal('Hola');
                    expect(error.actual).to.equal('Hello World!');
                });
        });

        /** @test {and} */
        it('does not meet the second expectation', () => {
            return expect(actorCalled('Astrid').attemptsTo(
                Ensure.that('Hello World!', and(startsWith('Hello'), endsWith('Mundo!'))),
            )).to.be.rejectedWith(AssertionError, `Expected 'Hello World!' to end with 'Mundo!`)
                .then((error: AssertionError) => {
                    expect(error.expected.toString()).to.equal('Mundo!');
                    expect(error.actual).to.equal('Hello World!');
                });
        });
    });

    /** @test {and} */
    it('contributes to a human-readable description', () => {
        expect(Ensure.that('Hello', and(startsWith('H'), endsWith('o'))).toString())
            .to.equal(`#actor ensures that 'Hello' does start with 'H' and end with 'o'`);
    });
});

