import { Stage, StageCrewMember } from '@serenity-js/core';
import { AssertionError } from '@serenity-js/core/lib';
import {
    ActivityRelatedArtifactGenerated,
    DomainEvent,
    InteractionFinished,
    InteractionStarts,
    SceneFinished,
    SceneStarts,
    TaskFinished,
    TaskStarts,
    TestRunFinished,
} from '@serenity-js/core/lib/events';
import {
    ActivityDetails,
    AssertionReport,
    Duration,
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
    LogEntry,
    Name,
    Outcome,
    ProblemIndication,
    Timestamp,
} from '@serenity-js/core/lib/model';
import { Instance as ChalkInstance } from 'chalk';
import { ensure, isDefined, isInstanceOf, match } from 'tiny-types';
import { Printer } from './Printer';
import { Summary } from './Summary';
import { SummaryFormatter } from './SummaryFormatter';
import { TerminalTheme, ThemeForDarkTerminals, ThemeForLightTerminals, ThemeForMonochromaticTerminals } from './themes';

/**
 * @desc
 *  Uses [standard output](https://en.wikipedia.org/wiki/Standard_streams)
 *  to report on progress of your Serenity/JS acceptance tests.
 *
 *  `ConsoleReporter` ships with colour themes for both dark and light terminals,
 *  as well as a monochromatic theme for those moments when you're in a noir mood
 *  (or have a terminal that doesn't support colours, like the good old `cmd.exe` on Windows).
 *
 * @example <caption>Registering the reporter programmatically</caption>
 * import { serenity } form '@serenity-js/core';
 * import { ConsoleReporter } form '@serenity-js/console-reporter';
 *
 * serenity.configure({
 *     crew: [ ConsoleReporter.withDefaultColourSupport() ],
 * });
 *
 * @example <caption>Registering the reporter using Protractor configuration</caption>
 * // protractor.conf.js
 * const { ConsoleReporter } = require('@serenity-js/console-reporter');
 *
 * exports.config = {
 *   framework:     'custom',
 *   frameworkPath: require.resolve('@serenity-js/protractor/adapter'),
 *
 *   serenity: {
 *     crew: [
 *       ConsoleReporter.withDefaultColourSupport(),
 *     ],
 *     // other Serenity/JS config
 *   },
 *
 *   // other Protractor config
 * };
 *
 * @public
 * @implements {@serenity-js/core/lib/stage~StageCrewMember}
 */
export class ConsoleReporter implements StageCrewMember {

    private startTimes = new StartTimes();
    private artifacts = new ActivityRelatedArtifacts();
    private summary = new Summary();
    private firstError: FirstError = new FirstError();
    private readonly summaryFormatter: SummaryFormatter;

    /**
     * @desc
     *  Instantiates a `ConsoleReporter` that auto-detects
     *  your terminal's support for colours and use a colour theme
     *  for dark terminals if successful.
     *
     *  Please note that spawning your test process from another process
     *  (by using [npm-failsafe](https://www.npmjs.com/package/npm-failsafe), for example)
     *  causes the `ConsoleReporter` to use the monochromatic colour scheme,
     *  as colour support can't be detected in child processes.
     *
     *  If the above describes your setup, use {@link ConsoleReporter#forDarkTerminals}
     *  or {@link ConsoleReporter#forLightTerminals} to make the sub-process produce colour output.
     *
     * @returns {ConsoleReporter}
     */
    static withDefaultColourSupport() {
        return new ConsoleReporter(
            new Printer(process.stdout),
            new ThemeForDarkTerminals(new ChalkInstance(/* auto-detect */)),
        );
    }

    /**
     * @desc
     *  Instantiates a `ConsoleReporter` with a monochromatic colour theme.
     *  Good for terminals with no colour support (like the `cmd.exe` on Windows),
     *  or for when you need to pipe the output to a text file and want
     *  to avoid printing control characters.
     *
     * @returns {ConsoleReporter}
     */
    static forMonochromaticTerminals() {
        return new ConsoleReporter(
            new Printer(process.stdout),
            new ThemeForMonochromaticTerminals(),
        );
    }

    /**
     * @desc
     *  Instantiates a `ConsoleReporter` with a colour theme optimised for terminals with dark backgrounds.
     *
     * @returns {ConsoleReporter}
     */
    static forDarkTerminals() {
        return new ConsoleReporter(
            new Printer(process.stdout),
            new ThemeForDarkTerminals(new ChalkInstance({ level: 2 })),
        );
    }

    /**
     * @desc
     *  Instantiates a `ConsoleReporter` with a colour theme optimised for terminals with light backgrounds.
     *
     * @returns {ConsoleReporter}
     */
    static forLightTerminals() {
        return new ConsoleReporter(
            new Printer(process.stdout),
            new ThemeForLightTerminals(new ChalkInstance({ level: 2 })),
        );
    }

    /**
     * @param {Printer} printer
     * @param {TerminalTheme} theme
     * @param {@serenity-js/core/lib/stage~Stage} stage
     */
    constructor(
        private readonly printer: Printer,
        private readonly theme: TerminalTheme,
        private readonly stage: Stage = null,
    ) {
        ensure('printer', printer, isDefined());
        ensure('theme', theme, isDefined());

        this.summaryFormatter = new SummaryFormatter(this.theme);
    }

    /**
     * @desc
     *  Creates a new instance of this {@link @serenity-js/core/lib/stage~StageCrewMember}
     *  and assigns it to a given {@link @serenity-js/core/lib/stage~Stage}.
     *
     * @see {@link @serenity-js/core/lib/stage~StageCrewMember}
     *
     * @param {@serenity-js/core/lib/stage~Stage} stage - An instance of a {@link @serenity-js/core/lib/stage~Stage} this {@link @serenity-js/core/lib/stage~StageCrewMember} will be assigned to
     * @returns {@serenity-js/core/lib/stage~StageCrewMember} - A new instance of this {@link @serenity-js/core/lib/stage~StageCrewMember}
     */
    assignedTo(stage: Stage) {
        return new ConsoleReporter(this.printer, this.theme, stage);
    }

    /**
     * @desc
     *  Handles {@link @serenity-js/core/lib/events~DomainEvent} objects emitted by the {@link @serenity-js/core/lib/stage~StageCrewMember}.
     *
     * @see {@link @serenity-js/core/lib/stage~StageCrewMember}
     *
     * @param {@serenity-js/core/lib/events~DomainEvent} event
     * @returns {void}
     */
    notifyOf(event: DomainEvent): void {
        match(event)
            .when(SceneStarts, (e: SceneStarts) => {

                this.firstError = new FirstError();
                this.startTimes.recordStartOf(e);

                // Print scenario header
                this.printer.println(this.theme.separator('-'));
                this.printer.println(e.value.location.path.value, e.value.location.line ? `:${ e.value.location.line }` : '');
                this.printer.println();
                this.printer.println(this.theme.heading(e.value.category.value, ': ', e.value.name.value));
                this.printer.println();

            })
            .when(TaskStarts, (e: TaskStarts) => {

                this.printer.indent();

                if (! this.firstError.alreadyRecorded()) {
                    this.printer.println(e.value.name.value);
                }

            })
            .when(InteractionStarts, (e: InteractionStarts) => {

                this.startTimes.recordStartOf(e);

            })
            .when(InteractionFinished, (e: InteractionFinished) => {

                this.printer.indent();
                this.printer.println(this.formattedOutcome(e));

                this.printer.indent();

                if (e.outcome instanceof ProblemIndication) {
                    this.firstError.recordIfNeeded(e.outcome.error);

                    if (! (e.outcome.error instanceof AssertionError)) {
                        this.printer.println(this.theme.outcome(e.outcome, e.outcome.error.toString()));
                    }
                }

                const artifacts = this.artifacts.recordedFor(e.value);

                if (artifacts.filter(a => a instanceof AssertionReport || a instanceof LogEntry).length > 0) {
                    this.printer.println();
                }

                artifacts.forEach(evt => {
                    if (evt.artifact instanceof AssertionReport) {
                        const details = evt.artifact.map(
                            (artifactContents: { expected: string, actual: string }) =>
                                this.theme.diff(
                                    artifactContents.expected,
                                    artifactContents.actual,
                                ),
                            );

                        this.printer.println();

                        this.printer.println(details);

                        this.printer.println();
                    }

                    if (evt.artifact instanceof LogEntry) {
                        const details = evt.artifact.map((artifactContents: { data: string }) => artifactContents.data);

                        if (evt.name.value !== details) {
                            this.printer.println(this.theme.log(evt.name.value, ':'));
                        }

                        this.printer.println(details);
                        this.printer.println();
                    }
                });

                this.printer.outdent();

                this.printer.outdent();

            })
            .when(ActivityRelatedArtifactGenerated, (e: ActivityRelatedArtifactGenerated) => {

                this.artifacts.record(e);

            })
            .when(TaskFinished, (e: TaskFinished) => {

                this.printer.outdent();

                if (e.outcome instanceof ProblemIndication) {
                    this.printer.indent();
                    this.printer.indent();

                    if (! this.firstError.alreadyRecorded()) {
                        this.printer.println(this.theme.outcome(e.outcome, this.iconFrom(e.outcome), e.outcome.error.toString()));
                    }

                    this.printer.outdent();
                    this.printer.outdent();

                    this.firstError.recordIfNeeded(e.outcome.error);
                }

                else if (! (e.outcome instanceof ExecutionSuccessful)) {
                    this.printer.indent();
                    this.printer.println(this.iconFrom(e.outcome), e.value.name.value);

                    this.printer.outdent();
                }

            })
            .when(SceneFinished, (e: SceneFinished) => {

                this.summary.record(e.value, e.outcome, this.startTimes.eventDurationOf(e));

                this.printer.println();
                this.printer.println(this.theme.outcome(e.outcome, this.formattedOutcome(e, this.deCamelCased(e.outcome.constructor.name))));

                if (e.outcome instanceof ProblemIndication) {

                    this.printer.println();
                    this.printer.indent();

                    this.printer.println(e.outcome.error.stack);

                    this.printer.outdent();
                }

            })
            .when(TestRunFinished, (e: TestRunFinished) => {
                this.printer.println(this.theme.separator('='));

                this.printer.print(this.summaryFormatter.format(this.summary.aggregated()));

                this.printer.println(this.theme.separator('='));
            })
            .else((e: DomainEvent) => {
                return void 0;
            });
    }

    private formattedOutcome(event: IdentifiableEvent & { outcome: Outcome }, description: string = event.value.name.value) {
        const
            icon = `${ this.iconFrom(event.outcome) }`,
            message = `${ description } (${ this.startTimes.eventDurationOf(event) })`;

        return (event.outcome instanceof ProblemIndication)
            ? this.theme.outcome(event.outcome, `${icon}${message}`)
            : `${ this.theme.outcome(event.outcome, icon) }${ message }`;
    }

    private deCamelCased(name: string) {
        const deCamelCased = name.replace(/([^A-Z])([A-Z])/g, '$1 $2');

        return deCamelCased.charAt(0).toUpperCase() + deCamelCased.slice(1).toLocaleLowerCase();
    }

    private iconFrom(outcome: Outcome): string {
        switch (outcome.constructor) {
            case ExecutionCompromised:
            case ExecutionFailedWithError:
            case ExecutionFailedWithAssertionError:
                return ' ';
            case ImplementationPending:
                return '';
            case ExecutionSkipped:
                return ' ';
            case ExecutionIgnored:
                return '? ';
            case ExecutionSuccessful:
                return ' ';
            default:
                return '';
        }
    }
}

interface IdentifiableEvent {
    value: { name: Name, toString(): string };
    timestamp: Timestamp;
}

class StartTimes {
    private times: { [correlationId: string]: Timestamp } = {};

    recordStartOf(event: IdentifiableEvent) {
        this.times[event.value.toString()] = event.timestamp;
    }

    eventDurationOf(event: IdentifiableEvent & { outcome: Outcome }): Duration {
        return event.timestamp.diff(this.times[event.value.toString()]);
    }
}

class FirstError {
    private error: Error;

    recordIfNeeded(error: Error) {
        if (! this.error) {
            this.error = error;
        }
    }

    alreadyRecorded(): boolean {
        return !! this.error;
    }

    get(): Error {
        return this.error;
    }
}

class ActivityRelatedArtifacts {

    private readonly events: ActivityRelatedArtifactGenerated[] = [];

    record(event: ActivityRelatedArtifactGenerated) {
        this.events.push(event);
    }

    recordedFor(details: ActivityDetails): ActivityRelatedArtifactGenerated[] {
        return this.events
            .filter(event => event.details.equals(details));
    }
}

import WritableStream = NodeJS.WritableStream;

/**
 * @desc
 *  A simple abstraction around the standard output stream
 *  with support for indenting the printed text.
 *
 * @public
 */
export class Printer {
    private indentation = new Indentation(2);

    /**
     * @param {WritableStream} stdout
     */
    constructor(private readonly stdout: WritableStream = process.stdout) {
    }

    /**
     * @desc
     *  Prints the `args` to the `stdout`, followed by a new line character (`\n`).
     *
     *  Please note that this method bypasses any indentation as it's intended
     *  to be used to print separator lines, etc.
     *
     * @param {...any[]} args
     *
     * @returns {void}
     */
    println(...args: any[]) {
        return this.print(...args, '\n');
    }

    /**
     * @desc
     *  Prints the `args` to the `stdout`, indenting the output according to
     *  the current indentation level.
     *
     * @param {...any[]} args
     *
     * @returns {void}
     */
    print(...args: any[]) {
        this.stdout.write(
            this.indentation.indented(`${ args.map(String).join('') }`),
        );
    }

    /**
     * @desc
     *  Increases the current indentation by 2 spaces.
     *
     * @returns {void}
     */
    indent() {
        this.indentation.increase();
    }

    /**
     * @desc
     *  Reduces the current indentation by 2 spaces.
     *
     * @returns {void}
     */
    outdent() {
        this.indentation.decrease();
    }
}

/**
 * @package
 */
class Indentation {
    private current = 0;

    constructor(private readonly step: number) {
    }

    increase() {
        this.current += this.step;
    }

    decrease() {
        if (this.current - this.step >= 0) {
            this.current -= this.step;
        }
    }

    indented(...fragments: string[]) {
        return fragments.join('')
            .split('\n')
            .map(line => !! line ? ' '.repeat(this.current) + line : '')
            .join('\n');
    }
}

import { Duration } from '@serenity-js/core';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError, ExecutionIgnored,
    ExecutionSkipped, ExecutionSuccessful,
    ImplementationPending,
    Outcome,
    ScenarioDetails,
} from '@serenity-js/core/lib/model';

/**
 * @package
 */
export class Summary {
    private readonly records: SummaryRecord[] = [];

    record(details: ScenarioDetails, outcome: Outcome, duration: Duration) {
        this.records.push({ details, outcome, duration });
    }

    aggregated(): AggregatedCategories {
        return this.records.reduce((acc: AggregatedCategories, record) => {
            acc.totalTime = acc.totalTime.plus(record.duration);

            const categoryName = record.details.category.value;

            if (! acc.categories[categoryName]) {
                acc.categories[categoryName] = {
                    name: categoryName,
                    outcomes: {
                        [ExecutionCompromised.name]:                { count: 0, code: ExecutionCompromised.Code                 },
                        [ExecutionFailedWithError.name]:            { count: 0, code: ExecutionFailedWithError.Code             },
                        [ExecutionFailedWithAssertionError.name]:   { count: 0, code: ExecutionFailedWithAssertionError.Code    },
                        [ImplementationPending.name]:               { count: 0, code: ImplementationPending.Code                },
                        [ExecutionSkipped.name]:                    { count: 0, code: ExecutionSkipped.Code                     },
                        [ExecutionIgnored.name]:                    { count: 0, code: ExecutionIgnored.Code                     },
                        [ExecutionSuccessful.name]:                 { count: 0, code: ExecutionSuccessful.Code                  },
                    },
                    totalTime: Duration.ofMilliseconds(0),
                };
            }

            acc.categories[categoryName].outcomes[record.outcome.constructor.name].count ++;

            acc.categories[categoryName].totalTime = acc.categories[categoryName].totalTime.plus(record.duration);

            return acc;
        }, { categories: {}, totalTime: Duration.ofMilliseconds(0), numberOfScenarios: this.records.length });
    }
}

/**
 * @package
 */
export interface AggregatedCategories {
    categories: {[categoryName: string]: AggregatedCategory};
    totalTime: Duration;
    numberOfScenarios: number;
}

/**
 * @package
 */
export interface AggregatedCategory {
    name: string;
    outcomes: {[outcomeType: string]: { count: number, code: number }};
    totalTime: Duration;
}

interface SummaryRecord {
    details: ScenarioDetails;
    outcome: Outcome;
    duration: Duration;
}

import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
} from '@serenity-js/core/lib/model';
import { AggregatedCategories, AggregatedCategory } from './Summary';
import { TerminalTheme } from './themes';

/**
 * @package
 */
export class SummaryFormatter {
    constructor(private readonly theme: TerminalTheme) {
    }

    format(aggregatedCategories: AggregatedCategories): string {
        const
            categoryNames                   = this.sorted(Object.keys(aggregatedCategories.categories)),
            maxCategoryNameLength           = Math.max(...(categoryNames.map(categoryName => categoryName.length))),
            maxCategoryNameLengthAllowed    = Math.min(Math.max(maxCategoryNameLength, 10), 30);

        return [
            this.theme.heading('Execution Summary'),
            '',
            ...categoryNames.map(categoryName => {
                const displayName = (categoryName.length > maxCategoryNameLengthAllowed
                    ? categoryName.substring(0, maxCategoryNameLengthAllowed - 4) + `...:`
                    : `${ categoryName }:`).padEnd(maxCategoryNameLengthAllowed + 1);

                return `${ this.theme.heading(displayName) } ${ this.formatTotalsFor(aggregatedCategories.categories[categoryName]) }`;
            }),
            ``,
            `Total time: ${ this.theme.heading(aggregatedCategories.totalTime) }`,
            `Scenarios:  ${ this.theme.heading(aggregatedCategories.numberOfScenarios) }`,
            '',
        ].join('\n');
    }

    private sorted(strings: string[]) {
        return strings.sort((a, b) => a.localeCompare(b, undefined, { sensitivity: 'base' }));
    }

    private formatTotalsFor(category: AggregatedCategory): string {
        const outcomes = Object.keys(category.outcomes)
            .filter(outcomeName => category.outcomes[outcomeName].count > 0)
            .sort((left, right) => category.outcomes[left].code > category.outcomes[right].code ? 1 : -1)
            .reduce((acc, outcomeName) => {
                acc.push(this.theme.outcome(outcomeName, `${category.outcomes[outcomeName].count} ${this.nameOf(outcomeName)}`));
                return acc;
            } , [])
            .join(', ');

        const total = Object.keys(category.outcomes).map(outcomeName => category.outcomes[outcomeName].count).reduce((acc, count) => acc + count, 0);

        return `${ outcomes }, ${ total } total (${ category.totalTime })`;
    }

    private nameOf(outcomeName: string) {
        switch (outcomeName) {
            case ExecutionCompromised.name:                 return 'compromised';
            case ExecutionFailedWithError.name:             return 'broken';
            case ExecutionFailedWithAssertionError.name:    return 'failed';
            case ImplementationPending.name:                return 'pending';
            case ExecutionSkipped.name:                     return 'skipped';
            case ExecutionIgnored.name:                     return 'ignored';
            case ExecutionSuccessful.name:                  return 'successful';
        }
    }
}

import { AssertionReportDiffer } from '@serenity-js/core/lib/io';
import { Outcome } from '@serenity-js/core/lib/model';

/**
 * @desc
 *  Decorates text with control characters to make the terminal
 *  print output in colour.
 *
 * @public
 * @abstract
 */
export abstract class TerminalTheme {
    abstract heading(...parts: any[]): string;
    abstract outcome(outcome: Outcome | string, ...parts: any[]): string;
    abstract separator(pattern: string): string;
    abstract diff(expected: string, actual: string): string;
    abstract log(...parts: any[]): string;

    /**
     * @param {@serenity-js/core/lib/io~AssertionReportDiffer} differ
     */
    constructor(protected readonly differ: AssertionReportDiffer) {
    }

    /**
     * @desc
     *  Converts the `parts` to `string` and joins them together.
     *
     * @protected
     *
     * @param {any[]} parts
     *
     * @returns {string}
     */
    protected joined(parts: any[]): string {
        return parts.map(String).join('');
    }

    /**
     * @desc
     *  Repeats a given `pattern` so that it takes up to `maxLength` characters.
     *  Used to produce separator lines.
     *
     * @protected
     *
     * @param {string} pattern
     * @param {number} [maxLength=80] maxLength
     *
     * @returns {string}
     */
    protected repeat(pattern: string, maxLength = 80) {
        if (! pattern) {
            return '';
        }

        const repetitions = Math.floor(maxLength / pattern.length);

        return pattern.repeat(repetitions);
    }
}

import { AssertionReportDiffer } from '@serenity-js/core/lib/io';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
    Outcome,
} from '@serenity-js/core/lib/model';
import { Chalk } from 'chalk';
import { TerminalTheme } from './TerminalTheme';

/**
 * @desc
 *  Base class for {@link TerminalTheme} implementations intended
 *  to print to terminals that support colour output.
 *
 * @extends {TerminalTheme}
 *
 * @public
 * @abstract
 */
export abstract class ThemeForColourTerminals extends TerminalTheme {

    /**
     * @param {chalk~Chalk} chalk
     *
     * @see https://www.npmjs.com/package/chalk
     */
    constructor(protected readonly chalk: Chalk) {
        super(new AssertionReportDiffer({
            expected: line => this.chalk.green(`+ ${ line }`),
            actual:   line => this.chalk.red(`- ${ line }`),
            matching: line => `  ${ line }`,
        }));
    }

    /**
     * @desc
     *  Joins the `parts` into a single string and decorates it
     *  using a colour appropriate for a given {@link @serenity-js/core/lib/model~Outcome}.
     *
     * @param {@serenity-js/core/lib/model~Outcome | string} outcome
     *  an instance of an {@link @serenity-js/core/lib/model~Outcome}
     *  or a string class name of one of its implementations.
     *
     * @param {...any[]} parts
     *  the parts of the message to be decorated
     *
     * @returns {string}
     */
    outcome(outcome: Outcome | string, ...parts: any[]): string {
        const outcomeName = (outcome instanceof Outcome)
            ? outcome.constructor.name
            : outcome;

        switch (outcomeName) {
            case ExecutionCompromised.name:
                return this.chalk.magenta(this.joined(parts));
            case ExecutionFailedWithError.name:
            case ExecutionFailedWithAssertionError.name:
                return this.chalk.red(this.joined(parts));
            case ImplementationPending.name:
                return this.chalk.blue(this.joined(parts));
            case ExecutionSkipped.name:
            case ExecutionIgnored.name:
                return this.chalk.blackBright(this.joined(parts));
            case ExecutionSuccessful.name:
                return this.chalk.green(this.joined(parts));
        }
    }

    /**
     * @desc
     *  Turns the serialised `expectedValue` and `actualValue` into
     *  a visual diff, so that it's easier for the developer to spot
     *  the difference between the two values.
     *
     * @param {string} expectedValue
     * @param {string} actualValue
     *
     * @returns {string}
     */
    diff(expectedValue: string, actualValue: string): string {
        return this.differ.diff(expectedValue, actualValue);
    }
}

import { Chalk } from 'chalk';
import { ThemeForColourTerminals } from './ThemeForColourTerminals';

/**
 * @desc
 *  A simple colour theme for terminals with dark backgrounds.
 *
 * @extends {ThemeForColourTerminals}
 *
 * @public
 */
export class ThemeForDarkTerminals extends ThemeForColourTerminals {

    /**
     * @param {chalk~Chalk} chalk
     *
     * @see https://www.npmjs.com/package/chalk
     */
    constructor(chalk: Chalk) {
        super(chalk);
    }

    /**
     * @desc
     *  Formats the heading
     *
     * @param {...any[]} parts
     *  `parts` to be converted to string, joined together, and formatted as a heading
     *
     * @returns {string}
     */
    heading(...parts: any[]): string {
        return this.chalk.bold.white(this.joined(parts));
    }

    /**
     * @desc
     *  Decorates the heading with theme colours.
     *
     * @param {string} pattern
     *  The pattern to be repeated to create a separator, for example `-`, ` - - `, etc.
     *
     * @returns {string}
     */
    separator(pattern: string): string {
        return this.chalk.yellow(this.repeat(pattern));
    }

    /**
     * @desc
     *  Decorates the log entries that the developer wanted to have captured in the output.
     *
     * @param {...any[]} parts
     *
     * @returns {string}
     */
    log(...parts): string {
        return this.chalk.yellow(this.joined(parts));
    }
}

import { Chalk } from 'chalk';
