/**
 * @desc
 *  A simple colour theme for terminals with light backgrounds.
 *
 * @extends {ThemeForColourTerminals}
 *
 * @public
 */
export class ThemeForLightTerminals extends ThemeForColourTerminals {

    /**
     * @param {chalk~Chalk} chalk
