import { Change, diffLines } from 'diff';

export class AssertionReportDiffer {
    constructor(private readonly mappers: AssertionReportDiffMappers) {
    }

    diff(expectedValue: string, actualValue: string): string {
        const changes = diffLines(actualValue, expectedValue)
            .map(change => this.markChanges(change));

        return [
            `Difference (${ this.mappers.expected('expected') }, ${ this.mappers.actual('actual') }):`,
            '',
            changes.length === 2
                ? changes.join('\n')
                : changes.join(''),
        ].join('\n');
    }

    private markChanges(change: Change): string {

        return !! change.added
            ? this.eachLineOf(change.value, this.mappers.expected)
            : change.removed
                ? this.eachLineOf(change.value, this.mappers.actual)
                : this.eachLineOf(change.value, this.mappers.matching);
    }

    private eachLineOf(lines: string, mapper: (line: string) => string): string {
        return lines.split('\n').map(line => !! line.trim() ? mapper(line) : line).join('\n');
    }
}

export interface AssertionReportDiffMappers {
    expected:   (line: string) => string;
    actual:     (line: string) => string;
    matching:   (line: string) => string;
}

import { JSONObject } from 'tiny-types';
import * as serenitySpecificErrors from '../errors';
import { parse, stringify } from './json';

/**
 *
 * @extends {tiny-types~JSONObject}
 * @public
 */
export interface SerialisedError extends JSONObject {
    /**
     *  Name of the constructor function used to instantiate
     *  the original {@link Error} object.
     */
    name:    string;

    /**
     *  Message of the original {@link Error} object
     */
    message: string;

    /**
     *  Stack trace of the original {@link Error} object
     */
    stack:   string;
}

export class ErrorSerialiser {
    private static recognisedErrors = [
        ...Object.keys(serenitySpecificErrors).map(key => serenitySpecificErrors[key]),
        Error,
        EvalError,
        RangeError,
        ReferenceError,
        SyntaxError,
        TypeError,
        URIError,
    ];

    static serialise(error: Error): string {
        const serialisedError = Object.getOwnPropertyNames(error).reduce((serialised, key) => {
            serialised[key] = error[key]
            return serialised;
        }, { name: error.constructor.name || error.name }) as SerialisedError;

        return stringify(serialisedError);
    }

    static deserialise<E extends Error>(stringifiedError: string): E {
        const serialisedError = parse(stringifiedError) as SerialisedError;

        // todo: de-serialise the cause map well
        const constructor = ErrorSerialiser.recognisedErrors.find(errorType => errorType.name === serialisedError.name) || Error;
        const deserialised = Object.create(constructor.prototype);
        for (const prop in serialisedError) {
            if (serialisedError.hasOwnProperty(prop)) {
                deserialised[prop] = serialisedError[prop];
            }
        }
        return deserialised;
    }

    static deserialiseFromStackTrace(stack: string) {
        const lines = stack.split('\n');

        const pattern = /^([^\s:]*Error):\s(.*)$/;
        if (! pattern.test(lines[0])) {
            return new Error(stack);
        }

        const [, name, message ] = lines[0].match(pattern);

        return ErrorSerialiser.deserialise(stringify({ name, message, stack }));
    }
}

