const Module = require('module');             // tslint:disable-line:no-var-requires     No type definitions available
import * as path from 'path';
import { Version } from './Version';

/**
 * @package
 */
export class ModuleLoader {
    constructor(public readonly cwd: string) {
    }

    /**
     * @desc
     *  Returns true if a given module is available to be required, false otherwise.
     *
     * @param {string} moduleId
     *  NPM module id, for example 'cucumber' or '@serenity-js/core'
     */
    hasAvailable(moduleId: string): boolean {
        try {
            return !! this.require(moduleId);
        } catch (e) {
            return false;
        }
    }

    /**
     * @package
     *
     * @param moduleId
     *  NPM module id, for example 'cucumber' or '@serenity-js/core'
     */
    resolve(moduleId: string): string {
        const fromFile = path.join(this.cwd, 'noop.js');

        return Module._resolveFilename(moduleId, {
            id: fromFile,
            filename: fromFile,
            paths: Module._nodeModulePaths(this.cwd),
        });
    }

    require(moduleId: string): any {
        try {
            return require(this.resolve(moduleId));
        }
        catch (e) {
            return require(moduleId);
        }
    }

    versionOf(moduleId: string): Version {
        return new Version(this.require(`${ moduleId }/package`).version);
    }
}

import { ensure, isDefined, isGreaterThan, property, TinyType } from 'tiny-types';

import filenamify = require('filenamify');
import path = require('upath');

export class Path extends TinyType {
    private static readonly Separator = '/';
    public readonly value: string;

    static fromJSON(v: string) {
        return new Path(v);
    };

    static from(...segments: string[]) {
        return new Path(path.joinSafe(...segments));
    }

    static fromSanitisedString(value: string) {
        const
            normalised = path.normalize(value).replace(/[\/\\"':]/gi, ''),
            extension  = path.extname(normalised),
            basename   = path.basename(normalised, extension),
            filename   = filenamify(basename, { replacement: '-', maxLength: 250 })
                .trim()
                .replace(/[\s-]+/g, '-');

        return new Path(path.join(
            path.dirname(normalised),
            `${ filename }${ extension }`,
        ));
    }

    constructor(value: string) {
        super();
        ensure(Path.name, value, isDefined(), property('length', isGreaterThan(0)));

        this.value = path.normalize(value);
    }

    join(another: Path) {
        return new Path(path.join(this.value, another.value));
    }

    split(): string[] {
        return this.value
            .split(Path.Separator)
            .filter(segment => !! segment); // so that we ignore the trailing path separator in absolute paths
    }

    resolve(another: Path) {
        return new Path(path.resolve(this.value, another.value));
    }

    directory() {
        return new Path(path.dirname(this.value));
    }

    basename(): string {
        return path.basename(this.value);
    }

    isAbsolute(): boolean {
        return path.isAbsolute(this.value);
    }

    root(): Path {
        return new Path(path.parse(this.value).root);
    }
}

import semver = require('semver');
import { ensure, isDefined, isString, Predicate, TinyType } from 'tiny-types';

export class Version extends TinyType {

    static fromJSON(version: string) {
        return new Version(version);
    }

    constructor(private readonly version: string) {
        super();
        ensure('version', version, isDefined(), isString(), isValid());
    }

    isAtLeast(other: Version) {
        return semver.gte(this.version, other.version);
    }

    major(): number {
        return Number(this.version.split('.')[0]);
    }

    toString() {
        return `${ this.version }`;
    }
}

function isValid(): Predicate<string> {
    return Predicate.to(`be a valid version number`, (version: string) =>
        !! semver.valid(version),
    );
}

/**
 * @desc
 *  Describes a collection providing
 *  a [`map`-like interface](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/map).
 *
 * @interface
 *
 * @see {@link Question#map}
 * @see {@link ElementArrayFinder}
 */
export interface Mappable<Item> {

    /**
     * @desc
     *  Applies a {@link MappingFunction} function to each element of a {@link Mappable} collection.
     *
     *  The callback receives an item from the collection as the first argument
     *  and its index as the second argument.
     *
     * @abstract
     *
     * @type {function<U>(callback: (item?: Item, index?: number) => U): PromiseLike<U[]> | U[]}
     */
    map: <U>(callback: (item?: Item, index?: number) => U) =>
        PromiseLike<U[]> | U[];
}

/**
 * @desc
 *  A mapping function converting one type into another.
 *
 * @public
 *
 * @typedef {function(item?: V, index?: number) => Promise<O> | O} Mapping<V,O>
 */
export type MappingFunction<V, O> =
    (item?: V, index?: number) => O;

/**
 * @desc
 *  Checks if the value is a {@link Mappable} collection of items.
 *
 * @example <caption>An Array</caption>
 *  import { Mappable } from '@serenity-js/core/lib/io';
 *
 *  Mappable.isMappable([ 1, 2, 3 ]) === true
 *
 * @example <caption>Protractor's ElementArrayFinder</caption>
 *  import { Mappable } from '@serenity-js/core/lib/io';
 *  import { element } from 'protractor';
 *
 *  Mappable.isMappable(element.all(by.tagName('li')) === true
 *
 * @param {Mappable<Item> | any} maybeCollection
 * @returns {boolean}
 */
export function isMappable<Item>(maybeCollection: Mappable<Item> | any): maybeCollection is Mappable<Item> {
    return !! maybeCollection
        && !! maybeCollection.map
        && typeof maybeCollection.map === 'function';
}

/**
 * @desc
 *  Describes a collection providing
 *  a [`reduce`-like interface](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/reduce).
 *
 * @interface
 *
 * @see {@link ElementArrayFinder}
 * @see {@link Loop}
 */
export interface Reducible {
    /**
     * @type {function<T,A>(callback: (accumulator: A, currentValue: T, index: number) => A, initialValue: A): PromiseLike<A> | A}
     */
    reduce: <T, A>(fn: (accumulator: A, currentValue: T, index: number) => A, initialValue: A) => PromiseLike<A> | A;
}

/**
 * @desc
 *  Produces a comma-separated list based on the list provided.
 *
 * @param {Array<string>} list
 * @param {function(item: string): string} map
 * @param {string} [acc=''] acc
 *
 * @returns {string}
 */
export function commaSeparated(
    list: Array<string | { toString: () => string }>,
    map = item => `${ item }`.trim(),
    acc: string = '',
): string {
    switch (list.length) {
        case 0:     return acc;
        case 1:     return commaSeparated(tail(list), map, `${ acc }${ map(head(list)) }`);
        case 2:     return commaSeparated(tail(list), map, `${ acc }${ map(head(list)) } and `);
        default:    return commaSeparated(tail(list), map, `${ acc }${ map(head(list)) }, `);
    }
}

/** @package */
function head<T>(list: T[]): T {
    return list[0];
}

/** @package */
function tail<T>(list: T[]): T[] {
    return list.slice(1);
}

import { Answerable } from '../screenplay/Answerable';
import { inspected } from './inspected';

/**
 * @desc
 *  A tag function returning a human-readable description of a template containing one or more {@link Answerable}s.
 *
 * @link https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Template_literals#Tagged_template_literals
 *
 * @param {TemplateStringsArray} templates
 * @param {Array<Answerable<any>>} placeholders
 * @returns {string}
 */
export function formatted(templates: TemplateStringsArray, ...placeholders: Array<Answerable<any>>) {
    const compacted = (multiline: string) => multiline.replace(/\r?\n/g, ' ').replace(/\s+/g, ' ');

    return templates
        .map((template, i) => i < placeholders.length
            ? [ template, compacted(inspected(placeholders[i])) ]
            : [ template ])
        .reduce((acc, tuple) => acc.concat(tuple))
        .join('');
}

