import { inspect } from 'util';
import { Answerable } from '../screenplay/Answerable';
import { Question } from '../screenplay/Question';

/**
 * @desc
 *  Provides a human-readable description of the {@link Answerable<T>}.
 *  Similar to {@link util~inspect}.
 *
 * @public
 * @param {Answerable<any>} value
 * @return {string}
 */
export function inspected(value: Answerable<any>): string {
    if (! isDefined(value)) {
        return inspect(value);
    }

    if (Array.isArray(value)) {
        return [
            '[',
            value.map(item => `  ${ inspected(item) }`).join(',\n'),
            ']',
        ].join('\n');
    }

    if (isAPromise(value)) {
        return `a Promise`;
    }

    if (Question.isAQuestion(value)) {
        return value.toString();
    }

    if (isADate(value)) {
        return value.toISOString();
    }

    if (hasItsOwnToString(value)) {
        return value.toString();
    }

    if (isInspectable(value)) {
        return value.inspect();
    }

    if (isANamedFunction(value)) {
        return `${ value.name } property`;
    }

    if (! hasCustomInspectionFunction(value) && isPlainObject(value) && isSerialisableAsJSON(value)) {
        return JSON.stringify(value, null, 4);
    }

    return inspect(value, { breakLength: Infinity, compact: true, sorted: false  });
}

/**
 * @desc
 * Checks if the value is defined
 *
 * @private
 * @param {Answerable<any>} v
 */
function isDefined(v: Answerable<any>) {
    return !! v;
}

/**
 * @desc
 * Checks if the value defines its own `toString` method
 *
 * @private
 * @param {Answerable<any>} v
 */
function hasItsOwnToString(v: Answerable<any>): v is { toString: () => string } {
    return typeof v === 'object'
        && !! (v as any).toString
        && typeof (v as any).toString === 'function'
        && ! isNative((v as any).toString);
}

/**
 * @desc
 * Checks if the value defines its own `inspect` method
 *
 * @private
 * @param {Answerable<any>} v
 */
function isInspectable(v: Answerable<any>): v is { inspect: () => string } {
    return !! (v as any).inspect && typeof (v as any).inspect === 'function';
}

/**
 * @desc
 * Checks if the value is a {@link Date}
 *
 * @private
 * @param {Answerable<any>} v
 */
function isADate(v: Answerable<any>): v is Date {
    return v instanceof Date;
}

/**
 * @desc
 * Checks if the value is a {@link Promise}
 *
 * @private
 * @param {Answerable<any>} v
 */
function isAPromise<T>(v: Answerable<T>): v is Promise<T> {
    return !! (v as any).then;
}

/**
 * @desc
 * Checks if the value is a named {@link Function}
 *
 * @private
 * @param {Answerable<any>} v
 */
function isANamedFunction<T>(v: any): v is { name: string } {
    return {}.toString.call(v) === '[object Function]' && (v as any).name !== '';
}

/**
 * @desc
 * Checks if the value defines its own `inspect` method
 * See: https://nodejs.org/api/util.html#util_util_inspect_custom
 *
 * @private
 * @param {Answerable<any>} v
 */
function hasCustomInspectionFunction(v: Answerable<any>): v is object {
    return v && v[Symbol.for('nodejs.util.inspect.custom')];
}

/**
 * @desc
 * Checks if the value has a good chance of being a plain JavaScript object
 *
 * @private
 * @param {Answerable<any>} v
 */
function isPlainObject(v: Answerable<any>): v is object {

    // Basic check for Type object that's not null
    if (typeof v === 'object' && v !== null) {

        // If Object.getPrototypeOf supported, use it
        if (typeof Object.getPrototypeOf === 'function') {
            const proto = Object.getPrototypeOf(v);
            return proto === Object.prototype || proto === null;
        }

        // Otherwise, use internal class
        // This should be reliable as if getPrototypeOf not supported, is pre-ES5
        return Object.prototype.toString.call(v) === '[object Object]';
    }

    // Not an object
    return false;
}

/**
 * @desc
 * Checks if the value is a JSON object that can be stringified
 *
 * @private
 * @param {Answerable<any>} v
 */
function isSerialisableAsJSON<T>(v: any): v is object {
    try {
        JSON.stringify(v);

        return true;
    } catch (e) {
        return false;
    }
}

/**
 * https://davidwalsh.name/detect-native-function
 * @param {any} v
 */
function isNative(v: any): v is Function {      // tslint:disable-line:ban-types

    const
        toString        = Object.prototype.toString,       // Used to resolve the internal `[[Class]]` of values
        fnToString      = Function.prototype.toString,   // Used to resolve the decompiled source of functions
        hostConstructor = /^\[object .+?Constructor\]$/; // Used to detect host constructors (Safari > 4; really typed array specific)

    // Compile a regexp using a common native method as a template.
    // We chose `Object#toString` because there's a good chance it is not being mucked with.
    const nativeFnTemplate = RegExp(
        '^' +
        // Coerce `Object#toString` to a string
        String(toString)
        // Escape any special regexp characters
            .replace(/[.*+?^${}()|[\]\/\\]/g, '\\$&')
            // Replace mentions of `toString` with `.*?` to keep the template generic.
            // Replace thing like `for ...` to support environments like Rhino which add extra info
            // such as method arity.
            .replace(/toString|(function).*?(?=\\\()| for .+?(?=\\\])/g, '$1.*?') +
        '$',
    );

    const type = typeof v;
    return type === 'function'
        // Use `Function#toString` to bypass the value's own `toString` method
        // and avoid being faked out.
        ? nativeFnTemplate.test(fnToString.call(v))
        // Fallback to a host object check because some environments will represent
        // things like typed arrays as DOM methods which may not conform to the
        // normal native pattern.
        : (v && type === 'object' && hostConstructor.test(toString.call(v))) || false;
}

// Based on work of Douglas Crockford
//  https://github.com/jan-molak/JSON-js/blob/master/cycle.js

/**
 * @desc
 *  Converts a JavaScript value to a JavaScript Object Notation (JSON) string.
 *  Supports objects with cyclic references.
 *
 * @param {any} value
 *  A JavaScript value, usually an object or array, to be converted.
 *
 * @param {function(this: any, key: string, value: any): any} [replacer]
 *  A function that transforms the results.
 *
 * @param {string | number} [space]
 *  Adds indentation, white space, and line break characters to the return-value JSON text to make it easier to read.
 */
export function stringify<T>(value: any, replacer?: (this: any, key: string, value: any) => any, space?: string | number): string {
    return JSON.stringify(decycle(value), replacer, space);
}

/**
 * @desc
 *  Converts a JavaScript Object Notation (JSON) string into an object.
 *  Supports objects with cyclic references.
 *
 * @param text A valid JSON string.
 * @param reviver A function that transforms the results. This function is called for each member of the object.
 *   If a member contains nested objects, the nested objects are transformed before the parent object is.
 */
export function parse(text: string, reviver?: (this: any, key: string, value: any) => any) {
    return retrocycle(JSON.parse(text, reviver));
}

/**
 * @desc
 *  Makes a deep copy of an object or array, assuring that there is at most
 *  one instance of each object or array in the resulting structure. The
 *  duplicate references (which might be forming cycles) are replaced with
 *  an object of the form:
 *
 *  ```
 *      {"$ref": PATH}
 *  ```
 *
 *  where the PATH is a JSONPath string that locates the first occurrence.
 *
 *  So,
 *  ```
 *      var a = [];
 *      a[0] = a;
 *      return JSON.stringify(decycle(a));
 *  ```
 *
 *  produces the string `[{"$ref":"$"}]`.
 *
 *  JSONPath is used to locate the unique object. $ indicates the top level of
 *  the object or array. [NUMBER] or [STRING] indicates a child element or property.
 *
 *  Based on work by Douglas Crockford
 *   https://github.com/jan-molak/JSON-js/blob/master/cycle.js
 *
 * @param {any} object
 *
 * @package
 */
function decycle(object: any) {
    const objects = new WeakMap<any, string>();     // object to path mappings

    // The derez function recurses through the object, producing a deep copy
    return (function derez(value, path) {

        let old_path,   // The path of an earlier occurance of value
            nu;         // The new object or array

        if (
            typeof value === 'object'
            && value !== null
            && !(value instanceof Boolean)
            && !(value instanceof Date)
            && !(value instanceof Number)
            && !(value instanceof RegExp)
            && !(value instanceof String)
        ) {
            // If the value is an object or array, look to see if we have already
            // encountered it. If so, return a {"$ref":PATH} object.

            old_path = objects.get(value);
            if (old_path !== undefined) {
                return { $ref: old_path };
            }
            // Otherwise, accumulate the unique value and its path.

            objects.set(value, path);

            // If it is an array, replicate the array.

            if (Array.isArray(value)) {
                nu = [];
                value.forEach(function (element, i) {
                    nu[i] = derez(element, path + '[' + i + ']');
                });
            } else {

            // If it is an object, replicate the object.

                nu = {};
                Object.keys(value).forEach(function (name) {
                    nu[name] = derez(
                        value[name],
                        path + '[' + JSON.stringify(name) + ']'
                    );
                });
            }
            return nu;
        }
        return value;
    }(object, '$'));
}

/**
 * @desc
 *  Restore an object that was reduced by decycle. Members which values are
 *  objects of the form
 *  ```
 *       {$ref: PATH}
 *  ```
 *  are replaced with references to the value found by the PATH.
 *  This will restore cycles. The object will be MUTATED.
 *
 *  The eval function is used to locate the values described by a PATH. The
 *  root object is kept in a $ variable. A regular expression is used to
 *  assure that the PATH is extremely well formed. The regexp contains nested
 *  * quantifiers. That has been known to have extremely bad performance
 *  problems on some browsers for very long strings. A PATH is expected to be
 *  reasonably short. A PATH is allowed to belong to a very restricted subset of
 *  Goessner's JSONPath.
 *
 *  So,
 *  ```
 *       var s = '[{"$ref":"$"}]';
 *       return retrocycle(JSON.parse(s));
 *  ```
 *  produces an array containing a single element which is the array itself.
 *
 *  Based on work by Douglas Crockford
 *   https://github.com/jan-molak/JSON-js/blob/master/cycle.js
 *
 * @param {any} $
 *
 * @package
 */
function retrocycle($: any) {
    const px = /^\$(?:\[(?:\d+|"(?:[^\\"\u0000-\u001f]|\\(?:[\\"\/bfnrt]|u[0-9a-zA-Z]{4}))*")\])*$/;

    (function rez(value) {

        // The rez function walks recursively through the object looking for $ref
        // properties. When it finds one that has a value that is a path, then it
        // replaces the $ref object with a reference to the value that is found by
        // the path.

        if (value && typeof value === 'object') {
            if (Array.isArray(value)) {
                value.forEach(function (element, i) {
                    if (typeof element === 'object' && element !== null) {
                        const path = element.$ref;
                        if (typeof path === 'string' && px.test(path)) {
                            value[i] = eval(path);      // tslint:disable-line:no-eval
                        } else {
                            rez(element);
                        }
                    }
                });
            } else {
                Object.keys(value).forEach(function (name) {
                    const item = value[name];
                    if (typeof item === 'object' && item !== null) {
                        const path = item.$ref;
                        if (typeof path === 'string' && px.test(path)) {
                            value[name] = eval(path);   // tslint:disable-line:no-eval
                        } else {
                            rez(item);
                        }
                    }
                });
            }
        }
    }($));

    return $;
}

export interface Dictionary {
    [key: string]: any;
}

/**
 * @private
 */
export function caseInsensitive<T extends Dictionary>(dictionary: T): T & Dictionary {
    return new Proxy(dictionary, {
        get: <K extends keyof T & string>(obj: T & Dictionary, key: K) => {
            const found = Object.keys(obj)
                .find(k => k.toLocaleLowerCase() === key.toLocaleLowerCase());

            return found && obj[found];
        },
    });
}

