import { Ability } from './Ability';
import { UsesAbilities } from './actor';

/**
 * @desc
 *  An interface describing the static access method that every {@link Ability} class
 *  needs to provide in order to be accessible from within an {@link Interaction}.
 */
export interface AbilityType<A extends Ability> extends Function {

    /**
     * @desc
     *  Retrieves the Ability in question from the {@link Actor}, provided that the {@link Actor} has it.
     *
     * @example
     * import { Ability, Actor, Interface } from '@serenity-js/core';
     *
     * class MakePhoneCalls implements Ability {
     *     static as(actor: UsesAbilities): MakesPhoneCalls {
     *         return actor.abilityTo(MakePhoneCalls);
     *     }
     *
     *     static using(phone: Phone) {
     *         return new MakePhoneCalls(phone);
     *     }
     *
     *     constructor(private readonly phone: Phone) {}
     *
     *     // some method that allows us to interact with the external interface of the system under test
     *     dial(phoneNumber: string) {
     *       // ...
     *     }
     * }
     *
     * const Connie = Actor.named('Connie').whoCan(MakePhoneCalls.using(phone));
     *
     * const Call = (phoneNumber: string) => Interaction.where(`#actor calls ${ phoneNumber }`, actor =>
     *  MakePhoneCalls.as(actor).dial(phoneNumber);
     * );
     *
     * @param {UsesAbilities} actor
     */
    as(actor: UsesAbilities): A;
}

import { AnswersQuestions, PerformsActivities, UsesAbilities } from './actor';

/**
 * @desc
 *  A command object representing an activity that an {@link Actor} can perform.
 *
 * @see {@link Actor}
 * @see https://en.wikipedia.org/wiki/Command_pattern
 */
export interface Activity {

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this Activity.
     *
     * @param {PerformsActivities | UsesAbilities | AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     * @see {@link UsesAbilities}
     * @see {@link AnswersQuestions}
     */
    performAs(actor: PerformsActivities | UsesAbilities | AnswersQuestions): PromiseLike<any>;

    /**
     * @desc
     *  Generates a description to be used when reporting this Activity.
     *
     * @returns {string}
     */
    toString(): string;
}

import { Activity } from './Activity';
import { Actor, AnswersQuestions, CollectsArtifacts, UsesAbilities } from './actor';

/**
 * @desc
 *  Directly exercises the {@link Actor}'s {@link Ability} to interact
 *  with the System Under Test.
 *
 * @implements {Activity}
 * @see {@link Ability}
 * @see {@link Actor}
 */
export abstract class Interaction implements Activity {
    static where(
        description: string,
        interaction: (actor: UsesAbilities & AnswersQuestions & CollectsArtifacts) => PromiseLike<void> | void,
    ): Interaction {
        return new DynamicallyGeneratedInteraction(description, interaction);
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link UsesAbilities}
     * @see {@link AnswersQuestions}
     */
    abstract performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void>;
}

/**
 * @package
 */
class DynamicallyGeneratedInteraction extends Interaction {
    constructor(
        private readonly description: string,
        private readonly interaction: (actor: UsesAbilities & AnswersQuestions & CollectsArtifacts) => PromiseLike<void> | void,
    ) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Interaction}.
     *
     * @param {Actor} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     */
    performAs(actor: Actor): PromiseLike<void> {
        try {
            return Promise.resolve(this.interaction(actor));
        } catch (error) {
            return Promise.reject(error);
        }
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

import { isMappable, Mappable } from '../io/collections';
import { AnswersQuestions, UsesAbilities } from './actor';
import { AnswerMappingFunction } from './questions/mappings';

/**
 * @desc
 *  Enables the {@link Actor} to query the system under test.
 *
 * @example <caption>A basic Question</caption>
 *  import { Actor, AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 *  import { Ensure, equals } from '@serenity-js/assertions'
 *
 *  const LastItemOf = <T>(list: T[]): Question<T> =>
 *      Question.about('last item from the list', (actor: AnswersQuestions & UsesAbilities) => {
 *          return list[list.length - 1];
 *      });
 *
 *  Actor.named('Quentin').attemptsTo(
 *      Ensure.that(LastItemFrom([1,2,3]), equals(3)),
 *  );
 *
 * @example <caption>A question using the Actor's Ability to do something</caption>
 *  import { AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 *  import { CallAnApi } from '@serenity-js/rest'
 *
 *  const TextOfLastResponseStatus = () =>
 *      Question.about<number>(`the text of the last response status`, actor => {
 *          return CallAnApi.as(actor).mapLastResponse(response => response.statusText);
 *      });
 *
 *  @example <caption>Mapping answers to other questions</caption>
 *  import { Actor, AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 *  import { CallAnApi, LastResponse } from '@serenity-js/rest'
 *  import { Ensure, equals } from '@serenity-js/assertions';
 *
 *  const RequestWasSuccessful = () =>
 *      Question.about<number>(`the text of the last response status`, actor => {
 *          return LastResponse.status().answeredBy(actor) === 200;
 *      });
 *
 *  const actor = Actor.named('Quentin').whoCan(CallAnApi.at('https://myapp.com/api'));
 *
 *  actor.attemptsTo(
 *      Send.a(GetRequest.to('/books/0-688-00230-7')),
 *      Ensure.that(RequestWasSuccessful(), isTrue()),
 *  );
 *
 * @see {@link Actor}
 * @see {@link Interaction}
 * @see {@link Ability}
 *
 * @abstract
 */
export abstract class Question<T> {

    /**
     * @param {string} subject
     *  The subject of this question
     *
     * @protected
     */
    protected constructor(protected subject: string) {
    }

    /**
     * @desc
     *  Factory method that simplifies the process of defining custom questions.
     *
     * @example
     *  const EnvVariable = (name: string) =>
     *      Question.about(`the ${ name } env variable`, actor => process.env[name])
     *
     * @static
     *
     * @param {string} description
     * @param {function(actor: AnswersQuestions & UsesAbilities): R} body
     *
     * @returns {Question<R>}
     */
    static about<R>(description: string, body: (actor: AnswersQuestions & UsesAbilities) => R): Question<R> {
        return new AnonymousQuestion<R>(description, body);
    }

    /**
     * @desc
     *  Checks if the value is a {@link Question}.
     *
     * @static
     *
     * @param {any} maybeQuestion
     *  The value to check
     *
     * @returns {boolean}
     */
    static isAQuestion<T>(maybeQuestion: any): maybeQuestion is Question<T> {
        return !! maybeQuestion && !! (maybeQuestion as any).answeredBy;
    }

    /**
     * @desc
     *  Describes the subject of this {@link Question}.
     *
     * @returns {string}
     */
    toString() {
        return this.subject;
    }

    /**
     * @desc
     *  Changes the description of this question's subject.
     *
     * @param {string} subject
     * @returns {Question<T>}
     */
    describedAs(subject: string): this {
        this.subject = subject;

        return this;
    }

    /**
     * @desc
     *  Creates a new {@link Question}, which value is a result of applying the `mapping`
     *  function to the value of this {@link Question}.
     *
     * @example <caption>Mapping a Question<Promise<string>> to Question<Promise<number>></caption>
     *  import { Question, replace, toNumber } from '@serenity-js/core';
     *
     *  Question.about('the price of some item', actor => '$3.99')
     *      .map(replace('$', ''))
     *      .map(toNumber)
     *
     *  // => Question<Promise<number>>
     *  //      3.99
     *
     * @example <caption>Mapping all items of Question<string[]> to Question<Promise<number>></caption>
     *  import { Question, trim } from '@serenity-js/core';
     *
     *  Question.about('things to do', actor => [ ' walk the dog  ', '  read a book  ' ])
     *      .map(trim())
     *
     *  // => Question<Promise<string[]>>
     *  //      [ 'walk the dog', 'read a book' ]
     *
     * @example <caption>Using a custom mapping function</caption>
     *  import { Question } from '@serenity-js/core';
     *
     *  Question.about('normalised percentages', actor => [ 0.1, 0.3, 0.6 ])
     *      .map((actor: AnswersQuestions) => (value: number) => value * 100)
     *
     *  // => Question<Promise<number[]>>
     *  //      [ 10, 30, 60 ]
     *
     * @param {function(value: A, index?: number): Promise<O> | O} mapping
     *  A mapping function that receives a value of type `<A>`, which is either:
     *  - an answer to the original question, if the question is defined as `Question<Promise<A>>` or `Question<A>`
     *  - or, if the question is defined as `Question<Promise<Mappable<A>>`, `Question<Mappable<A>>` - each item of the {@link Mappable} collection,
     *
     * @returns {Question<Promise<Mapped>>}
     *  A new Question which value is a result of applying the `mapping` function
     *  to the value of the current question, so that:
     *  - if the answer to the current question is a `Mappable<A>`, the result becomes `Question<Promise<O[]>>`
     *  - if the answer is a value `<A>` or `Promise<A>`, the result becomes `Question<Promise<O>>`
     *
     * @see {@link AnswerMappingFunction}
     * @see {@link Mappable}
     */
    map<O>(mapping: AnswerMappingFunction<AnswerOrItemOfMappableCollection<T>, O>): Question<Promise<Mapped<T, O>>> {
        return Question.about(this.subject, actor =>
            actor.answer(this).then(value =>
                (isMappable(value)
                    ? Promise.all(((value).map(mapping(actor)) as Array<PromiseLike<O> | O>))
                    : mapping(actor)(value as AnswerOrItemOfMappableCollection<T>)
                ) as Promise<Mapped<T, O>>
            )
        ) as Question<Promise<Mapped<T, O>>>;
    }

    /**
     * @abstract
     */
    abstract answeredBy(actor: AnswersQuestions & UsesAbilities): T;
}

/**
 * @package
 */
type AnswerOrItemOfMappableCollection<V> =
    V extends PromiseLike<infer PromisedValue>
        ? PromisedValue extends Mappable<infer Item>
            ? Item
            : PromisedValue
        : V extends Mappable<infer Item>
            ? Item
            : V;

/**
 * @package
 */
type Mapped<T, O> =
    T extends PromiseLike<infer PromisedValue>
        ? PromisedValue extends Mappable<infer Item>
            ? O[]
            : O
        : T extends Mappable<infer Item>
            ? O[]
            : O

/**
 * @package
 */
class AnonymousQuestion<T> extends Question<T> {
    constructor(private description: string, private body: (actor: AnswersQuestions & UsesAbilities) => T) {
        super(description);
    }

    answeredBy(actor: AnswersQuestions & UsesAbilities) {
        return this.body(actor);
    }

    /**
     * Changes the description of this question's subject
     * and produces a new instance without mutating the original one.
     *
     * @param {string} subject
     * @returns {Question<T>}
     */
    describedAs(subject: string): this {
        this.subject = subject;

        return this;
    }
}

import { ImplementationPendingError } from '../errors';
import { Activity } from './Activity';
import { PerformsActivities } from './actor';

/**
 * @desc
 *  Aggregates {@link Interaction}s so that they represents logical
 *  steps of a business process an {@link Actor} can perform.
 *
 * @implements {Activity}
 * @see {@link Interaction}
 * @see {@link Actor}
 */
export abstract class Task implements Activity {

    /**
     * @desc
     *  A factory method to make defining the Tasks more convenient.
     *  Please note that calling this method without providing the list of {@link Activity} objects
     *  will result in producing a Task that's marked as "pending" in the test report.
     *
     *  This feature is useful when you want to quickly write down a task that will be needed in the scenario,
     *  but you're not yet sure what activities will constitute it.
     *
     * @example <caption>Modelling a task</caption>
     * import { Log, Task } from '@serenity-js/core';
     *
     * const Debug = (something: Answerable<any>) =>
     *     Task.where(`#actor prints the answer to a question`,
     *          Log.the(something),
     *     );
     *
     * @example <caption>Modelling a not implemented task</caption>
     * import { Task } from '@serenity-js/core';
     *
     * const SignUp = () =>
     *     Task.where(`#actor signs up for a newsletter`); // no activities given
     *                                                     // => task marked as pending
     *
     * @param {string} description - A description to be used when reporting this task
     * @param {Activity[]} activities - A list of lower-level activities that constitute the task
     *
     * @returns {Task}
     */
    static where(description: string, ...activities: Activity[]): Task {
        return activities.length > 0
            ? new DynamicallyGeneratedTask(description, activities)
            : new NotImplementedTask(description);
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    abstract performAs(actor: PerformsActivities): PromiseLike<void>;
}

/**
 * @package
 */
class DynamicallyGeneratedTask extends Task {
    constructor(private description: string, private activities: Activity[]) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    performAs(actor: PerformsActivities): PromiseLike<void> {
        return actor.attemptsTo(...this.activities);
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

/**
 * @package
 */
class NotImplementedTask extends Task {
    constructor(private description: string) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    performAs(actor: PerformsActivities): PromiseLike<void> {
        return Promise.reject(
            new ImplementationPendingError(`A task where "${ this.toString() }" has not been implemented yet`),
        );
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

import { LogicError } from '../../errors';
import { Ability } from '../Ability';
import { AnswersQuestions, UsesAbilities } from '../actor';
import { Question } from '../Question';
import { Discardable } from './Discardable';

/**
 * @desc
 *  Enables the {@link Actor} to {@link TakeNote} of an answer to a given {@link Question}
 *  and to retrieve their {@link Note} later.
 *
 * @see {@link Note}
 * @see {@link TakeNote}
 *
 * @abstract
 * @implements {Ability}
 */
export abstract class TakeNotes implements Ability {

    /**
     * @desc
     *  Instantiates an ability to {@link TakeNotes} that uses an empty notepad.
     *
     *  This method should be used when you need the {@link Actor} to {@link TakeNote}
     *  of an answer to some {@link Question} and retrieve such {@link Note} later on in the same scenario,
     *  but you don't need the {@link Actor}s to share their notes.
     *
     * @example
     *  import { actorCalled, Log, Note, TakeNote, TakeNotes } from '@serenity-js/core'
     *  import { BrowseTheWeb, Target, Text } from '@serenity-js/protractor'
     *  import { by, protractor } from 'protractor';
     *
     *  const actor = actorCalled('Apisitt').whoCan(
     *      TakeNotes.usingAnEmptyNotepad(),
     *      CallAnApi.at('https://api.example.org'),
     *  );
     *
     *  actor.attemptsTo(
     *      Send.a(GetRequest.to('/customers')),
     *      TakeNote.of(LastResponse.status()),     // remember the LastResponse.status()
     *      // ... perform some other activities
     *      Log.the(Note.of(LastResponse.body())),  // log it later, or pass to any other interaction
     *  );
     *
     * @example
     *  import { actorCalled, Note, TakeNote, TakeNotes } from '@serenity-js/core'
     *  import { BrowseTheWeb, Target, Text } from '@serenity-js/protractor'
     *  import { by, protractor } from 'protractor';
     *
     *  class Vouchers {
     *      static code             = Target.the('voucher code').located(by.id('voucher'));
     *      static appliedVoucher   = Target.the('voucher code').located(by.id('applied-voucher'));
     *  }
     *
     *  const actor = actorCalled('Noah').whoCan(
     *      TakeNotes.usingAnEmptyNotepad(),
     *      BrowseTheWeb.using(protractor.browser),
     *  );
     *
     *  actor.attemptsTo(
     *      TakeNote.of(Text.of(Vouchers.code)),
     *      // ... add the product to a basket, go to checkout, etc.
     *      Ensure.that(Text.of(Vouchers.appliedVoucher), equals(Note.of(Text.of(Vouchers.code)))),
     *  );
     *
     * @returns {TakeNotes & Discardable}
     */
    static usingAnEmptyNotepad(): TakeNotes & Discardable {
        return new TakeNotesUsingAnEmptyNotepad();
    }

    /**
     * @desc
     *  Instantiates an ability to {@link TakeNotes} that uses a notepad that can be shared amongst {@link Actor}s.
     *
     *  This method should be used when you need the {@link Actor}s to {@link TakeNote}s
     *  of answers to some {@link Question}s, share those notes amongst themselves,
     *  and retrieve such shared {@link Note}s later on in the same scenario.
     *
     * @example
     *  import { actorCalled, Note, TakeNote, TakeNotes } from '@serenity-js/core'
     *  import { ManageALocalServer, StartLocalTestServer, StopLocalTestServer } from '@serenity-js/local-server'
     *  import { BrowseTheWeb } from '@serenity-js/protractor'
     *
     *  const server = http.createServer(function (request, response) {
     *      response.setHeader('Connection', 'close');
     *      response.end('Hello!');
     *  });
     *
     *  const Adam = actorCalled('Adam').whoCan(
     *      ManageALocalServer.using(server),
     *      TakeNotes.usingASharedNotepad(),
     *  );
     *
     *  const Becky = actorCalled('Becky').whoCan(
     *      TakeNotes.usingASharedNotepad(),
     *      BrowseTheWeb.using(protractor.browser),
     *  );
     *
     *  Adam.attemptsTo(
     *      StartLocalTestServer.onRandomPort(),
     *      TakeNote.of(LocalServer.url()),
     *  );
     *
     *  Becky.attemptsTo(
     *      Navigate.to(Note.of(LocalServer.url())),
     *      // perform some UI interactions
     *  );
     *
     * @returns {TakeNotes}
     */
    static usingASharedNotepad(): TakeNotes {
        return new TakeNotesUsingASharedNotepad();
    }

    /**
     * @desc
     *  Retrieves the {@link Ability} to {@link TakeNotes}
     *  for a given {@link Actor} to be used in custom {@link Interaction}s.
     *
     * @see {@link Ability}
     * @see {@link Interaction}
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     *
     * @returns {TakeNotes}
     */
    static as(actor: UsesAbilities & AnswersQuestions): TakeNotes {
        return actor.abilityTo(TakeNotes);
    }

    /**
     * @desc
     *  Instantiates the {@link Ability} to {@link TakeNotes}
     *
     * @param {Map<string, any>} notepad
     */
    protected constructor(protected readonly notepad: Map<string, any>) {
    }

    /**
     * @desc
     *  Records the answer to a given {@link Question}
     *
     * @param {Question<Promise<Answer>> | Question<Answer> | string} subject
     *  Question or name to record the Answer as
     *
     * @param {Promise<Answer> | Answer} value
     *  The Answer to record
     *
     * @returns {void}
     */
    record<Answer>(subject: Question<Promise<Answer>> | Question<Answer> | string, value: Promise<Answer> | Answer): void {
        this.notepad.set(subject.toString(), value);
    }

    /**
     * @desc
     *  Recalls the answer to a given {@link Question}
     *
     * @param {Question<Promise<Answer>> | Question<Answer> | string} subject
     *  Question or name the Answer was recorded as
     *
     * @returns {Promise<Answer>}
     */
    answerTo<Answer>(subject: Question<Promise<Answer>> | Question<Answer> | string): Promise<Answer> {
        const key = subject.toString();

        return ! this.notepad.has(key)
            ? Promise.reject(new LogicError(`The answer to "${ key }" has never been recorded`))
            : Promise.resolve(this.notepad.get(key));
    }
}

/**
 * @package
 */
class TakeNotesUsingAnEmptyNotepad extends TakeNotes implements Discardable {

    /**
     * @desc
     *  Instantiates the {@link Ability} to {@link TakeNotes}
     *
     * @param {Map<string, any>} notepad
     */
    constructor() {
        super(new Map<string, any>());
    }

    /**
     * @desc
     *  Enables the {@link Actor} to clear the notepad when the {@link SceneFinishes}.
     *
     * @returns {Promise<void> | void}
     */
    discard(): Promise<void> | void {
        this.notepad.clear();
    }
}

/**
 * @package
 */
class TakeNotesUsingASharedNotepad extends TakeNotes {
    private static sharedNotepad = new Map<string, any>();

    /**
     * @desc
     *  Instantiates the {@link Ability} to {@link TakeNotes}
     *
     * @param {Map<string, any>} notepad
     */
    constructor() {
        super(TakeNotesUsingASharedNotepad.sharedNotepad);
    }
}

