import 'mocha';

import sinon = require('sinon');
import { ActivityFinished, ActivityStarts, DomainEvent, TestRunnerDetected } from '../src/events';
import { CorrelationId, Name } from '../src/model';
import { Actor, Interaction } from '../src/screenplay';
import { Serenity } from '../src/Serenity';
import { Cast, Clock, Stage, StageCrewMember } from '../src/stage';
import { expect } from './expect';

describe('Serenity', () => {

    it('constructs a Stage and connects it with a provided Cast', () => {

        const prepareSpy = sinon.spy();

        // no-op actors with no special Abilities
        class Extras implements Cast {
            prepare(actor: Actor): Actor {
                prepareSpy(actor);
                return actor;
            }
        }

        const serenity = new Serenity(new Clock());

        serenity.configure({
            actors: new Extras(),
        });

        const Joe = serenity.theActorCalled('Joe');

        expect(prepareSpy).to.have.been.calledOnce;                      // tslint:disable-line:no-unused-expression
        expect(prepareSpy.getCall(0).args[0]).to.equal(Joe);
    });

    it('enables propagation of DomainEvents triggered by Actors\' Activities and StageCrewMembers', () => {

        class Extras implements Cast {
            prepare(actor: Actor): Actor {
                return actor;
            }
        }

        const PerformSomeInteraction = () => Interaction.where(`#actor performs some interaction`, actor => {
            return void 0;
        });

        const frozenClock = new Clock(() => new Date('1983-07-03'));
        const serenity = new Serenity(frozenClock);
        const listener = new Listener<ActivityStarts | ActivityFinished>();

        serenity.configure({
            actors: new Extras(),
            crew: [ listener ],
        });

        return serenity.theActorCalled('Joe').attemptsTo(
            PerformSomeInteraction(),
        ).
        then(() => serenity.waitForNextCue()).
        then(() => {
            expect(listener.events).to.have.lengthOf(2);

            expect(listener.events[0]).to.be.instanceOf(ActivityStarts);
            expect(listener.events[0].details.name.value).to.equal(`Joe performs some interaction`);

            expect(listener.events[1]).to.be.instanceOf(ActivityFinished);
            expect(listener.events[1].details.name.value).to.equal(`Joe performs some interaction`);
        });
    });

    it('allows for external parties, such as test runner adapters, to announce DomainEvents', () => {

        const frozenClock = new Clock(() => new Date('1983-07-03'));
        const serenity = new Serenity(frozenClock);
        const listener = new Listener<TestRunnerDetected>();

        const testRunnerName = new Name('mocha');

        serenity.configure({ crew: [ listener ] });

        serenity.announce(new TestRunnerDetected(CorrelationId.create(), testRunnerName, serenity.currentTime()));

        return serenity.waitForNextCue().
            then(() => {
                expect(listener.events).to.have.lengthOf(1);

                expect(listener.events[0]).to.be.instanceOf(TestRunnerDetected);
                expect(listener.events[0].name).to.equal(testRunnerName);
            });
    });

    class Listener<Event_Type extends DomainEvent> implements StageCrewMember {
        public readonly events: Event_Type[] = [];

        constructor(private stage: Stage = null) {
        }

        assignedTo(stage: Stage): StageCrewMember {
            this.stage = stage;

            return this;
        }

        notifyOf(event: Event_Type): void {
            this.events.push(event);
        }
    }
});

import 'mocha';

import { RuntimeError } from '../../src/errors';
import { expect } from '../expect';

describe('RuntimeError', () => {

    describe('subclass', () => {

        class CustomError extends RuntimeError {
            constructor(message: string) {
                super(CustomError, message);
            }
        }

        let error: RuntimeError;
        beforeEach(() => {
            error = new CustomError('something happened');
        });

        /** @test {RuntimeError} */
        it('is recognised as an instance of its type', () => {
            expect(error).to.be.instanceOf(Error);
            expect(error).to.be.instanceOf(RuntimeError);
            expect(error).to.be.instanceOf(CustomError);
        });

        /** @test {RuntimeError} */
        it('has a message', () => {
            expect(error.message).to.equal('something happened');
        });

        /** @test {RuntimeError} */
        it('retains the stacktrace', () => {
            const frames = error.stack.split('\n');

            expect(frames[0]).to.equal('CustomError: something happened');
            expect(frames[1]).to.contain(__filename);
        });
    });

    describe('when propagating errors', () => {

        class ApplicationError extends RuntimeError {
            constructor(message: string, cause?: Error) {
                super(ApplicationError, message, cause);
            }
        }

        class IOError extends RuntimeError {
            constructor(message: string, cause?: Error) {
                super(IOError, message, cause);
            }
        }

        /** @test {RuntimeError} */
        it('includes the stack trace of the original errors', () => {

            const fsError  = new Error('ENOENT: no such file or directory');
            const ioError  = new IOError(`Directory not writable`, fsError);
            const appError = new ApplicationError('Report could not be saved', ioError);

            const significantFrames = appError.stack.split('\n').filter(frame => ! frame.startsWith('    '));

            expect(significantFrames).to.deep.equal([
                'ApplicationError: Report could not be saved',
                'Caused by: IOError: Directory not writable',
                'Caused by: Error: ENOENT: no such file or directory',
            ]);
        });
    });
});

import 'mocha';

import { AssertionError } from '../../src/errors';
import { ErrorSerialiser, parse } from '../../src/io';
import { expect } from '../expect';

describe ('ErrorSerialiser', () => {

    /** @test {ErrorSerialiser} */
    it('serialises an Error object to JSON', () => {
        const e = new Error(`Something happened`);

        expect(ErrorSerialiser.serialise(e)).to.equal(JSON.stringify({
            name: 'Error',
            stack: e.stack,
            message: 'Something happened',
        }));
    });

    /** @test {ErrorSerialiser} */
    it('deserialises a serialised Error object from JSON', () => {
        const stack = [
            'Error: Something happened',
            '    at /app/index.js:38:20',
            '    at Generator.next (<anonymous>)',
        ].join('\n');

        const error = ErrorSerialiser.deserialise(JSON.stringify({
            name: 'Error',
            message: 'Something happened',
            stack,
        }));

        expect(error).to.be.instanceOf(Error);
        expect(error.name).to.equal(`Error`);
        expect(error.message).to.equal(`Something happened`);
        expect(error.stack).to.equal(stack);
    });

    /** @test {ErrorSerialiser} */
    it('serialises a custom Error object to JSON', () => {
        const
            error = new AssertionError(`Expected false to equal true`, true, false),
            serialised = ErrorSerialiser.serialise(error);

        expect(parse(serialised).name).to.equal('AssertionError');
        expect(parse(serialised).message).to.equal('Expected false to equal true');
    });

    /** @test {ErrorSerialiser} */
    it('deserialises a serialised custom Error object from JSON', () => {
        const stack = [
            'AssertionError: Expected false to equal true',
            '    at /app/index.js:38:20',
            '    at Generator.next (<anonymous>)',
        ].join('\n');

        const error = ErrorSerialiser.deserialise(JSON.stringify({
            name: 'AssertionError',
            message: 'Expected false to equal true',
            stack,
        }));

        expect(error).to.be.instanceOf(AssertionError);
        expect(error.name).to.equal(`AssertionError`);
        expect(error.message).to.equal(`Expected false to equal true`);
        expect(error.stack).to.equal(stack);
    });

    /** @test {ErrorSerialiser} */
    it('deserialises the error object from a stack trace alone (Cucumber event protocol)', () => {
        const stack = `Error: Something's wrong\n    at World.<anonymous> (features/step_definitions/synchronous.steps.ts:9:15)`;

        const error: Error = ErrorSerialiser.deserialiseFromStackTrace(stack);

        expect(error).to.be.instanceOf(Error);
        expect(error.name).to.equal(`Error`);
        expect(error.message).to.equal(`Something's wrong`);
        expect(error.stack).to.equal(stack);
    });

    /** @test {ErrorSerialiser} */
    it('deserialises the error object from a string (Cucumber event protocol)', () => {
        const stack = `function has 2 arguments, should have 3 (if synchronous or returning a promise) or 4 (if accepting a callback)`;

        const error: Error = ErrorSerialiser.deserialiseFromStackTrace(stack);

        expect(error).to.be.instanceOf(Error);
        expect(error.name).to.equal(`Error`);
        expect(error.message).to.equal(`function has 2 arguments, should have 3 (if synchronous or returning a promise) or 4 (if accepting a callback)`);
    });
});

import 'mocha';

import { given } from 'mocha-testdata';
import { expect } from '../expect';
import { FileFinder, Path } from '../../src/io';

/** @test {FileFinder} */
describe('FileFinder', () => {

    const cwd = Path.from(__dirname, 'finder-examples');
    let finder: FileFinder;

    beforeEach(() => {
        finder = new FileFinder(cwd);
    });

    given([
        null,
        undefined
    ]).
    it('returns an empty list when given an undefined pattern', (pattern: any) => {
        expect(finder.filesMatching(pattern)).to.deep.equal([])
    });

    /** @test {FileFinder} */
    it(`returns an empty list when the pattern provided doesn't match any files`, () => {
        expect(finder.filesMatching('non-existent.txt')).to.deep.equal([])
    });

    /** @test {FileFinder} */
    it('returns a list with an exact match', () => {
        const matches = finder.filesMatching('file1.txt');

        expect(matches).to.have.lengthOf(1);
        expect(matches[0]).to.equal(cwd.join(Path.from('file1.txt')));
    });

    /** @test {FileFinder} */
    it('returns a list with exact matches', () => {
        const matches = finder.filesMatching([ 'file1.txt', 'file2.txt' ]);

        expect(matches).to.have.lengthOf(2);
        expect(matches[0]).to.equal(cwd.join(Path.from('file1.txt')));
        expect(matches[1]).to.equal(cwd.join(Path.from('file2.txt')));
    });

    /** @test {FileFinder} */
    it('supports glob patterns', () => {
        const matches = finder.filesMatching([ 'file*.txt' ]);

        expect(matches).to.have.lengthOf(2);
        expect(matches[0]).to.equal(cwd.join(Path.from('file1.txt')));
        expect(matches[1]).to.equal(cwd.join(Path.from('file2.txt')));
    });

    /** @test {FileFinder} */
    it('supports globstar patterns', () => {
        const matches = finder.filesMatching([ '**/file*.txt' ]);

        expect(matches).to.have.lengthOf(4);
        expect(matches[0]).to.equal(cwd.join(Path.from('file1.txt')));
        expect(matches[1]).to.equal(cwd.join(Path.from('file2.txt')));
        expect(matches[2]).to.equal(cwd.join(Path.from('nested/file3.txt')));
        expect(matches[3]).to.equal(cwd.join(Path.from('nested/file4.txt')));
    });
})

import 'mocha';

import { FileSystem, Path } from '../../src/io';
import { expect } from '../expect';
import { FakeFS } from '../FakeFS';

/** @test {FileSystem} */
describe ('FileSystem', () => {

    const
        image        = 'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEX/TQBcNTh/AAAAAXRSTlPM0jRW/QAAAApJREFUeJxjYgAAAAYAAzY3fKgAAAAASUVORK5CYII=',
        imageBuffer  = Buffer.from(image, 'base64'),
        originalJSON = { name: 'jan' },
        processCWD   = new Path('/Users/jan/projects/serenityjs');

    describe ('when storing JSON files', () => {

        it ('stores a JSON file in a desired location', () => {
            const
                fs = FakeFS.with({
                    [ processCWD.value ]: FakeFS.Empty_Directory,
                }),
                out = new FileSystem(processCWD, fs);

            return expect(out.store(new Path('outlet/some.json'), JSON.stringify(originalJSON))).to.be.fulfilled.then(absolutePath => {
                expect(fs.existsSync(absolutePath.value)).to.equal(true);
                expect(jsonFrom(fs.readFileSync(absolutePath.value))).to.eql(originalJSON);
            });
        });

        it ('tells the absolute path to a JSON file once it is saved', () => {
            const
                fs = FakeFS.with({
                    [ processCWD.value ]: FakeFS.Empty_Directory,
                }),
                out = new FileSystem(processCWD, fs),
                dest = new Path('outlet/some.json');

            return expect(out.store(dest, JSON.stringify(originalJSON))).to.be.fulfilled.
                then(result => expect(result.equals(processCWD.resolve(dest))));
        });

        it (`complains when the file can't be written`, () => {
            const fs = FakeFS.with(FakeFS.Empty_Directory);

            (fs as any).writeFile = () => { // memfs doesn't support mocking error conditions or permissions
                throw new Error('EACCES, permission denied');
            };

            const out = new FileSystem(new Path('/'), fs);

            return expect(out.store(new Path('dir/file.json'), JSON.stringify(originalJSON)))
                .to.be.eventually.rejectedWith('EACCES, permission denied');
        });
    });

    describe ('when storing pictures', () => {

        it ('stores a base64-encoded picture at a desired location', () => {
            const
                fs = FakeFS.with({
                    [ processCWD.value ]: FakeFS.Empty_Directory,
                }),
                out = new FileSystem(processCWD, fs);

            return expect(out.store(new Path('outlet/some.png'), imageBuffer)).to.be.fulfilled.then(absolutePath => {
                expect(fs.existsSync(absolutePath.value)).to.equal(true);
                expect(pictureAt(fs.readFileSync(absolutePath.value))).to.eql(image);
            });
        });

        it ('tells the absolute path to a JSON file once it is saved', () => {
            const
                fs = FakeFS.with({
                    [ processCWD.value ]: FakeFS.Empty_Directory,
                }),
                out = new FileSystem(processCWD, fs),
                dest = new Path('outlet/some.png');

            return expect(out.store(dest, imageBuffer)).to.be.fulfilled.then(absolutePath => {
                expect(absolutePath.equals(processCWD.join(dest))).to.equal(true);
            });
        });
    });

    describe ('when removing', () => {

        describe('individual files', () => {

            it('removes the file', () => {
                const
                    fs = FakeFS.with({
                        [processCWD.value]: {
                            outlet: {
                                subdir: {
                                    'file-to-be-deleted.json': '{}',
                                    'file-not-to-be-deleted.json': '{}',
                                },
                            },
                        },
                    }),
                    out = new FileSystem(processCWD, fs);

                return expect(out.remove(new Path('outlet/subdir/file-to-be-deleted.json'))).to.be.fulfilled.then(() => {

                    expect(fs.existsSync(processCWD.join(new Path('outlet/subdir/file-to-be-deleted.json')).value)).to.equal(false);
                    expect(fs.existsSync(processCWD.join(new Path('outlet/subdir/file-not-to-be-deleted.json')).value)).to.equal(true);
                });
            });
        });

        describe('directories', () => {

            it('removes the directory recursively', () => {
                const
                    fs = FakeFS.with({
                        [processCWD.value]: {
                            outlet: {
                                subdir: {
                                    'file-to-be-deleted.json': '{}',
                                },
                                another: {
                                    'file-not-to-be-deleted.json': '{}',
                                },
                            },
                        },
                    }),
                    out = new FileSystem(processCWD, fs);

                return expect(out.remove(new Path('outlet/subdir'))).to.be.fulfilled.then(() => {

                    expect(fs.existsSync(processCWD.join(new Path('outlet/subdir/file-to-be-deleted.json')).value)).to.equal(false);
                    expect(fs.existsSync(processCWD.join(new Path('outlet/subdir')).value)).to.equal(false);
                    expect(fs.existsSync(processCWD.join(new Path('outlet/another/file-not-to-be-deleted.json')).value)).to.equal(true);
                });
            });
        });
    });

    function jsonFrom(file: Buffer) {
        return JSON.parse(file.toString('ascii'));
    }

    function pictureAt(file: Buffer) {
        return Buffer.from(file).toString('base64');
    }
});

