            new ExecutionSuccessful(),
        ]).
        it('can be serialised and deserialised', (outcome: Outcome) => {
            const deserialised: any = Outcome.fromJSON(outcome.toJSON());

            expect(deserialised).to.be.instanceOf(outcome.constructor);
        });
    });

    /**
     * @test {ExecutionCompromised}
     * @test {ExecutionFailedWithError}
     * @test {ExecutionFailedWithAssertionError}
     */
    describe('outcome indicating an error', () => {

        given([
            { outcome: new ExecutionCompromised(new TestCompromisedError('Database is down')), description: ExecutionCompromised.name },
            { outcome: new ExecutionFailedWithError(new Error(`Something's wrong`)), description: ExecutionFailedWithError.name },
            { outcome: new ExecutionFailedWithAssertionError(assertionError()), description: ExecutionFailedWithAssertionError.name },
            { outcome: new ImplementationPending(new ImplementationPendingError('method missing')), description: ImplementationPending.name },
            { outcome: new ExecutionIgnored(new Error('Test failed but will be retried')), description: ImplementationPending.name },
        ]).
        it('can be serialised and deserialised', ({ outcome }: { outcome: ProblemIndication }) => {
            const deserialised: any = Outcome.fromJSON(outcome.toJSON());

            expect(deserialised).to.be.instanceOf(outcome.constructor);

            expect(deserialised.error.name).to.equal(outcome.error.constructor.name);
            expect(deserialised.error.message).to.equal(outcome.error.message);
            expect(deserialised.error.stack).to.equal(outcome.error.stack);
        });
    });

    function assertionError() {
        try {
            expect(true).to.equal(false);
        } catch (e) {
            return e;
        }
    }
});

import 'mocha';
import * as sinon from 'sinon';

import { ArtifactGenerated } from '../../src/events';
import { JSONData, Name } from '../../src/model';
import { Actor, Interaction } from '../../src/screenplay';
import { Stage, StageManager } from '../../src/stage';
import { Extras } from '../../src/stage/Extras';
import { expect } from '../expect';

describe('Interaction', () => {

    let stage: Stage,
        Ivonne: Actor,
        stageManager: sinon.SinonStubbedInstance<StageManager>;

    beforeEach(() => {
        stageManager = sinon.createStubInstance(StageManager);

        stage = new Stage(
            new Extras(),
            stageManager as unknown as StageManager,
        );
        Ivonne = new Actor('Ivonne', stage);
    });

    describe('when defining an interaction', () => {

        /** @test {Interaction} */
        it('provides a convenient factory method for synchronous interactions', () => {
            const spy = sinon.spy();

            const InteractWithTheSystem = () => Interaction.where(`#actor interacts with the system`, (actor: Actor) => {
                spy(actor);
            });

            return expect(Ivonne.attemptsTo(
                InteractWithTheSystem(),
            ))
            .to.be.fulfilled
            .then(() => {
                expect(spy).to.have.been.calledWith(Ivonne);
            });
        });

        /** @test {Interaction} */
        it('provides a convenient factory method for asynchronous interactions', () => {
            const spy = sinon.spy();

            const InteractWithTheSystem = () => Interaction.where(`#actor interacts with the system`, (actor: Actor) => {
                spy(actor);

                return Promise.resolve();
            });

            return expect(Ivonne.attemptsTo(
                InteractWithTheSystem(),
            ))
            .to.be.fulfilled
            .then(() => {
                expect(spy).to.have.been.calledWith(Ivonne);
            });
        });
    });

    describe('when handling errors', () => {
        const error = new Error(`We're sorry, something happened`);

        it('rejects the promise when the interaction function rejects a promise', () => {
            const InteractWithTheSystem = () => Interaction.where(`#actor interacts with the system`, (actor: Actor) => Promise.reject(error));

            return expect(Ivonne.attemptsTo(
                InteractWithTheSystem(),
            )).to.be.rejectedWith(error);
        });

        it('rejects the promise when the interaction function throws an error', () => {

            const InteractWithTheSystem = () => Interaction.where(`#actor interacts with the system`, (actor: Actor) => { throw error; });

            return expect(Ivonne.attemptsTo(
                InteractWithTheSystem(),
            )).to.be.rejectedWith(error);
        });
    });

    /** @test {Interaction} */
    it('can optionally emit an artifact to be attached to the report or stored', () => {
        const
            expectedArtifact = JSONData.fromJSON({ token: '123' }),
            expectedArtifactName = new Name('Session Token');

        const InteractWithTheSystem = () => Interaction.where(`#actor interacts with the system`, (actor: Actor) => {
            actor.collect(expectedArtifact, expectedArtifactName);
        });

        return expect(Ivonne.attemptsTo(
            InteractWithTheSystem(),
        ))
        .to.be.fulfilled
        .then(() => {
            const event = stageManager.notifyOf.args[1][0] as ArtifactGenerated;

            expect(event.name).to.equal(expectedArtifactName);
            expect(event.artifact).to.equal(expectedArtifact);
        });
    });
});

import 'mocha';
import { given } from 'mocha-testdata';

import { actorCalled, AnswersQuestions } from '../../src';
import { Mappable } from '../../src/io';
import { Actor, Question, replace, toNumber, trim } from '../../src/screenplay';

import { expect } from '../expect';

/** @test {Question} */
describe('Question', () => {

    describe('provides a convenient way to define a custom question that', () => {

        /** @test {Question.about} */
        it('returns a static value', () => {
            const Name = () =>
                Question.about('a name', (actor: Actor) => actor.name);

            const answer = Name().answeredBy(actorCalled('Jacques'));

            expect(answer).to.equal('Jacques');
        });

        /** @test {Question.about} */
        it('returns a Promise of a value', () => {
            const Name = () =>
                Question.about('a name', (actor: Actor) => Promise.resolve(actor.name));

            const answer = Name().answeredBy(actorCalled('Jill'));

            return expect(answer).to.eventually.equal('Jill');
        });

        /** @test {Question.about} */
        /** @test {Question#toString} */
        it('has a description', () => {
            const Name = () =>
                Question.about('a name', (actor: Actor) => actor.name);

            expect(Name().toString()).to.equal('a name');
        });
    });

    /** @test {Question.about} */
    /** @test {Question#describedAs} */
    it('allows for a custom description to override the default one', () => {
        const Name = () =>
            Question.about('a name', (actor: Actor) => actor.name);

        expect(Name().describedAs('first name').toString())
            .to.equal('first name');
    });

    describe('when mapping the answer', () => {

        /** @test {Question.about} */
        /** @test {Question#map} */
        it('works with a static value', () => {

            const SomeResult = () =>
                Question.about('result of some calculation', (actor: Actor) => ' 6.67%\n');

            const endResult = SomeResult()
                .map(trim())
                .map(replace('%', ''))
                .map(toNumber())
                .answeredBy(actorCalled('Jacques'))

            return expect(endResult).to.eventually.equal(6.67);
        });

        /** @test {Question.about} */
        /** @test {Question#map} */
        it('works with a promise', () => {

            const SomeResult = () =>
                Question.about('result of some calculation', (actor: Actor) => Promise.resolve(' 6.67%\n'));

            const endResult = SomeResult()
                .map(trim())
                .map(replace('%', ''))
                .map(toNumber())
                .answeredBy(actorCalled('Jacques'))

            return expect(endResult).to.eventually.equal(6.67);
        });

        /** @test {Question.about} */
        /** @test {Question#map} */
        it('works with a static list of values', () => {
            const SomeResults = () =>
                Question.about<string[]>('results of some calculation', (actor: Actor) => [
                    ' 6.67%\n',
                    ' 3.34%\n',
                ]);

            const endResult = SomeResults()
                .map(trim())
                .map(replace('%', ''))
                .map(toNumber())
                .answeredBy(actorCalled('Jacques'))

            return expect(endResult).to.eventually.deep.equal([6.67, 3.34]);
        });

        /** @test {Question.about} */
        /** @test {Question#map} */
        it('works with a promised list', () => {
            const SomeResults = () =>
                Question.about<Promise<string[]>>('results of some calculation', (actor: Actor) => Promise.resolve([
                    ' 6.67%\n',
                    ' 3.34%\n',
                ]));

            const endResult = SomeResults()
                .map(trim())
                .map(replace('%', ''))
                .map(toNumber())
                .answeredBy(actorCalled('Jacques'))

            return expect(endResult).to.eventually.deep.equal([6.67, 3.34]);
        });

        /** @test {Question.about} */
        /** @test {Question#map} */
        it('works with a mappable collection (Array, ElementArrayFinder, etc.)', () => {
            const SomeResults = () =>
                Question.about<Mappable<string>>('results of some calculation', (actor: Actor) => [
                    ' 6.67%\n',
                    ' 3.34%\n',
                ]);

            const endResult = SomeResults()
                .map(trim())
                .map(replace('%', ''))
                .map(toNumber())
                .answeredBy(actorCalled('Jacques'))

            return expect(endResult).to.eventually.deep.equal([6.67, 3.34]);
        });
    });

    given([
        { isAQuestion: false, value: null                                           },
        { isAQuestion: false, value: false                                          },
        { isAQuestion: false, value: void 0                                         },
        { isAQuestion: false, value: ''                                             },
        { isAQuestion: false, value: {}                                             },
        { isAQuestion: false, value: 42                                             },
        { isAQuestion: false, value: () => void 0                                   },
        { isAQuestion: true,  value: Question.about('something', actor => void 0)   },
    ]).
    it('recognises if something is a question', ({ value, isAQuestion }) => {
        expect(Question.isAQuestion(value)).to.equal(isAQuestion);
    });
});

import 'mocha';

import * as sinon from 'sinon';
import { ImplementationPendingError } from '../../src/errors';
import { CorrelationId } from '../../src/model';
import { Actor, Interaction, Task } from '../../src/screenplay';
import { Stage } from '../../src/stage';
import { expect } from '../expect';

describe('Task', () => {

    let stage: sinon.SinonStubbedInstance<Stage>;

    beforeEach(() => {
        stage = sinon.createStubInstance(Stage);

        const
            sceneId = new CorrelationId('some-scene-id'),
            activityId = new CorrelationId('some-activity-id');

        stage.currentSceneId.returns(sceneId);
        stage.assignNewActivityId.returns(activityId);
        stage.currentActivityId.returns(activityId);
    });

    const
        Nock    = () => Interaction.where(`#actor places an arrow on the bow`, actor => void 0),
        Draw    = () => Interaction.where(`#actor pulls back the bow string`, actor => void 0),
        Loose   = () => Interaction.where(`#actor releases an arrow from the bow`, actor => void 0);

    const ShootAnArrow = () => Task.where(`#actor shoots an arrow`,
        Nock(),
        Draw(),
        Loose(),
    );

    /** @test {Task} */
    it('provides a convenient factory method for defining tasks', () => {

        const Lara = new Actor('Lara', stage as unknown as Stage);

        return expect(Lara.attemptsTo(ShootAnArrow())).to.be.fulfilled;
    });

    /** @test {Task} */
    it('provides a way to describe a collection of activities', () => {
        expect(ShootAnArrow().toString()).to.equal(`#actor shoots an arrow`);
    });

    /** @test {Task} */
    it('generates a pending task if no activities are provided', () => {

        const Lara = new Actor('Lara', stage as unknown as Stage);

        const ClimbAMountain = () => Task.where(`#actor climbs a mountain`);

        return expect(Lara.attemptsTo(ClimbAMountain()))
            .to.be.rejectedWith(ImplementationPendingError, `A task where "#actor climbs a mountain" has not been implemented yet`);
    });
});

import 'mocha';

import { actorCalled, engage, LogicError, serenity } from '../../../src';
import { SceneFinished, SceneFinishes, SceneStarts } from '../../../src/events';
import { FileSystemLocation, Path } from '../../../src/io';
import { Category, CorrelationId, ExecutionSuccessful, Name, ScenarioDetails } from '../../../src/model';
import { Actor, Note, Question, TakeNote, TakeNotes } from '../../../src/screenplay';
import { Cast } from '../../../src/stage';
import { expect } from '../../expect';
import { EnsureSame } from '../EnsureSame';

/** @test {TakeNotes} */
describe('TakeNotes', () => {

    class Actors implements Cast {
        prepare(actor: Actor): Actor {
            switch (actor.name) {
                case 'Alice':
                case 'Bob':
                    return actor.whoCan(
                        TakeNotes.usingASharedNotepad(),
                    );
                case 'Emma':
                case 'Wendy':
                default:
                    return actor.whoCan(
                        TakeNotes.usingAnEmptyNotepad()
                    );
            }
        }
    }

    const drinks = {
        'Alice': 'Apple Juice',
        'Bob': 'Beer',
        'Emma': 'Earl Grey tea',
        'Wendy': 'Water',
    }

    const AFavouriteDrink = () =>
        Question.about<string>(`a favourite drink`, (actor: Actor) => drinks[actor.name]);

    const sceneId = new CorrelationId(`some scene id`); // the actual value doesn't matter since discarding abilities is not scene id-dependent

    before(() => engage(new Actors()));

    /** @test {TakeNotes.usingAnEmptyNotepad} */
    describe('usingAnEmptyNotepad', () => {

        beforeEach(async () => {
            serenity.announce(new SceneStarts(sceneId, scenarioDetails));
        });

        afterEach(async () => {
            serenity.announce(new SceneFinishes(sceneId, scenarioDetails, new ExecutionSuccessful()));
            serenity.announce(new SceneFinished(sceneId, scenarioDetails, new ExecutionSuccessful()));
        });

        /**
         * @test {TakeNotes.usingAnEmptyNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it('enables the actor to take note of an answer to a given question and recall it later', () =>
            actorCalled('Emma').attemptsTo(
                TakeNote.of(AFavouriteDrink()),
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Emma),
            ));

        /**
         * @test {TakeNotes.usingAnEmptyNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it('enables the actor to take note of an answer to a given question under a custom name', () =>
            actorCalled('Emma').attemptsTo(
                TakeNote.of(AFavouriteDrink()).as('favourite drink'),
                EnsureSame(Note.of<string>('favourite drink'), drinks.Emma),
            ));

        /**
         * @test {TakeNotes.usingAnEmptyNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it(`ensures that actors have their own notepads and don't share notes`, async () => {
            await actorCalled('Emma').attemptsTo(
                TakeNote.of(AFavouriteDrink())
            );

            await actorCalled('Wendy').attemptsTo(
                TakeNote.of(AFavouriteDrink()),
            );

            await actorCalled('Emma').attemptsTo(
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Emma),
            );

            await actorCalled('Wendy').attemptsTo(
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Wendy),
            );
        });

        /**
         * @test {TakeNotes.usingAnEmptyNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it(`ensures the notepad is cleared between test scenarios`, async () => {

            await actorCalled('Emma').attemptsTo(
                TakeNote.of(AFavouriteDrink())
            );

            serenity.announce(new SceneFinishes(sceneId, scenarioDetails, new ExecutionSuccessful()))
            serenity.announce(new SceneFinished(sceneId, scenarioDetails, new ExecutionSuccessful()))

            await serenity.waitForNextCue()

            serenity.announce(new SceneStarts(sceneId, scenarioDetails))

            return expect(actorCalled('Emma').attemptsTo(
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Alice),
            )).to.be.rejectedWith(LogicError, `The answer to "a favourite drink" has never been recorded`);
        });
    });

    /** @test {TakeNotes.usingASharedNotepad} */
    describe('usingASharedNotepad', () => {

        /**
         * @test {TakeNotes.usingASharedNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it('enables the actor to take note of an answer to a given question and recall it later', () =>
            actorCalled('Alice').attemptsTo(
                TakeNote.of(AFavouriteDrink()),
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Alice),
            ));

        /**
         * @test {TakeNotes.usingASharedNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it(`ensures that actors can share their notes`, async () => {
            await actorCalled('Alice').attemptsTo(
                TakeNote.of(AFavouriteDrink()),
            );

            await actorCalled('Bob').attemptsTo(
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Alice),
            );
        });

        /**
         * @test {TakeNotes.usingASharedNotepad}
         * @test {Note}
         * @test {TakeNote}
         */
        it(`ensures the notepad is shared between test scenarios`, async () => {
            await actorCalled('Alice').attemptsTo(
                TakeNote.of(AFavouriteDrink()),
            );

            serenity.announce(new SceneFinishes(sceneId, scenarioDetails, new ExecutionSuccessful()));
            serenity.announce(new SceneFinished(sceneId, scenarioDetails, new ExecutionSuccessful()));
            await serenity.waitForNextCue();

            serenity.announce(new SceneStarts(sceneId, scenarioDetails))

            return expect(actorCalled('Alice').attemptsTo(
                EnsureSame(Note.of(AFavouriteDrink()), drinks.Alice),
            )).to.be.fulfilled;
        });
    });

    const scenarioDetails = new ScenarioDetails(
        new Name('ensures the notepad is cleared between test scenarios'),
        new Category('TakeNotes'),
        new FileSystemLocation(new Path('TakeNotes.spec.ts'))
    );
});

import 'mocha';

import * as sinon from 'sinon';
import { ConfigurationError, TestCompromisedError } from '../../src/errors';

import { InteractionFinished, InteractionStarts } from '../../src/events';
import { CorrelationId, ExecutionSuccessful, Name, Timestamp } from '../../src/model';
import { Ability, Actor, Initialisable, See } from '../../src/screenplay';
import { Stage } from '../../src/stage';
import { expect } from '../expect';
import { AcousticGuitar, Chords, Guitar, MusicSheets, NumberOfGuitarStringsLeft, PlayAChord, PlayAGuitar, PlayASong } from './example-implementation';

const equals = (expected: number) => (actual: PromiseLike<number>) => expect(actual).to.equal(expected);

describe('Actor', () => {

    const
        sceneId = new CorrelationId('some-scene-id'),
        activityId = new CorrelationId('some-activity-id');

    let
        guitar: sinon.SinonStubbedInstance<Guitar>,
        stage: sinon.SinonStubbedInstance<Stage>;

    beforeEach(() => {
        guitar = sinon.createStubInstance(AcousticGuitar);
        stage = sinon.createStubInstance(Stage);

        stage.assignNewActivityId.returns(activityId);
        stage.currentSceneId.returns(sceneId);
        stage.currentActivityId.returns(activityId);
    });

    function actor(name: string) {
        return new Actor(name, stage as unknown as Stage);
    }

    /** @test {Actor} */
    it('can be identified by their name', () => {

        expect(actor('Chris').name).to.equal('Chris');
    });

    /** @test {Actor} */
    it('provides a developer-friendly toString', () => {
        class DoCoolThings implements Ability {
        }

        expect(actor('Chris').toString()).to.equal('Actor(name=Chris, abilities=[])');

        expect(actor('Chris').whoCan(new DoCoolThings()).toString()).to.equal('Actor(name=Chris, abilities=[DoCoolThings])');
    });

    /** @test {Actor} */
    it('has Abilities allowing them to perform Activities and interact with a given interface of the system under test', () =>

        actor('Chris').whoCan(PlayAGuitar.suchAs(guitar)).attemptsTo(
            PlayAChord.of(Chords.AMajor),
        ).
        then(() => {
            expect(guitar.play).to.have.been.calledWith(Chords.AMajor);
        }));

    /** @test {Actor} */
    it('performs composite Tasks recursively to accomplish their Business Goals', () =>

        actor('Chris').whoCan(PlayAGuitar.suchAs(guitar)).attemptsTo(
            PlayASong.from(MusicSheets.Wild_Thing),
        ).
        then(() => {
            expect(guitar.play.getCall(0)).to.have.been.calledWith(Chords.AMajor);
            expect(guitar.play.getCall(1)).to.have.been.calledWith(Chords.DMajor);
            expect(guitar.play.getCall(2)).to.have.been.calledWith(Chords.EMajor);
        }));

    describe('asks Questions about the state of the system', () => {
        /** @test {Actor} */
        it('fulfills the promise should the question be answered as expected', () => {
            guitar.availableStrings.returns(Promise.resolve(['E2', 'A2', 'D3', 'G3', 'B3', 'E4' ]));

            return expect(actor('Chris').whoCan(PlayAGuitar.suchAs(guitar)).attemptsTo(
                PlayASong.from(MusicSheets.Wild_Thing),
                See.if(NumberOfGuitarStringsLeft(), equals(6)),
            )).to.be.fulfilled;
        });

        /** @test {Actor} */
        it('rejects the promise should the answer differ from what was expected', () => {
            const oneStringMissing = ['E2', 'A2', 'D3', 'G3', 'B3' ];
            guitar.availableStrings.returns(Promise.resolve(oneStringMissing));

            return expect(actor('Chris').whoCan(PlayAGuitar.suchAs(guitar)).attemptsTo(
                PlayASong.from(MusicSheets.Wild_Thing),
                See.if(NumberOfGuitarStringsLeft(), equals(6)),
            )).to.be.rejectedWith('expected 5 to equal 6');
        });
    });

    describe('when using abilities', () => {

        /** @test {Actor} */
        it('admits if it does not have the Ability necessary to accomplish a given Interaction', () =>

            expect(actor('Ben').attemptsTo(
                PlayAChord.of(Chords.AMajor),
            )).to.be.eventually.rejectedWith(ConfigurationError, `Ben can't PlayAGuitar yet. Did you give them the ability to do so?`));

        /** @test {Actor} */
        it('complains if given the same ability twice', () => {

            expect(() =>
                actor('Ben').whoCan(PlayAGuitar.suchAs(guitar), PlayAGuitar.suchAs(guitar))
            ).to.throw(ConfigurationError, `Ben already has an ability to PlayAGuitar, so you don't need to give it to them again.`);
        });

        describe('that have to be initialised', () => {

            class UseDatabase implements Initialisable, Ability {
                public callsToInitialise = 0;
                private connection = null;

                initialise(): Promise<void> | void {
                    this.connection = 'some connection';

                    this.callsToInitialise++;
                }

                isInitialised(): boolean {
                    return !! this.connection;
                }
            }

            class UseBrokenDatabase implements Initialisable, Ability {
                initialise(): Promise<void> | void {
                    throw new Error('DB server is down, please cheer it up');
                }

                isInitialised(): boolean {
                    return false;
                }
            }

            /** @test {Actor} */
            it('initialises them upon the first call to attemptsTo', async () => {

                const useDatabase = new UseDatabase();

                await actor('Dibillo').whoCan(useDatabase).attemptsTo(/* */);

                expect(useDatabase.isInitialised()).to.equal(true);
            });

            /** @test {Actor} */
            it(`initialises them only if they haven't been initialised before`, async () => {

                const useDatabase = new UseDatabase();

                await actor('Dibillo').whoCan(useDatabase).attemptsTo(/* */);
                await actor('Dibillo').whoCan(useDatabase).attemptsTo(/* */);
                await actor('Dibillo').whoCan(useDatabase).attemptsTo(/* */);

                expect(useDatabase.callsToInitialise).to.equal(1);
            });

            /** @test {Actor} */
            it(`complains if the ability could not be initialised`, () => {

                return expect(actor('Dibillo').whoCan(new UseBrokenDatabase()).attemptsTo(/* */))
                    .to.be.rejectedWith(TestCompromisedError, `Dibillo couldn't initialise the ability to UseBrokenDatabase`)
                    .then(error => {
                        expect(error.cause.message).to.equal('DB server is down, please cheer it up')
                    });
            });
        });
    });

    describe('DomainEvent handling', () => {

        let Bob: Actor;
        const now = new Timestamp(new Date('2018-06-10T22:57:07.112Z'));
        const activityName = new Name('Bob plays the chord of A');

        beforeEach(() => {
            stage = sinon.createStubInstance(Stage);
            stage.currentTime.returns(now);

            stage.currentSceneId.returns(sceneId);
            stage.assignNewActivityId.returns(activityId);
            stage.currentActivityId.returns(activityId);

            Bob = new Actor('Bob', stage as unknown as Stage);
        });

        describe('announces the events that activities it performs', () => {

            /** @test {Actor} */
            it('notifies when an activity begins and ends', () => Bob.whoCan(PlayAGuitar.suchAs(guitar)).attemptsTo(
                PlayAChord.of(Chords.AMajor),
            ).then(() => {
                expect(stage.announce).to.have.callCount(2);

                const
                    firstEvent = stage.announce.getCall(0).args[0],
                    secondEvent = stage.announce.getCall(1).args[0];

                expect(firstEvent).to.be.instanceOf(InteractionStarts);
                expect(firstEvent).to.have.property('details').property('name').equal(activityName);
                expect(firstEvent).to.have.property('timestamp').equal(now);

                expect(secondEvent).to.be.instanceOf(InteractionFinished);
                expect(secondEvent).to.have.property('details').property('name').equal(activityName);
                expect(secondEvent).to.have.property('outcome').equal(new ExecutionSuccessful());
                expect(secondEvent).to.have.property('timestamp').equal(now);
            }));
        });
    });
});

import { AssertionError } from 'chai';

import sinon = require('sinon');
import { Actor, Question, See } from '../../../src/screenplay';
import { Stage } from '../../../src/stage';
import { expect } from '../../expect';

describe('Interactions', () => {

    describe('See', () => {

        const stage = sinon.createStubInstance(Stage);

        describe('when used with async questions', () => {
            const SomeAsyncResult = () => Question.about<Promise<string>>('some async result', actor => Promise.resolve('some value'));

            /** @test {See} */
            it('allows the actor to verify a condition', () => {
                const actor = new Actor('James', stage as unknown as Stage);

                const promise = See.if(SomeAsyncResult(), r => expect(r).to.equal('some value')).performAs(actor);

                return expect(promise).to.be.eventually.fulfilled;
            });

            /** @test {See} */
            it('rejects the promise if the condition is not met', () => {
                const actor = new Actor('James', stage as unknown as Stage);

                const promise = See.if(SomeAsyncResult(), r => expect(r).to.equal('other value')).performAs(actor);

                return expect(promise).to.be.eventually.rejectedWith(AssertionError, `expected 'some value' to equal 'other value'`);
            });
        });

        describe('when used with sync questions', () => {
            const SomeSyncResult = () => Question.about<string>('some async result', actor => 'some value');

            /** @test {See} */
            it('allows the actor to verify a condition', () => {
                const actor = new Actor('James', stage as unknown as Stage);

                const promise = See.if(SomeSyncResult(), r => expect(r).to.equal('some value')).performAs(actor);

                return expect(promise).to.be.eventually.fulfilled;
            });

            /** @test {See} */
            it('rejects the promise if the condition is not met', () => {
                const actor = new Actor('James', stage as unknown as Stage);

                const promise = See.if(SomeSyncResult(), r => expect(r).to.equal('other value')).performAs(actor);

                return expect(promise).to.be.eventually.rejectedWith(AssertionError, `expected 'some value' to equal 'other value'`);
            });
        });
    });
});

import 'mocha';

import * as sinon from 'sinon';
import { LogicError } from '../../../src/errors';
import { CorrelationId } from '../../../src/model';
import { Ability, Actor, Log, Note, Question, TakeNote, TakeNotes } from '../../../src/screenplay';
import { Stage } from '../../../src/stage';
import { expect } from '../../expect';
import { EnsureSame } from '../EnsureSame';

describe('Note', () => {
    const
        expectedHobby = 'DYI',
        NameOfAHobby = () => Question.about(`the name of a hobby`, someActor => Promise.resolve(expectedHobby));

    const
        sceneId = new CorrelationId('some-scene-id'),
        activityId = new CorrelationId('some-activity-id');

    let stage: sinon.SinonStubbedInstance<Stage>,
        Noah: Actor;

    beforeEach(() => {
        stage = sinon.createStubInstance(Stage);

        stage.currentSceneId.returns(sceneId);
        stage.assignNewActivityId.returns(activityId);
        stage.currentActivityId.returns(activityId);

        Noah = new Actor('Noah', stage as unknown as Stage)
            .whoCan(TakeNotes.usingAnEmptyNotepad());
    });

    /**
     * @test {TakeNotes}
     * @test {Note}
     */
    it('enables the actor to recall the answer to a given question', () =>
        actorWhoCan(TakeNotes.usingAnEmptyNotepad()).attemptsTo(
            TakeNote.of(NameOfAHobby()),
            EnsureSame(Note.of(NameOfAHobby()), 'DYI'),
        ));

    /**
     * @test {TakeNotes}
     * @test {Note}
     */
    it('enables the actor to recall the answer on a given subject', () =>
        actorWhoCan(TakeNotes.usingAnEmptyNotepad()).attemptsTo(
            TakeNote.of(NameOfAHobby()).as('custom subject'),
            EnsureSame(Note.of('custom subject'), 'DYI'),
        ));

    /**
     * @test {TakeNotes}
     * @test {Note}
     */
    it('complains if no answer to a given question has ever been remembered', () =>
        expect(actorWhoCan(TakeNotes.usingAnEmptyNotepad()).attemptsTo(
            Log.the(Note.of(NameOfAHobby())),
        )).to.be.rejectedWith(LogicError, 'The answer to "the name of a hobby" has never been recorded'));

    /**
     * @test {TakeNotes}
     * @test {Note}
     */
    it('complains if no answer on a given subject has ever been remembered', () =>
        expect(actorWhoCan(TakeNotes.usingAnEmptyNotepad()).attemptsTo(
            Log.the(Note.of('some subject')),
        )).to.be.rejectedWith(LogicError, 'The answer to "some subject" has never been recorded'));

    function actorWhoCan(...abilities: Ability[]): Actor {
        return new Actor('Noah', stage as unknown as Stage)
            .whoCan(...abilities);
    }
});

import { given } from 'mocha-testdata';
import * as sinon from 'sinon';

import { Actor, Answerable, Question, Transform } from '../../../src/screenplay';
import { Stage } from '../../../src/stage';
import { expect } from '../../expect';

/** @test {TransformAnswer} */
describe('TransformAnswer', () => {

    const stage = sinon.createStubInstance(Stage);

    const expectedAnswer = {
        headers: { header: 'value' },
        body: 'some content',
    };

    type EA = typeof expectedAnswer;

    const actor = new Actor('Bumblebee', stage as unknown as Stage);

    given([
        expectedAnswer,
        p(expectedAnswer),
        q(expectedAnswer),
        q(p(expectedAnswer)),
    ]).
    it('transforms an answer to Answerable<T> to another type', (answerable: Answerable<EA>) =>
       expect(
           Transform.the(answerable, complexObject => complexObject.headers.header).answeredBy(actor),
       ).to.eventually.equal(expectedAnswer.headers.header));

    it('transforms answers to Array<Answerable<T>> to another type', () =>
        expect(
            Transform.the(
                [ q(p(expectedAnswer)), q(expectedAnswer), expectedAnswer],
                (ea1: EA, ea2: EA, ea3: EA) => ea1.headers.header + ea2.body + Object.keys(ea3).length,
            ).answeredBy(actor),
        ).to.eventually.equal(expectedAnswer.headers.header + expectedAnswer.body + Object.keys(expectedAnswer).length));

    it('transforms answers to questions of different types to another type', () =>
        expect(
            Transform.the(
                [ q('the answer to life the universe and everything'), q(p(42)) ],
                (a1: string, a2: number) => `${ a1 } is ${ a2 }`,
            ).answeredBy(actor),
        ).to.eventually.equal(`the answer to life the universe and everything is 42`));

    it('provides a sensible default description', () => {
        expect(Transform.the(expectedAnswer, _ => _).toString()).to.equal('a transformed answer');
    });

    it('allows for the default description to be changed', () => {
        expect(Transform.the(expectedAnswer, _ => _).as('a better description').toString()).to.equal('a better description');
    });

    function q<T>(value: T): Question<T> {
        return Question.about(`something`, someActor => value);
    }

    function p<T>(value: T) {
        return Promise.resolve(value);
    }
});

import 'mocha';

import { given } from 'mocha-testdata';
import { actorCalled } from '../../../../../src';
import { append, Question } from '../../../../../src/screenplay';
import { expect } from '../../../../expect';

/** @test {append} */
/** @test {Question#map} */
describe('append', () => {

    const quentin = actorCalled('Quentin');

    const q = <T>(value: T) =>
        Question.about<T>('some value', actor => value);

    const p = <T>(value: T) =>
        Promise.resolve(value);

    given([
        { value: null,              expected: 'The value to be mapped should be defined'    },
        { value: undefined,         expected: 'The value to be mapped should be defined'    },
        { value: 1,                 expected: 'The value to be mapped should be a string'   },
        { value: { },               expected: 'The value to be mapped should be a string'   },
        { value: [null],            expected: 'The value to be mapped should be defined'    },
        { value: [undefined],       expected: 'The value to be mapped should be defined'    },
        { value: [1],               expected: 'The value to be mapped should be a string'   },
        { value: [{ }],             expected: 'The value to be mapped should be a string'   },
        { value: p(null),           expected: 'The value to be mapped should be defined'    },
        { value: p(undefined),      expected: 'The value to be mapped should be defined'    },
        { value: p(1),              expected: 'The value to be mapped should be a string'   },
        { value: p({ }),            expected: 'The value to be mapped should be a string'   },
        { value: p([ null ]),       expected: 'The value to be mapped should be defined'    },
        { value: p([ undefined ]),  expected: 'The value to be mapped should be defined'    },
        { value: p([ 1 ]),          expected: 'The value to be mapped should be a string'   },
        { value: p([ { } ]),        expected: 'The value to be mapped should be a string'   },
    ]).
    it('complains if the original answer is of a wrong type at runtime', ({ value, expected }) => {
        const result = q<any>(value)
            .map(append('other value'))
            .answeredBy(quentin);

        return expect(result).to.be.rejectedWith(expected);
    });

    /** @test {append} */
    it('allows for a string to be appended to the original answer', () => {

        const result = q('Hello')
            .map(append('!'))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hello!');
    });

    /** @test {append} */
    it('allows for a Promise<string> to be appended to the original answer', () => {

        const result = q('Hello')
            .map(append(p('!')))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hello!');
    });

    /** @test {append} */
    it('allows for a Question<string> to be appended to the original answer', () => {

        const result = q('Hello')
            .map(append(q('!')))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hello!');
    });

    /** @test {append} */
    it('allows for a Question<Promise<string>> to be appended to the original answer', () => {

        const result = q('Hello')
            .map(append(q(p('!'))))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hello!');
    });
});

import 'mocha';

import { given } from 'mocha-testdata';
import { actorCalled } from '../../../../../src';
import { normalize, Question } from '../../../../../src/screenplay';
import { expect } from '../../../../expect';

/** @test {normalize} */
/** @test {Question#map} */
describe('normalize', () => {

    const quentin = actorCalled('Quentin');

    const q = <T>(value: T) =>
        Question.about<T>('some value', actor => value);

    const p = <T>(value: T) =>
        Promise.resolve(value);

    /**
     * U+1E9B: LATIN SMALL LETTER LONG S WITH DOT ABOVE
     * U+0323: COMBINING DOT BELOW
     *
     * @see https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/normalize
     */
    const initialString = '\u1E9B\u0323';

    given([
        { value: null,              expected: 'The value to be mapped should be defined'    },
        { value: undefined,         expected: 'The value to be mapped should be defined'    },
        { value: 1,                 expected: 'The value to be mapped should be a string'   },
        { value: { },               expected: 'The value to be mapped should be a string'   },
        { value: [null],            expected: 'The value to be mapped should be defined'    },
        { value: [undefined],       expected: 'The value to be mapped should be defined'    },
        { value: [1],               expected: 'The value to be mapped should be a string'   },
        { value: [{ }],             expected: 'The value to be mapped should be a string'   },
        { value: p(null),           expected: 'The value to be mapped should be defined'    },
        { value: p(undefined),      expected: 'The value to be mapped should be defined'    },
        { value: p(1),              expected: 'The value to be mapped should be a string'   },
        { value: p({ }),            expected: 'The value to be mapped should be a string'   },
        { value: p([ null ]),       expected: 'The value to be mapped should be defined'    },
        { value: p([ undefined ]),  expected: 'The value to be mapped should be defined'    },
        { value: p([ 1 ]),          expected: 'The value to be mapped should be a string'   },
        { value: p([ { } ]),        expected: 'The value to be mapped should be a string'   },
    ]).
    it('complains if the original answer is of a wrong type at runtime', ({ value, expected }) => {
        const result = q<any>(value)
            .map(normalize('other value'))
            .answeredBy(quentin);

        return expect(result).to.be.rejectedWith(expected);
    });

    given([
        undefined,
        p(undefined),
        q(undefined),
        q(p(undefined)),
    ]).
    it('uses Canonical Decomposition, followed by Canonical Composition (NFC) form by default', form => {

        const result = q(initialString)
            .map(normalize(form))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('\u1E9B\u0323');
    });

    given([
        'NFC',
        p('NFC'),
        q('NFC'),
        q(p('NFC')),
    ]).
    it('supports Canonical Decomposition, followed by Canonical Composition (NFC) form', form => {

        const result = q(initialString)
            .map(normalize(form))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('\u1E9B\u0323');
    });

    given([
        'NFD',
        p('NFD'),
        q('NFD'),
        q(p('NFD')),
    ]).
    it('supports Canonical Decomposition (NFD) form', form => {

        const result = q(initialString)
            .map(normalize(form))
            .answeredBy(quentin);

        // U+017F: latin small letter long s
        // U+0323: combining dot below
        // U+0307: combining dot above
        return expect(result).to.eventually.equal('\u017F\u0323\u0307');
    });

    given([
        'NFKC',
        p('NFKC'),
        q('NFKC'),
        q(p('NFKC')),
    ]).
    it('supports Compatibility Decomposition, followed by Canonical Composition (NFKC) form', form => {

        const result = q(initialString)
            .map(normalize(form))
            .answeredBy(quentin);

        // U+1E69: latin small letter s with dot below and dot above
        return expect(result).to.eventually.equal('\u1E69');
    });

    given([
        'NFKD',
        p('NFKD'),
        q('NFKD'),
        q(p('NFKD')),
    ]).
    it('supports Compatibility Decomposition (NFKD) form', form => {

        const result = q(initialString)
            .map(normalize(form))
            .answeredBy(quentin);

        // U+0073: latin small letter s
        // U+0323: combining dot below
        // U+0307: combining dot above
        return expect(result).to.eventually.equal('\u0073\u0323\u0307');
    });
});

import 'mocha';

import { given } from 'mocha-testdata';
import { actorCalled } from '../../../../../src';
import { replace, Question } from '../../../../../src/screenplay';
import { expect } from '../../../../expect';

/** @test {replace} */
/** @test {Question#map} */
describe('replace', () => {

    const quentin = actorCalled('Quentin');

    const q = <T>(value: T) =>
        Question.about<T>('some value', actor => value);

    const p = <T>(value: T) =>
        Promise.resolve(value);

    given([
        { value: null,              expected: 'The value to be mapped should be defined'  },
        { value: undefined,         expected: 'The value to be mapped should be defined'  },
        { value: 1,                 expected: 'The value to be mapped should be a string' },
        { value: { },               expected: 'The value to be mapped should be a string' },
        { value: [null],            expected: 'The value to be mapped should be defined'  },
        { value: [undefined],       expected: 'The value to be mapped should be defined'  },
        { value: [1],               expected: 'The value to be mapped should be a string' },
        { value: [{ }],             expected: 'The value to be mapped should be a string' },
        { value: p(null),           expected: 'The value to be mapped should be defined'  },
        { value: p(undefined),      expected: 'The value to be mapped should be defined'  },
        { value: p(1),              expected: 'The value to be mapped should be a string' },
        { value: p({ }),            expected: 'The value to be mapped should be a string' },
        { value: p([ null ]),       expected: 'The value to be mapped should be defined'  },
        { value: p([ undefined ]),  expected: 'The value to be mapped should be defined'  },
        { value: p([ 1 ]),          expected: 'The value to be mapped should be a string' },
        { value: p([ { } ]),        expected: 'The value to be mapped should be a string' },
    ]).
    it('complains if the original answer is of a wrong type at runtime', ({ value, expected }) => {
        const result = q<any>(value)
            .map(replace('pattern', 'replacement'))
            .answeredBy(quentin);

        return expect(result).to.be.rejectedWith(expected);
    });

    given([
        {  pattern: 'Hello',        replacement: 'Hi'       },
        {  pattern: p('Hello'),     replacement: 'Hi'       },
        {  pattern: q('Hello'),     replacement: 'Hi'       },
        {  pattern: q(p('Hello')),  replacement: 'Hi'       },
        {  pattern: 'Hello',        replacement: p('Hi')    },
        {  pattern: 'Hello',        replacement: q('Hi')    },
        {  pattern: 'Hello',        replacement: q(p('Hi')) },
    ]).
    it('allows for a string to be replaced with another string', ({ pattern, replacement }) => {

        const result = q('Hello World!')
            .map(replace(pattern, replacement))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hi World!');
    });

    given([
        {  pattern: /(H|h)ello/,        replacement: '$1i'       },
        {  pattern: p(/(H|h)ello/),     replacement: '$1i'       },
        {  pattern: q(/(H|h)ello/),     replacement: '$1i'       },
        {  pattern: q(p(/(H|h)ello/)),  replacement: '$1i'       },
        {  pattern: /(H|h)ello/,        replacement: p('$1i')    },
        {  pattern: /(H|h)ello/,        replacement: q('$1i')    },
        {  pattern: /(H|h)ello/,        replacement: q(p('$1i')) },
    ]).
    it('allows for a RegEx pattern to be replaced with a string', ({ pattern, replacement }) => {

        const result = q('Hello World!')
            .map(replace(pattern, replacement))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('Hi World!');
    });

    given([
        {  pattern: /[A-Z]/g,        replacement: _ => _.toLowerCase()       },
        {  pattern: p(/[A-Z]/g),     replacement: _ => _.toLowerCase()       },
        {  pattern: q(/[A-Z]/g),     replacement: _ => _.toLowerCase()       },
        {  pattern: q(p(/[A-Z]/g)),  replacement: _ => _.toLowerCase()       },
        {  pattern: /[A-Z]/g,        replacement: p(_ => _.toLowerCase())    },
        {  pattern: /[A-Z]/g,        replacement: q(_ => _.toLowerCase())    },
        {  pattern: /[A-Z]/g,        replacement: q(p(_ => _.toLowerCase())) },
    ]).
    it('allows for a pattern to be replaced with a replacement function', ({ pattern, replacement }) => {

        const result = q('Hello World!')
            .map(replace(pattern, replacement))
            .answeredBy(quentin);

        return expect(result).to.eventually.equal('hello world!');
    });
});

