describe('Stage', () => {

    class Extras implements Cast {
        prepare(actor: Actor): Actor {
            return actor;
        }
    }

    const stageManager = sinon.createStubInstance(StageManager);

    /**
     * @test {Stage#actor}
     * @test {Stage#theActorCalled}
     */
    it('provides both the more verbose and more concise way of accessing the actors', () => {
        const
            name   = 'Alice',
            actors = new Extras(),
            stage  = new Stage(actors, stageManager as unknown as StageManager);

        expect(stage.actor(name)).to.equal(stage.theActorCalled(name));
    });

    describe('when instantiating actors', () => {

        /** @test {Stage#actor} */
        it('instantiates a new actor when their name is called for the first time', () => {
            const
                name   = 'Alice',
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            sinon.spy(actors, 'prepare');

            const actor = stage.actor(name);

            expect((actors.prepare as any).getCall(0).args[0].name).to.equal(name);
            expect(actor.name).to.equal('Alice');
        });

        /** @test {Stage#actor} */
        it('returns an existing actor if it has already been instantiated before', () => {
            const
                name   = 'Alice',
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            sinon.spy(actors, 'prepare');

            const a1 = stage.actor(name);
            const a2 = stage.actor(name);

            expect((actors.prepare as any).getCall(0).args[0].name).to.equal(name);
            expect(actors.prepare).to.have.been.calledOnce;                    // tslint:disable-line:no-unused-expression

            expect(a1).to.equal(a2);
        });
    });

    describe('when referencing a recently retrieved actor', () => {

        /** @test {Stage#actor} */
        it('retrieves the current actor, if there is any', () => {
            const
                name   = 'Alice',
                stage  = new Stage(new Extras(), stageManager as unknown as StageManager);

            const a1 = stage.actor(name);
            const a2 = stage.theActorInTheSpotlight();

            expect(a2).to.equal(a1);
        });

        /**
         * @test {Stage#theActorInTheSpotlight}
         * @test {Stage#theActorInTheSpotlight}
         */
        it('provides both the more verbose and more concise way of accessing the actors in the spotlight', () => {
            const
                name   = 'Alice',
                stage  = new Stage(new Extras(), stageManager as unknown as StageManager);

            const a1 = stage.actor(name);
            const a2 = stage.theActorCalled(name);
            const a3 = stage.theActorInTheSpotlight();

            expect(a1).to.equal(a2);
            expect(a1).to.equal(a3);
        });

        /**
         * @test {Stage#theActorInTheSpotlight}
         * @test {Stage#theActorInTheSpotlight}
         */
        it(`complains if you try to access the actor in the spotlight, but there isn't any yet`, () => {
            const
                stage  = new Stage(new Extras(), stageManager as unknown as StageManager);

            expect(
                () => stage.theActorInTheSpotlight(),
            ).to.throw(LogicError, `There is no actor in the spotlight yet. Make sure you instantiate one with stage.actor(actorName) before calling this method.`);
        });
    });

    describe('when instantiating and dismissing the actors', () => {

        class SomeAbilityThatNeedsDiscarding implements Discardable, Ability {
            discard(): Promise<void> | void {
                return Promise.resolve();
            }
        }

        class Spies implements Cast {
            public readonly calls: Actor[] = [];

            prepare(actor: Actor): Actor {
                this.calls.push(actor);

                sinon.spy(actor, 'dismiss');

                return actor.whoCan(new SomeAbilityThatNeedsDiscarding());
            }
        }

        const
            sceneId = new CorrelationId('example scene'),
            anotherSceneId = new CorrelationId('another example scene'),
            scenario = new ScenarioDetails(
                new Name('Paying with a default card'),
                new Category('Online Checkout'),
                new FileSystemLocation(
                    new Path('payments/checkout.feature'),
                ),
        );

        let manager: StageManager;

        beforeEach(() => {
            manager = new StageManager(Duration.ofMilliseconds(100), new Clock());
        });

        describe('performing a single scene', () => {
            it('dismisses actors instantiated after SceneStarts when SceneFinished', async () => {
                const stage = new Stage(new Spies(), manager);

                stage.announce(new SceneStarts(sceneId, scenario));

                const actor = stage.actor('Bob');

                stage.announce(new SceneFinishes(sceneId, scenario, new ExecutionSuccessful()));

                expect(actor.dismiss).to.have.been.calledOnce;  // tslint:disable-line:no-unused-expression

                stage.announce(new SceneFinished(sceneId, scenario, new ExecutionSuccessful()));

                stage.announce(new TestRunFinishes());
                stage.announce(new TestRunFinished());

                await stage.waitForNextCue()

                // make sure it's not called again
                expect(actor.dismiss).to.have.been.calledOnce;  // tslint:disable-line:no-unused-expression
            });

            it('re-instantiates actors dismissed when the SceneFinished', async () => {
                const actors = new Spies();

                const stage = new Stage(actors, manager);

                stage.announce(new SceneStarts(sceneId, scenario));
                stage.actor('Bob');
                stage.announce(new SceneFinishes(sceneId, scenario, new ExecutionSuccessful()));
                stage.announce(new SceneFinished(sceneId, scenario, new ExecutionSuccessful()));

                await stage.waitForNextCue();

                stage.announce(new SceneStarts(anotherSceneId, scenario));
                stage.actor('Bob');
                stage.announce(new SceneFinishes(anotherSceneId, scenario, new ExecutionSuccessful()));
                stage.announce(new SceneFinished(anotherSceneId, scenario, new ExecutionSuccessful()));

                await stage.waitForNextCue();

                stage.announce(new TestRunFinishes());
                stage.announce(new TestRunFinished());

                await stage.waitForNextCue();

                expect(actors.calls).to.have.lengthOf(2);
            });
        });

        describe('performing across multiple scenes', () => {

            it('dismisses actors instantiated before SceneStarts when TestRunFinishes', async () => {
                const stage = new Stage(new Spies(), manager);

                const actor = stage.actor('Bob');

                stage.announce(new SceneStarts(sceneId, scenario));
                stage.announce(new SceneFinishes(sceneId, scenario, new ExecutionSuccessful()));

                expect(actor.dismiss).to.have.not.been.called;  // tslint:disable-line:no-unused-expression

                stage.announce(new SceneFinished(sceneId, scenario, new ExecutionSuccessful()));

                stage.announce(new TestRunFinishes());
                stage.announce(new TestRunFinished());

                await stage.waitForNextCue()

                // make sure it's called
                expect(actor.dismiss).to.have.been.calledOnce;  // tslint:disable-line:no-unused-expression
            });

            it('retains instances of actors instantiated before the SceneStarts', async () => {
                const actors = new Spies();

                const stage = new Stage(actors, manager);

                stage.actor('Bob');

                stage.announce(new SceneStarts(sceneId, scenario));
                stage.actor('Bob');
                stage.announce(new SceneFinished(sceneId, scenario, new ExecutionSuccessful()));

                await stage.waitForNextCue();

                stage.announce(new SceneStarts(anotherSceneId, scenario));
                stage.actor('Bob');
                stage.announce(new SceneFinished(anotherSceneId, scenario, new ExecutionSuccessful()));

                await stage.waitForNextCue();

                stage.announce(new TestRunFinishes());
                stage.announce(new TestRunFinished());

                await stage.waitForNextCue();

                expect(actors.calls).to.have.lengthOf(1);
            });
        });
    });

    describe('when correlating activities', () => {

        it('assigns sceneIds', () => {
            const
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            const assigned = stage.assignNewSceneId();
            const retrieved = stage.currentSceneId();

            expect(assigned).to.equal(retrieved);
        });

        it('returns a default sceneId when activities are performed outside of a test runner', () => {
            const
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            const retrieved = stage.currentSceneId();

            expect(retrieved.value).to.equal('unknown');
        });

        it('assigns activityIds', () => {
            const
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            const assigned = stage.assignNewActivityId();
            const retrieved = stage.currentActivityId();

            expect(assigned).to.equal(retrieved);
        });

        it('complains if an activityId is attempted to be retrieved before is has been assigned', () => {
            const
                actors = new Extras(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            expect(() => stage.currentActivityId()).to.throw(LogicError, 'No activity is being performed. Did you call assignNewActivityId before invoking currentActivityId?');
        });
    });

    describe('when an error occurs', () => {

        it('complains when instantiated with no Cast', () => {
            expect(() => {
                const stage = new Stage(null, stageManager as unknown as StageManager);
            }).to.throw(Error, 'Cast should be defined');
        });

        it('complains when instantiated with no StageManager', () => {
            expect(() => {
                const stage = new Stage(new Extras(), null);
            }).to.throw(Error, 'StageManager should be defined');
        });

        /** @test {Stage#actor} */
        it('complains if the Cast does not provide a way to instantiate a given actor', () => {
            const
                name   = 'Alice',
                actors: Cast = {
                    prepare: (actor: Actor) => undefined,
                },
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            expect(() => {
                stage.actor(name);
            }).to.throw(ConfigurationError, `Instead of a new instance of actor "${ name }", Cast returned undefined`);
        });

        /** @test {Stage#actor} */
        it('complains if the Cast does not provide a way to prepare a given actor and mentions the type of the Cast, when available', () => {
            class AwesomeActors implements Cast {
                prepare(actor: Actor): Actor {
                    return undefined;
                }
            }

            const
                name   = 'Alice',
                actors = new AwesomeActors(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            expect(() => {
                stage.actor(name);
            }).to.throw(ConfigurationError, `Instead of a new instance of actor "${ name }", AwesomeActors returned undefined`);
        });

        /** @test {Stage#actor} */
        it('complains if the Cast throws an error during actor instantiation', () => {
            const
                name   = 'Alice',
                actors: Cast = {
                    prepare: (actor: Actor) => { throw new Error(`I'm not working today`); },
                },
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            expect(() => {
                stage.actor(name);
            }).to.throw(ConfigurationError, `Cast encountered a problem when preparing actor "${ name }" for stage`);
        });

        /** @test {Stage#actor} */
        it('complains if the Cast throws an error during actor instantiation and mentions the type of the Cast, when available', () => {
            class MoodyActors implements Cast {
                prepare(actor: Actor): Actor {
                    throw new Error(`I'm not working today`);
                }
            }

            const
                name   = 'Alice',
                actors = new MoodyActors(),
                stage  = new Stage(actors, stageManager as unknown as StageManager);

            expect(() => {
                stage.actor(name);
            }).to.throw(ConfigurationError, `MoodyActors encountered a problem when preparing actor "${ name }" for stage`);
        });
    });
});

import 'mocha';

import { AsyncOperationAttempted, AsyncOperationCompleted, AsyncOperationFailed, DomainEvent } from '../../src/events';
import { CorrelationId, Description, Duration } from '../../src/model';
import { Clock, StageManager } from '../../src/stage';

import { expect } from '../expect';
import { Recorder } from '../Recorder';

describe('StageManager', () => {

    class TestEvent extends DomainEvent {
        constructor() {
            super();
        }
    }

    const testEvent = new TestEvent();

    /** @test {StageManager} */
    it('broadcasts the domain event it receives to all the registered subscribers', () => {

        const stageManager = new StageManager(Duration.ofMilliseconds(250), new Clock());
        const crewMember1 = new Recorder();
        const crewMember2 = new Recorder();

        stageManager.register(crewMember1, crewMember2);

        stageManager.notifyOf(testEvent);

        expect(crewMember1.events).to.have.lengthOf(1);
        expect(crewMember1.events[0]).to.be.instanceOf(TestEvent);
        expect(crewMember2.events).to.have.lengthOf(1);
        expect(crewMember2.events[0]).to.be.instanceOf(TestEvent);
    });

    /**
     * @test {StageManager}
     * @test {AsyncOperationAttempted}
     * @test {AsyncOperationCompleted}
     */
    it('keeps track of the work in progress', () => {

        const stageManager = new StageManager(Duration.ofMilliseconds(250), new Clock());

        const id = CorrelationId.create();

        stageManager.notifyOf(new AsyncOperationAttempted(
            new Description('Saving a file...'),
            id,
        ));
        stageManager.notifyOf(new AsyncOperationCompleted(
            new Description('File saved'),
            id,
        ));

        return expect(stageManager.waitForNextCue()).to.be.fulfilled;
    });

    /**
     * @test {StageManager}
     * @test {AsyncOperationAttempted}
     */
    it('provides details should the work in progress fail to complete', () => {

        const timeout       = Duration.ofMilliseconds(250);
        const stageManager = new StageManager(timeout, new Clock());

        stageManager.notifyOf(new AsyncOperationAttempted(
            new Description('[Service 1] Starting...'),
            CorrelationId.create(),
        ));

        stageManager.notifyOf(new AsyncOperationAttempted(
            new Description('[Service 2] Starting...'),
            CorrelationId.create(),
        ));

        return expect(stageManager.waitForNextCue()).to.be.rejected.then(error => {
            const lines = error.message.split('\n');

            expect(lines[0]).to.equal('2 async operations have failed to complete within a 250ms cue timeout:');
            expect(lines[1]).to.match(/^[\d]+ms - \[Service 1] Starting...$/, error.message);
            expect(lines[2]).to.match(/^[\d]+ms - \[Service 2] Starting...$/, error.message);
        });
    });

    /**
     * @test {StageManager}
     * @test {AsyncOperationAttempted}
     * @test {AsyncOperationFailed}
     */
    it('provides details should the work in progress fail with an error', () => {

        const timeout       = Duration.ofMilliseconds(100);
        const stageManager  = new StageManager(timeout, new Clock());
        const correlationId = CorrelationId.create();

        stageManager.notifyOf(new AsyncOperationAttempted(
            new Description('[Service 1] Starting...'),
            correlationId,
        ));

        stageManager.notifyOf(new AsyncOperationFailed(
            new Error('Something happened'),
            correlationId,
        ));

        return expect(stageManager.waitForNextCue()).to.be.rejected.then(error => {
            const lines = error.message.split('\n');

            expect(lines[0]).to.equal('1 async operation has failed to complete:');
            expect(lines[1]).to.equal('[Service 1] Starting... - Error: Something happened');
        });
    });
});

import 'mocha';
import * as sinon from 'sinon';

import { Actor, StageCrewMember } from '../../../../src';
import { ArtifactArchived, ArtifactGenerated, DomainEvent } from '../../../../src/events';
import { FileSystem, Path } from '../../../../src/io';
import { CorrelationId, Duration, JSONData, Name, TestReport } from '../../../../src/model';
import { ArtifactArchiver, Cast, Clock, Stage, StageManager } from '../../../../src/stage';

import { expect } from '../../../expect';
import { photo } from '../samples';

/** @test {ArtifactArchiver} */
describe('ArtifactArchiver', () => {

    const
        json = { key: 'value' },
        jsonValueShortHash = '64cdd772d3',
        photoShortHash = '6808b2e9fe',
        sceneId = new CorrelationId('a-scene');

    let stage:          Stage,
        fs:             sinon.SinonStubbedInstance<FileSystem>,
        archiver:       StageCrewMember;

    class Extras implements Cast {
        prepare(actor: Actor): Actor {
            return actor;
        }
    }

    beforeEach(() => {
        fs = sinon.createStubInstance(FileSystem);
        fs.store.callsFake((path: Path, contents: any) => Promise.resolve(path));
    });

    describe('stores the artifacts generated by other stage crew members', () => {

        beforeEach(() => {
            stage = new Stage(new Extras(), new StageManager(Duration.ofMilliseconds(250), new Clock()));

            archiver = new ArtifactArchiver(fs as any, stage);
            stage.assign(archiver);
        });

        const
            jsonArtifactName = new Name('Scenario Name'),
            expectedJsonFileName = 'scenario-name',
            pngArtifactName  = new Name('PNG Artifact name'),
            expectedPngFileName = 'png-artifact-name';

        /**
         * @test {ArtifactArchiver}
         * @test {ArtifactGenerated}
         */
        it('notifies the StageManager when an artifact is saved so that the promise of a stage cue can be fulfilled', () => {
            stage.announce(new ArtifactGenerated(
                sceneId,
                jsonArtifactName,
                JSONData.fromJSON(json),
            ));

            return expect(stage.waitForNextCue()).to.be.fulfilled;
        });

        /**
         * @test {ArtifactArchiver}
         * @test {ArtifactGenerated}
         */
        it('notifies the StageManager when an artifact cannot be saved so that the promise of a stage cue can be rejected', () => {
            fs.store.returns(Promise.reject(new Error('Something happened')));

            stage.announce(new ArtifactGenerated(
                sceneId,
                pngArtifactName,
                photo,
            ));

            return expect(stage.waitForNextCue()).to.be.rejected;
        });

        /**
         * @test {ArtifactArchiver}
         * @test {ArtifactGenerated}
         */
        it('correctly saves the test report to a unique file', () => {
            stage.announce(new ArtifactGenerated(
                sceneId,
                jsonArtifactName,
                TestReport.fromJSON(json),
            ));

            return stage.waitForNextCue().then(() => {
                expect(fs.store).to.have.been.calledWith(
                    new Path(`scenario-${ expectedJsonFileName }-${ jsonValueShortHash }.json`),
                    JSON.stringify(json),
                );
            });
        });

        /**
         * @test {ArtifactArchiver}
         * @test {ArtifactGenerated}
         */
        it('correctly saves PNG content to a file', () => {
            stage.announce(new ArtifactGenerated(
                sceneId,
                pngArtifactName,
                photo,
            ));

            return stage.waitForNextCue().then(() => {
                expect(fs.store).to.have.been.calledWith(
                    new Path(`photo-${ expectedPngFileName }-${ photoShortHash }.png`),
                    photo.base64EncodedValue,
                    'base64',
                );
            });
        });

        /**
         * @test {ArtifactArchiver}
         * @test {ArtifactGenerated}
         *
         * @see https://github.com/serenity-js/serenity-js/issues/634
         */
        it(`ensures that the generate file name doesn't contain special characters`, () => {
            const emittedEventName = 'linux-chrome-87-0.4280.88-jasmine-navigates-to-https://www.bounteous.com/#sr=g&m=o&cp=or&ct=-tmc&st=(opu%20qspwjefe)&ts=1402322447';
            const expectedFileName = `photo-linux-chrome-87-0.4280.88-jasmine-navigates-to-https-www.bount-${ photoShortHash }.png`;

            stage.announce(new ArtifactGenerated(
                sceneId,
                new Name(emittedEventName),
                photo,
            ));

            return stage.waitForNextCue().then(() => {
                expect(fs.store).to.have.been.calledWith(
                    new Path(expectedFileName),
                    photo.base64EncodedValue,
                    'base64',
                );
            });
        });
    });

    describe(`when it encounters events it's not interested in`, () => {

        class SomeEvent extends DomainEvent {
            constructor() {
                super();
            }
        }

        const someEvent = new SomeEvent();

        /**
         * @test {ArtifactArchiver}
         */
        it('ignores them', () => {
            const stageManager = sinon.createStubInstance(StageManager);

            fs           = sinon.createStubInstance(FileSystem);
            stage        = new Stage(new Extras(), stageManager as unknown as StageManager);

            archiver     = new ArtifactArchiver(fs as any, stage);
            stage.assign(archiver);

            archiver.notifyOf(
                someEvent,
            );

            expect(stageManager.notifyOf).to.not.have.been.called;            // tslint:disable-line:no-unused-expression
            expect(fs.store).to.not.have.been.called;                         // tslint:disable-line:no-unused-expression
        });
    });

    /**
     * @test {ArtifactArchiver}
     * @test {ArtifactGenerated}
     * @test {ArtifactArchived}
     */
    it('notifies the StageManager when the artifact is correctly archived', () => {

        const stageManager = new StageManager(Duration.ofMilliseconds(250), new Clock());

        stage = new Stage(new Extras(), stageManager);

        archiver = new ArtifactArchiver(fs as any, stage);
        stage.assign(archiver);

        const notifyOf = sinon.spy(stageManager, 'notifyOf');

        stageManager.notifyOf(new ArtifactGenerated(
            sceneId,
            new Name('Some Report Name'),
            TestReport.fromJSON(json),
        ));

        return expect(stageManager.waitForNextCue()).to.be.fulfilled.then(() => {

            const archived: ArtifactArchived = notifyOf.getCall(2).lastArg;

            expect(archived).to.be.instanceOf(ArtifactArchived);
            expect(archived.sceneId).to.equal(sceneId);
            expect(archived.name).to.equal(new Name('Some Report Name'));
            expect(archived.type).to.equal(TestReport);
            expect(archived.path).to.equal(new Path(`scenario-some-report-name-${ jsonValueShortHash }.json`));
        });
    });

    describe('when instantiated using a factory method', () => {
        it('joins the path segments provided so that the developer doesn\'t need to worry about cross-OS compatibility of the path', () => {
            archiver = ArtifactArchiver.storingArtifactsAt(process.cwd(), 'target', 'site/serenity');

            expect((archiver as any).fileSystem.root).to.equal(new Path(process.cwd()).join(new Path('target/site/serenity')));
        });

        it('complains if the destination is not provided', () => {
            expect(() => ArtifactArchiver.storingArtifactsAt()).to.throw(Error, `Path to destination directory should have a property "length" that is greater than 0`);
        });
    });
});

import 'mocha';

import * as sinon from 'sinon';
import { Writable } from 'stream';

import { Actor, Cast, Clock, Duration, Stage, StageManager, StreamReporter } from '../../../../src';
import { TestRunFinished } from '../../../../src/events';
import { Timestamp } from '../../../../src/model';
import { expect } from '../../../expect';

/** @test {StreamReporter} */
describe('StreamReporter', () => {

    let stage:          Stage,
        output:         sinon.SinonStubbedInstance<Writable>;

    class Extras implements Cast {
        prepare(actor: Actor): Actor {
            return actor;
        }
    }

    beforeEach(() => {
        stage = new Stage(
            new Extras(),
            new StageManager(Duration.ofSeconds(2), new Clock()),
        );

        output = sinon.createStubInstance(Writable);
    });

    /** @test {StreamReporter} */
    it('prints the events it receives to output stream', () => {
        const reporter = new StreamReporter(output as unknown as Writable, stage);
        stage.assign(reporter);

        stage.announce(new TestRunFinished(Timestamp.fromJSON('2021-01-13T18:00:00Z')));

        expect(output.write).to.have.been.calledWith(
            `{"type":"TestRunFinished","event":"2021-01-13T18:00:00.000Z"}\n`
        );
    });
});
