import { ensure, isDefined, isInstanceOf, property } from 'tiny-types';

import { DomainEvent, SceneStarts } from './events';
import { ErrorStackParser } from './io';
import { Duration, Timestamp } from './model';
import { Actor } from './screenplay/actor';
import { SerenityConfig } from './SerenityConfig';
import { Cast, Clock, Stage, StageCrewMember, StageManager } from './stage';
import { Extras } from './stage/Extras';

export class Serenity {
    private static defaultCueTimeout    = Duration.ofSeconds(5);
    private static defaultActors        = new Extras();

    private stage: Stage;

    constructor(private readonly clock: Clock = new Clock()) {
        this.stage = new Stage(
            Serenity.defaultActors,
            new StageManager(Serenity.defaultCueTimeout, clock),
        );

        this.stage.assign(new StageHand());
    }

    /**
     * @desc
     *  Configures Serenity/JS. Every call to this function
     *  replaces the previous configuration provided,
     *  so this function should called be exactly once
     *  in your test suite.
     *
     * @param {SerenityConfig} config
     * @return {void}
     */
    configure(config: SerenityConfig): void {

        const cueTimeout = !! config.cueTimeout
            ? ensure('cueTimeout', config.cueTimeout, isInstanceOf(Duration))
            : Serenity.defaultCueTimeout;

        this.stage = new Stage(
            Serenity.defaultActors,
            new StageManager(cueTimeout, this.clock),
        );

        if (!! config.actors) {
            this.engage(config.actors);
        }

        this.stage.assign(new StageHand());

        if (Array.isArray(config.crew)) {
            this.stage.assign(...config.crew);
        }
    }

    /**
     * @desc
     *  Re-configures Serenity/JS with a new {@link Cast} of {@link Actor}s
     *  you'd like to use in any subsequent call to {@link actorCalled}.
     *
     *  This method provides an alternative to calling {@link Actor#whoCan}
     *  directly in your tests and you'd typically us it in a "before each"
     *  hook of your test runner of choice.
     *
     * @example <caption>Engaging a cast of actors</caption>
     *  import { Actor, Cast } from '@serenity-js/core';
     *
     *  class Actors implements Cast {
     *      prepare(actor: Actor) {
     *          return actor.whoCan(
     *              // ... abilities you'd like the Actor to have
     *          );
     *      }
     *  }
     *
     * engage(new Actors();
     *
     * @example <caption>Usage with Jasmine</caption>
     *  import 'jasmine';
     *
     *  beforeEach(() => engage(new Actors()));
     *
     * @example <caption>Usage with Cucumber</caption>
     *  import { Before } from 'cucumber';
     *
     *  Before(() => engage(new Actors());
     *
     * @param {Cast} actors
     * @returns {void}
     *
     * @see {@link Actor}
     * @see {@link Cast}
     */
    engage(actors: Cast): void {
        this.stage.engage(
            ensure('actors', actors, property('prepare', isDefined())),
        );
    }

    /**
     * @desc
     *  Instantiates or retrieves an actor {@link Actor}
     *  called `name` if one has already been instantiated.
     *
     * @example <caption>Usage with Jasmine</caption>
     *   import 'jasmine';
     *   import { actorCalled } from '@serenity-js/core';
     *
     *   describe('Feature', () => {
     *
     *      it('should have some behaviour', () =>
     *          actorCalled('James').attemptsTo(
     *              // ... activities
     *          ));
     *   });
     *
     * @example <caption>Usage with Cucumber</caption>
     *   import { actorCalled } from '@serenity-js/core';
     *   import { Given } from 'cucumber';
     *
     *   Given(/(.*?) is a registered user/, (name: string) =>
     *      actorCalled(name).attemptsTo(
     *              // ... activities
     *          ));
     *
     * @param {string} name
     *  The name of the actor to instantiate or retrieve
     *
     * @returns {Actor}
     *
     * @see {@link engage}
     * @see {@link Actor}
     * @see {@link Cast}
     */
    theActorCalled(name: string): Actor {
        return this.stage.theActorCalled(name);
    }

    /**
     * @desc
     *  Retrieves an actor who was last instantiated or retrieved
     *  using {@link actorCalled}.
     *
     *  This function is particularly useful when automating Cucumber scenarios.
     *
     * @example <captiongit>Usage with Cucumber</caption>
     *   import { actorCalled } from '@serenity-js/core';
     *   import { Given, When } from 'cucumber';
     *
     *   Given(/(.*?) is a registered user/, (name: string) =>
     *      actorCalled(name).attemptsTo(
     *              // ... activities
     *          ));
     *
     *   When(/(?:he|she|they) browse their recent orders/, () =>
     *      actorInTheSpotlight().attemptsTo(
     *              // ... activities
     *          ));
     *
     * @returns {Actor}
     *
     * @see {@link engage}
     * @see {@link actorCalled}
     * @see {@link Actor}
     * @see {@link Cast}
     */
    theActorInTheSpotlight(): Actor {
        return this.stage.theActorInTheSpotlight();
    }

    announce(event: DomainEvent): void {
        this.stage.announce(event);
    }

    currentTime(): Timestamp {
        return this.stage.currentTime();
    }

    /**
     * @package
     */
    waitForNextCue(): Promise<void> {
        return this.stage.waitForNextCue();
    }

    /**
     * @deprecated
     * @experimental
     * @param stageCrewMembers
     */
    setTheStage(...stageCrewMembers: StageCrewMember[]): void {
        deprecated('serenity.setTheStage', 'Please use the new `configure({ crew: stageCrewMembers }) from @serenity-js/core instead.');

        this.stage.assign(...stageCrewMembers);
    }

    /**
     * @deprecated
     * @param actors
     */
    callToStageFor(actors: Cast): Stage {
        deprecated('serenity.callToStageFor(...)', 'Please use `actorCalled(name)` and `actorInTheSpotlight()` functions from @serenity-js/core to access the actors instead.');

        return this.stage.callFor(actors);
    }
}

// todo: remove when the deprecated methods are removed
function deprecated(method: string, message: string) {
    const callers = new ErrorStackParser().parse(new Error())
        .filter(frame => ! /(node_modules)/.test(frame.fileName));

    const location = !! callers[2]
        ? `[${ callers[2].fileName }:${ callers[2].lineNumber }] `
        : '';

    console.warn(`${ location }${ method } has been deprecated. ${ message }`);   // tslint:disable-line:no-console
}

/**
 * @private
 */
class StageHand implements StageCrewMember {
    constructor(
        private readonly stage: Stage = null) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new StageHand(stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneStarts) {
            this.stage.resetActors();
        }
    }
}

import { Duration } from './model';
import { Cast, StageCrewMember } from './stage';

/**
 * @interface
 *
 * @see {@link configure}
 */
export abstract class SerenityConfig {
    /**
     * @desc
     *  A {@link Cast} of {@link Actor}s to be used when {@link actorCalled}
     *  and {@link actorInTheSpotlight} functions are called.
     *
     * @type {?Cast}
     */
    actors?:        Cast;

    /**
     * @desc
     *  A list of {@link StageCrewMember}s to be notified of {@link DomainEvent}s
     *  that occur during the scenario execution.
     *
     * @type {?StageCrewMember[]}
     */
    crew?:          StageCrewMember[];

    /**
     * @desc
     *  The maximum amount of time Serenity/JS should wait for any post-scenario
     *  async operations to complete. Those include generating the screenshots,
     *  saving reports to disk and so on. **Please note** that this is not
     *  a scenario timeout, which should be configured in your test runner.
     *
     * @type {?Duration}
     */
    cueTimeout?:    Duration;
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that an assertion has failed.
 *
 * @extends {RuntimeError}
 */
export class AssertionError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {any} expected - The value that was expected
     * @param {any} actual - The value that was received instead of the expected one
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, public readonly expected: any, public readonly actual: any, cause?: Error) {
        super(AssertionError, message, cause);
    }
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that a test framework or test suite configuration error occurs.
 *
 * @extends {RuntimeError}
 */
export class ConfigurationError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, cause?: Error) {
        super(ConfigurationError, message, cause);
    }
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that the {@link Actor} attempted to perform
 *  a {@link Task} or {@link Interaction} that hasn't been implemented yet.
 *
 * @example <caption>A pending Task</caption>
 *  import { Task } from '@serenity-js/core';
 *
 *  // A pending Task is one that encapsulates no other activities
 *  const Authenticate = () =>
 *      Task.where(`#actor authenticates with the service up a test database` );
 *
 * @example <caption>A pending Interaction</caption>
 *  import { Interaction } from '@serenity-js/core';
 *
 *  // A pending Interaction is one that doesn't perform any action
 *  const DoubleClick = () =>
 *      Interaction.where(`#actor double-clicks`);
 *
 * @extends {RuntimeError}
 */
export class ImplementationPendingError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, cause?: Error) {
        super(ImplementationPendingError, message, cause);
    }
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that an {@link Interaction}, a {@link Task} or a test scenario
 *  can't be executed due to a logical error.
 *  For example, it's not possible to assert on the last HTTP Response if the request
 *  hasn't been performed yet.
 *
 * @extends {RuntimeError}
 */
export class LogicError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, cause?: Error) {
        super(LogicError, message, cause);
    }
}

/**
 * @desc
 *  Base class for custom errors that may occur during execution of a test scenario.
 *
 * @example <caption>Custom Error definition</caption>
 * import { RuntimeError } from '@serenity-js/core';
 *
 * export class CustomError extends RuntimeError {
 *   constructor(message: string, cause?: Error) {
 *       super(CustomError, message, cause);
 *   }
 * }
 *
 * @example <caption>Sync error handling</caption>
 * try {
 *     operationThatMightThrowAnError();
 * } catch(error) {
 *     // catch and re-throw
 *     throw new CustomError('operationThatMightThrowAnError has failed', error);
 * }
 *
 * @example <caption>Async error handling</caption>
 * operationThatMightRejectAPromise().catch(error => {
 *     // catch and re-throw
 *     throw new CustomError('operationThatMightThrowAnError has failed', error);
 * });
 *
 * @extends {Error}
 */
export abstract class RuntimeError extends Error {

    /**
     * @param {Function} type - Constructor function used to instantiate a subclass of a RuntimeError
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    protected constructor(
        type: new (...args: any[]) => RuntimeError,
        message: string,
        public readonly cause?: Error,
    ) {
        super(message);
        Object.setPrototypeOf(this, type.prototype);
        this.name = this.constructor.name;

        Error.captureStackTrace(this, type);

        if (!! cause) {
            this.stack = `${ this.stack }\nCaused by: ${ cause.stack }`;
        }
    }

    /**
     * @desc
     *  Human-readable description
     */
    toString() {
        return `${ this.constructor.name }: ${ this.message }`;
    }
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that the test can't be performed due to an issue with a downstream dependency.
 *  For example, it makes no sense to run a full-stack integration test if we already know that
 *  the database server is down.
 *
 * @example <caption>Throwing a TestCompromisedError from a custom Interaction</caption>
 *  import { Interaction } from '@serenity-js/core';
 *
 *  const SetUpTestDatabase = () =>
 *      Interaction.where(`#actor sets up a test database`, actor => {
 *          return SomeCustomDatabaseSpecificAbility.as(actor).setUpTestDatabase().catch(error => {
 *              throw new TestCompromisedError('Could not set up the test database', error);
 *          });
 *      });
 *
 * @extends {RuntimeError}
 */
export class TestCompromisedError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, cause?: Error) {
        super(TestCompromisedError, message, cause);
    }
}

import { RuntimeError } from './RuntimeError';

/**
 * @desc
 *  Thrown to indicate that an unknown error has occurred.
 *
 * @extends {RuntimeError}
 */
export class UnknownError extends RuntimeError {

    /**
     * @param {string} message - Human-readable description of the error
     * @param {Error} [cause] - The root cause of this {@link RuntimeError}, if any
     */
    constructor(message: string, cause?: Error) {
        super(UnknownError, message, cause);
    }
}

