import { Ability } from './Ability';
import { UsesAbilities } from './actor';

/**
 * @desc
 *  An interface describing the static access method that every {@link Ability} class
 *  needs to provide in order to be accessible from within an {@link Interaction}.
 */
export interface AbilityType<A extends Ability> {

    /**
     * @desc
     *  Every Ability should be constructible and ideally receive the client communicating with
     *  the desired external interface of the system under test as an argument.
     *
     * @param {...any} args
     */
    new (...args): A;

    /**
     * @desc
     *  Retrieves the Ability in question from the {@link Actor}, provided that the {@link Actor} has it.
     *
     * @example
     * import { Ability, Actor, Interface } from '@serenity-js/core';
     *
     * class MakePhoneCalls implements Ability {
     *     static as(actor: UsesAbilities): MakesPhoneCalls {
     *         return actor.abilityTo(MakePhoneCalls);
     *     }
     *
     *     static using(phone: Phone) {
     *         return new MakePhoneCalls(phone);
     *     }
     *
     *     constructor(private readonly phone: Phone) {}
     *
     *     // some method that allows us to interact with the external interface of the system under test
     *     dial(phoneNumber: string) {
     *       // ...
     *     }
     * }
     *
     * const Connie = Actor.named('Connie').whoCan(MakePhoneCalls.using(phone));
     *
     * const Call = (phoneNumber: string) => Interaction.where(`#actor calls ${ phoneNumber }`, actor =>
     *  MakePhoneCalls.as(actor).dial(phoneNumber);
     * );
     *
     * @param {UsesAbilities} actor
     */
    as(actor: UsesAbilities): A;
}

import { AnswersQuestions, PerformsActivities, UsesAbilities } from './actor';

/**
 * @desc
 *  A command object representing an activity that an {@link Actor} can perform.
 *
 * @see {@link Actor}
 * @see https://en.wikipedia.org/wiki/Command_pattern
 */
export interface Activity {

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this Activity.
     *
     * @param {PerformsActivities | UsesAbilities | AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     * @see {@link UsesAbilities}
     * @see {@link AnswersQuestions}
     */
    performAs(actor: PerformsActivities | UsesAbilities | AnswersQuestions): PromiseLike<any>;

    /**
     * @desc
     *  Generates a description to be used when reporting this Activity.
     *
     * @returns {string}
     */
    toString(): string;
}

import { Question } from './Question';

/**
 * @public
 *
 * @typedef {Question<Promise<T>> | Question<T> | Promise<T> | T} Answerable<T>
 */
export type Answerable<T> = Question<Promise<T>> | Question<T> | Promise<T> | T;

import { Activity } from './Activity';
import { Actor, AnswersQuestions, CollectsArtifacts, UsesAbilities } from './actor';

/**
 * @desc
 *  Directly exercises the {@link Actor}'s {@link Ability} to interact
 *  with the System Under Test.
 *
 * @implements {Activity}
 * @see {@link Ability}
 * @see {@link Actor}
 */
export abstract class Interaction implements Activity {
    static where(
        description: string,
        interaction: (actor: UsesAbilities & AnswersQuestions & CollectsArtifacts) => PromiseLike<void> | void,
    ): Interaction {
        return new DynamicallyGeneratedInteraction(description, interaction);
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link UsesAbilities}
     * @see {@link AnswersQuestions}
     */
    abstract performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void>;
}

/**
 * @package
 */
class DynamicallyGeneratedInteraction extends Interaction {
    constructor(
        private readonly description: string,
        private readonly interaction: (actor: UsesAbilities & AnswersQuestions & CollectsArtifacts) => PromiseLike<void> | void,
    ) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Interaction}.
     *
     * @param {Actor} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     */
    performAs(actor: Actor): PromiseLike<void> {
        try {
            return Promise.resolve(this.interaction(actor));
        } catch (error) {
            return Promise.reject(error);
        }
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

import { AnswersQuestions, UsesAbilities } from './actor';

/**
 * @desc
 *  Enables the {@link Actor} to query the system under test.
 *
 * @example <caption>A basic Question</caption>
 * import { Actor, AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 * import { Ensure, equals } from '@serenity-js/assertions'
 *
 * const LastItemOf = <T>(list: T[]): Question<T> =>
 *     Question.about('last item from the list', (actor: AnswersQuestions & UsesAbilities) => {
 *         return list[list.length - 1];
 *     });
 *
 * Actor.named('Quentin').attemptsTo(
 *     Ensure.that(LastItemFrom([1,2,3]), equals(3)),
 * );
 *
 * @example <caption>A question using the Actor's Ability to do something</caption>
 * import { AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 * import { CallAnApi } from '@serenity-js/rest'
 *
 * const TextOfLastResponseStatus = () =>
 *     Question.about<number>(`the text of the last response status`, actor => {
 *         return CallAnApi.as(actor).mapLastResponse(response => response.statusText);
 *     });
 *
 * @example <caption>Mapping answers to other questions</caption>
 * import { Actor, AnswersQuestions, UsesAbilities, Question } from '@serenity-js/core'
 * import { CallAnApi, LastResponse } from '@serenity-js/rest'
 * import { Ensure, equals } from '@serenity-js/assertions';
 *
 * const RequestWasSuccessful = () =>
 *     Question.about<number>(`the text of the last response status`, actor => {
 *         return LastResponse.status().answeredBy(actor) === 200;
 *     });
 *
 * const actor = Actor.named('Quentin').whoCan(CallAnApi.at('https://myapp.com/api'));
 *
 * actor.attemptsTo(
 *     Send.a(GetRequest.to('/books/0-688-00230-7')),
 *     Ensure.that(RequestWasSuccessful(), equals(true)),
 * );
 *
 * @see {@link Actor}
 * @see {@link Interaction}
 * @see {@link Ability}
 */
export abstract class Question<T> {

    /**
     *  Factory method that simplifies the process of defining custom questions.
     */
    static about<R>(description: string, body: (actor: AnswersQuestions & UsesAbilities) => R): Question<R> {
        return new AnonymousQuestion<R>(description, body);
    }

    /**
     * @desc
     *  Checks if the value is a {@link Question}
     *
     * @param {any} maybeQuestion
     * @returns {boolean}
     */
    static isAQuestion<T>(maybeQuestion: any): maybeQuestion is Question<T> {
        return !! (maybeQuestion as any).answeredBy;
    }

    /**
     * @abstract
     */
    abstract answeredBy(actor: AnswersQuestions & UsesAbilities): T;
}

/**
 * @package
 */
class AnonymousQuestion<T> implements Question<T> {
    constructor(private description: string, private body: (actor: AnswersQuestions & UsesAbilities) => T) {
    }

    answeredBy(actor: AnswersQuestions & UsesAbilities) {
        return this.body(actor);
    }

    toString() {
        return this.description;
    }
}

import { ImplementationPendingError } from '../errors';
import { Activity } from './Activity';
import { PerformsActivities } from './actor';

/**
 * @desc
 *  Aggregates {@link Interaction}s so that they represents logical
 *  steps of a business process an {@link Actor} can perform.
 *
 * @implements {Activity}
 * @see {@link Interaction}
 * @see {@link Actor}
 */
export abstract class Task implements Activity {

    /**
     * @desc
     *  A factory method to make defining the Tasks more convenient.
     *  Please note that colling this method without providing the list of {@link Activity} objects
     *  will result in producing a Task that's marked as "pending" in the test report.
     *
     *  This feature is useful when you want to quickly write down a task that will be needed in the scenario,
     *  but you're not yet sure what activities will constitute it.
     *
     * @example <caption>Modelling a task</caption>
     * import { Log, Task } from '@serenity-js/core';
     *
     * const Debug = (something: Answerable<any>) =>
     *     Task.where(`#actor prints the answer to a question`,
     *          Log.the(something),
     *     );
     *
     * @example <caption>Modelling a not implemented task</caption>
     * import { Task } from '@serenity-js/core';
     *
     * const SignUp = () =>
     *     Task.where(`#actor signs up for a newsletter`); // no activities given
     *                                                     // => task marked as pending
     *
     * @param {string} description - A description to be used when reporting this task
     * @param {Activity[]} activities - A list of lower-level activities that constitute the task
     *
     * @returns {Task}
     */
    static where(description: string, ...activities: Activity[]): Task {
        return activities.length > 0
            ? new DynamicallyGeneratedTask(description, activities)
            : new NotImplementedTask(description);
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    abstract performAs(actor: PerformsActivities): PromiseLike<void>;
}

/**
 * @package
 */
class DynamicallyGeneratedTask extends Task {
    constructor(private description: string, private activities: Activity[]) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    performAs(actor: PerformsActivities): PromiseLike<void> {
        return actor.attemptsTo(...this.activities);
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

/**
 * @package
 */
class NotImplementedTask extends Task {
    constructor(private description: string) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link Actor}
     *  perform this {@link Task}.
     *
     * @param {PerformsActivities} actor
     * @returns {Promise<void>}
     *
     * @see {@link Actor}
     * @see {@link PerformsActivities}
     */
    performAs(actor: PerformsActivities): PromiseLike<void> {
        return Promise.reject(
            new ImplementationPendingError(`A task where "${ this.toString() }" has not been implemented yet`),
        );
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link Activity}.
     *
     * @returns {string}
     */
    toString() {
        return this.description;
    }
}

import { LogicError } from '../../errors';
import { Ability } from '../Ability';
import { AnswersQuestions, UsesAbilities } from '../actor';
import { Question } from '../Question';

/**
 * @desc
 *  Enables the {@link Actor} to remember an answer to a given {@link Question},
 *  and recall it later.
 *
 * @example
 *  import { Note, TakeNote, TakeNotes } from '@serenity-js/core'
 *  import { BrowseTheWeb, Target, Text } from '@serenity-js/protractor'
 *  import { by, protractor } from 'protractor';
 *
 *  class Vouchers {
 *      static code             = Target.the('voucher code').located(by.id('voucher'));
 *      static appliedVoucher   = Target.the('voucher code').located(by.id('applied-voucher'));
 *  }
 *
 *  const actor = Actor.named('Noah').whoCan(
 *      TakeNotes.usingAnEmptyNotepad(),
 *      BrowseTheWeb.using(protractor.browser),
 *  );
 *
 *  actor.attemptsTo(
 *      TakeNote.of(Text.of(Vouchers.code)),
 *      // ... add the product to a basket, go to checkout, etc.
 *      Ensure.that(Text.of(Vouchers.appliedVoucher), equals(Note.of(Text.of(Vouchers.code)))),
 *  );
 *
 * @see {@link Note}
 * @see {@link TakeNote}
 *
 * @implements {Ability}
 */
export class TakeNotes implements Ability {
    /**
     * @returns {TakeNotes}
     */
    static usingAnEmptyNotepad(): TakeNotes {
        return new TakeNotes({});
    }

    /**
     * @param {UsesAbilities & AnswersQuestions} actor
     * @returns {TakeNotes}
     */
    static as(actor: UsesAbilities & AnswersQuestions): TakeNotes {
        return actor.abilityTo(TakeNotes);
    }

    /**
     * @param {object} notepad
     */
    constructor(private readonly notepad: { [key: string]: any }) {
    }

    /**
     * Records the answer to a given {@link Question}
     *
     * @param {Question<Promise<Answer>> | Question<Answer>} question
     * @param {Promise<Answer> | Answer} value
     */
    record<Answer>(question: Question<Promise<Answer>> | Question<Answer>, value: Promise<Answer> | Answer): void {
        this.notepad[question.toString()] = value;
    }

    /**
     * Recalls the answer to a given {@link Question}
     *
     * @param {Question<Promise<Answer>> | Question<Answer>} question
     */
    answerTo<Answer>(question: Question<Promise<Answer>> | Question<Answer>): Promise<Answer> {
        const key = question.toString();

        return ! ~Object.keys(this.notepad).indexOf(key)
            ? Promise.reject(new LogicError(`The answer to "${ key }" has never been recorded`))
            : Promise.resolve(this.notepad[key]);
    }
}

