import { Name } from '../../model';
import { Activity } from '../Activity';

/** @package */
export class ActivityDescriber {

    describe(activity: Activity, actor: { name: string }): Name {
        const template = activity.toString() !== ({}).toString()
            ? activity.toString()
            : `#actor performs ${ activity.constructor.name }`;

        return new Name(
            this.includeActorName(template, actor),
        );
    }

    private includeActorName(template: string, actor: { name: string }) {
        return template.replace('#actor', actor.name);
    }
}

import { match } from 'tiny-types';
import { AssertionError, ImplementationPendingError, TestCompromisedError } from '../../errors';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ImplementationPending,
    ProblemIndication,
} from '../../model';

/** @package */
export class OutcomeMatcher {
    outcomeFor(error: Error | any): ProblemIndication {
        return match<Error, ProblemIndication>(error)
            .when(ImplementationPendingError, _ => new ImplementationPending(error))
            .when(TestCompromisedError, _ => new ExecutionCompromised(error))
            .when(AssertionError, _ => new ExecutionFailedWithAssertionError(error))
            .when(Error, _ => /AssertionError/.test(error.constructor.name) // mocha
                    ? new ExecutionFailedWithAssertionError(error)
                    : new ExecutionFailedWithError(error))
            .else(_ => new ExecutionFailedWithError(error));
    }
}

import { InteractionFinished, InteractionStarts, TaskFinished, TaskStarts } from '../../events';
import { ActivityDetails, CorrelationId, ExecutionSuccessful } from '../../model';
import { Stage } from '../../stage';
import { Activity } from '../Activity';
import { AnswersQuestions, PerformsActivities, UsesAbilities } from '../actor';
import { Interaction } from '../Interaction';
import { ActivityDescriber } from './ActivityDescriber';
import { OutcomeMatcher } from './OutcomeMatcher';

/** @package */
export class TrackedActivity implements Activity {

    protected static readonly describer = new ActivityDescriber();
    protected static readonly outcomes = new OutcomeMatcher();

    constructor(
        protected readonly activity: Activity,
        protected readonly stage: Stage,
    ) {
    }

    performAs(actor: (PerformsActivities | UsesAbilities | AnswersQuestions) & { name: string }): PromiseLike<void> {
        const details = this.stage.activityDetailsFor(this.activity, actor);

        const [ activityStarts, activityFinished] = this.activity instanceof Interaction
            ? [ InteractionStarts, InteractionFinished ]
            : [ TaskStarts, TaskFinished ];

        return Promise.resolve()
            .then(() => this.stage.announce(new activityStarts(details, this.stage.currentTime())))
            .then(() => this.activity.performAs(actor))
            .then(() => {
                const outcome = new ExecutionSuccessful();
                this.stage.announce(new activityFinished(details, outcome, this.stage.currentTime()));
            })
            .catch(error => {
                const outcome = TrackedActivity.outcomes.outcomeFor(error);
                this.stage.announce(new activityFinished(details, outcome, this.stage.currentTime()));

                throw error;
            });
    }

    toString() {
        return this.activity.toString();
    }
}

import { ActivityRelatedArtifactGenerated } from '../../events';
import { Ability, AbilityType, Answerable, Cast, ConfigurationError, serenity } from '../../index';
import { Artifact, Name } from '../../model';
import { Stage } from '../../stage';
import { TrackedActivity } from '../activities';
import { Activity } from '../Activity';
import { Question } from '../Question';
import { AnswersQuestions } from './AnswersQuestions';
import { CanHaveAbilities } from './CanHaveAbilities';
import { CollectsArtifacts } from './CollectsArtifacts';
import { PerformsActivities } from './PerformsActivities';
import { UsesAbilities } from './UsesAbilities';

export class Actor implements PerformsActivities, UsesAbilities, CanHaveAbilities<Actor>, AnswersQuestions, CollectsArtifacts {
    // todo: Actor should have execution strategies
    // todo: the default one executes every activity
    // todo: there could be a dry-run mode that default to skip strategy

    /**
     * @deprecated
     * @param name
     */
    static named(name: string): CanHaveAbilities<Actor> {
        return {
            whoCan: (...abilities): Actor => {
                serenity.configure({
                    actors: Cast.whereEveryoneCan(...abilities),
                });

                return serenity.theActorCalled(name);
            },
        };
    }

    constructor(
        public readonly name: string,
        private readonly stage: Stage,
        private readonly abilities: Map<AbilityType<Ability>, Ability> = new Map<AbilityType<Ability>, Ability>(),
    ) {
    }

    abilityTo<T extends Ability>(doSomething: AbilityType<T>): T {
        if (! this.can(doSomething)) {
            throw new ConfigurationError(`${ this.name } can't ${ doSomething.name } yet. ` +
                `Did you give them the ability to do so?`);
        }

        return this.abilities.get(doSomething) as T;
    }

    attemptsTo(...activities: Activity[]): Promise<void> {
        return activities
            .map(activity => new TrackedActivity(activity, this.stage))  // todo: TrackedInteraction, TrackedTask
            .reduce((previous: Promise<void>, current: Activity) => {
                return previous.then(() => {
                    /* todo: add an execution strategy */
                    return current.performAs(this);
                });
            }, Promise.resolve(void 0));
    }

    whoCan(...abilities: Ability[]): Actor {
        abilities.forEach(ability => {
            this.abilities.set(ability.constructor as AbilityType<Ability>, ability);
        });

        return this;
    }

    /**
     * @param {Answerable<T>} answerable - a Question<Promise<T>>, Question<T>, Promise<T> or T
     * @returns {Promise<T>} The answer to the Answerable
     */
    answer<T>(answerable: Answerable<T>): Promise<T> {
        function isAPromise<V>(v: Answerable<V>): v is Promise<V> {
            return !!(v as any).then;
        }

        function isDefined<V>(v: Answerable<V>) {
            return ! (answerable === undefined || answerable === null);
        }

        if (isDefined(answerable) && isAPromise(answerable)) {
            return answerable;
        }

        if (isDefined(answerable) && Question.isAQuestion(answerable)) {
            return this.answer(answerable.answeredBy(this));
        }

        return Promise.resolve(answerable as T);
    }

    /**
     * @desc
     *  Announce collection of an {@link Artifact} so that it can be picked up by a {@link StageCrewMember}.
     *
     * @param {Artifact} artifact
     * @param {?(string | Name)} name
     */
    collect(artifact: Artifact, name?: string | Name) {
        this.stage.announce(new ActivityRelatedArtifactGenerated(
            this.stage.currentActivityDetails(),
            this.nameFrom(name || new Name(artifact.constructor.name)),
            artifact,
            this.stage.currentTime(),
        ));
    }

    toString() {
        const abilities = Array.from(this.abilities.keys()).map(type => type.name);

        return `Actor(name=${ this.name }, abilities=[${ abilities.join(', ') }])`;
    }

    private can<T extends Ability>(doSomething: AbilityType<T>): boolean {
        return this.abilities.has(doSomething);
    }

    /**
     * @desc
     *  Instantiates a {@link Name} based on the string value of the parameter,
     *  or returns the argument if it's already an instance of {@link Name}.
     *
     * @param {string | Name} maybeName
     * @returns {Name}
     */
    private nameFrom(maybeName: string | Name): Name {
        return typeof maybeName === 'string'
            ? new Name(maybeName)
            : maybeName;
    }
}

import { Answerable } from '../Answerable';
import { Question } from '../Question';

/**
 * @desc
 *  Enables the {@link Actor} to answer a {@link Question} about the system under test
 *
 * @public
 */
export interface AnswersQuestions {
    answer<T>(knownUnknown: Answerable<T>): Promise<T>;
}

import { Ability } from '../Ability';
import { Activity } from '../Activity';
import { UsesAbilities } from './UsesAbilities';

/**
 * @desc
 *  Enables the {@link Actor} to have an {@link Ability} or Abilities to perform some {@link Activity}.
 *
 * @public
 */
export interface CanHaveAbilities<Returned_Type = UsesAbilities> {
    /**
     * @param {Ability[]} abilities
     * @returns {Actor}
     */
    whoCan(...abilities: Ability[]): Returned_Type;
}

import { Artifact, Name } from '../../model';

/**
 * @desc
 *  Enables the {@link Actor} to collect {@link Artifact}s while the scenario is being executed
 *
 * @public
 */
export interface CollectsArtifacts {
    /**
     * @desc
     * Makes the {@link Actor} collect an {@link Artifact} so that it's included in the test report.
     *
     * @param {Artifact} artifact - The artifact to be collected, such as {@link JSONData}
     * @param {Name} [name] - The name of the artifact to make it easy to recognise in the test report
     */
    collect(artifact: Artifact, name?: Name): void;
}

import { Activity } from '../Activity';

/**
 * @desc
 *  Enables the {@link Actor} to perform an {@link Activity}, such as a {@link Task} or an {@link Interaction}
 *
 * @public
 */
export interface PerformsActivities {
    attemptsTo(...tasks: Activity[]): Promise<void>;
}

import { Ability } from '../Ability';
import { AbilityType } from '../AbilityType';
import { Activity } from '../Activity';

/**
 * @desc
 *  Enables the {@link Actor} to use an {@link Ability} to perform some {@link Activity}.
 *
 * @public
 */
export interface UsesAbilities {

    /**
     * @desc
     *  Grants access to the Actor's ability
     *
     * @param {AbilityType<T extends Ability>} doSomething
     * @returns {T}
     */
    abilityTo<T extends Ability>(doSomething: AbilityType<T>): T;
}

