import { AnswersQuestions, UsesAbilities } from '../actor';
import { Answerable } from '../Answerable';
import { Question } from '../Question';

/**
 * @experimental
 */
export class Transform<Answer_Type extends any, Output_Type> implements Question<Promise<Output_Type>> {
    static the<AT extends any, OT>(questions: Answerable<AT> | Array<Answerable<AT>>, transformation: (...answers: AT[]) => OT) {
        return new Transform<AT, OT>([].concat(questions), transformation);
    }

    constructor(
        private readonly questions: Array<Answerable<Answer_Type>>,
        private readonly transformation: (...answers: Answer_Type[]) => Output_Type,
        private readonly description: string = `a transformed answer`,
    ) {
    }

    /**
     * @desc
     *  Overrides the default {@link Transform#toString} representation of this object.
     *
     * @param {string} description
     * @returns {Transform}
     */
    as(description: string) {
        return new Transform(this.questions, this.transformation, description);
    }

    answeredBy(actor: AnswersQuestions & UsesAbilities): Promise<Output_Type> {
        return Promise.all(this.questions.map(question => actor.answer(question)))
            .then(answers => this.transformation(...answers));
    }

    toString() {
        return this.description;
    }
}

import { Ability, Actor } from '../screenplay';

/**
 * @desc
 *  Describes the {@link Actor}s available to take part in the performance.
 *
 * @example <caption>Define a cast of actors interacting with a Web UI</caption>
 *
 *  import { engage, Actor, Cast } from '@serenity-js/core';
 *  import { BrowseTheWeb } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  class UIActors implements Cast {
 *      prepare(actor: Actor) {
 *          return actor.whoCan(BrowseTheWeb.using(protractor.browser));
 *      }
 *  }
 *
 *  beforeEach(() => engage(new UIActors()));
 *
 * @example <caption>Using a generic cast</caption>
 *
 *  import { engage, Cast } from '@serenity-js/core';
 *  import { BrowseTheWeb } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  beforeEach(() => engage(BrowseTheWeb.using(protractor.browser)));
 *
 * @example <caption>Preparing actors differently based on their name</caption>
 *
 *  import { actorCalled, engage, Cast } from '@serenity-js/core';
 *  import { BrowseTheWeb } from '@serenity-js/protractor';
 *  import { CallAnApi } from '@serenity-js/rest';
 *  import { protractor } from 'protractor';
 *
 *  class Actors implements Cast {
 *      prepare(actor: Actor) {
 *          switch (actor.name) {
 *              case 'James':
 *                  return actor.whoCan(BrowseTheWeb.using(protractor.browser));
 *              default:
 *                  return actor.whoCan(CallAnApi.at(protractor.browser.baseUrl));
 *          }
 *      }
 *  }
 *
 *  beforeEach(() => engage(new Actors()));
 *
 *  actorCalled('James') // returns an actor using a browser
 *  actorCalled('Alice') // returns an actor interacting with an API
 *
 * @see {@link Stage}
 * @interface
 */
export abstract class Cast {
    /**
     * @desc
     *  Creates a generic `Cast` implementation, where every actor
     *  is given all the abilities specified when the method is called.
     *
     * @param {Ability[]} abilities
     * @returns {Cast}
     */
    static whereEveryoneCan(...abilities: Ability[]): Cast {
        return new GenericCast(abilities);
    }

    abstract prepare(actor: Actor): Actor;
}

/**
 * @package
 */
class GenericCast implements Cast {
    constructor(private readonly abilities: Ability[]) {
    }

    prepare(actor: Actor): Actor {
        return actor.whoCan(...this.abilities);
    }
}

import { Timestamp } from '../model';

/**
 * @desc A {@link Clock} tells the time.
 *
 * @example
 * const now: Timestamp = new Clock().now()
 */
export class Clock {
    constructor(private readonly checkTime: () => Date = () => new Date()) {
    }

    /**
     * @returns {Timestamp} current time
     */
    now(): Timestamp {
        return new Timestamp(this.checkTime());
    }
}

import { Ability, Actor } from '../screenplay';

/**
 * @desc
 *  Prepares the {@link Actor} for the performance.
 *
 * @example <caption>A dressing room for actors interacting with a Web UI</caption>
 *
 *  import { serenity, Actor, DressingRoom } from '@serenity-js/core'
 *  import { BrowseTheWeb } from '@serenity-js/protractor'
 *  import { protractor } from 'protractor'
 *
 *  class UIActors implements DressingRoom {
 *      prepare(actor: Actor) {
 *          return actor.whoCan(BrowseTheWeb.using(protractor.browser));
 *      }
 *  }
 *
 *  const stage = serenity.callToStageFor(new UIActors());
 *
 * @see {@link Stage}
 * @see {@link Cast}
 * @interface
 *
 * @deprecated Please use the Cast instead
 */
export abstract class DressingRoom {
    /**
     * @desc
     *  Creates a generic `DressingRoom` implementation, where every actor
     *  is given all the abilities specified when the method is called.
     *
     * @param {Ability[]} abilities
     * @returns {DressingRoom}
     */
    static whereEveryoneCan(...abilities: Ability[]): DressingRoom {
        return new GenericDressingRoom(abilities);
    }

    abstract prepare(actor: Actor): Actor;
}

/**
 * @package
 */
class GenericDressingRoom implements DressingRoom {
    constructor(private readonly abilities: Ability[]) {
    }

    prepare(actor: Actor): Actor {
        return actor.whoCan(...this.abilities);
    }
}

import { Actor } from '../screenplay';
import { Cast } from './Cast';

/**
 * @desc
 *  Produces no-op actors with no special {@link Ability}
 *
 * @private
 */
export class Extras implements Cast {
    prepare(actor: Actor): Actor {
        return actor;
    }
}

import { ensure, isDefined } from 'tiny-types';
import { ConfigurationError, LogicError } from '../errors';
import { DomainEvent } from '../events';
import { ActivityDetails, CorrelationId, Timestamp } from '../model';
import { Activity } from '../screenplay';
import { ActivityDescriber } from '../screenplay/activities/ActivityDescriber';
import { Actor } from '../screenplay/actor';
import { Cast } from './Cast';
import { StageCrewMember } from './StageCrewMember';
import { StageManager } from './StageManager';

export class Stage {
    private static readonly describer = new ActivityDescriber();

    private actorsOnStage: { [name: string]: Actor } = {};
    private actorInTheSpotlight: Actor = null;

    private detailsOfCurrentActivity: ActivityDetails = null;

    constructor(
        private cast: Cast,
        private readonly manager: StageManager,
    ) {
        ensure('Cast', cast, isDefined());
        ensure('StageManager', manager, isDefined());
    }

    /**
     * @desc An alias for {@link Stage#actor}
     * @see {Stage#actor}
     * @alias {Stage#actor}
     * @param {string} name
     * @return {Actor}
     */
    theActorCalled(name: string): Actor {
        return this.actor(name);
    }

    /**
     * @desc Instantiates a new {@link Actor} or fetches an existing one
     * by their name if they've already been instantiated.
     *
     * @param {string} name - case-sensitive name of the Actor
     * @return {Actor}
     */
    actor(name: string): Actor {
        if (! this.actorsOnStage[name]) {
            let actor;
            try {
                actor = this.cast.prepare(new Actor(name, this));
            }
            catch (error) {
                throw new ConfigurationError(`${ this.typeOf(this.cast) } encountered a problem when preparing actor "${ name }" for stage`, error);
            }

            if (! (actor instanceof Actor)) {
                throw new ConfigurationError(`Instead of a new instance of actor "${ name }", ${ this.typeOf(this.cast) } returned ${ actor }`);
            }

            this.actorsOnStage[name] = actor;
        }

        this.actorInTheSpotlight = this.actorsOnStage[name];

        return this.actorInTheSpotlight;
    }

    /**
     * @desc
     *  Returns the last {@link Actor} instantiated via {@link Stage#actor}.
     *  Useful when you don't can't or choose not to reference the actor by their name.
     *
     * @throws {LogicError} if no {Actor} has been activated yet
     * @return {Actor}
     */
    theActorInTheSpotlight(): Actor {
        if (! this.actorInTheSpotlight) {
            throw new LogicError(`There is no actor in the spotlight yet. Make sure you instantiate one with stage.actor(actorName) before calling this method.`);
        }

        return this.actorInTheSpotlight;
    }

    /**
     * @desc
     *  Returns {true} if there is an {@link Actor} in the spotlight, {false} otherwise.
     *
     * @return {boolean}
     */
    theShowHasStarted(): boolean {
        return !! this.actorInTheSpotlight;
    }

    /**
     * @deprecated
     * @param {Cast} actors
     * @return {Stage}
     */
    callFor(actors: Cast): Stage {
        this.resetActors();
        this.engage(actors);

        return this;
    }

    resetActors(): void {
        this.actorsOnStage       = {};
        this.actorInTheSpotlight = null;
    }

    engage(actors: Cast) {
        ensure('Cast', actors, isDefined());

        this.cast        = actors;
    }

    assign(...stageCrewMembers: StageCrewMember[]) {
        stageCrewMembers.forEach(stageCrewMember => {
            this.manager.register(stageCrewMember.assignedTo(this));
        });
    }

    announce(event: DomainEvent): void {
        this.manager.notifyOf(event);
    }

    currentTime(): Timestamp {
        return this.manager.currentTime();
    }

    activityDetailsFor(activity: Activity, actor: { name: string }): ActivityDetails {
        this.detailsOfCurrentActivity = new ActivityDetails(
            Stage.describer.describe(activity, actor),
            CorrelationId.create(),
        );

        return this.detailsOfCurrentActivity;
    }

    currentActivityDetails(): ActivityDetails {
        if (! this.detailsOfCurrentActivity) {
            throw new LogicError(`No activity is being performed. Did you call activityDetailsFor before invoking currentActivityDetails?`);
        }

        return this.detailsOfCurrentActivity;
    }

    waitForNextCue(): Promise<void> {
        return this.manager.waitForNextCue();
    }

    /**
     * @private
     * @param {Cast} cast
     */
    private typeOf(cast: Cast): string {
        return this.cast.constructor !== Object
            ? this.cast.constructor.name
            : 'Cast';
    }

    // todo: might be useful to ensure that the actors release any resources they're holding.
    // drawTheCurtain() {
        // dismiss all the actors
        // https://github.com/serenity-bdd/serenity-core/blob/master/serenity-screenplay/src/main/java/net/serenitybdd/screenplay/actors/Stage.java
    // }
}

import { DomainEvent } from '../events';
import { Stage } from './Stage';
import { StageManager } from './StageManager';

/**
 * @desc
 *  You can think of the {@link StageCrewMember} as an in-memory micro-service that reacts to {@link DomainEvent}s
 *  from the {@link StageManager}. Every {@link StageCrewMember} receives a reference to the {@link Stage},
 *  and therefore {@link StageManager} as well, which enables them to emit {@link DomainEvent}s back.
 *
 *  Useful when you're interested in implementing custom reporters.
 */
export interface StageCrewMember {

    /**
     * @desc
     *  Creates a new instance of this {@link StageCrewMember} and assigns it to a given {@link Stage}.
     *
     * @param {Stage} stage - An instance of a {@link Stage} this {@link StageCrewMember} will be assigned to
     * @returns {StageCrewMember} - A new instance of this {@link StageCrewMember}
     */
    assignedTo(stage: Stage): StageCrewMember;

    /**
     * @desc
     *  Handles {@link DomainEvent} objects emitted by the {@link Stage}
     *  this {@link StageCrewMember} is assigned to.
     *
     * @param {DomainEvent} event
     * @returns void
     */
    notifyOf(event: DomainEvent): void;
}

import { AsyncOperationAttempted, AsyncOperationCompleted, AsyncOperationFailed, DomainEvent } from '../events';
import { CorrelationId, Description, Duration, Timestamp } from '../model';
import { StageCrewMember } from './StageCrewMember';

interface AsyncOperationDetails {
    taskDescription:    Description;
    startedAt:          Timestamp;
}

interface FailedAsyncOperationDetails {
    taskDescription:    Description;
    startedAt:          Timestamp;
    duration:           Duration;
    error:              Error;
}

export class StageManager {
    private readonly subscribers: StageCrewMember[] = [];
    private readonly wip: Map<CorrelationId, AsyncOperationDetails> = new Map();
    private readonly failedOperations: FailedAsyncOperationDetails[] = [];

    constructor(private readonly cueTimeout: Duration,
                private readonly clock) {
    }

    register(...stageCrewMembers: StageCrewMember[]) {
        this.subscribers.push(...stageCrewMembers);
    }

    notifyOf(event: DomainEvent): void {
        this.handleAsyncOperation(event);

        this.subscribers.forEach(crewMember => crewMember.notifyOf(event));
    }

    waitForNextCue(): Promise<void> {
        return new Promise((resolve, reject) => {

            let interval: NodeJS.Timer,
                timeout: NodeJS.Timer;

            timeout = setTimeout(() => {
                clearInterval(interval);

                if (this.wip.size > 0) {
                    let message = `Some of the ${ this.wip.size } async operations have failed to complete within ${ this.cueTimeout.toString()}:\n`;

                    this.wip.forEach((op: AsyncOperationDetails) => {
                        message += `${ this.clock.now().diff(op.startedAt) } - ${ op.taskDescription.value }\n`;
                    });

                    return reject(new Error(message));
                }

                if (this.failedOperations.length > 0) {
                    let message = `Some of the async operations have failed:\n`;

                    this.failedOperations.forEach((op: FailedAsyncOperationDetails) => {
                        message += `${ op.taskDescription.value } - ${ op.error.stack }\n---\n`;
                    });

                    return reject(new Error(message));
                }

                // "else" can't happen because this case is covered by the interval check below

            }, this.cueTimeout.inMilliseconds());

            interval = setInterval(() => {
                if (this.wip.size === 0 && this.failedOperations.length === 0) {
                    clearTimeout(timeout);
                    clearInterval(interval);

                    return resolve();
                }
            }, 10);
        });
    }

    currentTime(): Timestamp {
        return this.clock.now();
    }

    private handleAsyncOperation(event: DomainEvent): void {
        if (event instanceof AsyncOperationAttempted) {
            this.wip.set(event.correlationId, {
                taskDescription: event.taskDescription,
                startedAt: event.timestamp,
            });
        }
        else if (event instanceof AsyncOperationCompleted) {
            this.wip.delete(event.correlationId);
        }
        else if (event instanceof AsyncOperationFailed) {
            const original = this.wip.get(event.correlationId);
            this.failedOperations.push({
                taskDescription:    original.taskDescription,
                startedAt:          original.startedAt,
                duration:           event.timestamp.diff(original.startedAt),
                error:              event.error,
            });
            this.wip.delete(event.correlationId);
        }
    }
}

import { Stage } from './Stage';

/**
 * @desc Makes the {@link Stage} object setup in your test runner configuration visible
 * to test steps or test scenarios.
 *
 * When using Cucumber, the {@link Stage} is typically set up using the Cucumber World Constructor.
 * When using Jasmine, the {@link Stage} is typically set up in the `beforeEach` step.
 *
 * @see https://github.com/cucumber/cucumber-js/blob/v5.0.1/docs/support_files/api_reference.md
 * @see https://github.com/cucumber/cucumber-js/blob/v5.0.1/docs/support_files/step_definitions.md
 * @see https://www.typescriptlang.org/docs/handbook/functions.html#this-parameters
 *
 * @example <caption>Usage with Cucumber.js</caption>
 * // features/support/configure_serenity.ts
 * import { WithStage } from '@serenity-js/core';
 * import { setWorldConstructor } from 'cucumber';
 *
 * setWorldConstructor(function (this: WithStage, { parameters }) {
 *   this.stage = serenity.callToStageFor(new SomeImplementationOfTheCastInterface());
 * });
 *
 * // features/step_definitions/some.steps.ts
 * import { WithStage } from '@serenity-js/cucumber';
 *
 * Given(/(.*?) is a registered customer/, function (this: WithStage, actorName: string) {
 *   return this.stage.actor(actorName).attemptsTo(
 *
 *   );
 * });
 *
 * @example <caption>Usage with Jasmine</caption>
 * // spec/some.spec.ts
 * import { serenity, WithStage } from '@serenity-js/core';
 *
 * describe('Using the Stage', () => {
 *   beforeEach(function (this: WithStage) {
 *     this.stage = serenity.callToStageFor(new SomeImplementationOfTheCastInterface());
 *   });
 *
 *   it('makes it easy to access the Actors', function(this: WithStage) {
 *     return this.stage.theActorCalled('Barry').attemptsTo(
 *       // tasks
 *     );
 *   })
 * });
 *
 * @deprecated Please use serenity.actor() and serenity.actorInTheSpotlight() instead
 */
export interface WithStage {
    stage: Stage;
}

import { ensure, isGreaterThan, property } from 'tiny-types';
import {
    ActivityRelatedArtifactArchived,
    ActivityRelatedArtifactGenerated,
    ArtifactArchived,
    ArtifactGenerated,
    AsyncOperationAttempted,
    AsyncOperationCompleted,
    AsyncOperationFailed,
    DomainEvent,
} from '../../../events';
import { FileSystem, Path } from '../../../io';
import { Artifact, ArtifactType, CorrelationId, Description, Name, Photo, TestReport } from '../../../model';
import { Stage } from '../../Stage';
import { StageCrewMember } from '../../StageCrewMember';
import { Hash } from './Hash';

/**
 * @desc Stores any {@link Artifact}s emitted through {@link ArtifactGenerated} events on the {@link FileSystem}
 *
 * @access public
 */
export class ArtifactArchiver implements StageCrewMember {

    static storingArtifactsAt(...destination: string[]): ArtifactArchiver {
        ensure('Path to destination directory', destination, property('length', isGreaterThan(0)));

        const pathToDestination = destination.map(segment => new Path(segment)).reduce((acc, current) => acc.join(current));

        return new ArtifactArchiver(new FileSystem(pathToDestination));
    }

    constructor(
        private readonly fileSystem: FileSystem,
        private readonly stage: Stage = null,
    ) {
    }

    assignedTo(stage: Stage) {
        return new ArtifactArchiver(this.fileSystem, stage);
    }

    notifyOf(event: DomainEvent): void {

        if (!(event instanceof ArtifactGenerated)) {
            // ignore any other events
            return void 0;
        }

        if (event.artifact instanceof Photo) {
            const filename = this.fileNameFor('photo', event.name, event.artifact, 'png');

            this.archive(
                filename,
                event.artifact.base64EncodedValue,
                'base64',
                this.archivisationAnnouncement(event, filename),
            );
        }

        if (event.artifact instanceof TestReport) {
            const filename = this.fileNameFor('scenario', event.name, event.artifact, 'json');

            this.archive(
                filename,
                event.artifact.map(JSON.stringify),
                'utf8',
                this.archivisationAnnouncement(event, filename),
            );
        }
    }

    private fileNameFor(prefix: string, artifactName: Name, artifact: Artifact, extension: string): Path {
        const hash = Hash.of(artifact.base64EncodedValue).short();
        return Path.fromSanitisedString(
            // Ensure that the file name is shorter than 250 chars, which is safe with all the filesystems
            // note: we can't do that in the Path constructor as the Path can be used to join other paths,
            // so restricting the length of the _path_ itself would not be correct.
            `${ prefix.substring(0, 10) }-${ artifactName.value.toLocaleLowerCase().substring(0, 220) }-${ hash }.${ extension }`,
            // characters:     10        1         220                                                 1    10   1    4            < 250

        );
    }

    private archive(relativePath: Path, contents: string, encoding: string, announce: (absolutePath: Path) => void): void {
        const id = CorrelationId.create();

        this.stage.announce(new AsyncOperationAttempted(
            new Description(`[${ this.constructor.name }] Saving '${ relativePath.value }'...`),
            id,
        ));

        this.fileSystem.store(relativePath, contents, encoding)
            .then(absolutePath => {
                announce(relativePath);

                this.stage.announce(new AsyncOperationCompleted(
                    new Description(`[${ this.constructor.name }] Saved '${ absolutePath.value }'`),
                    id,
                ));
            })
            .catch(error => {
                this.stage.announce(new AsyncOperationFailed(error, id));
            });
    }

    private archivisationAnnouncement(evt: ArtifactGenerated | ActivityRelatedArtifactGenerated, relativePathToArtifact: Path) {
        return (absolutePath: Path) => {
            if (evt instanceof ActivityRelatedArtifactGenerated) {
                this.stage.announce(new ActivityRelatedArtifactArchived(
                    evt.details,
                    evt.name,
                    evt.artifact.constructor as ArtifactType,
                    relativePathToArtifact,
                ));
            } else if (evt instanceof ArtifactGenerated) {
                this.stage.announce(new ArtifactArchived(
                    evt.name,
                    evt.artifact.constructor as ArtifactType,
                    relativePathToArtifact,
                ));
            }
        };
    }
}

import { createHash } from 'crypto';
import { ensure, isDefined, TinyType } from 'tiny-types';

/**
 * @package
 */
export class Hash extends TinyType {
    static of(value: string): Hash {
        return new Hash(createHash('sha1').update(value).digest('hex'));
    }

    constructor(public readonly value: string) {
        super();
        ensure(this.constructor.name, value, isDefined());
    }

    long(): string {
        return this.value;
    }

    short(): string {
        return this.value.substring(0, 10);
    }
}

import WriteStream = NodeJS.WriteStream;
import { DomainEvent } from '../../../events';
import { Stage } from '../../Stage';
import { StageCrewMember } from '../../StageCrewMember';

/**
 * @desc
 *  Serialises all the {@link DomainEvent} objects it receives and streams
 *  them as [ndjson](http://ndjson.org/)
 *  to the output stream.
 *
 * @example <caption>Writing DomainEvents to standard output</caption>
 * import { serenity, StreamReporter } form '@serenity-js/core';
 *
 * serenity.configure({
 *     crew: [ new StreamReporter(process.stdout) ],
 * });
 *
 * @example <caption>Writing DomainEvents to a file</caption>
 * import { serenity, StreamReporter } form '@serenity-js/core';
 * import fs = require('fs');
 *
 * serenity.configure({
 *     crew: [ new StreamReporter(fs.createWriteStream('./events.ndjson')) ],
 * });
 *
 * @implements {StageCrewMember}
 */
export class StreamReporter implements StageCrewMember {

    /**
     * @param {WriteStream} output - A WriteStream that should receive the output
     * @param {Stage} [stage=null] - The stage this {@link StageCrewMember} should be assigned to
     */
    constructor(
        private readonly output: WriteStream = process.stdout,
        private readonly stage: Stage = null,
    ) {
    }

    /**
     * @desc
     *  Creates a new instance of this {@link StageCrewMember} and assigns it to a given {@link Stage}.
     *
     * @see {@link StageCrewMember}
     *
     * @param {Stage} stage - An instance of a {@link Stage} this {@link StageCrewMember} will be assigned to
     * @returns {StageCrewMember} - A new instance of this {@link StageCrewMember}
     */
    assignedTo(stage: Stage): StageCrewMember {
        return new StreamReporter(this.output, stage);
    }

    /**
     * @desc
     *  Handles {@link DomainEvent} objects emitted by the {@link StageManager}.
     *
     * @see {@link StageCrewMember}
     *
     * @param {DomainEvent} event
     * @returns {void}
     */
    notifyOf(event: DomainEvent): void {
        this.output.write(
            JSON.stringify({ type: event.constructor.name, event: event.toJSON() }) + '\n',
        );
    }
}

