/* istanbul ignore file covered in integration tests */
import { FileSystem, ModuleLoader, TestRunnerAdapter, Version } from '@serenity-js/core/lib/io';
import { ExecutionIgnored, ImplementationPending, Outcome } from '@serenity-js/core/lib/model';

import { CucumberConfig } from './CucumberConfig';
import { CucumberOptions } from './CucumberOptions';
import { OutputDescriptor, SerenityFormatterOutput } from './output';

/**
 * @desc
 *  Allows for programmatic execution of Cucumber test scenarios.
 *
 * @implements {@serenity-js/core/lib/io~TestRunnerAdapter}
 */
export class CucumberCLIAdapter implements TestRunnerAdapter {

    private pathsToScenarios: string[] = [];

    private readonly options: CucumberOptions;

    /**
     * @param {@serenity-js/core/lib/io~Config<CucumberConfig>} config
     * @param {@serenity-js/core/lib/io~ModuleLoader} loader
     * @param {SerenityFormatterOutput} output
     *
     * @see {@link CucumberConfig}
     */
    constructor(
        config: CucumberConfig,
        private readonly loader: ModuleLoader,
        fileSystem: FileSystem,
        private readonly output: SerenityFormatterOutput,
    ) {
        this.options = new CucumberOptions(fileSystem, config);
    }

    /**
     * @desc
     *  Scenario success threshold for this test runner, calculated based on {@link CucumberConfig}
     *
     * @returns {Outcome | { Code: number }}
     */
    successThreshold(): Outcome | { Code: number } {
        return this.options.isStrict()
            ? ExecutionIgnored
            : ImplementationPending;
    }

    /**
     * @desc
     *  Loads feature files.
     *
     * @param {string[]} pathsToScenarios
     *  Absolute or relative paths to feature files
     *
     * @returns {Promise<void>}
     */
    async load(pathsToScenarios: string[]): Promise<void> {
        // todo: implement loading
        this.pathsToScenarios = pathsToScenarios;

        return Promise.resolve();
    }

    /**
     * @desc
     *  Returns the number of loaded scenarios
     *
     * @throws {@serenity-js/core/lib/errors~LogicError}
     *  If called before `load`
     *
     * @returns {number}
     */
    scenarioCount(): number {
        // todo: implement counting
        return 1;

        // if (this.totalScenarios === undefined) {
        //     throw new LogicError('Make sure to call `load` before calling `scenarioCount`');
        // }
        //
        // return this.totalScenarios;
    }

    /**
     * @desc
     *  Instructs Cucumber to execute feature files located at `pathsToScenarios`
     *
     * @returns {Promise<void>}
     */
    async run(): Promise<void> {
        const version = this.loader.hasAvailable('@cucumber/cucumber')
            ? this.loader.versionOf('@cucumber/cucumber')
            : this.loader.versionOf('cucumber');

        const serenityListener = this.loader.resolve('@serenity-js/cucumber');

        return this.runScenarios(version, serenityListener, this.pathsToScenarios);
    }

    private runScenarios(version: Version, serenityListener: string, pathsToScenarios: string[]): Promise<void> {
        const argv = this.options.asArgumentsForCucumber(version);

        if (version.isAtLeast(new Version('7.0.0'))) {
            return this.runWithCucumber7(argv, serenityListener, pathsToScenarios);
        }

        if (version.isAtLeast(new Version('3.0.0'))) {
            return this.runWithCucumber3to6(argv, serenityListener, pathsToScenarios);
        }

        if (version.isAtLeast(new Version('2.0.0'))) {
            return this.runWithCucumber2(argv, serenityListener, pathsToScenarios);
        }

        return this.runWithCucumber0to1(argv, serenityListener, pathsToScenarios);
    }

    private runWithCucumber7(argv: string[], pathToSerenityListener: string, pathsToScenarios: string[]): Promise<void> {
        const cucumber  = this.loader.require('@cucumber/cucumber');
        const output    = this.output.get();

        return new cucumber.Cli({
            argv:   argv.concat('--format', `${ pathToSerenityListener }:${ output.value() }`, ...pathsToScenarios),
            cwd:    this.loader.cwd,
            stdout: process.stdout,
        })
        .run()
        .then(cleanUpAndPassThrough(output), cleanUpAndReThrow(output));
    }

    private runWithCucumber3to6(argv: string[], pathToSerenityListener: string, pathsToScenarios: string[]): Promise<void> {
        const cucumber  = this.loader.require('cucumber');
        const output    = this.output.get();

        return new cucumber.Cli({
            argv:   argv.concat('--format', `${ pathToSerenityListener }:${ output.value() }`, ...pathsToScenarios),
            cwd:    this.loader.cwd,
            stdout: process.stdout,
        })
        .run()
        .then(cleanUpAndPassThrough(output), cleanUpAndReThrow(output));
    }

    private runWithCucumber2(argv: string[], pathToSerenityListener: string, pathsToScenarios: string[]): Promise<void> {
        const cucumber = this.loader.require('cucumber');

        return new cucumber.Cli({
            argv:   argv.concat('--require', pathToSerenityListener, ...pathsToScenarios),
            cwd:    this.loader.cwd,
            stdout: process.stdout,
        }).run();
    }

    private runWithCucumber0to1(argv: string[], pathToSerenityListener: string, pathsToScenarios: string[]): Promise<void> {
        return new Promise((resolve, reject) => {
            this.loader.require('cucumber')
                .Cli(argv.concat('--require', pathToSerenityListener, ...pathsToScenarios))
                .run((wasSuccessful: boolean) => resolve());
        })
    }
}

/**
 * @private
 */
function cleanUpAndPassThrough<T>(output: OutputDescriptor): (result: T) => Promise<T> {
    return (result: T) => {
        return output.cleanUp()
            .then(() => result);
    }
}

/**
 * @private
 */
function cleanUpAndReThrow(output: OutputDescriptor): (error: Error) => Promise<void> {
    return (error: Error) => {
        return output.cleanUp()
            .then(() => {
                throw error;
            }, ignoredError => {
                throw error;
            });
    }
}

import { ensure, isNotBlank,isString } from 'tiny-types';

/**
 * @desc
 *  Represents a Cucumber.js formatter
 *
 * @see https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#built-in-formatters
 */
export class CucumberFormat {
    public readonly formatter: string;
    public readonly output: string;

    /**
     * @param {string} value
     *  Cucumber format expression, like `pretty` or `json:out.json`
     */
    constructor(public readonly value: string) {
        [ this.formatter, this.output ] = CucumberFormat.split(
            ensure('format', value, isString(), isNotBlank())
        );
    }

    /**
     * @see https://github.com/cucumber/cucumber-js/blob/master/src/cli/option_splitter.ts
     * @param {string} format
     * @private {string[]}
     */
    private static split(format: string): [string, string] {
        const parts = format.split(/([^A-Z]):(?!\\)/);

        const result = parts.reduce((memo: string[], part: string, i: number) => {
            if (partNeedsRecombined(i)) {
                memo.push(parts.slice(i, i + 2).join(''));
            }

            return memo;
        }, []);

        if (result.length === 1) {
            result.push('');
        }

        return result as [string, string];
    }
}

/**]
 * @private
 */
function partNeedsRecombined(i: number): boolean {
    return i % 2 === 0;
}

import { FileSystem, Path, Version } from '@serenity-js/core/lib/io';

import { CucumberConfig } from './CucumberConfig';

/**
 * @private
 */
export class CucumberOptions {
    constructor(
        private readonly fileSystem: FileSystem,
        private readonly config: CucumberConfig,
    ) {
    }

    isStrict(): boolean {
        return this.asBoolean('strict', true);
    }

    asArgumentsForCucumber(version: Version): string[] {

        return Object.keys(this.config)
            .reduce(
                (acc, option: keyof CucumberConfig) =>
                    isNotEmpty(this.config[option])
                        ? acc.concat(this.optionToValues(option, this.config[option], version))
                        : acc,

                // Cucumber ignores the first two arguments anyway, but let's add them for completeness
                //  https://github.com/cucumber/cucumber-js/blob/d74bc45ba98132bdd0af62e0e52d1fe9ff017006/src/cli/helpers.js#L15
                [ 'node', 'cucumber-js' ],
            )
            .concat(this.config.rerun && this.fileSystem.exists(Path.from(this.config.rerun)) ? this.config.rerun : []);
    }

    private optionToValues<O extends keyof CucumberConfig>(option: O, value: CucumberConfig[O], version: Version): string[] {
        const cliOption = this.asCliOptionName(option);

        switch (true) {
            case cliOption === 'tags' && version.isAtLeast(new Version('2.0.0')) && value !== false:
                return this.valuesToArgs(cliOption, this.tagsToCucumberExpressions(listOf(value as string | string[])));
            case cliOption === 'rerun':
                return [];  // ignore since we're appending the rerun file anyway
            case typeof value === 'boolean':
                return listOf(this.flagToArg(cliOption, value as boolean));
            case this.isObject(value):
                return this.valuesToArgs(cliOption, JSON.stringify(value, undefined, 0));
            default:
                return this.valuesToArgs(cliOption, listOf(value as string | string[]));
        }
    }

    private asBoolean<K extends keyof CucumberConfig>(key: K, defaultValue: boolean): boolean {
        if (typeof this.config[key] === 'boolean') {
            return this.config[key] as boolean;
        }

        if (typeof this.config[negated(key)] === 'boolean') {
            return ! this.config[negated(key)] as boolean;
        }

        return defaultValue;
    }

    private isObject(value: any): value is object { // eslint-disable-line @typescript-eslint/ban-types
        return typeof value === 'object'
            && Array.isArray(value) === false
            && Object.prototype.toString.call(value) === '[object Object]';
    }

    /**
     * @desc
     *  Converts camelCase option names to kebab-case.
     *
     * @param {string} option
     * @returns {string}
     *
     * @private
     */
    private asCliOptionName(option: string): string {
        return option
            .replace(/([\da-z]|(?=[A-Z]))([A-Z])/g, '$1-$2')
            .toLowerCase();
    }

    private tagsToCucumberExpressions(tags: string[]): string {
        return tags.filter(tag => !! tag.replace)
                .map(tag => tag.replace(/~/g, 'not '))
                .join(' and ');
    }

    private flagToArg(option: string, value: boolean): string {
        switch (true) {
            case !! value:
                return `--${ option }`;
            case isNegated(option) && ! value:
                return `--${ option.replace(/^no-/, '') }`;
            default:
                return `--no-${ option }`;
        }
    }

    private valuesToArgs(option: string, values: string | string[]): string[] {
        return listOf(values)
            .map(value => [ `--${ option }`, value])
            .reduce((acc, tuple) => acc.concat(tuple), []);
    }
}

function isNegated(optionName: string) {
    return optionName.startsWith('no-');
}

// this method will need to be smarter if it was to be public, i.e. to avoid double negatives like noStrict=false
function negated(name: string) {
    return 'no' + name.charAt(0).toUpperCase() + name.slice(1);
}

function isNotEmpty(value: any): boolean {
    return value !== undefined
        && value !== null
        && value !== ''
        && ! (Array.isArray(value) && value.length === 0);
}

function listOf<T>(valueOrValues: T | T[]): T[] {
    return [].concat(valueOrValues).filter(isNotEmpty);
}

