import { TinyType } from 'tiny-types';

import { UnableToRetrieveFeatureFileMap } from './errors';

/**
 * @private
 */
export class Cache<Key extends TinyType, Value> {
    constructor(private store: { [id: string]: Value } = {}) {
    }

    set(key: Key, value: Value): void {
        this.store[this.idOf(key)] = value;
    }

    get(key: Key): Value {
        if (! this.store[this.idOf(key)]) {
            throw new UnableToRetrieveFeatureFileMap(
                `Make sure you cache a value under ${ key.toString() } before trying to retrieve it`,
            );
        }

        return this.store[this.idOf(key)];
    }

    has(key: Key): boolean {
        return !! this.store[this.idOf(key)];
    }

    size(): number {
        return Object.keys(this.store).length;
    }

    clear(): void {
        this.store = {};
    }

    private idOf(key: Key): string {
        return key.toString();
    }
}

import { Path } from '@serenity-js/core/lib/io';

import { Cache } from './Cache';
import { FeatureFileMap } from './FeatureFileMap';
import { FeatureFileMapper } from './FeatureFileMapper';
import { FeatureFileParser } from './FeatureFileParser';

/**
 * @private
 */
export class FeatureFileLoader {
    constructor(
        private readonly parser: FeatureFileParser,
        private readonly mapper: FeatureFileMapper,
        private readonly cache: Cache<Path, FeatureFileMap> = new Cache(),
    ) {
    }

    load(path: Path): Promise<FeatureFileMap> {
        if (this.cache.has(path)) {
            return Promise.resolve(this.cache.get(path));
        }

        return this.parser.parse(path)
            .then(document => this.mapper.map(document, path))  // eslint-disable-line unicorn/no-array-method-this-argument
            .then(map => {
                this.cache.set(path, map);
                return this.cache.get(path);
            });
    }
}

