                    .next(SceneTagged,         event => expect(event.tag).to.equal(new FeatureTag('Serenity/JS recognises Screenplay activities')))
                    // before
                    .next(ActivityStarts,      event => expect(event.details.name).to.equal(new Name('Before')))
