import { ModuleLoader, Version } from '@serenity-js/core/lib/io';
import { CucumberConfig } from './CucumberConfig';
import { CucumberOptions } from './CucumberOptions';

/**
 * @private
 */
export class CucumberCLIAdapter {

    private readonly options: CucumberOptions;

    constructor(
        config: CucumberConfig,
        private readonly loader: ModuleLoader,
    ) {
        this.options = new CucumberOptions(config);
    }

    run(pathsToScenarios: string[]): Promise<void> {
        return new Promise((resolve, reject) => {

            const
                cucumber: any   = this.loader.require('cucumber'),
                version         = this.loader.versionOf('cucumber'),
                argv            = this.options.asArgumentsForCucumber(version),

                serenityListener = this.loader.resolve('@serenity-js/cucumber');

            if (version.isAtLeast(new Version('2.0.0'))) {
                return new cucumber.Cli({
                    argv:   argv.concat('--format', serenityListener, ...pathsToScenarios),
                    cwd:    this.loader.cwd,
                    stdout: process.stdout,
                }).run().then(resolve, reject);
            }
            else {
                cucumber.Cli(argv.concat('--require', serenityListener, ...pathsToScenarios))
                    .run((wasSuccessful: boolean) =>
                        wasSuccessful
                            ? resolve()
                            : reject(new Error('Cucumber test run has failed')),
                    );
            }
        });
    }
}

/**
 * @public
 */
export interface CucumberConfig {
    /**
     * @desc
     *  Step definitions and support files can be written in languages that transpile to JavaScript.
     *  To do set the `compiler` option to <file_extension>:<module_name>
     *
     *  For Cucumber 4.x and above use the `require` option instead.
     *
     * @example <caption>Enable TypeScript support in Cucumber 1.x - 3.x</caption>
     *  compiler: 'ts:ts-node/register'
     *
     * @link https://github.com/cucumber/cucumber-js/blob/3.x/docs/cli.md#transpilers
     * @version 1.x - 3.x
     */
    compiler?: string;

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#formats
     */
    format?: string;

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#format-options
     */
    formatOptions?: string;

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#running-specific-features
     */
    name?: string[];

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#profiles
     */
    profile?: string[];

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#requiring-support-files
     */
    require?: string[];

    /**
     * @desc
     *  Cucumber 1.x requires the `tags` option to be an array of Cucumber tags.
     *  For example, to run all scenarios tagged with `@smoketest`, but not with `@wip`:
     *
     * @example <caption>Cucumber 1.x</caption>
     * tag: [ '@smoketest', '~@wip' ]
     *
     * @example <caption>Cucumber >= 2.x</caption>
     * tag: '@smoketest and not @wip'
     *
     * @link https://github.com/cucumber/cucumber-js/blob/1.x/docs/cli.md#tags
     * @link https://github.com/cucumber/cucumber-js/blob/2.x/docs/cli.md#tags
     *
     */
    tags?: string[] | string;

    /**
     * @link https://github.com/angular/protractor/blob/e5a5d59fcabe15860b30944e714bbd8e81ceaeae/docs/frameworks.md#using-cucumber
     */
    strict?: boolean;

    'no-colors'?: boolean;

    /**
     * @link https://github.com/cucumber/cucumber-js/blob/master/docs/cli.md#world-parameters
     */
    worldParameters?: string;
}

import { Version } from '@serenity-js/core/lib/io';
import { CucumberConfig } from './CucumberConfig';

/**
 * @private
 */
export class CucumberOptions {
    constructor(private readonly config: CucumberConfig) {
    }

    asArgumentsForCucumber(version: Version): string[] {

        return Object.keys(this.config)
            .reduce(
                (acc, option: keyof CucumberConfig) =>
                    acc.concat(this.optionToValues(option, this.config[option], version)),

                // Cucumber ignores the first two arguments anyway, but let's add them for completeness
                //  https://github.com/cucumber/cucumber-js/blob/d74bc45ba98132bdd0af62e0e52d1fe9ff017006/src/cli/helpers.js#L15
                [ 'node', 'cucumber-js' ],
            );
    }

    private optionToValues<O extends keyof CucumberConfig>(option: O, value: CucumberConfig[O], version: Version): string[] {
        switch (true) {
            case typeof value === 'boolean':
                return listOf(this.flagToArg(option, value as boolean));
            case option === 'tags' && version.isAtLeast(new Version('2.0.0')):
                return this.valuesToArgs(option, this.tagsToCucumberExpressions(listOf(value as string | string[])));
            default:
                return this.valuesToArgs(option, listOf(value as string | string[]));
        }
    }

    private tagsToCucumberExpressions(tags: string[]): string[] {
        return tags.filter(tag => !! tag.replace)
                .map(tag => tag.replace(/~/g, 'not '));
    }

    private flagToArg(option: string, value: boolean): string {
        const isNegated = (optionName: string) => optionName.startsWith('no-');

        switch (true) {
            case !! value:
                return `--${ option }`;
            case isNegated(option) && ! value:
                return `--${ option.replace(/^no-/, '') }`;
            default:
                return `--no-${ option }`;
        }
    }

    private valuesToArgs(option: string, values: string | string[]): string[] {
        return listOf(values)
            .map(value => [ `--${ option }`, value])
            .reduce((acc, tuple) => acc.concat(tuple), []);
    }
}

function listOf<T>(valueOrValues: T | T[]): T[] {
    return [].concat(valueOrValues);
}

