import { listenerForCucumber } from './listeners';

const loader = new ModuleLoader(process.cwd());

const version = loader.versionOf('cucumber');
const cucumber = loader.require('cucumber');

export = listenerForCucumber(version, cucumber, serenity);

import { AssertionError, ImplementationPendingError, TestCompromisedError, UnknownError } from '@serenity-js/core/lib/errors';
import { ErrorSerialiser, FileSystemLocation, Path } from '@serenity-js/core/lib/io';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending, Name,
    Outcome,
} from '@serenity-js/core/lib/model';

import { AmbiguousStepDefinitionError } from '../errors';
import { Feature, Hook, ScenarioOutline, Step } from '../gherkin';
import { Scenario } from '../gherkin/model';
import { CucumberFormatterOptions } from './CucumberFormatterOptions';
import { Dependencies } from './Dependencies';

import { ensure, isDefined } from 'tiny-types';

interface Location {
    uri: string;
    line: number;
}

interface StepLocations {
    actionLocation?: Location;
    sourceLocation?: Location;
}

/**
 * @private
 */
export function cucumberEventProtocolAdapter({ serenity, notifier, mapper, cache }: Dependencies) {
    return class CucumberEventProtocolAdapter {

        // note: exported class expression can't have private properties
        public readonly log: any;

        constructor({ eventBroadcaster, log }: CucumberFormatterOptions) {
            this.log = log;

            eventBroadcaster.on('gherkin-document', ({ uri, document }) => {
                ensure('gherkin-document :: uri', uri, isDefined());
                ensure('gherkin-document :: document', document, isDefined());

                const path = new Path(uri);
                cache.set(path, mapper.map(document, path));
            });

            eventBroadcaster.on('test-case-prepared', ({ steps, sourceLocation }: {
                steps: StepLocations[],
                sourceLocation: Location,
            }) => {
                ensure('test-case-prepared :: steps', steps, isDefined());
                ensure('test-case-prepared :: sourceLocation', sourceLocation, isDefined());

                const
                    path = new Path(sourceLocation.uri),
                    map = cache.get(path),
                    scenario = map.get(Scenario).onLine(sourceLocation.line);

                if (!! scenario.outline) {
                    const outline = map.get(ScenarioOutline).onLine(scenario.outline.line);

                    map.set(new ScenarioOutline(
                        outline.location,
                        outline.name,
                        outline.description,
                        outline.steps,
                        outline.parameters,
                    )).onLine(scenario.outline.line);
                }

                map.set(new Scenario(
                    scenario.location,
                    scenario.name,
                    scenario.description,
                    interleaveStepsAndHooks(scenario.steps, steps),
                    scenario.tags,
                    scenario.outline,
                )).onLine(sourceLocation.line);
            });

            function interleaveStepsAndHooks(steps: Step[], stepsLocations: StepLocations[]): Array<Step | Hook> {
                const
                    isAHook = (stepLocations: StepLocations) =>
                        stepLocations.actionLocation && ! stepLocations.sourceLocation,
                    matching  = (location: StepLocations) =>
                        (step: Step) =>
                            step.location.path.equals(new Path(location.sourceLocation.uri)) &&
                            step.location.line === location.sourceLocation.line;

                return stepsLocations.map(location =>
                    isAHook(location)
                        ?   new Hook(new FileSystemLocation(new Path(location.actionLocation.uri), location.actionLocation.line), new Name('Setup'))
                        :   steps.find(matching(location)),
                );
            }

            eventBroadcaster.on('test-case-started', ({ sourceLocation }) => {
                ensure('test-case-started :: sourceLocation', sourceLocation, isDefined());

                const
                    map = cache.get(new Path(sourceLocation.uri)),
                    scenario = map.get(Scenario).onLine(sourceLocation.line);

                if (!! scenario.outline) {
                    const outline = map.get(ScenarioOutline).onLine(scenario.outline.line);
                    notifier.outlineDetected(scenario, outline, map.getFirst(Feature));
                }

                notifier.scenarioStarts(scenario, map.getFirst(Feature));
            });

            eventBroadcaster.on('test-step-started', ({ index, testCase }) => {

                ensure('test-step-started :: index', index, isDefined());
                ensure('test-step-started :: testCase', testCase, isDefined());

                const
                    map      = cache.get(new Path(testCase.sourceLocation.uri)),
                    scenario = map.get(Scenario).onLine(testCase.sourceLocation.line),
                    step     = scenario.steps[index];

                if (step instanceof Step) { // ignore hooks
                    notifier.stepStarts(step);
                }
            });

            eventBroadcaster.on('test-step-finished', ({ index, result, testCase }) => {

                ensure('test-step-finished :: index', index, isDefined());
                ensure('test-step-finished :: result', result, isDefined());
                ensure('test-step-finished :: testCase', testCase, isDefined());

                const
                    map      = cache.get(new Path(testCase.sourceLocation.uri)),
                    scenario = map.get(Scenario).onLine(testCase.sourceLocation.line),
                    step     = scenario.steps[index];

                if (step instanceof Step) { // ignore hooks
                    notifier.stepFinished(step, this.outcomeFrom(result));
                }
            });

            eventBroadcaster.on('test-case-finished', ({ result, sourceLocation }) => {

                ensure('test-case-finished :: result', result, isDefined());
                ensure('test-case-finished :: sourceLocation', sourceLocation, isDefined());

                const
                    map             = cache.get(new Path(sourceLocation.uri)),
                    scenario        = map.get(Scenario).onLine(sourceLocation.line),
                    nonHookSteps    = scenario.steps.filter(step => step instanceof Step);

                const outcome: Outcome = nonHookSteps.length > 0
                    ? this.outcomeFrom(result)
                    : new ImplementationPending(new ImplementationPendingError(`"${ scenario.name.value }" has no test steps`));

                notifier.scenarioFinished(scenario, map.getFirst(Feature), outcome);
            });
        }

        outcomeFrom(result: { duration: number, exception: string | Error, status: string }): Outcome {
            const error = !! result.exception && this.errorFrom(result.exception);

            // tslint:disable:switch-default
            switch (result.status) {
                case 'undefined':
                    return new ImplementationPending(new ImplementationPendingError('Step not implemented'));

                case 'ambiguous':
                case 'failed':
                    switch (true) {
                        case error instanceof AssertionError:       return new ExecutionFailedWithAssertionError(error as AssertionError);
                        case error instanceof TestCompromisedError: return new ExecutionCompromised(error as TestCompromisedError);
                        default:                                    return new ExecutionFailedWithError(error);
                    }

                case 'pending':
                    return new ImplementationPending(new ImplementationPendingError('Step not implemented'));

                case 'passed':
                    return new ExecutionSuccessful();

                case 'skipped':
                    return new ExecutionSkipped();
            }
            // tslint:enable:switch-default
        }

        errorFrom(exception: Error | string): Error {
            switch (true) {
                case exception instanceof Error:
                    return exception as Error;
                case typeof exception === 'string' && exception.startsWith('Multiple step definitions match'):
                    return new AmbiguousStepDefinitionError(exception as string);
                default:
                    return ErrorSerialiser.deserialiseFromStackTrace(exception as string);
            }
        }
    };
}

/**
 * @private
 */
export interface CucumberFormatterOptions {
    colorFns: any;
    cwd: any;
    eventDataCollector: any;
    eventBroadcaster: any;
    log: typeof process.stdout.write;
    snippetBuilder: any;
    stream: any;
    supportCodeLibrary: any;
}

import { Serenity } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import { Cache, FeatureFileLoader, FeatureFileMap, FeatureFileMapper } from '../gherkin';
import { Notifier } from '../notifier';

/**
 * @private
 */
export interface Dependencies {
    serenity: Serenity;
    notifier: Notifier;
    mapper: FeatureFileMapper;
    cache: Cache<Path, FeatureFileMap>;
    loader: FeatureFileLoader;
    cucumber: any;
}

import { AssertionError, ImplementationPendingError, TestCompromisedError } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
    Outcome,
} from '@serenity-js/core/lib/model';

import { AmbiguousStepDefinitionError } from '../errors';
import { Feature, FeatureFileMap, Scenario, ScenarioOutline, Step } from '../gherkin';
import { Dependencies } from './Dependencies';

export = function ({ serenity, notifier, loader, cache }: Dependencies) {
    return function () {
        this.registerHandler('BeforeFeature', function (feature, callback) {
            loader.load(get(feature, 'uri').as(Path))
                .then(_ => callback(), error => callback(error));
        });

        this.registerHandler('BeforeScenario', function (scenario) {
            const
                path  = get(scenario, 'uri').as(Path),
                line  = get(scenario, 'line').value() as number,
                lines = get(scenario, 'lines').value() as number[],
                isOutline = lines.length === 2;

            const map = cache.get(path);

            if (isOutline) {
                notifier.outlineDetected(map.get(Scenario).onLine(line), map.get(ScenarioOutline).onLine(lines[ 1 ]), map.getFirst(Feature));
            }

            notifier.scenarioStarts(map.get(Scenario).onLine(line), map.getFirst(Feature));
        });

        this.registerHandler('BeforeStep', function (step) {
            if (shouldIgnore(step)) {
                return void 0;
            }

            const
                scenario = get(step, 'scenario').value(),
                path     = get(scenario, 'uri').as(Path);

            notifier.stepStarts(findStepMatching(step, cache.get(path)));
        });

        this.registerHandler('StepResult', function (result) {
            const
                step     = get(result, 'step').value(),
                scenario = get(step, 'scenario').value(),
                path     = get(scenario, 'uri').as(Path);

            if (shouldIgnore(step)) {
                return void 0;
            }

            notifier.stepFinished(findStepMatching(step, cache.get(path)), stepOutcomeFrom(result));
        });

        this.registerHandler('AfterScenario', function (scenario, callback) {
            const
                path     = get(scenario, 'uri').as(Path),
                line     = get(scenario, 'line').value() as number;

            const map = cache.get(path);

            notifier.scenarioFinishes(map.get(Scenario).onLine(line), map.getFirst(Feature));

            serenity.waitForNextCue()
                .then(() => callback(), error => callback(error));
        });

        this.registerHandler('ScenarioResult', function (result) {

            const
                scenario = get(result, 'scenario').value(),
                path     = get(scenario, 'uri').as(Path),
                line     = get(scenario, 'line').value() as number;

            const map = cache.get(path);

            notifier.scenarioFinished(map.get(Scenario).onLine(line), map.getFirst(Feature), scenarioOutcomeFrom(result));
        });

        this.registerHandler('AfterFeatures', (features, callback) => {
            notifier.testRunFinishes();

            serenity.waitForNextCue()
                .then(() => notifier.testRunFinished())
                .then(() => callback(), error => callback(error));
        });
    };
};

function get(object, property) {
    const getter = 'get' + property.charAt(0).toUpperCase() + property.slice(1);

    const value = object[getter]
        ? object[getter]()
        : object[property];

    return ({
        as: function<T>(type: new (v: any) => T): T {  // tslint:disable-line:object-literal-shorthand esdoc doesn't understand generic anonymous functions
            return new type(value);
        },
        value: () => value,
    });
}

function is(object, property): boolean {
    const getter = 'is' + property.charAt(0).toUpperCase() + property.slice(1);
    return object[getter] ? object[getter]() : object[getter];
}

function findStepMatching(step, map: FeatureFileMap): Step {
    const
        stepLine     = get(step, 'line').value() as number,
        scenario     = get(step, 'scenario').value(),
        path         = get(scenario, 'uri').as(Path),
        scenarioLine = get(scenario, 'line').value() as number;

    const matchedStep = map.get(Scenario).onLine(scenarioLine).steps.find(s => s.location.line === stepLine);

    if (! matchedStep) {
        throw new Error(`No step was found in ${ path } on line ${ stepLine }. This looks like a bug.`);
    }

    return matchedStep;
}

function scenarioOutcomeFrom(result): Outcome {
    const
        status: string = get(result, 'status').value(),
        error: Error   = errorFrom(get(result, 'failureException').value());

    return outcomeFrom(status, error);
}

function stepOutcomeFrom(result): Outcome {
    const
        status: string                          = get(result, 'status').value(),
        ambiguousStepsError: Error | undefined  = ambiguousStepsDetectedIn(result),
        error: Error | undefined                = errorFrom(get(result, 'failureException').value());

    return outcomeFrom(status, error || ambiguousStepsError);
}

function ambiguousStepsDetectedIn(result): Error | undefined {
    const ambiguousStepDefinitions = get(result, 'ambiguousStepDefinitions').value() || [];

    if (ambiguousStepDefinitions.length === 0) {
        return void 0;
    }

    return ambiguousStepDefinitions
        .map(step => `${ get(step, 'pattern').value().toString() } - ${ get(step, 'uri').value() }:${ get(step, 'line').value() }`)
        .reduce((err: Error, issue) => {
            err.message += `\n${issue}`;
            return err;
        }, new AmbiguousStepDefinitionError('Multiple step definitions match:'));
}

function errorFrom(error: Error | string | undefined): Error | undefined {
    switch (typeof error) {
        case 'string':   return new Error(error as string);
        case 'object':   return error as Error;
        case 'function': return error as Error;
        default:         return void 0;
    }
}

function outcomeFrom(status: string, error?: Error) {
    if (error && /timed out/.test(error.message)) {
        return new ExecutionFailedWithError(error);
    }

    // tslint:disable:switch-default
    switch (true) {
        case status === 'undefined':
            return new ImplementationPending(new ImplementationPendingError('Step not implemented'));

        case status === 'ambiguous':
            if (! error) {
                // Only the step result contains the "ambiguous step def error", the scenario itself doesn't
                return new ExecutionFailedWithError(new AmbiguousStepDefinitionError('Multiple step definitions match'));
            }

            return new ExecutionFailedWithError(error);

        case status === 'failed':
            switch (true) {
                case error instanceof AssertionError:       return new ExecutionFailedWithAssertionError(error as AssertionError);
                case error instanceof TestCompromisedError: return new ExecutionCompromised(error as TestCompromisedError);
                default:                                    return new ExecutionFailedWithError(error);
            }

        case status === 'pending':
            return new ImplementationPending(new ImplementationPendingError('Step not implemented'));

        case status === 'passed':
            return new ExecutionSuccessful();

        case status === 'skipped':
            return new ExecutionSkipped();
    }
    // tslint:enable:switch-default
}

function shouldIgnore(step): boolean {
    return is(step, 'hidden')                                       // cucumber 0-1
        || (step.constructor && step.constructor.name === 'Hook');  // cucumber 2
}

import { Dependencies } from './Dependencies';

export = function ({ serenity, notifier, loader, cucumber, cache }: Dependencies) {
    const adapter = require('./cucumber-0'); // tslint:disable-line:no-var-requires

    cucumber.defineSupportCode(support => adapter({ serenity, notifier, loader, cucumber, cache }).call(support));

    return function () {
        // no-op
    };
};

import { cucumberEventProtocolAdapter } from './CucumberEventProtocolAdapter';
import { Dependencies } from './Dependencies';

export = function (dependencies: Dependencies) {

    dependencies.cucumber.defineSupportCode(({ After, AfterAll }) => {
        After(function () {
            dependencies.notifier.currentScenarioFinishes();

            return dependencies.serenity.waitForNextCue();
        });

        AfterAll(function () {
            dependencies.notifier.testRunFinishes();

            return dependencies.serenity.waitForNextCue()
                .then(() => {
                    dependencies.notifier.testRunFinished();
                });
        });
    });

    return cucumberEventProtocolAdapter(dependencies);
};

import { cucumberEventProtocolAdapter } from './CucumberEventProtocolAdapter';
import { Dependencies } from './Dependencies';

export = function (dependencies: Dependencies) {
    const { After, AfterAll } = dependencies.cucumber;

    After(function () {
        dependencies.notifier.currentScenarioFinishes();

        return dependencies.serenity.waitForNextCue();
    });

    AfterAll(function () {
        dependencies.notifier.testRunFinishes();

        return dependencies.serenity.waitForNextCue()
            .then(() => {
                dependencies.notifier.testRunFinished();
            });
    });

    return cucumberEventProtocolAdapter(dependencies);
};

import { ConfigurationError, Serenity } from '@serenity-js/core';
import { Path, Version } from '@serenity-js/core/lib/io';

import Gherkin = require('gherkin');

import { Cache, FeatureFileLoader, FeatureFileMap, FeatureFileMapper, FeatureFileParser } from '../gherkin';
import { Notifier } from '../notifier';

export function listenerForCucumber(version: Version, cucumber: any, serenity: Serenity) {

    try {
        const
            notifier = new Notifier(serenity),
            mapper   = new FeatureFileMapper(),
            cache    = new Cache<Path, FeatureFileMap>(),
            loader   = new FeatureFileLoader(
                new FeatureFileParser(new Gherkin.Parser()),
                mapper,
                cache,
            );

        return require(`./cucumber-${ version.major() }`)({
            serenity,
            notifier,
            mapper,
            cache,
            loader,
            cucumber,
        });
    }
    catch (error) {
        throw new ConfigurationError(`Cucumber version ${ version.toString() } is not supported yet.`, error);
    }
}

import {
    DomainEvent,
    FeatureNarrativeDetected,
    SceneDescriptionDetected,
    SceneFinished,
    SceneFinishes,
    SceneParametersDetected,
    SceneSequenceDetected,
    SceneStarts,
    SceneTagged,
    SceneTemplateDetected,
    TaskFinished,
    TaskStarts,
    TestRunFinished,
    TestRunFinishes,
    TestRunnerDetected,
} from '@serenity-js/core/lib/events';
import { ActivityDetails, CapabilityTag, Category, Description, FeatureTag, Name, Outcome, ScenarioDetails, Tag, ThemeTag } from '@serenity-js/core/lib/model';

import { Serenity } from '@serenity-js/core/lib/Serenity';
import { Feature, Scenario, ScenarioOutline, Step } from '../gherkin';
import { FeatureFileNode } from '../gherkin/model/FeatureFileNode';

function notEmpty<T>(list: T[]) {
    return list.filter(item => !! item);
}

/**
 * @private
 */
export class Notifier {
    private currentScenario: ScenarioDetails;

    constructor(private readonly serenity: Serenity) {
    }

    outlineDetected(scenario: Scenario, outline: ScenarioOutline, feature: Feature): void {
        const
            outlineDetails  = this.detailsOf(outline, feature),
            scenarioDetails = this.detailsOf(scenario, feature),
            template        = outline.steps.map(step => step.name.value).join('\n');

        this.emit(...notEmpty([
            new SceneSequenceDetected(outlineDetails, this.serenity.currentTime()),
            new SceneTemplateDetected(new Description(template), this.serenity.currentTime()),
            new SceneParametersDetected(
                scenarioDetails,
                outline.parameters[ scenario.location.line ],
                this.serenity.currentTime(),
            ),
        ]));
    }

    scenarioStarts(scenario: Scenario, feature: Feature): void {
        const details = this.detailsOf(scenario, feature);

        this.currentScenario = details;

        this.emit(...notEmpty([
            new SceneStarts(details, this.serenity.currentTime()),
            feature.description && new FeatureNarrativeDetected(feature.description, this.serenity.currentTime()),
            new TestRunnerDetected(new Name('Cucumber'), this.serenity.currentTime()),
            ...this.scenarioHierarchyTagsFor(feature).map(tag => new SceneTagged(details, tag, this.serenity.currentTime())),
            !! scenario.description && new SceneDescriptionDetected(scenario.description, this.serenity.currentTime()),
            ...scenario.tags.map(tag => new SceneTagged(details, tag, this.serenity.currentTime())),
        ]));
    }

    stepStarts(step: Step): void {
        this.emit(new TaskStarts(new ActivityDetails(step.name), this.serenity.currentTime()));
    }

    stepFinished(step: Step, outcome: Outcome): void {
        this.emit(
            new TaskFinished(
                new ActivityDetails(step.name),
                outcome,
                this.serenity.currentTime(),
            ),
        );
    }

    scenarioFinishes(scenario: Scenario, feature: Feature): void {
        this.emitSceneFinishes(this.detailsOf(scenario, feature));
    }

    currentScenarioFinishes() {
        this.emitSceneFinishes(this.currentScenario);
    }

    scenarioFinished(scenario: Scenario, feature: Feature, outcome: Outcome): void {
        const details = this.detailsOf(scenario, feature);

        this.emit(
            new SceneFinished(
                details,
                outcome,
                this.serenity.currentTime(),
            ),
        );
    }

    testRunFinishes() {
        this.emit(
            new TestRunFinishes(this.serenity.currentTime()),
        );
    }

    testRunFinished() {
        this.emit(
            new TestRunFinished(this.serenity.currentTime()),
        );
    }

    private emitSceneFinishes(details: ScenarioDetails) {
        this.emit(
            new SceneFinishes(
                details,
                this.serenity.currentTime(),
            ),
        );
    }

    private detailsOf(scenario: FeatureFileNode, feature: Feature): ScenarioDetails {
        return new ScenarioDetails(
            scenario.name,
            new Category(feature.name.value),
            scenario.location,
        );
    }

    private scenarioHierarchyTagsFor(feature: Feature): Tag[] {
        const
            directories     = notEmpty(feature.location.path.directory().split()),
            featuresIndex   = directories.indexOf('features'),
            hierarchy       = [ ...directories.slice(featuresIndex + 1), feature.name.value ] as string[];

        const [ featureName, capabilityName, themeName ]: string[] = hierarchy.reverse();

        return notEmpty([
            themeName       && new ThemeTag(themeName),
            capabilityName  && new CapabilityTag(capabilityName),
            feature         && new FeatureTag(featureName),
        ]);
    }

    private emit(...events: DomainEvent[]) {
        events.forEach(event => this.serenity.announce(event));
    }
}

