import { AssertionError, ImplementationPendingError, Serenity, TestCompromisedError } from '@serenity-js/core';
import {
    DomainEvent,
    SceneFinished,
    SceneFinishes,
    SceneStarts,
    SceneTagged,
    TaskFinished,
    TaskStarts,
    TestRunFinished,
    TestRunFinishes,
    TestRunnerDetected,
    TestSuiteFinished,
    TestSuiteStarts,
} from '@serenity-js/core/lib/events';
import { ErrorSerialiser, FileSystemLocation } from '@serenity-js/core/lib/io';
import {
    ActivityDetails,
    Category,
    CorrelationId,
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionSkipped,
    ExecutionSuccessful,
    FeatureTag,
    ImplementationPending,
    Name,
    Outcome,
    ProblemIndication,
    ScenarioDetails,
    TestSuiteDetails,
} from '@serenity-js/core/lib/model';
import { Expectation, JasmineDoneInfo, SpecResult, SuiteResult } from './jasmine';

/**
 * @desc
 *  [Jasmine reporter](https://jasmine.github.io/tutorials/custom_reporter) that translates Jasmine-specific test events
 *  to Serenity/JS events.
 *
 * @see {@link bootstrap}
 */
export class SerenityReporterForJasmine {

    private static readonly errorMessagePattern = /^([^\s:]*Error):\s(.*)$/;
    private describes: SuiteResult[] = [];

    constructor(private readonly serenity: Serenity) {
    }

    suiteStarted(result: SuiteResult) {
        this.describes.push(result);

        this.emit(new TestSuiteStarts(this.testSuiteDetailsOf(result), this.serenity.currentTime()));
    }

    suiteDone(result: SuiteResult) {
        this.describes = this.describes.filter(suite => suite.id !== result.id);

        this.emit(new TestSuiteFinished(this.testSuiteDetailsOf(result), this.outcomeFrom(result), this.serenity.currentTime()));
    }

    specStarted(result: SpecResult) {
        const details = this.scenarioDetailsOf(result);

        this.emit(
            new SceneStarts(details, this.serenity.currentTime()),
            new SceneTagged(details, new FeatureTag(this.currentFeatureName()), this.serenity.currentTime()),
            new TestRunnerDetected(new Name('Jasmine'), this.serenity.currentTime()),
        );
    }

    specDone(result: SpecResult): Promise<void> {

        /**
         * Serenity doesn't allow for more than one failure per activity, but Jasmine does.
         * If there are multiple failures we wrap them up in fake activities so that they're all reported correctly.
         */
        if (result.failedExpectations.length > 1) {
            result.failedExpectations.forEach(failedExpectation => {
                const details = new ActivityDetails(new Name('Expectation'), CorrelationId.create());

                this.emit(
                    new TaskStarts(details, this.serenity.currentTime()),
                    new TaskFinished(details, this.failureOutcomeFrom(failedExpectation), this.serenity.currentTime()),
                );
            });
        }

        this.emit(new SceneFinishes(
            this.scenarioDetailsOf(result),
            this.serenity.currentTime(),
        ));

        return this.serenity.waitForNextCue()
            .then(() => {
                this.emit(new SceneFinished(
                    this.scenarioDetailsOf(result),
                    this.outcomeFrom(result),
                    this.serenity.currentTime(),
                ));
            });
    }

    /**
     * @param {JasmineDoneInfo} suiteInfo
     */
    jasmineDone(suiteInfo: JasmineDoneInfo) {
        this.emit(new TestRunFinishes(this.serenity.currentTime()));

        return this.serenity.waitForNextCue()
            .then(() => {
                this.emit(new TestRunFinished(this.serenity.currentTime()));
            });
    }

    /**
     * @private
     * @param {DomainEvent[]} events
     */
    private emit(...events: DomainEvent[]): void {
        events.forEach(event => this.serenity.announce(event));
    }

    /**
     * @private
     * @param {SpecResult} spec
     * @returns {ScenarioDetails}
     */
    private scenarioDetailsOf(spec: SpecResult): ScenarioDetails {
        return new ScenarioDetails(
            new Name(this.currentScenarioNameFor(spec.description)),
            new Category(this.currentFeatureName()),
            FileSystemLocation.fromJSON(spec.location as any),
        );
    }

    /**
     * @private
     * @param {SuiteResult} result
     * @returns {TestSuiteDetails}
     */
    private testSuiteDetailsOf(result: SuiteResult): TestSuiteDetails {
        return new TestSuiteDetails(
            new Name(result.description),
            FileSystemLocation.fromJSON(result.location as any),
            new CorrelationId(result.id),
        );
    }

    /**
     * @private
     * @returns {string}
     */
    private currentFeatureName(): string {
        return !! this.describes[0]
            ? this.describes[0].description
            : 'Unknown feature';
    }

    /**
     * @private
     * @param {string} itBlockDescription
     * @returns {string}
     */
    private currentScenarioNameFor(itBlockDescription: string): string {
        const [ topSuite, ...rest ] = this.describes;

        return rest.reverse().reduce((acc, current) => `${ current.description } ${ acc }`, itBlockDescription);
    }

    /**
     * @private
     * @param {SpecResult | SuiteResult} result
     * @returns {Outcome}
     */
    private outcomeFrom(result: SpecResult | SuiteResult): Outcome {
        switch (result.status) {
            case 'failed':
                return this.failureOutcomeFrom(result.failedExpectations[0]);
            case 'pending':
                return new ImplementationPending(new ImplementationPendingError((result as any).pendingReason || ''));
            case 'excluded':
                return new ExecutionSkipped();
            case 'passed':
            default:
                return new ExecutionSuccessful();
        }
    }

    /**
     * @private
     * @param {Expectation} failure
     * @returns {ProblemIndication}
     */
    private failureOutcomeFrom(failure: Expectation): ProblemIndication {
        const error = this.errorFrom(failure);

        if (error instanceof AssertionError) {
            // sadly, Jasmine error propagation mechanism is rather basic
            // and unable to serialise the expected/actual properties of the AssertionError object
            return new ExecutionFailedWithAssertionError(error);
        }

        if (error instanceof TestCompromisedError) {
            return new ExecutionCompromised(error);
        }

        if (!! failure.matcherName) {                       // the presence of a non-empty matcherName property indicates a Jasmine-specific assertion error
            return new ExecutionFailedWithAssertionError(
                new AssertionError(failure.message, failure.expected, failure.actual, error),
            );
        }

        return new ExecutionFailedWithError(error);
    }

    private errorFrom(failure: Expectation): Error {
        if (this.containsCorrectlySerialisedError(failure)) {
            return ErrorSerialiser.deserialiseFromStackTrace(failure.stack);
        }

        if (this.containsIncorrectlySerialisedError(failure)) {
            return ErrorSerialiser.deserialiseFromStackTrace(this.repairedStackTraceOf(failure));
        }

        return new Error(failure.message);
    }

    private containsCorrectlySerialisedError(failure: Expectation): boolean {
        return !! failure.stack && SerenityReporterForJasmine.errorMessagePattern.test(failure.stack.split('\n')[0]);
    }

    private containsIncorrectlySerialisedError(failure: Expectation): boolean {
        return !! failure.stack && SerenityReporterForJasmine.errorMessagePattern.test(failure.message);
    }

    /**
     * It seems like Jasmine mixes up serialisation and display logic,
     * which means that its "failure.stack" is not really an Error stacktrace,
     * but rather something along the lines of:
     * "error properties: AssertionError: undefined"
     * where the error message is lost, and there's an "error properties:" prefix present.
     *
     * Probably caused by this:
     * https://github.com/jasmine/jasmine/blob/b4cbe9850fbe192eaffeae450669f96e79a574ed/src/core/ExceptionFormatter.js#L93
     *
     * @param {Expectation} failure
     */
    private repairedStackTraceOf(failure: Expectation): string {
        return [
            failure.message,
            ...failure.stack.split('\n').slice(1),
        ].join('\n');
    }
}

import { ModuleLoader } from '@serenity-js/core/lib/io';
import reporter = require('../index');
import { JasmineConfig } from './JasmineConfig';

/**
 * @desc
 *  Allows for programmatic execution of Jasmine test scenarios,
 *  using {@link SerenityReporterForJasmine} to report progress.
 */
export class JasmineAdapter {

    constructor(
        private readonly config: JasmineConfig,
        private readonly loader: ModuleLoader,
    ) {
    }

    /**
     * @param {string[]} pathsToScenarios
     * @returns {Promise<void>}
     */
    run(pathsToScenarios: string[]): Promise<void> {
        return new Promise((resolve, reject) => {
            const
                JasmineRunner   = this.loader.require('jasmine'),
                runner          = new JasmineRunner({           // instantiating the JasmineRunner has a side-effect...
                    projectBaseDir: '',
                }),
                jasmine         = (global as any).jasmine;      // ... of registering a global jasmine instance

            if (this.config.defaultTimeoutInterval) {
                jasmine.DEFAULT_TIMEOUT_INTERVAL = this.config.defaultTimeoutInterval;
            }

            runner.clearReporters();

            // tslint:disable-next-line:prefer-object-spread
            runner.loadConfig(Object.assign(
                {
                    /*
                     * Serenity/JS doesn't use Jasmine's assertions, so this mechanism can be disabled
                     */
                    oneFailurePerSpec: true,

                    /*
                     * A spec should stop execution as soon as there's a hook or spec failure
                     * See https://github.com/angular/protractor/issues/3234
                     */
                    stopSpecOnExpectationFailure: true,

                    /*
                     * Default to not executing tests at random.
                     * See https://github.com/angular/protractor/blob/4f74a4ec753c97adfe955fe468a39286a0a55837/lib/frameworks/jasmine.js#L76
                     */
                    random: false,
                },
                this.config,
            ));

            runner.addReporter(reporter(jasmine));

            runner.onComplete((passed: boolean) => resolve());

            runner.configureDefaultReporter(this.config);

            runner.execute(pathsToScenarios, this.config.grep);
        });
    }
}

/**
 * @desc
 *  Configuration object that will be passed to the JasmineRunner.
 *
 * @see https://jasmine.github.io/setup/nodejs.html
 */
export interface JasmineConfig {

    /**
     * @desc
     *  A list of paths to helper files that should be loaded and executed before the `requires` and the `specs`.
     *  Accepts relative and absolute paths as well as glob expressions.
     *
     * @type {string[] | undefined}
     * @see https://jasmine.github.io/tutorials/react_with_npm
     */
    helpers?: string[];

    /**
     * @desc
     *  A list of paths to files that should be required after the `helpers`, but before the `specs`.
     *  The path need to be compatible with Node {@link require}.
     *
     * @type {string[] | undefined}
     */
    requires?: string[];

    /**
     * @desc
     *  Whether or not the tests should be executed in a pseudo-random order.
     *
     * @type {boolean | undefined}
     */
    random?: boolean;

    /**
     * @desc
     *  Used to exclude any test scenarios which name doesn't match the pattern from the run.
     *
     * @type {string | RegExp | undefined}
     */
    grep?: string | RegExp;

    /**
     * @desc
     *  The randomisation seed that will be used to determine the pseudo-random order of execution,
     *  if `random` is set to `true`
     *
     * @type {string | undefined}
     * @see {@link JasmineConfig#random}
     */
    seed?: string;

    /**
     * @desc
     *  Sets the global `jasmine.DEFAULT_TIMEOUT_INTERVAL`,
     *  which defines the default number of milliseconds Jasmine will wait for an asynchronous spec to complete.
     *
     * @type {number | undefined}
     * @see https://jasmine.github.io/api/edge/jasmine#.DEFAULT_TIMEOUT_INTERVAL
     */
    defaultTimeoutInterval?: number;
}

import { RuntimeError, serenity } from '@serenity-js/core';
import { monkeyPatched } from './monkeyPatched';
import { SerenityReporterForJasmine } from './SerenityReporterForJasmine';

/**
 * @desc
 *  Monkey-patches Jasmine's Suite and Spec so that they provide more accurate information and
 *  returns a bootstrapped instance of the {@link SerenityReporterForJasmine} to be registered with Jasmine.
 *
 * @example <caption>Registering the reporter from the command line</caption>
 * jasmine --reporter=@serenity-js/jasmine
 *
 * @example <caption>Registering the reporter programmatically</caption>
 * import serenityReporterForJasmine = require('@serenity-js/jasmine');
 *
 * jasmine.getEnv().addReporter(serenityReporterForJasmine);
 *
 * @see {@link monkeyPatched}
 * @see {@link SerenityReporterForJasmine}
 *
 * @param {jasmine} jasmine - the global.jasmine instance
 * @returns {SerenityReporterForJasmine}
 */
export function bootstrap(jasmine = (global as any).jasmine) {
    const wrappers = {
        expectationResultFactory: originalExpectationResultFactory => ((attrs: { passed: boolean, error: Error }) => {
            const result = originalExpectationResultFactory(attrs);

            if (! attrs.passed && attrs.error instanceof RuntimeError) {
                result.stack = attrs.error.stack;
            }

            return result;
        }),
    };

    jasmine.Suite = monkeyPatched(jasmine.Suite, wrappers);
    jasmine.Spec = monkeyPatched(jasmine.Spec, wrappers);

    return new SerenityReporterForJasmine(serenity);
}

/**
 * @package
 * @see https://jasmine.github.io/api/edge/global.html#Expectation
 */
export interface Expectation {

    /** The name of the matcher that was executed for this expectation. */
    matcherName: string;

    /** The failure message for the expectation. */
    message: string;

    /** The stack trace for the failure if available. */
    stack?: string;

    /** Whether the expectation passed or failed. */
    passed:	boolean;

    /** If the expectation failed, what was the expected value. */
    expected?: any;

    /** If the expectation failed, what actual value was produced. */
    actual?: any;
}

