import { AssertionError, ImplementationPendingError, TestCompromisedError } from '@serenity-js/core';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
    Outcome,
} from '@serenity-js/core/lib/model';
import { Test } from 'mocha';

/**
 * @package
 */
export class MochaOutcomeMapper {
    public outcomeOf(test: Test, err?: Error): Outcome {
        const error = err || test.err;

        switch (true) {
            case !! error && this.isGoingToBeRetried(test):
                return new ExecutionIgnored(error);

            case !! error && this.isAssertionError(error):
                return new ExecutionFailedWithAssertionError(error as AssertionError);

            case !! error && error instanceof TestCompromisedError:
                return new ExecutionCompromised(error as TestCompromisedError);

            case !! error:
                return new ExecutionFailedWithError(error);

            case test.isPending() && !! test.fn:
                return new ExecutionSkipped();

            case test.isPending() && ! test.fn:
                return new ImplementationPending(new ImplementationPendingError(`Scenario not implemented`));

            default:
                return new ExecutionSuccessful();
        }
    }

    private isGoingToBeRetried(test: Test) {
        return ! test.isPassed()
            && ! test.isFailed()
            && ! test.isPending()
            && (test as any).currentRetry() < test.retries();
    }

    private isAssertionError(error: Error): error is AssertionError {
        return error instanceof AssertionError
            || this.looksLikeAnAssertionError(error);
    }

    private looksLikeAnAssertionError(error: Error): error is AssertionError {
        return /^AssertionError/.test(error.name)
            && error.hasOwnProperty('expected')
            && error.hasOwnProperty('actual')
    }
}

import { FileSystemLocation, Path } from '@serenity-js/core/lib/io';
import { Category, Name, ScenarioDetails } from '@serenity-js/core/lib/model';
import { Suite, Test } from 'mocha';

/**
 * @package
 */
export class MochaTestMapper {
    public detailsOf(test: Test): ScenarioDetails {

        function fileOf(t) {
            switch (true) {
                case !! t.ctx && !! t.ctx.currentTest && !! t.ctx.currentTest.file:
                    return t.ctx.currentTest.file;
                case !! t.file:
                    return t.file;
                case !! t.parent:
                    return fileOf(t.parent);
                default:
                    throw new Error(`Couldn't determine path of ${ t }`);
            }
        }

        return new ScenarioDetails(
            new Name(this.nameOf(test)),
            new Category(this.featureNameFor(test)),
            new FileSystemLocation(
                new Path(fileOf(test)),
            ),
        );
    }

    public featureNameFor(scenario: Test | Suite): string {
        const parentTitle = scenario?.parent?.title;

        return parentTitle !== undefined && parentTitle.trim() !== ''
            ? this.featureNameFor(scenario.parent)
            : scenario.title;
    }

    private fullNameOf(scenario: Test | Suite): string {
        return !! scenario.parent
            ? `${ this.fullNameOf(scenario.parent) } ${ scenario.title }`.trim()
            : scenario.title;
    }

    private nameOf(scenario: Test | Suite): string {
        return this.fullNameOf(scenario)
            .substring(this.featureNameFor(scenario).length)
            .trim();
    }
}

