import { SerenityConfig } from '@serenity-js/core';
import { Config as ProtractorConfig } from 'protractor';

/**
 * @desc
 *  Protractor configuration object with an additional
 *  section to configure the Serenity/JS framework.
 *
 * @public
 * @interface
 *
 * @see {@link @serenity-js/core~SerenityConfig}
 *
 * @see https://github.com/angular/protractor/blob/master/lib/config.ts
 * @see {@link @serenity-js/core~configure}
 */
export interface Config extends ProtractorConfig {
    serenity: SerenityConfig & { runner?: string };
}

import { ArtifactArchiver, Serenity } from '@serenity-js/core';

import deepmerge = require('deepmerge');
const isPlainObject = require('is-plain-object');   // tslint:disable-line:no-var-requires fails when using default import

import { protractor, Runner } from 'protractor';
import { BrowserDetector, StandardisedCapabilities } from './browser-detector';

import { Config } from './Config';
import { ProtractorReport, ProtractorReporter } from './reporter';
import { TestRunnerDetector } from './TestRunnerDetector';

/**
 * @private
 */
export class ProtractorFrameworkAdapter {
    constructor(
        private readonly serenity: Serenity,
        private readonly protractorRunner: Runner,
        private readonly detector: TestRunnerDetector,
    ) {
    }

    /**
     * @param {string[]} specs
     *  Paths to spec files
     *
     * @return {Promise<ProtractorReport>}
     */
    run(specs: string[]): Promise<ProtractorReport> {

        const noop      = () => void 0;
        const config    = deepmerge<Config>(this.defaultConfig(), this.protractorRunner.getConfig(), {
            isMergeableObject: isPlainObject,
        });

        const runner    = this.detector.runnerFor(config);
        const reporter  = new ProtractorReporter(this.protractorRunner);

        this.serenity.configure({
            cueTimeout:     config.serenity.cueTimeout,
            actors:         config.serenity.actors,
            crew:           [
                BrowserDetector.with(StandardisedCapabilities.of(() => protractor.browser)),
                ...config.serenity.crew,
                reporter,
            ],
        });

        return Promise.resolve()
            .then(() => this.protractorRunner.runTestPreparer(TestRunnerDetector.protractorCliOptions()))
            .then(() => runner.run(specs))
            .then(() => Promise.resolve((config.onComplete || noop)()))
            .then(() => reporter.report());
    }

    private defaultConfig(): Config {
        return {
            serenity: {
                crew: [
                    ArtifactArchiver.storingArtifactsAt(process.cwd(), 'target/site/serenity'),
                ],
            },
        };
    }
}

import { ModuleLoader } from '@serenity-js/core/lib/io';
import { Config } from 'protractor';

import { TestRunner } from './runners/TestRunner';

/**
 * @private
 */
export class TestRunnerDetector {

    static protractorCliOptions() {
        return [
            'cucumberOpts',
            'jasmineNodeOpts',
            'mochaOpts',
        ];
    }

    constructor(private readonly loader: ModuleLoader) {
    }

    // todo: when invoking, merge config
    //      Object.assign(
    //          {},
    //          config.cucumberOpts,
    //          config.capabilities.cucumberOpts
    //      )
    runnerFor(config: Config): TestRunner {

        // todo: simplify and introduce a config object with "as(String)", "as(Object)", etc. to avoid issues with undefined
        // todo: and config merge too, using on deepmerge

        const
            specifiesRunnerFor = (type: string) =>
                !!config.serenity &&
                !!config.serenity.runner &&
                config.serenity.runner === type;

        switch (true) {
            case specifiesRunnerFor('cucumber'):
                return this.useCucumber(config);
            case specifiesRunnerFor('jasmine'):
                return this.useJasmine(config);
            case specifiesRunnerFor('mocha'):
                return this.useMocha(config);
            case !! config.cucumberOpts:
                return this.useCucumber(config);
            case !! config.mochaOpts:
                return this.useMocha(config);
            case !! config.jasmineNodeOpts:
            default:
                return this.useJasmine(config);
        }
    }

    private useCucumber(config: Config): TestRunner {
        const { CucumberTestRunner } = require('./runners/CucumberTestRunner');
        return new CucumberTestRunner(config.cucumberOpts || {}, this.loader);
    }

    private useJasmine(config: Config): TestRunner {
        const { JasmineTestRunner } = require('./runners/JasmineTestRunner');
        return new JasmineTestRunner(config.jasmineNodeOpts, this.loader);
    }

    private useMocha(config: Config): TestRunner {
        const { MochaTestRunner } = require('./runners/MochaTestRunner');
        return new MochaTestRunner(config.mochaOpts, this.loader);
    }
}

import { Stage } from '@serenity-js/core';
import { AsyncOperationAttempted, AsyncOperationCompleted, DomainEvent, SceneStarts, SceneTagged } from '@serenity-js/core/lib/events';
import { BrowserTag, CorrelationId, Description, PlatformTag } from '@serenity-js/core/lib/model';
import { StageCrewMember } from '@serenity-js/core/lib/stage';
import { platform } from 'os';
import { protractor } from 'protractor';
import { StandardisedCapabilities } from './StandardisedCapabilities';

/**
 * @private
 *
 * @see https://github.com/serenity-js/serenity-js/issues/455
 * @see https://github.com/serenity-bdd/serenity-core/pull/1860/files
 * @see https://github.com/serenity-js/serenity-js/issues/132
 */
export class BrowserDetector implements StageCrewMember {

    static with(capabilities: StandardisedCapabilities) {
        return new BrowserDetector(capabilities);
    }

    constructor(
        private readonly capabilities: StandardisedCapabilities,
        private readonly stage: Stage = null,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new BrowserDetector(this.capabilities, stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneStarts) {
            const id = CorrelationId.create();

            this.stage.announce(new AsyncOperationAttempted(
                new Description(`[${ this.constructor.name }] Detecting web browser details...`),
                id,
                this.stage.currentTime(),
            ));

            Promise.all([
                this.capabilities.browserName(),
                this.capabilities.browserVersion(),
                this.capabilities.platformName(),
                this.capabilities.platformVersion(),
            ]).
            then(([browserName, browserVersion, platformName, platformVersion]) => {

                this.stage.announce(new SceneTagged(
                    event.value,
                    new BrowserTag(browserName, browserVersion),
                    this.stage.currentTime(),
                ));

                this.stage.announce(new SceneTagged(
                    event.value,
                    new PlatformTag(platformName, platformVersion),
                    this.stage.currentTime(),
                ));

                this.stage.announce(new AsyncOperationCompleted(
                    new Description(`[${ this.constructor.name }] Detected web browser details`),
                    id,
                    this.stage.currentTime(),
                ));
            });
        }
    }
}

import { Capabilities, ProtractorBrowser } from 'protractor';

/**
 * @private
 */
export class StandardisedCapabilities {
    static of(currentBrowser: () => ProtractorBrowser) {
        return new StandardisedCapabilities(currentBrowser);
    }

    constructor(private currentBrowser: () => ProtractorBrowser) {
    }

    browserName(): PromiseLike<string> {
        return this.get(
            caps => caps.get('browserName'),
        );
    }

    browserVersion(): PromiseLike<string> {
        return this.get(
            caps => caps.get('version'),
            caps => caps.get('browserVersion'),
            caps => caps.has('deviceManufacturer') && caps.has('deviceModel')
                ? `${ caps.get('deviceManufacturer') } ${ caps.get('deviceModel') }`
                : undefined,
            caps => caps.has('mobile') && caps.get('mobile').version,
        ).
        then(version =>
            this.get(
                caps => !! caps.get('mobileEmulationEnabled') && '(mobile emulation)',
            ).then(suffix =>
                [
                    version,
                    suffix,
                ].filter(_ => !!_).join(' '),
            ),
        );
    }

    platformName(): PromiseLike<string> {
        return this.get(
            caps => (!! caps.get('platformName') && ! /any/i.test(caps.get('platformName')))
                ? caps.get('platformName')
                : caps.get('platform'),
        );
    }

    platformVersion(): PromiseLike<string> {
        return this.get(
            caps => caps.get('platformVersion'),
        );
    }

    private get(...fetchers: Array<(capabilities: Capabilities) => string>): PromiseLike<string> {
        return this.currentBrowser().getCapabilities().then(caps => {
            for (const fetcher of fetchers) {
                const result = fetcher(caps);
                if (!! result) {
                    return result;
                }
            }
            return undefined;
        });
    }
}

export * from './ProtractorFrameworkAdapter';
export * from './run';
export * from './TestRunnerDetector';

import { SpecResult } from 'protractor/built/plugins';              // tslint:disable-line:no-submodule-imports

/**
 * @desc
 *  A JSON object which needs to be returned to Protractor when {@link ProtractorFrameworkAdapter}
 *  is done with executing the test scenarios.
 *
 * @see https://github.com/angular/protractor/blob/master/lib/frameworks/README.md
 *
 * @private
 */
export interface ProtractorReport {
    /**
     * Total number of failed scenarios
     */
    failedCount: number;

    /**
     * Results per scenario
     *
     * Note: duration is not part of the SpecResult, but is being used by the Jasmine reporter
     * https://github.com/angular/protractor/blob/4f74a4ec753c97adfe955fe468a39286a0a55837/lib/frameworks/jasmine.js#L32
     */
    specResults: Array<SpecResult & { duration: number }>;
}

import { Stage } from '@serenity-js/core';
import { AsyncOperationAttempted, AsyncOperationCompleted, AsyncOperationFailed, DomainEvent, SceneFinished, SceneFinishes, SceneStarts } from '@serenity-js/core/lib/events';
import { CorrelationId, Description, ExecutionSuccessful, ProblemIndication, Timestamp } from '@serenity-js/core/lib/model';
import { StageCrewMember } from '@serenity-js/core/lib/stage';
import { Runner } from 'protractor';
import { ProtractorReport } from './ProtractorReport';

/**
 * @private
 */
export class ProtractorReporter implements StageCrewMember {
    private readonly startTime: { [key: string ]: Timestamp } = {};

    constructor(
        private readonly runner: Runner,
        private readonly reported: ProtractorReport = { failedCount: 0, specResults: [] },
        private readonly stage: Stage = null,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new ProtractorReporter(this.runner, this.reported, stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneStarts) {
            this.recordStart(event);
        }

        else if (event instanceof SceneFinishes) {
            this.afterEach();
        }

        else if (event instanceof SceneFinished && event.outcome instanceof ExecutionSuccessful) {
            this.recordSuccess(event);

            this.runner.emit('testPass', {
                name:       event.value.name.value,
                category:   event.value.category.value,
            });
        }

        else if (event instanceof SceneFinished && event.outcome instanceof ProblemIndication) {
            this.recordFailure(event);

            this.runner.emit('testFail', {
                name:       event.value.name.value,
                category:   event.value.category.value,
            });
        }
    }

    report(): ProtractorReport {
        return this.reported;
    }

    private recordFailure(event: SceneFinished) {
        const outcome = (event.outcome as ProblemIndication);

        this.reported.failedCount++;

        this.reported.specResults.push({
            description: `${ event.value.category.value } ${ event.value.name.value }`,
            duration: event.timestamp.diff(this.startTime[event.value.toString()]).inMilliseconds(),
            assertions: [{
                passed: false,
                errorMsg: outcome.error.message,
                stackTrace: outcome.error.stack,
            }],
        });
    }

    private recordStart(event: SceneStarts) {
        this.startTime[event.value.toString()] = event.timestamp;
    }

    private recordSuccess(event: SceneFinished) {
        this.reported.specResults.push({
            description: `${ event.value.category.value } ${ event.value.name.value }`,
            duration: event.timestamp.diff(this.startTime[event.value.toString()]).inMilliseconds(),
            assertions: [{
                passed: true,
            }],
        });
    }

    private afterEach(): PromiseLike<void> {
        if (! this.runner.afterEach) {
            return Promise.resolve();
        }

        const id = CorrelationId.create();

        this.stage.announce(new AsyncOperationAttempted(
            new Description(`[${ this.constructor.name }] Invoking ProtractorRunner.afterEach...`),
            id,
            this.stage.currentTime(),
        ));

        return Promise.resolve(this.runner.afterEach() as PromiseLike<void> | undefined)
            .then(
                () =>
                    this.stage.announce(new AsyncOperationCompleted(
                        new Description(`[${ this.constructor.name }] ProtractorRunner.afterEach succeeded`),
                        id,
                        this.stage.currentTime(),
                    )),
                error =>
                    this.stage.announce(new AsyncOperationFailed(error, id, this.stage.currentTime())),
            );
    }
}

