            },
            expected: { browserName: 'safari', browserVersion: 'iPhone 8 Plus-11.0', platformName: 'iOS', platformVersion: '11.0' },
        }, {
            description: 'Safari on Mac',
            actual: { browserName: 'safari', version: '7.1.8', platform: 'MAC' },
            expected: { browserName: 'safari', browserVersion: '7.1.8', platformName: 'MAC' },
        }, {
            description: 'IE 6 on Windows',
            actual: { browserName: 'internet explorer', version: '6', platform: 'WINDOWS' },
            expected: { browserName: 'internet explorer', browserVersion: '6', platformName: 'WINDOWS' },
        }, {
            description: 'Chrome (mobile emulation) on Mac OS X',
            actual: {
                browserName: 'chrome', version: '80.0.3987.87', browserVersion: undefined,
                deviceManufacturer: undefined, deviceModel: undefined, mobile: undefined, mobileEmulationEnabled: true,
                platformName: undefined, platform: 'Mac OS X', platformVersion: undefined,
            },
            expected: {
                browserName: 'chrome', browserVersion: '80.0.3987.87 (mobile emulation)', platformName: 'Mac OS X',
            },
        },
    ]).
    it(`standardises the WebDriver capabilities across browsers and platforms`, ({ actual, expected }) => {
        const fakeBrowser = {
            getCapabilities: () => Promise.resolve(new Capabilities(actual)),
        };

        const capabilities = StandardisedCapabilities.of(() => fakeBrowser as unknown as ProtractorBrowser);

        return Promise.all([
            expect(capabilities.browserName()).to.eventually.equal(expected.browserName),
            expect(capabilities.browserVersion()).to.eventually.equal(expected.browserVersion),
            expect(capabilities.platformName()).to.eventually.equal(expected.platformName),
            expect(capabilities.platformVersion()).to.eventually.equal(expected.platformVersion),
        ]);
    });
});

import { expect } from '@integration/testing-tools';
import { Serenity } from '@serenity-js/core';
import { ArtifactGenerated, SceneFinished } from '@serenity-js/core/lib/events';
import { FileSystemLocation, Path } from '@serenity-js/core/lib/io';
import { Category, ExecutionSkipped, Name, ScenarioDetails, TextData } from '@serenity-js/core/lib/model';
import { Runner } from 'protractor';
import * as sinon from 'sinon';
import { ProtractorReporter } from '../../../src/adapter/reporter';

/**
 * See the {@link ProtractorFrameworkAdapter} specs to see how the {@link ProtractorReporter} is used in the context
 */
describe('ProtractorReporter', () => {

    let reporter:           ProtractorReporter,
        protractorRunner:   Runner,
        serenity:           Serenity;

    beforeEach(() => {
        protractorRunner    = sinon.createStubInstance(Runner);
        reporter            = new ProtractorReporter(protractorRunner);
        serenity            = new Serenity();

        serenity.configure({ crew: [ reporter ] });
    });

    const details = new ScenarioDetails(
        new Name('scenario name'),
        new Category('scenario category'),
        new FileSystemLocation(new Path('./some/scenario.spec.ts')),
    );

    /** @test {ProtractorReporter} */
    it('ignores events outcomes Protractor doesn\'t care about', () => {

        expect(reporter.notifyOf(new SceneFinished(details, new ExecutionSkipped()))).to.be.undefined;  // tslint:disable-line:no-unused-expression

        expect(reporter.report()).to.deep.equal({
            failedCount: 0,
            specResults: [],
        });
    });

    /** @test {ProtractorReporter} */
    it('ignores domain events Protractor doesn\'t care about', () => {

        const artifact = TextData.fromJSON({ contentType: 'text/plain', data: 'one,two,three'});

        expect(reporter.notifyOf(new ArtifactGenerated(new Name('report.csv'), artifact))).to.be.undefined;  // tslint:disable-line:no-unused-expression

        expect(reporter.report()).to.deep.equal({
            failedCount: 0,
            specResults: [],
        });
    });
});

