import { actorCalled, Duration } from '@serenity-js/core';
import { by } from 'protractor';

import { Navigate, Target, Text, Website } from '../../../src';
import { pageFromTemplate } from '../../fixtures';

describe('Navigate', () => {

    describe('to(url)', () => {

        /** @test {Navigate.to} */
        it('allows the actor to navigate to a desired destination', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(pageFromTemplate(`
                <html>
                    <body>
                        <h1 id="h">Hello World</h1>
                    </body>
                </html>
            `)),

            Ensure.that(Text.of(Target.the('heading').located(by.id('h'))), equals('Hello World')),
        ));

        /** @test {Navigate#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Navigate.to(`https://serenity-js.org`).toString())
                .to.equal(`#actor navigates to 'https://serenity-js.org'`);
        });
    });

    describe('to(url).withTimeout(duration)', function () {

        /** @test {Navigate#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Navigate.to(`https://serenity-js.org`).withTimeout(Duration.ofSeconds(5)).toString())
                .to.equal(`#actor navigates to 'https://serenity-js.org' waiting up to 5s for Angular to load`);
        });
    });

    describe('back', () => {

        /** @test {Navigate.back} */
        it('allows the actor to navigate back in the browser history', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(`chrome://version/`),
            Navigate.to(`chrome://accessibility/`),

            Navigate.back(),

            Ensure.that(Website.url(), endsWith('version/')),
        ));

        /** @test {Navigate#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Navigate.back().toString())
                .to.equal(`#actor navigates back in the browser history`);
        });
    });

    describe('forward', () => {

        /** @test {Navigate.forward} */
        it('allows the actor to navigate forward in the browser history', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(`chrome://version/`),
            Navigate.to(`chrome://accessibility/`),

            Navigate.back(),
            Navigate.forward(),

            Ensure.that(Website.url(), endsWith('accessibility/')),
        ));

        /** @test {Navigate#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Navigate.forward().toString())
                .to.equal(`#actor navigates forward in the browser history`);
        });
    });

    describe('reloadPage', () => {
        /** @test {Navigate.to} */
        it('allows the actor to navigate to a desired destination', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(pageFromTemplate(`
                <html>
                    <body>
                        <h1 id="h">Hello!</h1>
                    </body>
                    <script>
                        if(window.performance.navigation.type === window.performance.navigation.TYPE_RELOAD) {
                            document.getElementById('h').textContent = 'Reloaded'
                        }
                    </script>
                </html>
            `)),

            Navigate.reloadPage(),

            Ensure.that(Text.of(Target.the('heading').located(by.id('h'))), equals('Reloaded')),
        ));

        /** @test {Navigate#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Navigate.reloadPage().toString())
                .to.equal(`#actor reloads the page`);
        });
    });
});

import { expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled } from '@serenity-js/core';
import { given } from 'mocha-testdata';
import { by, Key } from 'protractor';

import { Navigate, Press, Target, Value } from '../../../src';
import { pageFromTemplate } from '../../fixtures';

describe('Press', () => {

    const Form = {
        Text_Field:     Target.the('text field').located(by.name('text')),
    };

    // todo: add check.whether

    const page = pageFromTemplate(`
            <html>
                <body>
                    <form>
                        <input type="text" name="text" />
                    </form>
                </body>
            </html>
        `);

    describe('single keys', () => {

        /** @test {Press} */
        /** @test {Press.the} */
        it('allows the actor to enter keys individually into a field', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(page),

            Press.the('a').in(Form.Text_Field),
            Press.the('A').in(Form.Text_Field),

            Ensure.that(Value.of(Form.Text_Field), equals('aA')),
        ));

    });

    describe('keyboard shortcuts', function () {

        /** @test {Press} */
        /** @test {Press.the} */
        it('allows the actor to use keyboard shortcuts', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(page),

            Press.the(Key.SHIFT, 'a').in(Form.Text_Field),

            Ensure.that(Value.of(Form.Text_Field), equals(`A`)),
        ));

    });

    given([
        {
            description: 'single key',
            interaction: Press.the('a').in(Form.Text_Field),
            expected:   `#actor presses A in the text field`,
        },
        {
            description: 'sequence of keys',
            interaction: Press.the('a', 'b', 'c').in(Form.Text_Field),
            expected:   `#actor presses A, B, C in the text field`,
        },
        {
            description: 'keyboard shortcut',
            interaction: Press.the(Key.CONTROL, 'a').in(Form.Text_Field),
            expected:   `#actor presses Control-A in the text field`,
        },
        {
            description: 'complex shortcut',
            interaction: Press.the(Key.COMMAND, Key.ALT, 'a').in(Form.Text_Field),
            expected:   `#actor presses Command-Alt-A in the text field`,
        },
    ]).
    /** @test {Press#toString} */
    it('provides a sensible description of the interaction being performed', ({ interaction, expected }) => {
        expect(interaction.toString()).to.equal(expected);
    });
});

