import { expect } from '@integration/testing-tools';
import { Ensure, isGreaterThan, isLessThan } from '@serenity-js/assertions';
import { actorCalled } from '@serenity-js/core';
import { by } from 'protractor';

import { ExecuteScript, LastScriptExecution, Navigate, Scroll, Target } from '../../../src';
import { pageFromTemplate } from '../../fixtures';

describe('Scroll', function () {

    const aLongSpell = pageFromTemplate(`
        <html>
            <body style="margin:0; padding:0 0 1024px 0;">
                <input type="submit" value="Cast!" id="cast" style="margin-top:1024px;" />
            </body>
        </html>
    `);

    const Page = {
        Execute_Button: Target.the('"Cast!" button').located(by.id('cast')),
    };

    /** @test {Scroll} */
    /** @test {Scroll.to} */
    it('allows the actor to scroll to a given target so that it appears in the viewport', () => actorCalled('Gandalf').attemptsTo(
        Navigate.to(aLongSpell),

        ExecuteScript.sync(`return arguments[0].getBoundingClientRect().top;`).withArguments(Page.Execute_Button),
        Ensure.that(LastScriptExecution.result<number>(), isGreaterThan(1000)),

        Scroll.to(Page.Execute_Button),

        ExecuteScript.sync(`return arguments[0].getBoundingClientRect().top;`).withArguments(Page.Execute_Button),
        Ensure.that(LastScriptExecution.result<number>(), isLessThan(1000)),
    ));

    /** @test {Enter#toString} */
    it('provides a sensible description of the interaction being performed', () => {
        expect(Scroll.to(Page.Execute_Button).toString())
            .to.equal(`#actor scrolls to the "Cast!" button`);
    });
});

import { EventRecorder, expect, PickEvent } from '@integration/testing-tools';
import { actorCalled, configure } from '@serenity-js/core';
import { ActivityRelatedArtifactGenerated } from '@serenity-js/core/lib/events';

import { Navigate, TakeScreenshot } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

describe('TakeScreenshot', () => {

    let recorder: EventRecorder;

    beforeEach(() => {
        recorder = new EventRecorder();

        configure({
            actors: new UIActors(),
            crew: [ recorder ],
        });
    });

    const page = pageFromTemplate(`
            <html>
                <body>
                    <form>
                        <input type="text" name="text" />
                    </form>
                </body>
            </html>
        `);

    /** @test {TakeScreenshot} */
    /** @test {TakeScreenshot.of} */
    it('allows the actor to take a screenshot with an arbitrary name', () => actorCalled('Bernie').attemptsTo(
        Navigate.to(page),
        TakeScreenshot.of('the page'),
    ).then(() => {
        PickEvent.from(recorder.events)
            .next(ActivityRelatedArtifactGenerated, (e: ActivityRelatedArtifactGenerated) => {
                expect(e.name.value).to.equal('the page');
            });
    }));

    /** @test {TakeScreenshot#toString} */
    it('provides a sensible description of the interaction being performed', () => {
        expect(TakeScreenshot.of('the page').toString()).to.equal(`#actor takes a screenshot of 'the page'`);
    });
});

import { expect } from '@integration/testing-tools';
import { Ensure, equals, isTrue } from '@serenity-js/assertions';
import { actorCalled, Question } from '@serenity-js/core';

import { protractor } from 'protractor';
import { UseAngular } from '../../../src';
import { promiseOf } from '../../../src/promiseOf';

describe('UseAngular', function () {

    describe('synchronisation', () => {

        afterEach(() => protractor.browser.waitForAngularEnabled(false));    // same as protractor.conf.js

        const IsSynchronisationEnabled = () => Question.about('angular synchronisation',
                actor => promiseOf(protractor.browser.waitForAngularEnabled()),
        );

        describe('when enabled', () => {
            beforeEach(() => promiseOf(protractor.browser.waitForAngularEnabled(true)));

            /** @test {UseAngular} */
            it('can be disabled', () => actorCalled('Bernie').attemptsTo(
                UseAngular.disableSynchronisation(),
                Ensure.that(IsSynchronisationEnabled(), equals(false)),
            ));
        });

        describe('when disabled', () => {
            beforeEach(() => promiseOf(protractor.browser.waitForAngularEnabled(false)));

            /** @test {UseAngular} */
            it('can be enabled', () => actorCalled('Bernie').attemptsTo(
                UseAngular.enableSynchronisation(),
                Ensure.that(IsSynchronisationEnabled(), isTrue()),
            ));
        });

        /** @test {UseAngular} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(UseAngular.enableSynchronisation().toString())
                .to.equal(`#actor enables synchronisation with Angular`);

            expect(UseAngular.disableSynchronisation().toString())
                .to.equal(`#actor disables synchronisation with Angular`);
        });
    });
});

import { expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled, AssertionError, Duration } from '@serenity-js/core';
import { by } from 'protractor';

import { Navigate, Target, Text, Wait } from '../../../src';
import { pageFromTemplate } from '../../fixtures';

describe('Wait', () => {

    const Status = Target.the('header').located(by.id('status'));

    const Test_Page = pageFromTemplate(`
        <html>
            <body>
                <h1 id="status">Loading...</h1>
                <script>
                    (function () {
                        setTimeout(function () {
                            document.getElementById('status').textContent = 'Ready!'
                        }, 200);
                    })();
                </script>
            </body>
        </html>
    `);

    describe('for', () => {

        /** @test {Wait} */
        /** @test {Wait.for} */
        it('pauses the actor flow for the length of an explicitly-set duration', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(Test_Page),

            Wait.for(Duration.ofMilliseconds(300)),

            Ensure.that(Text.of(Status), equals('Ready!')),
        ));

        /** @test {Wait#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Wait.for(Duration.ofMilliseconds(300)).toString())
                .to.equal(`#actor waits for 300ms`);
        });
    });

    describe('until', () => {

        /** @test {Wait} */
        /** @test {Wait.until} */
        it('pauses the actor flow until the expectation is met', () => actorCalled('Bernie').attemptsTo(
            Navigate.to(Test_Page),

            Wait.until(Text.of(Status), equals('Ready!')),

            Ensure.that(Text.of(Status), equals('Ready!')),
        ));

        /** @test {Wait} */
        /** @test {Wait.upTo} */
        /** @test {Wait.until} */
        it('fails the actor flow when the timeout expires', () => expect(actorCalled('Bernie').attemptsTo(
            Navigate.to(Test_Page),

            Wait.upTo(Duration.ofMilliseconds(10)).until(Text.of(Status), equals('Ready!')),
        )).to.be.rejected.then((error: AssertionError) => {
            expect(error).to.be.instanceOf(AssertionError);
            expect(error.message).to.be.equal(`Waited 10ms for the text of the header to equal 'Ready!'`);
            expect(error.actual).to.be.equal('Loading...');
            expect(error.expected).to.be.equal('Ready!');

            expect(error.cause.name).to.equal('TimeoutError');
            expect(error.cause.message).to.match(/^Wait timed out after.*/);
        }));

        /** @test {Wait#toString} */
        it('provides a sensible description of the interaction being performed', () => {
            expect(Wait.upTo(Duration.ofMilliseconds(10)).until(Text.of(Status), equals('Ready!')).toString())
                .to.equal(`#actor waits up to 10ms until the text of the header does equal 'Ready!'`);
        });
    });
});

import { EventRecorder, expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled, Question, Serenity } from '@serenity-js/core';
import { ActivityFinished, ActivityRelatedArtifactGenerated, ActivityStarts, ArtifactGenerated } from '@serenity-js/core/lib/events';
import { TextData } from '@serenity-js/core/lib/model';
import { Clock } from '@serenity-js/core/lib/stage';

import { by } from 'protractor';
import { ExecuteScript, Navigate, Target, Value } from '../../../../src';
import { pageFromTemplate } from '../../../fixtures';
import { UIActors } from '../../../UIActors';

/** @test {ExecuteAsynchronousScript} */
describe('ExecuteAsynchronousScript', function () {

    const page = pageFromTemplate(`
        <html>
            <body>
                <form>
                    <input type="text" id="name" />
                </form>
            </body>
        </html>
    `);

    class Sandbox {
        static Input = Target.the('input field').located(by.id('name'));
    }

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('allows the actor to execute an asynchronous script', () => actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        ExecuteScript.async(`
            var callback = arguments[arguments.length - 1];

            setTimeout(function () {
                document.getElementById('name').value = 'Joe';
                callback();
            }, 100);
        `),

        Ensure.that(Value.of(Sandbox.Input), equals(actorCalled('Joe').name)),
    ));

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('allows the actor to execute an asynchronous script with a static argument', () => actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        ExecuteScript.async(`
            var name = arguments[0];
            var callback = arguments[arguments.length - 1];

            setTimeout(function () {
                document.getElementById('name').value = name;
                callback();
            }, 100);
        `).withArguments(actorCalled('Joe').name),

        Ensure.that(Value.of(Sandbox.Input), equals(actorCalled('Joe').name)),
    ));

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('allows the actor to execute an asynchronous script with a promised argument', () => actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        ExecuteScript.async(`
            var name = arguments[0];
            var callback = arguments[arguments.length - 1];

            setTimeout(function () {
                document.getElementById('name').value = name;
                callback();
            }, 100);
        `).withArguments(Promise.resolve(actorCalled('Joe').name)),

        Ensure.that(Value.of(Sandbox.Input), equals(actorCalled('Joe').name)),
    ));

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('allows the actor to execute an asynchronous script with a Target argument', () => actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        ExecuteScript.async(`
            var name = arguments[0];
            var field = arguments[1];
            var callback = arguments[arguments.length - 1];

            setTimeout(function () {
                field.value = name;
                callback();
            }, 100);
        `).withArguments(actorCalled('Joe').name, Sandbox.Input),

        Ensure.that(Value.of(Sandbox.Input), equals(actorCalled('Joe').name)),
    ));

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    /** @test {ExecuteAsynchronousScript#toString} */
    it('provides a sensible description of the interaction being performed when invoked without arguments', () => {
        expect(ExecuteScript.async(`
            arguments[arguments.length - 1]();
        `).toString()).to.equal(`#actor executes an asynchronous script`);
    });

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript#toString} */
    it('provides a sensible description of the interaction being performed when invoked with arguments', () => {
        const arg3 = Question.about('arg number 3', actor => void 0);

        expect(ExecuteScript.async(`arguments[arguments.length - 1]();`)
            .withArguments(Promise.resolve('arg1'), 'arg2', arg3).toString(),
        ).to.equal(`#actor executes an asynchronous script with arguments: [ a Promise, 'arg2', arg number 3 ]`);
    });

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('complains if the script has failed', () => expect(actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        ExecuteScript.async(`
            var callback = arguments[arguments.length - 1];

            throw new Error("something's not quite right here");
        `),
    )).to.be.rejectedWith(Error, `something's not quite right here`));

    /** @test {ExecuteScript.async} */
    /** @test {ExecuteAsynchronousScript} */
    it('emits the events so that the details of the script being executed can be reported', () => {
        const frozenClock = new Clock(() => new Date('1970-01-01'));
        const serenity = new Serenity(frozenClock);
        const recorder = new EventRecorder();

        serenity.configure({
            actors: new UIActors(),
            crew: [ recorder ],
        });

        return serenity.theActorCalled('Ashwin').attemptsTo(
            ExecuteScript.async(`arguments[arguments.length - 1]();`),
        ).then(() => {
            const events = recorder.events;

            expect(events).to.have.lengthOf(3);
            expect(events[ 0 ]).to.be.instanceOf(ActivityStarts);
            expect(events[ 1 ]).to.be.instanceOf(ArtifactGenerated);
            expect(events[ 2 ]).to.be.instanceOf(ActivityFinished);

            const artifactGenerated = events[ 1 ] as ActivityRelatedArtifactGenerated;

            expect(artifactGenerated.name.value).to.equal(`Script source`);

            expect(artifactGenerated.artifact.equals(TextData.fromJSON({
                contentType: 'text/javascript;charset=UTF-8',
                data: 'arguments[arguments.length - 1]();',
            }))).to.equal(true, JSON.stringify(artifactGenerated.artifact.toJSON()));

            expect(artifactGenerated.timestamp.equals(frozenClock.now())).to.equal(true, artifactGenerated.timestamp.toString());
        });
    });
});

import { expect } from '@integration/testing-tools';
import { containAtLeastOneItemThat, Ensure, equals, includes, property } from '@serenity-js/assertions';
import { actorCalled, LogicError } from '@serenity-js/core';

import { by } from 'protractor';
import { Browser, ExecuteScript, Navigate, Target, Text } from '../../../../src';

/** @test {ExecuteScriptFromUrl} */
describe('ExecuteScriptFromUrl', function () {

    const
        pathToScript = `file://${ require.resolve('./resources/execute-script-sample.js') }`,
        pathToPage = `file://${ require.resolve('./resources/execute-script-sandbox.html') }`;

    class Sandbox {
        static Result = Target.the('sandbox result').located(by.id('result'));
    }

    /** @test {ExecuteScript.from} */
    /** @test {ExecuteScriptFromUrl} */
    it('allows the actor to execute a script stored at a specific location', () => actorCalled('Joe').attemptsTo(
        Navigate.to(pathToPage),

        ExecuteScript.from(pathToScript),

        Ensure.that(Text.of(Sandbox.Result), equals('Script loaded successfully')),
    ));

    /** @test {ExecuteScript.from} */
    /** @test {ExecuteScriptFromUrl} */
    it('complains if the script could not be loaded', () => expect(actorCalled('Joe').attemptsTo(
        Navigate.to(pathToPage),

        ExecuteScript.from(pathToScript + '.invalid'),
    )).to.be.rejectedWith(LogicError, `Couldn't load script from ${ pathToScript }.invalid`)
        .then(() => actorCalled('Joe').attemptsTo(
            Ensure.that(Browser.log(), containAtLeastOneItemThat(property('message', includes('execute-script-sample.js.invalid - Failed to load resource')))),
        )));

    /** @test {ExecuteScript.from} */
    /** @test {ExecuteScriptFromUrl} */
    it('complains if the script has already been loaded', () => expect(actorCalled('Joe').attemptsTo(
        Navigate.to(pathToPage),

        ExecuteScript.from(pathToScript),
        ExecuteScript.from(pathToScript),
    )).to.be.rejectedWith(LogicError, `Script from ${ pathToScript } has already been loaded`));

    /** @test {ExecuteScript.from} */
    /** @test {ExecuteScriptFromUrl#toString} */
    it('provides a sensible description of the interaction being performed', () => {
        expect(ExecuteScript.from(pathToScript).toString())
            .to.equal(`#actor executes a script from ${ pathToScript }`);
    });
});

