import 'mocha';

import { ConfigurationError, LogicError, TestCompromisedError } from '@serenity-js/core';
import axios, { Method } from 'axios';
import MockAdapter from 'axios-mock-adapter';
import { given } from 'mocha-testdata';

import { CallAnApi } from '../../../src';
import { expect } from '../../expect';

/** @test {CallAnApi} */
describe('CallAnApi', () => {

    given([
        { description: 'GET',       verification: 'onGet'      },
        { description: 'POST',      verification: 'onPost'     },
        { description: 'PUT',       verification: 'onPut'      },
        { description: 'HEAD',      verification: 'onHead'     },
        { description: 'DELETE',    verification: 'onDelete'   },
        { description: 'PATCH',     verification: 'onPatch'    },
        { description: 'OPTIONS',   verification: 'onAny'      },
    ]).
    it('allows the Actor to interact with HTTP endpoints via', ({description, verification}) => {
        const
            { callAnApi, mock } = mockedAxiosInstance(),
            method = description as Method,
            url = '/products/2';

        mock[verification](url).reply(200);

        return expect(callAnApi.request({ method, url })).to.be.fulfilled; // tslint:disable-line:no-unused-expression
    });

    /**
     * @test {CallAnApi}
     * @test {CallAnApi.at}
     */
    it('provides a convenient factory method to be used when no custom configuration is required', () => {
        const
            baseURL = 'https://mycompany.com/api',
            callAnApi = CallAnApi.at(baseURL);

        expect(callAnApi).to.be.instanceOf(CallAnApi);
        expect((callAnApi as any).axiosInstance.defaults.baseURL).to.equal(baseURL);
    });

    /**
     * @test {CallAnApi}
     * @test {CallAnApi.using}
     */
    it('provides a factory method to be used when custom configuration is required', () => {
        const
            baseURL = 'https://mycompany.com/api',
            callAnApi = CallAnApi.using(axios.create({ baseURL }));

        expect(callAnApi).to.be.instanceOf(CallAnApi);
        expect((callAnApi as any).axiosInstance.defaults.baseURL).to.equal(baseURL);
    });

    /**
     * @test {CallAnApi}
     * @test {CallAnApi.mapLastResponse}
     */
    it('caches the last response so that it can be easily asserted on', () => {
        const
            { callAnApi, mock } = mockedAxiosInstance(),
            url = '/products/2';

        mock.onGet(url).reply(200, { id: 2 });

        return expect(callAnApi.request({ method: 'get', url })).to.be.fulfilled.then(() => {
            callAnApi.mapLastResponse(response => expect(response.status).to.equal(200));
        });
    });

    /**
     * @test {CallAnApi}
     * @test {CallAnApi.request}
     * @test {CallAnApi.mapLastResponse}
     */
    it('caches the last response even when the server responds with an error. As long is it responds.', () => {
        const
            { callAnApi, mock } = mockedAxiosInstance(),
            url = '/products/2';

        mock.onGet(url).reply(500);

        return expect(callAnApi.request({ method: 'get', url })).to.be.fulfilled.then(() => {
            callAnApi.mapLastResponse(response => expect(response.status).to.equal(500));
        });
    });

    describe('when dealing with errors', () => {
        /**
         * @test {CallAnApi}
         * @test {CallAnApi.mapLastResponse}
         */
        it('complains if you try to retrieve the last response before making an API call', () => {
            const { callAnApi } = mockedAxiosInstance();

            expect(() => callAnApi.mapLastResponse(_ => _))
                .to.throw(LogicError, 'Make sure to perform a HTTP API call before checking on the response');
        });

        /**
         * @test {CallAnApi}
         * @test {CallAnApi.request}
         */
        it('complains when provided with invalid Axios configuration', () => {
            const
                axiosInstance = axios.create({
                    baseURL: ['bah', 'humbug', '!'] as any,
                }),
                callAnApi = CallAnApi.using(axiosInstance);

            return expect(callAnApi.request({ method: 'get', url: '/some/api' })).to.be.rejectedWith('Looks like there was an issue with Axios configuration')
                .then((error: ConfigurationError) => {
                    expect(error).to.be.instanceOf(ConfigurationError);
                    expect(error.stack).to.contain('Caused by: TypeError');
                });
        });

        /**
         * @test {CallAnApi}
         * @test {CallAnApi.request}
         */
        it('complains when the API call times out', () => {
            const { callAnApi, mock } = mockedAxiosInstance();

            mock.onAny().timeout();

            return expect(callAnApi.request({ method: 'get', url: '/some/api' })).to.be.rejectedWith('The request has timed out')
                .then((error: TestCompromisedError) => {
                    expect(error).to.be.instanceOf(TestCompromisedError);
                    expect(error.stack).to.contain('Caused by: Error: timeout of 0ms exceeded');
                });
        });

        /**
         * @test {CallAnApi}
         * @test {CallAnApi.request}
         */
        it('complains when the a network error occurs', () => {
            const { callAnApi, mock } = mockedAxiosInstance();

            mock.onAny().networkError();

            return expect(callAnApi.request({ method: 'get', url: '/some/api' })).to.be.rejectedWith('A network error has occurred')
                .then((error: TestCompromisedError) => {
                    expect(error).to.be.instanceOf(TestCompromisedError);
                    expect(error.stack).to.contain('Caused by: Error: Network Error');
                });
        });

        /**
         * @test {CallAnApi}
         * @test {CallAnApi.request}
         */
        it('complains when the Axios client rejects its promise', () => {
            const { callAnApi, mock } = mockedAxiosInstance();

            mock.onAny().reply(() => Promise.reject(new Error('something unpredictable')));

            return expect(callAnApi.request({ method: 'get', url: '/some/api' })).to.be.rejectedWith('The API call has failed')
                .then((error: TestCompromisedError) => {
                    expect(error).to.be.instanceOf(TestCompromisedError);
                    expect(error.stack).to.contain('Caused by: Error: something unpredictable');
                });
        });
    });

    function mockedAxiosInstance() {
        const
            axiosInstance = axios.create(),
            mock = new MockAdapter(axiosInstance),
            callAnApi = CallAnApi.using(axiosInstance);

        return { mock, callAnApi };
    }
});

import 'mocha';

import axios from 'axios';
import MockAdapter from 'axios-mock-adapter';

import { EventRecorder } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { Serenity } from '@serenity-js/core';
import { ActivityFinished, ActivityRelatedArtifactGenerated, ActivityStarts } from '@serenity-js/core/lib/events';
import { HTTPRequestResponse } from '@serenity-js/core/lib/model';
import { Clock } from '@serenity-js/core/lib/stage';

import { GetRequest, LastResponse, Send } from '../../../src';
import { actorUsingAMockedAxiosInstance, APIActors } from '../../actors';
import { expect } from '../../expect';

/** @test {Send} */
describe('Send', () => {

    /**
     * @test {Send.a}
     * @test {HTTPRequest}
     * @test {GetRequest.to}
     * @test {LastResponse.status}
     * @test {LastResponse.body}
     */
    it('enables the actor to send a HTTPRequest', () => {
        const { actor, mock } = actorUsingAMockedAxiosInstance();

        mock.onGet('/products/2').reply(200, {
            id: 2,
        });

        return actor.attemptsTo(
            Send.a(GetRequest.to('/products/2')),
            Ensure.that(LastResponse.status(), equals(200)),
            Ensure.that(LastResponse.body(), equals({ id: 2 })),
        );
    });

    /**
     * @test {Send.a}
     * @test {HTTPRequest}
     * @test {GetRequest.to}
     * @test {LastResponse.status}
     * @test {LastResponse.body}
     */
    it('enables the actor to send an Axios Request', () => {
        const { actor, mock } = actorUsingAMockedAxiosInstance();

        mock.onGet('/products/2').reply(200, {
            id: 2,
        });

        return actor.attemptsTo(
            Send.a({
                method: 'get',
                url: '/products/2',
            }),
            Ensure.that(LastResponse.status(), equals(200)),
            Ensure.that(LastResponse.body(), equals({ id: 2 })),
        );
    });

    /**
     * @test {Send.a}
     * @test {HTTPRequest}
     * @test {GetRequest.to}
     */
    it('emits the events so that the details of the HTTP interaction can be reported', () => {
        const frozenClock = new Clock(() => new Date('1970-01-01'));
        const axiosInstance = axios.create({
            url: 'https://myapp.com/api',
        });
        const mock     = new MockAdapter(axiosInstance);
        const serenity = new Serenity(frozenClock);
        const recorder = new EventRecorder();

        serenity.configure({
            actors: new APIActors(axiosInstance),
            crew: [ recorder ],
        });

        mock.onGet('/products/2').reply(200, {
            id: 2,
        }, {
            'Content-Type': 'application/json',
        });

        return serenity.theActorCalled('Apisitt').attemptsTo(
            Send.a(GetRequest.to('/products/2')),
        ).then(() => {
            const events = recorder.events;

            expect(events).to.have.lengthOf(3);
            expect(events[ 0 ]).to.be.instanceOf(ActivityStarts);
            expect(events[ 1 ]).to.be.instanceOf(ActivityRelatedArtifactGenerated);
            expect(events[ 2 ]).to.be.instanceOf(ActivityFinished);

            const artifactGenerated = events[ 1 ] as ActivityRelatedArtifactGenerated;

            expect(artifactGenerated.name.value).to.equal(`request get /products/2`);
            expect(artifactGenerated.artifact.equals(HTTPRequestResponse.fromJSON({
                request: {
                    method: 'get',
                    url: '/products/2',
                    headers: { Accept: 'application/json, text/plain, */*' },
                },
                response: {
                    status: 200,
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    data: { id: 2 },
                },
            }))).to.equal(true, JSON.stringify(artifactGenerated.artifact.toJSON()));

            expect(artifactGenerated.timestamp.equals(frozenClock.now()))
                .to.equal(true, artifactGenerated.timestamp.toString());
        });
    });
});

import 'mocha';

import { Ensure, equals } from '@serenity-js/assertions';
import { LogicError } from '@serenity-js/core';
import { GetRequest, LastResponse, Send } from '../../../src';
import { actorUsingAMockedAxiosInstance } from '../../actors';
import { expect } from '../../expect';

/** @test {LastResponse} */
describe('LastResponse', () => {

    const { actor, mock } = actorUsingAMockedAxiosInstance();

    afterEach(() =>  mock.reset());

    describe('when asserting on the response to the last HTTP request', () => {

        const
            url     = '/products/2',
            body    = { id: 2, name: 'apple' },
            headers = { 'Content-Type': 'application/json;charset=utf-8' };

        beforeEach(() =>  mock.onGet(url).reply(200, body, headers));

        /**
         * @test {LastResponse}
         * @test {LastResponse.status}
         */
        it('enables access to the response status', () => actor.attemptsTo(
            Send.a(GetRequest.to(url)),
            Ensure.that(LastResponse.status(), equals(200)),
        ));

        /**
         * @test {LastResponse}
         * @test {LastResponse.body}
         */
        it('enables access to the response body', () => actor.attemptsTo(
            Send.a(GetRequest.to(url)),
            Ensure.that(LastResponse.body(), equals(body)),
        ));

        /**
         * @test {LastResponse}
         * @test {LastResponse.header}
         */
        it('enables access to a specific response header', () => actor.attemptsTo(
            Send.a(GetRequest.to(url)),
            Ensure.that(LastResponse.header('Content-Type'), equals(headers['Content-Type'])),
        ));

        /**
         * @test {LastResponse}
         * @test {LastResponse.headers}
         */
        it('enables access to all response headers', () => actor.attemptsTo(
            Send.a(GetRequest.to(url)),
            Ensure.that(LastResponse.headers(), equals(headers)),
        ));
    });

    /**
     * @test {CallAnApi.mapLastResponse}
     */
    it('complains if the last response is attempted to be retrieved without making a request first', () =>
        expect(actor.attemptsTo(
            Ensure.that(LastResponse.status(), equals(200)),
        )).to.be.rejectedWith(LogicError, 'Make sure to perform a HTTP API call before checking on the response'),
    );
});

