/**
 * @package
 */
export interface Argv {
    [ key: string ]: string;
}

import chalk = require('chalk');

/**
 * @package
 */
export class Printer {

    constructor(
        public readonly out: NodeJS.WritableStream,
        public readonly err: NodeJS.WritableStream,     // eslint-disable-line unicorn/prevent-abbreviations
    ) {
    }

    info(message: string): void {
        this.out.write(chalk.green(message) + '\n');
    }

    error(message: string): void {
        this.out.write(chalk.red(message) + '\n');
    }
}

import { actorCalled, configure } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import * as path from 'path';   // eslint-disable-line unicorn/import-style

import { Argv } from '../Argv';
import { defaults } from '../defaults';
import { formatError } from '../io';
import { GAV } from '../model';
import { Printer } from '../Printer';
import { InvokeSerenityBDD, SerenityBDDArguments, SystemProperties } from '../screenplay';
import { NotificationReporter, ProgressReporter, RunCommandActors } from '../stage';

const yargs = require('yargs'); // eslint-disable-line @typescript-eslint/no-var-requires

export = {
    command: 'run',
    desc: 'Invokes the Serenity BDD CLI jar to produce a Serenity BDD HTML report from the Serenity/JS JSON reports',
    builder: {
        cacheDir: {
            default:   defaults.cacheDir,
            describe: 'A relative path to where the Serenity BDD CLI jar file is stored',
        },
        destination: {
            default:   defaults.reportDir,
            describe: 'A relative path to the directory where the Serenity BDD report should be produced',
        },
        features: {
            default:   defaults.featuresDir,
            describe: 'A relative path to the directory containing the Cucumber.js feature files',
        },
        artifact: {
            default:   defaults.artifact,
            describe: `The GAV identifier of the Serenity BDD CLI artifact to use; You're best off with the default option unless you want to experiment.`,
        },
        source: {
            default:   defaults.sourceDir,
            describe: `A relative path to the directory containing the Serenity/JS JSON reports`,
        },
        issueTrackerUrl: {
            describe: `Base URL for issue trackers other than JIRA`,
        },
        jiraProject: {
            describe: `JIRA project identifier`,
        },
        jiraUrl: {
            describe: `Base URL of your JIRA server`,
        },
        project: {
            default: path.basename(process.cwd()),
            describe: `Project name to appear in the Serenity reports`,
        },
        shortFilenames: {
            default: true,
            type: 'boolean',
            describe: `Use unique hashes instead of human-readable names for the HTML files`,
        },
        log: {
            default: defaults.log,
            choices: [ 'warn', 'info', 'debug' ],
            describe: `A Logback log level to pass to the Serenity BDD CLI jar`,
        },
    },
    handler: (argv: Argv): Promise<void> => {

        const
            printer         = new Printer(process.stdout, process.stderr),
            artifactGAV     = GAV.fromString(argv.artifact),
            pathToArtifact  = new Path(argv.cacheDir).join(artifactGAV.toPath()),
            moduleRoot      = path.resolve(__dirname, '../../../');

        configure({
            actors: new RunCommandActors(new Path(process.cwd())),
            crew: [
                new NotificationReporter(printer),
                new ProgressReporter(printer),
            ],
        });

        return Promise.resolve()
            .then(() =>
                actorCalled('Serenity/JS Updater').attemptsTo(
                    InvokeSerenityBDD.at(pathToArtifact)
                        .withProperties(SystemProperties.of({
                            'serenity.compress.filenames': `${ argv.shortFilenames }`,
                            'LOG_LEVEL': argv.log,
                            'logback.configurationFile': path.resolve(moduleRoot, './resources/logback.config.xml'),
                        }))
                        .withArguments(SerenityBDDArguments.from(argv)),
                )
            )
            .catch(error => {
                printer.error(formatError(error));
                yargs.exit(1, error.message);
            });
    },
};

import { isTrue } from '@serenity-js/assertions';
import { actorCalled, Check, configure } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import { URL } from 'url';

import { Argv } from '../Argv';
import { defaults } from '../defaults';
import { axiosClient, formatError } from '../io';
import { Credentials, GAV } from '../model';
import { Printer } from '../Printer';
import { DownloadArtifact, FileExists, Notify } from '../screenplay';
import { NotificationReporter, ProgressReporter, UpdateCommandActors } from '../stage';

const yargs = require('yargs'); // eslint-disable-line @typescript-eslint/no-var-requires

export = {
    command: 'update',
    desc: 'Makes sure the Serenity BDD CLI jar file is available and up to date',
    builder: {
        cacheDir: {
            default:   defaults.cacheDir,
            describe: 'A relative path to where the Serenity BDD CLI jar file should be stored',
        },
        ignoreSSL: {
            default:   false,
            type:     'boolean',
            describe: 'Ignore SSL certificates',
        },
        repository: {
            default:   defaults.repository,
            describe: 'Maven repository url where we should look for the Serenity BDD CLI artifact',
        },
        auth: {
            describe: `Credentials to authenticate with your repository - "<username>:<password>"`,
        },
        artifact: {
            default:   defaults.artifact,
            describe: `The GAV identifier of the Serenity BDD CLI artifact to use; You're best off with the default option unless you want to experiment.`,
        },
    },
    handler: (argv: Argv): Promise<void> => {

        const
            printer         = new Printer(process.stdout, process.stderr),
            artifactGAV     = GAV.fromString(argv.artifact),
            pathToArtifact  = new Path(argv.cacheDir).join(artifactGAV.toPath()),
            repository      = new URL(argv.repository);

        configure({
            actors: new UpdateCommandActors(
                new Path(process.cwd()),
                () => axiosClient(repository, !! argv.ignoreSSL, process.env, Credentials.fromString(argv.auth))
            ),
            crew: [
                new NotificationReporter(printer),
                new ProgressReporter(printer),
            ],
        });

        return Promise.resolve()
            .then(() =>
                actorCalled('Serenity/JS Updater').attemptsTo(
                    Check.whether(FileExists.at(pathToArtifact), isTrue())
                        .andIfSo(
                            Notify.that(`Looks like you're good to go! Serenity BDD CLI is already at ${ pathToArtifact.value }`),
                        )
                        .otherwise(
                            DownloadArtifact
                                .identifiedBy(artifactGAV)
                                .availableFrom(repository)
                                .to(pathToArtifact.directory())
                        ),
                )
            )
            .catch(error => {
                printer.error(formatError(error));
                yargs.exit(1, error.message);
            });
    },
};

import { ConfigurationError } from '@serenity-js/core';
import axios, { AxiosInstance, AxiosProxyConfig, AxiosRequestConfig } from 'axios';
import { httpAdapter } from 'axios/lib/adapters/http';
import * as fs from 'fs';
import * as https from 'https';
import { URL } from 'url';

import { Credentials } from '../model';

const HttpsProxyAgent = require('https-proxy-agent');   // eslint-disable-line @typescript-eslint/no-var-requires

/**
 * @package
 */
export function axiosClient(
    repository: URL,
    ignoreSsl: boolean,
    env: { [key: string]: string },
    repositoryAuth?: Credentials
): AxiosInstance {

    const configuredProxy = isHttps(repository.protocol)
        ? firstNonEmpty(env, 'npm_config_https_proxy', 'https_proxy', 'HTTPS_PROXY')
        : firstNonEmpty(env, 'npm_config_proxy', 'npm_config_http_proxy', 'http_proxy', 'HTTP_PROXY');

    const configuredNoProxy = firstNonEmpty(env, 'npm_config_noproxy', 'no_proxy', 'NO_PROXY');

    const configuredCa = certificationAuthority(env);

    const rejectUnauthorized = shouldRejectUnauthorizedCertificates(env, ignoreSsl);

    const options: AxiosRequestConfig = {
        baseURL:    repository.toString(),
        adapter:    httpAdapter,
        auth:       repositoryAuth,
    };

    if (isHttps(repository.protocol)) {
        options.proxy = false;
        options.httpsAgent = shouldProxy(repository, configuredProxy, configuredNoProxy)
            ? proxiedHttpsAgent(proxyConfigFrom(configuredProxy), configuredCa, rejectUnauthorized)
            : httpsAgent(configuredCa, rejectUnauthorized);
    }
    else {
        options.proxy = shouldProxy(repository, configuredProxy, configuredNoProxy)
            ? proxyConfigFrom(configuredProxy)
            : undefined;
    }

    if (env.npm_config_user_agent) {
        options.headers = { 'User-Agent': env.npm_config_user_agent };
    }

    return axios.create(options);
}

/**
 * @package
 */
export function shouldProxy(url: URL, configuredProxy: EnvVar, configuredNoProxy: EnvVar): boolean {
    // there's no proxy configured so don't proxy...
    if (! configuredProxy.value) {
        return false;
    }

    const noProxyDomains = (configuredNoProxy.value || '').split(',').map(_ => _.trim());

    return ! noProxyDomains.some(noProxyDomain => {
        if (! noProxyDomain) {
            return false;
        }

        if (noProxyDomain === '*') {
            return true;
        }

        if (noProxyDomain[0] === '.' && url.hostname.slice(url.hostname.length - noProxyDomain.length) === noProxyDomain) {
            return true;
        }

        return url.hostname === noProxyDomain;
    });
}

function proxyConfigFrom(proxyUrl: EnvVar): AxiosProxyConfig | undefined {

    if (! proxyUrl || ! proxyUrl.value) {
        return undefined;
    }

    let parsed: URL;

    try {
        parsed = new URL(proxyUrl.value);
    } catch (error) {
        throw new ConfigurationError(`Env variable ${ proxyUrl.name }=${ proxyUrl.value } should specify a valid URL`, error);
    }

    if (! isSupportedProtocol(parsed.protocol)) {
        throw new ConfigurationError(`Env variable ${ proxyUrl.name }=${ proxyUrl.value } should specify protocol to be used, i.e. http:// or https://`);
    }

    return {
        protocol: parsed.protocol,
        host: parsed.hostname,

        port: parsed.port
            ? Number.parseInt(parsed.port, 10)
            : (isHttps(parsed.protocol) ? 443 : 80),

        auth: (parsed.password || parsed.username) ? {
            password: parsed.password,
            username: parsed.username,
        } : undefined,
    };
}

function shouldRejectUnauthorizedCertificates(env: { [key: string]: string }, ignoreSsl: boolean): boolean {
    return ! ignoreSsl
        || env.npm_config_strict_ssl === 'true'
}

function proxiedHttpsAgent(proxyConfig: AxiosProxyConfig, ca: string, rejectUnauthorized: boolean) {
    return new HttpsProxyAgent({ ca, rejectUnauthorized, ...proxyConfig });
}

function httpsAgent(ca: string, rejectUnauthorized: boolean): https.Agent {
    return new https.Agent({ ca, rejectUnauthorized, });
}

function certificationAuthority(env: { [key: string]: string }): string | undefined {

    // old school, the value is specified in the env var itself
    if (env.npm_config_ca) {
        return env.npm_config_ca;
    }

    if (env.npm_config_cafile) {
        try {
            return fs.readFileSync(env.npm_config_cafile, { encoding: 'utf8' });
        } catch (error) {
            throw new ConfigurationError(`Could not read npm_config_cafile at ${ env.npm_config_cafile }`, error);
        }
    }

    return undefined;
}

type EnvVar = { name?: string, value?: string };    // eslint-disable-line unicorn/prevent-abbreviations

function firstNonEmpty(env: { [key: string]: string }, ...candidateVariables: string[]): EnvVar {
    const found = candidateVariables.find(value => !! env[value]);

    return found
        ? { name: found, value: env[found] }
        : {};
}

function isSupportedProtocol(protocol: string) {
    return [ 'http:', 'https:', 'socks4:', 'socks5:' ]
        .find(supported => supported === protocol);
}

function isHttps(urlOrProtocol: string) {
    return urlOrProtocol
        && urlOrProtocol.startsWith('https:');
}

import { RuntimeError } from '@serenity-js/core';

/**
 * @package
 */
export function formatError(error: RuntimeError | Error): string {

    const learnMore = `Learn more at https://serenity-js.org/modules/serenity-bdd`;

    switch (true) {
        case /ETIMEDOUT/.test(error.message):
            return lines(
                `Are you behind a proxy or a firewall that needs to be configured?`,
                ...messages(error),
                '',
                learnMore,
            );

        case /self signed certificate in certificate chain/.test(error.message):
            return lines(
                `If you're using a self-signed certificate please check if it's configured correctly or use the --ignoreSSL option.`,
                '',
                learnMore,
            );

        case error instanceof RuntimeError:
            return lines(
                ...messages(error),
                '',
                learnMore
            );

        default:
            return lines(
                `I'm terribly sorry, but something didn't go according to plan:`,
                ...messages(error),
            );
    }
}

function messages(error: RuntimeError | Error, acc: string[] = []): string[] {
    return error.message && (error as RuntimeError).cause
        ? messages((error as RuntimeError).cause, acc.concat(error.message))
        : acc.concat(error.message || `${ error }`);
}

function lines(...entries: string[]): string {
    return entries.filter(line => line !== void 0).join('\n');
}

import { JSONData } from '@serenity-js/core/lib/model';

/**
 * @desc
 *  An issue to be reported to the user.
 *
 * @package
 */
export class Complaint extends JSONData {
    static fromJSON(value: { description: string, message: string, stack?: string }): Complaint {
        return new Complaint(
            Buffer.from(JSON.stringify(value, undefined, 0), 'utf8').toString('base64')
        );
    }
}

import { ensure, Predicate, TinyType } from 'tiny-types';

/**
 * @package
 */
export class Credentials extends TinyType {

    static fromString(value: string): Credentials {
        if (! value) {
            return new Credentials(undefined, undefined);
        }

        ensure('Credentials', value, matches(/(.*):(.*)/, `follow the "<username>:<password>" format`));

        const index = value.indexOf(':');

        return new Credentials(
            value.slice(0, index),
            value.slice(index + 1)
        )
    }

    constructor(
        public readonly username: string,
        public readonly password: string,
    ) {
        super();
    }
}

/**
 * @package
 */
function matches(expression: RegExp, description?: string): Predicate<string> {
    return Predicate.to(description || `match pattern ${ expression }`, (value: string) => expression.test(value));
}

import { JSONData } from '@serenity-js/core/lib/model';

/**
 * @desc
 *  An update on the progress of the Serenity BDD jar download.
 *
 * @package
 */
export class DownloadProgressReport extends JSONData {
    static fromJSON(value: { downloadedBytes: number, totalDownloadedBytes: number, totalBytes: number }): DownloadProgressReport {
        return new DownloadProgressReport(
            Buffer.from(JSON.stringify(value, undefined, 0), 'utf8').toString('base64')
        );
    }
}

import { RuntimeError } from '@serenity-js/core';

/**
 * @desc
 *  An error that has occurred during the execution of the Serenity BDD jar.
 *
 * @extends {@serenity-js/core/lib/core/errors~RuntimeError}
 *
 * @package
 */
export class ExecutionError extends RuntimeError {
    constructor(message: string, cause?: Error) {
        super(ExecutionError, message, cause);
    }
}

/* eslint-disable unicorn/filename-case */
import { Path } from '@serenity-js/core/lib/io';
import { ensure, isDefined, isInRange, isInteger, matches, property, TinyType } from 'tiny-types';

/**
 * @package
 */
export class GAV extends TinyType {
    static fromString(gav: string): GAV {
        const parts = gav.split(':');

        ensure('GAV segments', parts, property('length', isInteger(), isInRange(3, 5)));

        switch (parts.length) {
            case 5:     return new GAV(parts[0], parts[1], parts[4], parts[2], parts[3]);
            case 4:     return new GAV(parts[0], parts[1], parts[3], parts[2]);
            default:    return new GAV(parts[0], parts[1], parts[2]);
        }
    }

    constructor(
        public readonly groupId: string,
        public readonly artifactId: string,
        public readonly version: string,
        public readonly extension: string = 'jar',
        public readonly classifier?: string,
    ) {
        super();
        ensure('groupId', groupId, isDefined(), matches(/^[a-z][\d_a-z-]+(?:\.[\d_a-z-]+)+[\d_a-z-]$/));
        ensure('artifactId', artifactId, isDefined(), matches(/^[\d_a-z-]+$/));
        ensure('version', version, isDefined(), matches(/^(?:\d+\.?){3}$/));
        ensure('extension', extension, isDefined(), matches(/^[a-z]+$/));
    }

    toPath(): Path {
        const name = [
            this.artifactId,
            this.version,
            this.classifier,
        ].filter(s => !!s).join('-');

        return new Path(`${ name }.${ this.extension}`);
    }
}

import { JSONData } from '@serenity-js/core/lib/model';

/**
 * @desc
 *  An information that the user should be notified about.
 *
 * @package
 */
export class Notification extends JSONData {
    static fromJSON(value: { message: string }): Notification {
        return new Notification(
            Buffer.from(JSON.stringify(value, undefined, 0), 'utf8').toString('base64')
        );
    }
}

export * from './Complaint';
export * from './Credentials';
export * from './DownloadProgressReport';
export * from './ExecutionError';
