export * from './Notification';

import { Ability, UsesAbilities } from '@serenity-js/core';
import { FileSystem, Path } from '@serenity-js/core/lib/io';
import { ReadStream, Stats, WriteStream } from 'fs';

/**
 * @package
 */
export class UseFileSystem implements Ability {
    static at(root: Path): UseFileSystem {
        return new UseFileSystem(new FileSystem(root));
    }

    static as(actor: UsesAbilities): UseFileSystem {
        return actor.abilityTo(UseFileSystem);
    }

    constructor(private readonly fileSystem: FileSystem) {
    }

    createReadStream(relativePathToFile: Path): ReadStream {
        return this.fileSystem.createReadStream(relativePathToFile);
    }

    createWriteStreamTo(relativePathToFile: Path): WriteStream {
        return this.fileSystem.createWriteStreamTo(relativePathToFile);
    }

    createDirectory(relativePathToDirectory: Path): Promise<Path> {
        return this.fileSystem.ensureDirectoryExistsAt(relativePathToDirectory);
    }

    remove(relativePathToFileOrDirectory: Path): Promise<void> {
        return this.fileSystem.remove(relativePathToFileOrDirectory);
    }

    rename(source: Path, destination: Path): Promise<void> {
        return this.fileSystem.rename(source, destination);
    }

    attributesOf(relativePathToFileOrDirectory: Path): Promise<Stats> {
        return this.fileSystem.stat(relativePathToFileOrDirectory);
    }
}

export * from './abilities';
export * from './interactions';
export * from './questions';
export * from './tasks';

import { Interaction } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';

import { UseFileSystem } from '../abilities';

/**
 * @package
 */
export const CreateDirectory = {
    at: (destination: Path): Interaction =>
        Interaction.where(`#actor creates a directory at ${ destination.value }`, actor =>
            UseFileSystem.as(actor).createDirectory(destination).then(_ => void 0)
        ),
};

import { Interaction } from '@serenity-js/core';

import { Notification } from '../../model';

/**
 * @package
 */
export const Notify = {
    that: (message: string): Interaction =>
        Interaction.where(`#actor notifies that "${ message }"`, actor =>
            actor.collect(Notification.fromJSON({ message })),
        ),
};

import { Interaction } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';

import { UseFileSystem } from '../abilities';

/**
 * @package
 */
export const RenameFile = {
    from: (source: Path) => ({  // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
        to: (destination: Path) => Interaction.where(`#actor renames ${ source.value } to ${ destination.value }`, actor =>
            UseFileSystem.as(actor).rename(source, destination)),
    }),
};

import { Answerable, AnswersQuestions, CollectsArtifacts, Interaction, UsesAbilities } from '@serenity-js/core';
import { formatted, Path } from '@serenity-js/core/lib/io';
import { spawn } from 'child_process';

import { Complaint, ExecutionError, Notification } from '../../model';

/**
 * @package
 */
export class Spawn extends Interaction {
    static the(pathToExecutable: Answerable<Path>, ...args: string[]): Spawn {
        return new Spawn(pathToExecutable, args);
    }

    constructor(
        private readonly pathToExecutable: Answerable<Path>,
        private readonly args: string[],
    ) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions & CollectsArtifacts} actor
     * @returns {Promise<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     * @see {@link @serenity-js/core/lib/screenplay/actor~CollectsArtifacts}
     */
    performAs(actor: UsesAbilities & AnswersQuestions & CollectsArtifacts): PromiseLike<void> {
        return actor.answer(this.pathToExecutable)
            .then(pathToExecutable => new Promise((resolve, reject) => {
                actor.collect(Notification.fromJSON({ message: `Spawning: ${ pathToExecutable.value } ${ this.args.join(' ') }` }));

                const spawned = spawn(pathToExecutable.value, this.args, { stdio: [process.stdin, process.stdout, process.stderr] });

                spawned.once('error', (error: Error) =>
                    actor.collect(Complaint.fromJSON({ description: `Invoking Serenity BDD CLI has failed`, message: error.message, stack: error.stack })),
                );

                spawned.once('exit', (exitCode: number) =>
                    exitCode !== 0
                        ? reject(new ExecutionError(`The following process exited with ${ exitCode }: ${ pathToExecutable.value } ${ this.args.join(' ') }`))
                        : resolve(void 0),
                );
            }));
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted `#actor executes ${ this.pathToExecutable } with ${ this.args }`;
    }
}

import { Answerable, AnswersQuestions, CollectsArtifacts, ConfigurationError, Interaction, UsesAbilities } from '@serenity-js/core';
import { formatted, Path } from '@serenity-js/core/lib/io';
import { CallAnApi } from '@serenity-js/rest';
import { AxiosRequestConfig } from 'axios';

import { DownloadProgressReport, Notification } from '../../model';
import { UseFileSystem } from '../abilities';

/**
 * @package
 */
export class StreamResponse extends Interaction {

    static to(request: Answerable<AxiosRequestConfig>) {    // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
        return {
            to: (destination: Path) => new StreamResponse(request, destination),
        };
    }

    constructor(
        private readonly request: Answerable<AxiosRequestConfig>,
        private readonly destination: Path,
    ) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & CollectsArtifacts & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~CollectsArtifacts}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return actor.answer(this.request)
            .then((config: AxiosRequestConfig) => CallAnApi.as(actor).request({
                ...config,
                responseType: 'stream',
            })
            .then(response => {
                if (response.status !== 200) {
                    throw new ConfigurationError(`Received: "${ response.status } ${ response.statusText }" when trying to download ${ config.url }`);
                }

                return response;
            })
            .then(response => new Promise((resolve, reject) => {

                actor.collect(Notification.fromJSON({ message: `Downloading ${ config.url } to ${ this.destination.value }` }));

                const
                    totalBytes  = Number.parseInt(response.headers['content-length'], 10),
                    output      = UseFileSystem.as(actor).createWriteStreamTo(this.destination);

                let totalDownloadedBytes  = 0;

                response.data.pipe(output);

                response.data.on('data', chunk => {
                    const downloadedBytes = chunk.length;
                    totalDownloadedBytes += downloadedBytes;

                    actor.collect(DownloadProgressReport.fromJSON({
                        downloadedBytes,
                        totalDownloadedBytes,
                        totalBytes,
                    }));
                });

                response.data.on('end', () => {
                    output.end();
                    return resolve();
                });

                response.data.on('error', error => reject(error));
            })));
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted `#actor saves response to ${ this.request } in ${ this.destination.value }`;
    }
}

