import { Stage, StageCrewMember } from '@serenity-js/core';
import { ArtifactGenerated, DomainEvent } from '@serenity-js/core/lib/events';

import { Complaint, Notification } from '../model';
import { Printer } from '../Printer';

/**
 * @desc
 *  Notifies the user of any {@link Notification} or {@link Complaint} type artifacts being reported.
 *
 * @package
 */
export class NotificationReporter implements StageCrewMember {
    constructor(
        private readonly printer: Printer,
        private readonly stage?: Stage,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new NotificationReporter(this.printer, stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof ArtifactGenerated && event.artifact instanceof Notification) {
            this.printer.info(event.artifact.map((notification: { message: string }) => notification.message));
        }

        else if (event instanceof ArtifactGenerated && event.artifact instanceof Complaint) {
            this.printer.error(event.artifact.map(({ description, message, stack }: { description: string, message: string, stack: string }) =>
                `${ description }\n${ stack }`));
        }
    }
}

import { Stage, StageCrewMember } from '@serenity-js/core';
import { ArtifactGenerated, DomainEvent } from '@serenity-js/core/lib/events';
import * as ProgressBar from 'progress';

import { DownloadProgressReport } from '../model';
import { Printer } from '../Printer';

/**
 * @desc
 *  Notifies the user of the progress of the Serenity BDD CLI jar download.
 *
 * @package
 */
export class ProgressReporter implements StageCrewMember {
    constructor(
        private readonly printer: Printer,
        private readonly stage?: Stage,
        private progressBar?: ProgressBar,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new ProgressReporter(this.printer, stage, this.progressBar);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof ArtifactGenerated && event.artifact instanceof DownloadProgressReport) {
            const { downloadedBytes, totalBytes } = event.artifact.map(report => report) as { downloadedBytes: number, totalDownloadedBytes: number, totalBytes: number };

            if (! this.progressBar) {
                this.progressBar = new ProgressBar(`[:bar] Downloaded :percent at :ratebps. We should be done in about :etas`, {
                    complete: '=',
                    incomplete: '-',
                    width: 20,
                    total: totalBytes,
                    stream: this.printer.out,
                });
            }

            this.progressBar.tick(downloadedBytes);
        }
    }
}

