        given(reporter).isNotifiedOfFollowingEvents(
            new SceneSequenceDetected(sceneIds[0], sequence),
                new SceneTemplateDetected(sceneIds[0], template),
                new SceneParametersDetected(
                    sceneIds[0],
                    scenarios[0],
                    new ScenarioParameters(
