                expect(report.testFailureCause).to.deep.equal({
                    errorType: 'TestCompromisedError',
                    message: `Test database not deployed, no point running the test`,
                    stackTrace: [
                        {
                            declaringClass: '',
                            fileName: '/fake/path/my-test/index.js',
                            lineNumber: 12,
                            methodName: 'callFn()',
