import 'mocha';

import { expect } from '@integration/testing-tools';
import { ConfigurationError } from '@serenity-js/core';
import { inspect } from 'util';

import { errorReportFrom } from '../../../../../../src/stage/crew/serenity-bdd-reporter/processors/mappers';

describe('errorReportFrom', () => {

    describe('when representing Errors as Serenity BDD-standard JSON reports', () => {

        it('supports native Error', () => {

            const report = errorReportFrom(thrown(Error, 'boom'))

            expect(report.errorType).to.equal('Error');
            expect(report.message).to.equal('boom');
            expect(report.stackTrace.length).to.be.greaterThan(0);
        });

        it('supports Serenity/JS Errors', () => {

            const report = errorReportFrom(thrown(ConfigurationError, 'wrong config'))

            expect(report.errorType).to.equal('ConfigurationError');
            expect(report.message).to.equal('wrong config');
            expect(report.stackTrace.length).to.be.greaterThan(0);
        });

        it('supports Serenity/JS Errors with root causes', () => {

            const report = errorReportFrom(thrown(ConfigurationError, 'wrong config', thrown(Error, 'config file does not exist')))

            expect(report.errorType).to.equal('ConfigurationError');
            expect(report.message).to.equal('wrong config');
            expect(report.rootCause.errorType).to.equal('Error');
            expect(report.rootCause.message).to.equal('config file does not exist');
            expect(report.stackTrace.length).to.be.greaterThan(0);
        });
    });

    describe('when representing non-Error "throwables" as Serenity BDD-standard JSON reports', () => {

        it('supports null', () => {
            const report = errorReportFrom(null);   // eslint-disable-line unicorn/no-null

            expect(report).to.deep.equal({
                'errorType': 'null',
                'message': '',
                'stackTrace': [],
            });
        });

        it('supports undefined', () => {
            const report = errorReportFrom(undefined);  // eslint-disable-line unicorn/no-useless-undefined

            expect(report).to.deep.equal({
                'errorType': 'undefined',
                'message': '',
                'stackTrace': [],
            });
        });

        it('supports objects', () => {
            const maybeError = { message: `something's wrong`, code: 500 };
            const report = errorReportFrom(maybeError);

            expect(report).to.deep.equal({
                'errorType': 'Object',
                'message': inspect(maybeError),
                'stackTrace': [],
            });
        });

        it('supports arrays', () => {
            const maybeError = [ 'you', 'really', `shouldn't`, 'throw', 'arrays' ];
            const report = errorReportFrom(maybeError);

            expect(report).to.deep.equal({
                'errorType': 'Array',
                'message': inspect(maybeError),
                'stackTrace': [],
            });
        });

        it('supports numbers', () => {
            const report = errorReportFrom(404);

            expect(report).to.deep.equal({
                'errorType': 'Number',
                'message': '404',
                'stackTrace': [],
            });
        });

        it('supports strings', () => {
            const report = errorReportFrom('skipped step');

            expect(report).to.deep.equal({
                'errorType': 'String',
                'message': 'skipped step',
                'stackTrace': [],
            });
        });

        it('supports booleans', () => {
            const report = errorReportFrom(false);

            expect(report).to.deep.equal({
                'errorType': 'Boolean',
                'message': 'false',
                'stackTrace': [],
            });
        });
    });
});

function thrown<T>(type: new (...args: any[]) => T, ...args: any[]): T {
    try {
        throw new type(...args);
    } catch (error) {
        return error;
    }
}

/* eslint-disable unicorn/filename-case */
import 'mocha';

import { contentsOf, EventRecorder, EventStreamEmitter, expect } from '@integration/testing-tools';
import { Actor, Cast, Clock, Duration, Stage, StageManager } from '@serenity-js/core';
import { ArtifactGenerated, DomainEvent } from '@serenity-js/core/lib/events';
import { TestReport } from '@serenity-js/core/lib/model';

import { SerenityBDDReporter } from '../../../../../src';

describe('SerenityBDDReporter', () => {

    let reporter: SerenityBDDReporter,
        stage: Stage,
        emitter: EventStreamEmitter,
        recorder: EventRecorder;

    beforeEach(() => {
        stage = new Stage(new Extras(), new StageManager(Duration.ofMilliseconds(250), new Clock()));
        emitter = new EventStreamEmitter(stage);
        recorder = new EventRecorder([], stage);

        reporter = new SerenityBDDReporter(stage);

        stage.assign(recorder);
        stage.assign(reporter);
    });

    /** @test {SerenityBDDReporter} */
    it(`includes orphaned events that happened before the scenario in first scenario, and those that happened after - in last scenario`, () => emitter.emit(
        contentsOf(__dirname, 'examples', 'scenario_with_interactions_in_before_and_after_hooks.events')
    ).then(() => {
        const reports: TestReport[] = testReportsFrom(recorder.events);
        expect(reports).to.have.lengthOf(1);

        const generated = reports[0].map(report => report);
        const expected = JSON.parse(contentsOf(__dirname, 'examples', 'scenario_with_interactions_in_before_and_after_hooks.json'));

        expect(generated).to.deep.equal(expected);
    }));
});

function testReportsFrom(events: DomainEvent[]): TestReport[] {
    return events
        .filter((e: DomainEvent) => e instanceof ArtifactGenerated && e.artifact instanceof TestReport)
        .map((e: ArtifactGenerated) => e.artifact);
}

class Extras implements Cast {
    prepare(actor: Actor): Actor {
        return actor;
    }
}
