import { Question } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import { UseFileSystem } from '../abilities';

/**
 * @package
 */
export const FileExists = {
    at: (path: Path) => Question.about<Promise<boolean>>(`${ path.value } exists`, actor =>
        UseFileSystem.as(actor).attributesOf(path)
            .then(_ => true)
            .catch(error => {
                if (error.code !== 'ENOENT') {
                    throw error;
                }

                return false;
            }),
    ),
};

import { AnswersQuestions, Question, UsesAbilities } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import * as path from 'path';
import { promisify } from 'util';
const findJavaHome = promisify(require('find-java-home'));  // tslint:disable-line:no-var-requires

/**
 * @package
 */
export class JavaExecutable extends Question<Promise<Path>> {
    answeredBy(actor: AnswersQuestions & UsesAbilities): Promise<Path> {
        return findJavaHome({ allowJre: true })
            .then(pathToJavaHome => path.join(pathToJavaHome, 'bin', this.javaFileName()))
            .then(Path.fromJSON);
    }

    toString() {
        return `java executable`;
    }

    private javaFileName() {
        return this.isWindows()
            ? 'java.exe'
            : 'java';
    }

    private isWindows() {
        return process.platform.indexOf('win') === 0;
    }
}

import { AnswersQuestions, Question, UsesAbilities } from '@serenity-js/core';
import { Argv } from '../../Argv';

/**
 * @package
 */
export class SerenityBDDArguments extends Question<string[]> {
    private static Allowed =  [
        'destination',
        'features',
        'issueTrackerUrl',
        'jiraProject',
        'jiraUrl',
        'project',
        'source',
    ];

    static from(argv: Argv) {
        return new SerenityBDDArguments(argv);
    }

    constructor(private readonly argv: Argv) {
        super();
    }

    answeredBy(actor: AnswersQuestions & UsesAbilities): string[] {
        return flatten(Object.keys(this.argv)
            .filter(key => !! ~ SerenityBDDArguments.Allowed.indexOf(key) && !! this.argv[key])
            .map(arg => [`--${ arg }`, this.argv[arg]]));
    }

    toString() {
        return 'Serenity BDD arguments';
    }
}

function flatten(list: any[]) {
    return list.reduce((acc, current) => {
        Array.isArray(current)
            ? acc.push(...flatten(current))
            : acc.push(current);
        return acc;
    }, []);
}

import { AnswersQuestions, Question, UsesAbilities } from '@serenity-js/core';
import { Argv } from '../../Argv';

/**
