import { Stage, StageCrewMember } from '@serenity-js/core';
import { ArtifactGenerated, DomainEvent, SceneSequenceDetected, SceneStarts, TestRunFinishes } from '@serenity-js/core/lib/events';
import { Name, ScenarioDetails, TestReport } from '@serenity-js/core/lib/model';
import { match } from 'tiny-types';

import { Current } from './Current';
import { SceneReports } from './reports';
import { SerenityBDDReport } from './SerenityBDDJsonSchema';
import { SceneReportingStrategy, SceneSequenceReportingStrategy, SingleSceneReportingStrategy } from './strategies';

/**
 * @desc Produces Serenity BDD-standard JSON reports.
 * @see http://serenity-bdd.info/
 *
 * @access public
 */
export class SerenityBDDReporter implements StageCrewMember {
    private readonly reports: SceneReports = new SceneReports();
    private currentScenario = new Current<ScenarioDetails>();
    private currentStrategy = new Current<SceneReportingStrategy>();

    constructor(private readonly stage: Stage = null) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new SerenityBDDReporter(stage);
    }

    notifyOf = (event: DomainEvent): void => match<DomainEvent, void>(event)
        .when(SceneSequenceDetected, (e: SceneSequenceDetected) => {
            this.use(SceneSequenceReportingStrategy, e.value);
        })
        .when(SceneStarts, (e: SceneStarts) => {
            this.use(SingleSceneReportingStrategy, e.value);

            this.reports.save(this.currentStrategy.value.handle(e, this.reports.for(this.currentScenario.value)));
        })
        .when(TestRunFinishes, _ => {
            this.reports.map(report => this.broadcast(report.toJSON()));
        })
        .else(e => {
            if (this.currentStrategy.isSet()) {
                this.reports.save(this.currentStrategy.value.handle(e, this.reports.for(this.currentScenario.value)));
            }
        })

    private use(strategy: new (sd: ScenarioDetails) => SceneReportingStrategy, scenario: ScenarioDetails) {
        if (! (this.currentStrategy.isSet() && this.currentStrategy.value.worksFor(scenario))) {
            this.currentStrategy.value = new strategy(scenario);
            this.currentScenario.value = scenario;
        }
    }

    private broadcast(report: Partial<SerenityBDDReport>) {
        this.stage.announce(new ArtifactGenerated(
            new Name(report.name),
            TestReport.fromJSON(report),
        ));
    }
}

import { RuntimeError } from '@serenity-js/core/lib/errors';
import { ErrorStackParser } from '@serenity-js/core/lib/io';

/** @package */
export class ErrorRenderer {
    private static parser = new ErrorStackParser();

    render(error: Error) {
        // tslint:disable-next-line:prefer-object-spread     Esdoc doesn't understand spread
        return Object.assign(
            {},
            this.renderError(error),
            ((error instanceof RuntimeError && error.cause) ? { rootCause: this.renderError(error.cause) } : {}),
        );
    }

    private renderError(error: Error) {
        return {
            errorType:    error.constructor.name,
            message:      error.message,
            stackTrace:   ErrorRenderer.parser.parse(error).map(frame => ({
                declaringClass: '',
                methodName:     `${ frame.functionName }(${ (frame.args || []).join(', ') })`,
                fileName:       frame.fileName,
                lineNumber:     frame.lineNumber,
            })),
        };
    }
}

import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ImplementationPending,
    Outcome,
} from '@serenity-js/core/lib/model';
import { match } from 'tiny-types';
import { ErrorDetails } from '../SerenityBDDJsonSchema';
import { ErrorRenderer } from './ErrorRenderer';

/** @package */
export class OutcomeMapper {
    private static errorRenderer = new ErrorRenderer();

    public mapOutcome(outcome: Outcome, mapAs: (result: string, error?: ErrorDetails) => void) {
        const render = OutcomeMapper.errorRenderer;

        return match<Outcome, void>(outcome).
            when(ExecutionCompromised,              ({ error }: ExecutionCompromised)               => mapAs('COMPROMISED', render.render(error))).
            when(ExecutionFailedWithError,          ({ error }: ExecutionFailedWithError)           => mapAs('ERROR', render.render(error))).
            when(ExecutionFailedWithAssertionError, ({ error }: ExecutionFailedWithAssertionError)  => mapAs('FAILURE', render.render(error))).
            when(ExecutionSkipped,      _ => mapAs('SKIPPED')).
            when(ExecutionIgnored,      _ => mapAs('IGNORED')).
            when(ImplementationPending, _ => mapAs('PENDING')).
            else(/* ExecutionSuccessful */ _ => /* ignore */ mapAs('SUCCESS'));
    }
}

import { Path } from '@serenity-js/core/lib/io';
import {
    ActivityDetails,
    BrowserTag,
    CapabilityTag,
    ContextTag,
    CorrelationId,
    Description,
    ExecutionSuccessful,
    FeatureTag,
    IssueTag,
    ManualTag,
    Name,
    Outcome, PlatformTag,
    RequestAndResponse,
    ScenarioDetails,
    ScenarioParameters,
    Tag,
    ThemeTag,
    Timestamp,
} from '@serenity-js/core/lib/model';
import { platform } from 'os';
import { JSONObject, match } from 'tiny-types';
import { equal } from 'tiny-types/lib/objects'; // tslint:disable-line:no-submodule-imports
import { inspect } from 'util';
import { DataTable, DataTableDataSetDescriptor, ErrorDetails, SerenityBDDReport, TestStep } from '../SerenityBDDJsonSchema';
import { OutcomeMapper } from './OutcomeMapper';
import { SceneReportId } from './SceneReportId';

/** @access private */
function extractValues<T>(dictionary: {[key: string]: T}) {
    return Object.keys(dictionary).map(key => dictionary[key]);
}

/** @access private */
interface ScenarioParametersResultLocation {
    parameters: ScenarioParameters;
    line: number;
    index: number;
    outcome?: Outcome;
}

/** @package */
export class SceneReport {
    private static outcomeMapper = new OutcomeMapper();

    private readonly report: Partial<SerenityBDDReport> & { children?: Array<Partial<TestStep>> };
    private readonly activities = new ActivityStack();
    private readonly parameters: ScenarioParametersResultLocation[] = [];
    private stepNumber = 0;

    constructor(public readonly scenarioDetails: ScenarioDetails) {

        this.report = {
            name:   this.scenarioDetails.name.value,
            title:  this.scenarioDetails.name.value,
            id:     new SceneReportId(this.scenarioDetails.category.value).append(this.scenarioDetails.name.value).value,
            manual: false,
            testSteps: [],
            get children() {
                // fake reference to make the report and the test step interfaces compatible
                // removed in 'toJSON'
                return this.testSteps;
            },
            userStory: {
                id:         new SceneReportId(this.scenarioDetails.category.value).value,
                storyName:  this.scenarioDetails.category.value,
                path:       this.scenarioDetails.location.path.value,
                type:       'feature',
            },
        };

        this.activities.push(this.report);
    }

    executionStartedAt(time: Timestamp): SceneReport {
        return this.withMutated(report => {
            report.startTime = report.startTime || time.toMillisecondTimestamp();
        });
    }

    executedBy(testRunner: Name): SceneReport {
        return this.withMutated(report => {
            report.testSource = testRunner.value;
        });
    }

    taggedWith(tag: Tag) {
        return this.withMutated(report => {
            function displayNameOfRecorded(typeOfTag: { Type: string }) {
                const found = report.tags.find(t => t.type === typeOfTag.Type);

                return found && found.displayName;
            }

            function serenityBDDTagFrom(serenityJSTag: Tag): { name: string, displayName: string, type: string } {
                return {
                    ...serenityJSTag.toJSON(),
                    displayName: serenityJSTag.name.replace(/_+/, ' '),
                };
            }

            const concatenated = (...names: string[]): string => names.filter(name => !! name).join('/');

            const serenityBDDTag = serenityBDDTagFrom(tag);

            if (! report.tags) {
                report.tags = [];
            }

            match<Tag, void>(tag)
                .when(ManualTag,     _ => report.manual = true)
                .when(ThemeTag,      _ => {
                    serenityBDDTag.displayName = tag.name;
                })
                .when(CapabilityTag, _ => {
                    serenityBDDTag.name = concatenated(displayNameOfRecorded(ThemeTag), tag.name);
                    serenityBDDTag.displayName = tag.name;
                })
                .when(FeatureTag,    _ => {
                    serenityBDDTag.name = concatenated(displayNameOfRecorded(CapabilityTag), tag.name);
                    serenityBDDTag.displayName = tag.name;
                    report.featureTag = serenityBDDTag;
                })
                .when(IssueTag,      _ => {
                    report.issues            = (report.issues           || []).concat(tag.name);
                    report.additionalIssues  = (report.additionalIssues || []).concat(tag.name);
                })
                .when(BrowserTag,    (browserTag: BrowserTag) => {
                    report.context   = [report.context, browserTag.browserName].filter(part => !! part).join(',');
                    report.driver    = browserTag.browserName;
                    report.id        = new SceneReportId(browserTag.name, report.id).value;
                })
                .when(PlatformTag,   (platformTag: PlatformTag) => {
                    report.context   = [report.context, platformTag.name].filter(part => !! part).join(',');
                    report.id        = new SceneReportId(platformTag.name, report.id).value;
                })
                .when(ContextTag,    _ => (report.context   = tag.name))
                .else(_ => void 0);

            if (! report.tags.find(current => equal(current, serenityBDDTag))) {
                report.tags.push(serenityBDDTag);
            }
        });
    }

    executionFinishedAt(time: Timestamp): SceneReport {
        return this.withMutated(report => {
            report.duration = Timestamp.fromMillisecondTimestamp(report.startTime).diff(time).inMilliseconds();
        });
    }

    activityStarted(activity: ActivityDetails, time: Timestamp) {
        return this.withMutated(report => {
            const activityReport: Partial<TestStep> = {
                number:      ++this.stepNumber,
                description: activity.name.value,
                startTime:   time.toMillisecondTimestamp(),
                children:    [],
                reportData:  [],
                screenshots: [],
            };

            this.activities.last().children.push(activityReport as any);
            this.activities.push(activityReport, activity.correlationId);
        });
    }

    activityFinished(value: ActivityDetails, outcome: Outcome, time: Timestamp) {
        return this.withMutated(report => this.mapOutcome(outcome, (result: string, error?: ErrorDetails) => {

            const activityReport = this.activities.pop();

            activityReport.result    = result;
            activityReport.duration  = Timestamp.fromMillisecondTimestamp(activityReport.startTime).diff(time).inMilliseconds();

            if (!! error && ! this.activities.haveFailed) {
                activityReport.exception = error;
                this.activities.haveFailed = true;
            }
        }));
    }

    withBackgroundOf(name: Name, description: Description) {
        return this.withMutated(report => {
            report.backgroundTitle       = name.value;
            report.backgroundDescription = description.value;
        });
    }

    withDescriptionOf(description: Description) {
        return this.withMutated(report => {
            report.description = description.value;
        });
    }

    withScenarioOutline(outline: Description) {
        return this.withMutated(report => {
            report.dataTable = this.dataTableFrom(report);

            report.dataTable.scenarioOutline = outline.value;
        });
    }

    withScenarioParametersOf(scenario: ScenarioDetails, parameters: ScenarioParameters) {

        const
            parameterSetName = parameters.name && parameters.name.value,
            parameterSetDescription = parameters.description && parameters.description.value;

        const matches = (params: ScenarioParameters) => (descriptor: DataTableDataSetDescriptor) => {
            return descriptor.name === parameterSetName
                && descriptor.description === parameterSetDescription;
        };

        return this.withMutated(report => {
            report.dataTable = this.dataTableFrom(report);
            report.dataTable.headers = Object.keys(parameters.values);

            let descriptor: DataTableDataSetDescriptor = report.dataTable.dataSetDescriptors.find(matches(parameters));

            if (! descriptor) {
                descriptor = {
                    name:           parameterSetName,
                    description:    parameterSetDescription,
                    startRow:       report.dataTable.dataSetDescriptors.reduce((acc, current) => acc + current.rowCount, 0),
                    rowCount:       0,
                };

                report.dataTable.dataSetDescriptors.push(descriptor);
            }

            descriptor.rowCount++;

            const length = report.dataTable.rows.push(({
                values: extractValues(parameters.values),
            }));

            this.parameters.push({ parameters, index: length - 1, line: scenario.location.line });
        });
    }

    withFeatureNarrativeOf(description: Description) {
        return this.withMutated(report => {
            report.userStory.narrative = description.value;
        });
    }

    photoTaken(details: ActivityDetails, path: Path) {
        return this.withMutated(report => {
            this.activities.itemByCorrelationId(details.correlationId).screenshots.push({ screenshot: path.basename() });
        });
    }

    arbitraryDataCaptured(name: Name, contents: string) {
        return this.withMutated(report => {
            this.activities.mostRecentlyAccessedItem().reportData.push({
                id: `report-data-${ CorrelationId.create().value }`,
                isEvidence: false,
                path: '',
                title: name.value,
                contents,
            });
        });
    }

    httpRequestCaptured(requestResponse: RequestAndResponse) {
        function mapToString(dictionary: {[key: string]: string}) {
            return Object.keys(dictionary).map(key => `${key}: ${dictionary[key]}`).join('\n');
        }

        return this.withMutated(report => {
            this.activities.mostRecentlyAccessedItem().restQuery = {
                method:          requestResponse.request.method.toUpperCase(),
                path:            requestResponse.request.url,
                content:         inspect(requestResponse.request.data),
                contentType:     requestResponse.request.headers['Content-Type'] || '', // todo: add a case insensitive proxy around this RFC 2616: 4.2
                requestHeaders:  mapToString(requestResponse.request.headers)  || '',
                requestCookies:  requestResponse.request.headers.Cookie || '', // todo: add a case insensitive proxy around this RFC 2616: 4.2
                statusCode:      requestResponse.response.status,
                responseHeaders: mapToString(requestResponse.response.headers) || '',
                responseCookies: requestResponse.response.headers.Cookie || '', // todo: add a case insensitive proxy around this RFC 2616: 4.2
                responseBody:    inspect(requestResponse.response.data) || '',
            };
        });
    }

    executionFinishedWith(scenario: ScenarioDetails, outcome: Outcome): SceneReport {
        return this.withMutated(report => this.mapOutcome(outcome, (result: string, error: ErrorDetails = undefined) => {

            report.result           = result;
            report.testFailureCause = error;

            if (this.parameters.length > 0) {
                const entry = this.parameters.find(p => p.line === scenario.location.line);
                if (!! entry) {
                    entry.outcome = outcome;

                    report.dataTable.rows[ entry.index ].result = result;

                    const worstOutcomeOverall = this.parameters
                        .filter(p => !! p.outcome)
                        .map(p => p.outcome)
                        .reduce((worstSoFar, current) => {
                            return current.isWorseThan(worstSoFar)
                                ? current
                                : worstSoFar;
                        }, new ExecutionSuccessful());

                    this.mapOutcome(worstOutcomeOverall, (r: string, e: ErrorDetails = undefined) => {
                        report.result           = r;
                        report.testFailureCause = e;
                    });
                }
            }
        }));
    }

    toJSON(): Partial<SerenityBDDReport> {
        const report = this.copyOf(this.report);

        delete report.children; // remove the fake reference

        this.parameters.forEach(entry => {

            const parameters = entry.parameters.values;
            const stringified = Object.keys(parameters).map(key => `${ key }: ${ parameters[key] }`).join(', ').trim();

            report.testSteps[entry.index].description += ` #${ entry.index + 1 } - ${ stringified }`;
        });

        // todo: optimise the report, remove empty arrays
        return report;
    }

    private dataTableFrom(report: Partial<SerenityBDDReport>): DataTable {
        return report.dataTable || {
            scenarioOutline: '',
            dataSetDescriptors: [],
            headers: [],
            rows: [],
            predefinedRows: true,
        };
    }

    private mapOutcome(outcome: Outcome, mapAs: (result: string, error?: ErrorDetails) => void) {
        return SceneReport.outcomeMapper.mapOutcome(outcome, mapAs);
    }

    private withMutated(mutate: (copied: Partial<SerenityBDDReport>) => void): SceneReport {
        mutate(this.report);

        return this;
    }

    private copyOf<T extends JSONObject>(json: T): T {
        return JSON.parse(JSON.stringify(json));
    }
}

/**
 * @package
 */
class ActivityStack {
    public haveFailed: boolean = false;
    private readonly items: Array<Partial<TestStep>> = [];
    private mostRecent: Partial<TestStep> = undefined;
    private correlations: { [key: string]: Partial<TestStep> } = {};

    push(item: Partial<TestStep>, correlationId?: CorrelationId): Partial<TestStep> {
        this.items.push(item);
        this.mostRecent = item;
        this.haveFailed = false;
        if (!! correlationId) {
            this.correlations[correlationId.value] = item;
        }

        return this.mostRecent;
    }

    pop() {
        const item = this.items.pop();
        if (this.items.length === 0) {
            this.haveFailed = false;
        }

        this.mostRecent = item;

        return this.mostRecent;
    }

    last() {
        return this.items[this.items.length - 1];
    }

    mostRecentlyAccessedItem() {
        return this.mostRecent;
    }

    itemByCorrelationId(correlationId: CorrelationId) {
        return this.correlations[correlationId.value];
    }
}

import { ensure, isDefined, isGreaterThan, property, TinyType } from 'tiny-types';

export class SceneReportId extends TinyType {

    public readonly value: string;

    constructor(segment: string, base?: string) {
        super();
        ensure('ID segment', segment, isDefined(), property('length', isGreaterThan(0)));

        this.value = [
            base,
            this.dashify(segment),
        ].filter(_ => !! _).join(';');
    }

    append(segment: string): SceneReportId {
        return new SceneReportId(segment, this.value);
    }

    private dashify(text: string) {
        return text
            .replace(/[ \t\W]/g, '-')
            .replace(/^-+|-+$/g, '')
            .toLowerCase();
    }
}

import { ScenarioDetails } from '@serenity-js/core/lib/model';
import { SceneReport } from './SceneReport';
import { SceneReportId } from './SceneReportId';

/** @package */
export class SceneReports {
    private readonly reports: { [entryId: string]: SceneReport } = {};

    for(scenarioDetails: ScenarioDetails): SceneReport {
        if (! this.alreadyHasAReportFor(scenarioDetails)) {
            return this.createReportFor(scenarioDetails);
        }

        return this.reportFor(scenarioDetails);
    }

    save(scenarioReport: SceneReport) {
        this.reports[this.correlationIdFor(scenarioReport.scenarioDetails)] = scenarioReport;
    }

    map<T>(fn: (report: SceneReport) => T): T[] {
        return Object.keys(this.reports).map(correlationId => this.reports[correlationId]).map(fn);
    }

    private alreadyHasAReportFor(scenarioDetails: ScenarioDetails): boolean {
        return !! this.reports[this.correlationIdFor(scenarioDetails)];
    }

    private reportFor(scenarioDetails: ScenarioDetails): SceneReport {
        return this.reports[this.correlationIdFor(scenarioDetails)];
    }

    private createReportFor(scenarioDetails: ScenarioDetails): SceneReport {
        return new SceneReport(scenarioDetails);
    }

    private correlationIdFor(scenarioDetails: ScenarioDetails): string {
        return new SceneReportId(scenarioDetails.category.value)
            .append(scenarioDetails.name.value)
            .append(scenarioDetails.location.path.value)    // todo: should this include the line?
            .value;
    }
}

import {
    ActivityRelatedArtifactArchived,
    ArtifactGenerated,
    DomainEvent,
    FeatureNarrativeDetected,
    SceneBackgroundDetected,
    SceneDescriptionDetected,
    SceneTagged,
    TestRunnerDetected,
} from '@serenity-js/core/lib/events';
import { AssertionReportDiffer } from '@serenity-js/core/lib/io';
import { Artifact, ArtifactType, AssertionReport, HTTPRequestResponse, JSONData, LogEntry, Photo, ScenarioDetails, TextData } from '@serenity-js/core/lib/model';
import { match } from 'tiny-types';
import { SceneReport } from '../reports';

/**
 * @package
 */
export abstract class SceneReportingStrategy {

    private static readonly differ = new AssertionReportDiffer({
        expected: line => `+ ${ line }`,
        actual:   line => `- ${ line }`,
        matching: line => `  ${ line }`,
    });

    constructor(protected readonly scenario: ScenarioDetails) {
    }

    abstract worksFor(anotherScenario: ScenarioDetails): boolean;

    handle(event: DomainEvent, report: SceneReport): SceneReport {
        return match<DomainEvent, SceneReport>(event)
            .when(FeatureNarrativeDetected, (e: FeatureNarrativeDetected)   => report.withFeatureNarrativeOf(e.description))
            .when(SceneBackgroundDetected,  (e: SceneBackgroundDetected)    => report.withBackgroundOf(e.name, e.description))
            .when(SceneDescriptionDetected, (e: SceneDescriptionDetected)   => report.withDescriptionOf(e.description))
            .when(SceneTagged,              (e: SceneTagged)                => report.taggedWith(e.tag))
            .when(TestRunnerDetected,       (e: TestRunnerDetected)         => report.executedBy(e.value))
            .when(ArtifactGenerated,        (e: ArtifactGenerated)          => match<Artifact, SceneReport>(e.artifact)
                .when(HTTPRequestResponse,  _ => report.httpRequestCaptured(e.artifact.map(data => data)))
                .when(TextData,             _ => report.arbitraryDataCaptured(e.name, e.artifact.map(artifactContents => artifactContents.data)))
                .when(LogEntry,             _ => report.arbitraryDataCaptured(e.name, e.artifact.map(artifactContents => artifactContents.data)))
                .when(AssertionReport,      _ =>
                    report.arbitraryDataCaptured(e.name, e.artifact.map(artifactContents =>
                        SceneReportingStrategy.differ.diff(artifactContents.expected, artifactContents.actual),
                    )),
                )
                .when(JSONData,             _ => report.arbitraryDataCaptured(e.name, e.artifact.map(artifactContents => JSON.stringify(artifactContents, null, 4))))
                .else(_ => report))
            .when(ActivityRelatedArtifactArchived, (e: ActivityRelatedArtifactArchived) => match<ArtifactType, SceneReport>(e.type)
                .when(Photo,                _ => report.photoTaken(e.details, e.path))
                .else(_ => report))
            .else(_ => report);
    }
}

import { ActivityFinished, ActivityStarts, DomainEvent, SceneFinished, SceneParametersDetected, SceneStarts, SceneTemplateDetected } from '@serenity-js/core/lib/events';
import { ActivityDetails, ScenarioDetails } from '@serenity-js/core/lib/model';
import { match } from 'tiny-types';

import { SceneReport } from '../reports';
import { SceneReportingStrategy } from './SceneReportingStrategy';

/**
 * @package
 */
export class SceneSequenceReportingStrategy extends SceneReportingStrategy {

    worksFor(anotherScenario: ScenarioDetails): boolean {
        return !! anotherScenario
            && this.scenario.name.equals(anotherScenario.name)             // todo: what about templated scenario names?
            && this.scenario.category.equals(anotherScenario.category)
            && this.scenario.location.path.equals(anotherScenario.location.path);
    }

    handle(event: DomainEvent, report: SceneReport): SceneReport {
        return match<DomainEvent, SceneReport>(event)
            .when(SceneStarts,                      (e: SceneStarts) => {
                return report
                    .activityStarted(this.asActivity(e.value), e.timestamp)
                    .executionStartedAt(e.timestamp);
            })
            .when(SceneTemplateDetected,    (e: SceneTemplateDetected) => {
                return report.withScenarioOutline(e.template);
            })
            .when(SceneParametersDetected,  (e: SceneParametersDetected) => {
                return report.withScenarioParametersOf(e.scenario, e.value);
            })
            .when(ActivityStarts,                   (e: ActivityStarts) => {
                return report.activityStarted(e.value, e.timestamp);
            })
            .when(ActivityFinished,                 (e: ActivityFinished) => {
                return report.activityFinished(e.value, e.outcome, e.timestamp);
            })
            .when(SceneFinished,                    (e: SceneFinished) => {
                return report
                    .activityFinished(this.asActivity(e.value), e.outcome, e.timestamp)
                    .executionFinishedWith(e.value, e.outcome)
                    .executionFinishedAt(e.timestamp);
            })
            .else(e => super.handle(e, report));
    }

    private asActivity(scenario: ScenarioDetails): ActivityDetails {
        return new ActivityDetails(scenario.name);
    }
}

import { ActivityFinished, ActivityStarts, DomainEvent, SceneFinished, SceneStarts } from '@serenity-js/core/lib/events';
import { ScenarioDetails } from '@serenity-js/core/lib/model';
import { match } from 'tiny-types';

import { SceneReport } from '../reports';
import { SceneReportingStrategy } from './SceneReportingStrategy';

/**
 * @package
 */
export class SingleSceneReportingStrategy extends SceneReportingStrategy {
    worksFor(anotherScenario: ScenarioDetails): boolean {
        return this.scenario.equals(anotherScenario);
    }

    handle(event: DomainEvent, report: SceneReport): SceneReport {
        return match<DomainEvent, SceneReport>(event)
            .when(SceneStarts,      (e: SceneStarts)      => report.executionStartedAt(e.timestamp))
            .when(ActivityStarts,   (e: ActivityStarts)   => report.activityStarted(e.value, e.timestamp))
            .when(ActivityFinished, (e: ActivityFinished) => report.activityFinished(e.value, e.outcome, e.timestamp))
            .when(SceneFinished,    (e: SceneFinished)    => {
                return report
                    .executionFinishedAt(e.timestamp)
                    .executionFinishedWith(e.value, e.outcome);
            })
            .else(e => super.handle(e, report));
    }
}

