        const env = create();

        stageManager    = env.stageManager;
        reporter        = env.reporter;
    });

    describe('reports the activities that took place during scenario execution:', () => {

        /**
         * @test {SerenityBDDReporter}
         * @test {SceneStarts}
         * @test {TaskStarts}
         * @test {TaskFinished}
         * @test {ExecutionSuccessful}
         * @test {SceneFinished}
         * @test {TestRunFinishes}
         */
