            expect(report.testSteps).to.have.lengthOf(1);
            expect(report.testSteps[0].children).to.have.lengthOf(1);
            expect(report.testSteps[0].children[0].description).to.equal(viewListOfCards.name.value);
            expect(report.testSteps[0].children[0].result).to.equal('SUCCESS');
        });
    });

    describe('order of events', () => {

        /**
         * @test {SerenityBDDReporter}
         * @test {SceneStarts}
         * @test {TaskStarts}
         * @test {ArtifactGenerated}
         * @test {TaskFinished}
         * @test {ExecutionSuccessful}
         * @test {SceneFinished}
         * @test {TestRunFinishes}
         */
        it('records the events in a correct order', () => {
            const pickACard = new ActivityDetails(new Name('Pick the default credit card'));

            given(reporter).isNotifiedOfFollowingEvents(
                new SceneStarts(defaultCardScenario),
                    new TaskStarts(pickACard),
                        new ActivityRelatedArtifactGenerated(pickACard, new Name('photo1'), photo),
                        new ActivityRelatedArtifactArchived(pickACard, new Name('photo1'), Photo, new Path('target/site/serenity/photo1.png')),
                    new TaskFinished(pickACard, new ExecutionSuccessful()),
                        new ActivityRelatedArtifactGenerated(pickACard, new Name('photo2'), photo),
                        new ActivityRelatedArtifactArchived(pickACard, new Name('photo2'), Photo, new Path('target/site/serenity/photo2.png')),
                new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                new TestRunFinishes(),
            );

            const report: SerenityBDDReport = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

            expect(report.testSteps).to.have.lengthOf(1);
            expect(report.testSteps[0].screenshots).to.deep.equal([
                { screenshot: 'photo1.png'},
                { screenshot: 'photo2.png'},
            ]);
        });

        /**
         * @test {SerenityBDDReporter}
         * @test {SceneStarts}
         * @test {TaskStarts}
         * @test {TaskFinished}
         * @test {ExecutionSuccessful}
         * @test {SceneFinished}
         * @test {TestRunFinishes}
         */
        it('records the order of test steps so that the Serenity BDD reporter can display the reportData in the correct context', () => {
            const
                pickACard   = new ActivityDetails(new Name('Pick a credit card')),
                makePayment = new ActivityDetails(new Name('Make a payment'));

            given(reporter).isNotifiedOfFollowingEvents(
                new SceneStarts(defaultCardScenario),
                    new TaskStarts(pickACard),
                        new ArtifactGenerated(new Name('pick a card message'), JSONData.fromJSON({ card: 'default' })),
                        new ArtifactArchived(new Name('pick a card message'), JSONData, new Path('target/site/serenity/pick-a-card-message-md5hash.json')),
                    new TaskFinished(pickACard, new ExecutionSuccessful()),
                    new TaskStarts(makePayment),
                        new ArtifactGenerated(new Name('make a payment message'), JSONData.fromJSON({ amount: '42' })),
                        new ArtifactArchived(new Name('make a payment message'), JSONData, new Path('target/site/serenity/make-a-payment-message-md5hash.json')),
                        new ArtifactGenerated(new Name('server log'), TextData.fromJSON({ contentType: 'text/plain', data: 'received payment request' })),
                    new TaskFinished(makePayment, new ExecutionSuccessful()),
                new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                new TestRunFinishes(),
            );

            const report: SerenityBDDReport = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

            expect(report.testSteps).to.have.lengthOf(2);
            expect(report.testSteps[0].number).to.equal(1);
            expect(report.testSteps[0].reportData[0].title).to.equal('pick a card message');
            expect(report.testSteps[0].reportData[0].contents).to.equal('{\n    "card": "default"\n}');

            expect(report.testSteps[1].number).to.equal(2);

            expect(report.testSteps[1].reportData[0].title).to.equal('make a payment message');
            expect(report.testSteps[1].reportData[0].contents).to.deep.equal('{\n    \"amount\": \"42\"\n}');

            expect(report.testSteps[1].reportData[1].title).to.equal('server log');
            expect(report.testSteps[1].reportData[1].contents).to.deep.equal('received payment request');
        });
    });

    describe('artifacts', () => {

        /**
         * @test {SerenityBDDReporter}
         * @test {SceneStarts}
         * @test {TaskStarts}
         * @test {ArtifactGenerated}
         * @test {TaskFinished}
         * @test {ExecutionSuccessful}
         * @test {SceneFinished}
         * @test {TestRunFinishes}
         */
        it('records the arbitrary JSON data emitted during the interaction', () => {
            const pickACard = new ActivityDetails(new Name('Pick the default credit card'));

            given(reporter).isNotifiedOfFollowingEvents(
                new SceneStarts(defaultCardScenario),
                new TaskStarts(pickACard),
                new ActivityRelatedArtifactGenerated(pickACard, new Name('photo1'), photo),
                new ActivityRelatedArtifactArchived(pickACard, new Name('photo1'), Photo, new Path('target/site/serenity/photo1.png')),
                new TaskFinished(pickACard, new ExecutionSuccessful()),
                new ActivityRelatedArtifactGenerated(pickACard, new Name('photo2'), photo),
                new ActivityRelatedArtifactArchived(pickACard, new Name('photo2'), Photo, new Path('target/site/serenity/photo2.png')),
                new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                new TestRunFinishes(),
            );

            const report: SerenityBDDReport = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

            expect(report.testSteps).to.have.lengthOf(1);
            expect(report.testSteps[0].screenshots).to.deep.equal([
                { screenshot: 'photo1.png'},
                { screenshot: 'photo2.png'},
            ]);
        });
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { StageManager } from '@serenity-js/core';
import { SceneFinished, SceneParametersDetected, SceneSequenceDetected, SceneStarts, SceneTemplateDetected, TestRunFinishes } from '@serenity-js/core/lib/events';
import { FileSystemLocation, Path } from '@serenity-js/core/lib/io';
import { Category, Description, ExecutionFailedWithError, ExecutionSuccessful, Name, ScenarioDetails, ScenarioParameters } from '@serenity-js/core/lib/model';
import * as sinon from 'sinon';

import { SerenityBDDReporter } from '../../../../../src/stage';
import { SerenityBDDReport } from '../../../../../src/stage/crew/serenity-bdd-reporter/SerenityBDDJsonSchema';
import { given } from '../../given';
import { create } from '../create';

describe('SerenityBDDReporter', () => {

    let stageManager: sinon.SinonStubbedInstance<StageManager>,
        reporter: SerenityBDDReporter;

    beforeEach(() => {
        const env = create();

        stageManager    = env.stageManager;
        reporter        = env.reporter;
    });

    // see examples/cucumber/features/reporting_results/reports_scenario_outlines.feature for more context
    const
        category = new Category('Reporting results'),
        name     = new Name('Reports scenario outlines'),
        path     = new Path(`reporting_results/reports_scenario_outlines.feature`),
        template = new Description(`
            When <Developer> makes a contribution of:
              | value      |
              | time       |
              | talent     |
              | great code |
            Then they help bring serenity to fellow devs
        `),
        sequence = new ScenarioDetails(name, category, new FileSystemLocation(
            path,
            7,
        )),
        scenario1 = new ScenarioDetails(name, category, new FileSystemLocation(
            path,
            25,
        )),
        scenario2 = new ScenarioDetails(name, category, new FileSystemLocation(
            path,
            26,
        ))
    ;

    /**
     * @test {SerenityBDDReporter}
     * @test {SceneSequenceDetected}
     * @test {SceneTemplateDetected}
     * @test {SceneParametersDetected}
     * @test {ScenarioParameters}
     * @test {SceneStarts}
     * @test {SceneFinished}
     * @test {ExecutionSuccessful}
     * @test {TestRunFinishes}
     */
    it('captures information about a sequence of scenes (2 scenes in a sequence)', () => {
        given(reporter).isNotifiedOfFollowingEvents(
            new SceneSequenceDetected(sequence),
                new SceneTemplateDetected(template),
                new SceneParametersDetected(
                    scenario1,
                    new ScenarioParameters(
                        new Name('Serenity/JS contributors'),
                        new Description(`Some of the people who have contributed their time and talent to the Serenity/JS project`),
                        { Developer: 'jan-molak', Twitter_Handle: '@JanMolak' },
                    ),
                ),
                new SceneStarts(scenario1),
                new SceneFinished(scenario1, new ExecutionSuccessful()),
            new SceneSequenceDetected(sequence),
                new SceneTemplateDetected(template),
                new SceneParametersDetected(
                    scenario2,
                    new ScenarioParameters(
                        new Name('Serenity/JS contributors'),
                        new Description(`Some of the people who have contributed their time and talent to the Serenity/JS project`),
                        { Developer: 'wakaleo', Twitter_Handle: '@wakaleo' },
                    ),
                ),
                new SceneStarts(scenario2),
                new SceneFinished(scenario2, new ExecutionSuccessful()),
            new TestRunFinishes(),
        );

        const report: SerenityBDDReport = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

        expect(report.name).to.equal(name.value);
        expect(report.dataTable).to.exist;  // tslint:disable-line:no-unused-expression
        expect(report.dataTable.scenarioOutline).to.equal(template.value);
        expect(report.dataTable.headers).to.deep.equal([
            'Developer',
            'Twitter_Handle',
        ]);

        expect(report.dataTable.dataSetDescriptors).to.deep.equal([{
            startRow: 0,
            rowCount: 2,
            name: 'Serenity/JS contributors',
            description: 'Some of the people who have contributed their time and talent to the Serenity/JS project',
        }]);

        expect(report.dataTable.rows).to.deep.equal([
            { values: [ 'jan-molak', '@JanMolak' ], result: 'SUCCESS' },
            { values: [ 'wakaleo', '@wakaleo' ], result: 'SUCCESS' },
        ]);

        expect(report.testSteps).to.have.lengthOf(2);
        expect(report.testSteps[0].description)
            .to.equal(`${name.value} #1 - Developer: jan-molak, Twitter_Handle: @JanMolak`);

        expect(report.testSteps[1].description)
            .to.equal(`${name.value} #2 - Developer: wakaleo, Twitter_Handle: @wakaleo`);
    });

    /**
     * @test {SerenityBDDReporter}
     * @test {SceneSequenceDetected}
     * @test {SceneTemplateDetected}
     * @test {SceneParametersDetected}
     * @test {ScenarioParameters}
     * @test {SceneStarts}
     * @test {SceneFinished}
     * @test {ExecutionFailedWithError}
     * @test {ExecutionSuccessful}
     * @test {TestRunFinishes}
     */
    it('determines the result of the sequence based on the worst result of the contributing scenarios', () => {
        given(reporter).isNotifiedOfFollowingEvents(
            new SceneSequenceDetected(sequence),
                new SceneTemplateDetected(template),
                new SceneParametersDetected(
                    scenario1,
                    new ScenarioParameters(
                        new Name('Serenity/JS contributors'),
                        new Description(`Some of the people who have contributed their time and talent to the Serenity/JS project`),
                        { Developer: 'jan-molak', Twitter_Handle: '@JanMolak' },
                    ),
                ),
                new SceneStarts(scenario1),
                new SceneFinished(scenario1, new ExecutionFailedWithError(new Error('Something happened'))),

            new SceneSequenceDetected(sequence),
                new SceneTemplateDetected(template),
                new SceneParametersDetected(
                    scenario2,
                    new ScenarioParameters(
                        new Name('Serenity/JS contributors'),
                        new Description(`Some of the people who have contributed their time and talent to the Serenity/JS project`),
                        { Developer: 'wakaleo', Twitter_Handle: '@wakaleo' },
                    ),
                ),
                new SceneStarts(scenario2),
                new SceneFinished(scenario2, new ExecutionSuccessful()),
            new TestRunFinishes(),
        );

        const report: SerenityBDDReport = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

        expect(report.dataTable.rows).to.deep.equal([
            { values: [ 'jan-molak', '@JanMolak' ], result: 'ERROR' },
            { values: [ 'wakaleo', '@wakaleo' ], result: 'SUCCESS' },
        ]);

        expect(report.testSteps).to.have.lengthOf(2);
        expect(report.testSteps[0].description)
            .to.equal(`${name.value} #1 - Developer: jan-molak, Twitter_Handle: @JanMolak`);
        // todo: check for error somewhere here
        // todo: map well map the main report

        expect(report.testSteps[1].description)
            .to.equal(`${name.value} #2 - Developer: wakaleo, Twitter_Handle: @wakaleo`);
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { StageManager } from '@serenity-js/core';
import { SceneFinished, SceneStarts, SceneTagged, TestRunFinishes } from '@serenity-js/core/lib/events';
import { ArbitraryTag, BrowserTag, CapabilityTag, ContextTag, ExecutionSuccessful, FeatureTag, IssueTag, ManualTag, ThemeTag } from '@serenity-js/core/lib/model';
import * as sinon from 'sinon';

import { SerenityBDDReporter } from '../../../../../src/stage';
import { SerenityBDDReport } from '../../../../../src/stage/crew/serenity-bdd-reporter/SerenityBDDJsonSchema';
import { given } from '../../given';
import { defaultCardScenario } from '../../samples';
import { create } from '../create';

describe('SerenityBDDReporter', () => {

    let stageManager: sinon.SinonStubbedInstance<StageManager>,
        reporter: SerenityBDDReporter;

    beforeEach(() => {
        const env = create();

        stageManager    = env.stageManager;
        reporter        = env.reporter;
    });

    describe('produces a SerenityBDDReport that', () => {

        let report: SerenityBDDReport;

        describe('can be tagged', () => {

            beforeEach(() => {
                given(reporter).isNotifiedOfFollowingEvents(
                    new SceneStarts(defaultCardScenario),
                );
            });

            describe('@manual', () => {

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 */
                it('is marked as automated (non-manual) by default', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.manual).to.equal(false);
                });

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {ManualTag}
                 */
                it('can be optionally tagged as manual', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new ManualTag()),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.manual).to.equal(true);
                    expect(report.tags).to.deep.include.members([{
                        name: 'Manual',
                        displayName: 'Manual',
                        type: 'External Tests',
                    }]);
                });
            });

            describe('@issue', () => {

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {IssueTag}
                 */
                it('can be tagged with an issue number', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new IssueTag('ABC-1234')),
                        new SceneTagged(defaultCardScenario, new IssueTag('DEF-5678')),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.tags).to.deep.include.members([{
                        name:        'ABC-1234',
                        displayName: 'ABC-1234',
                        type:        'issue',
                    }, {
                        name:        'DEF-5678',
                        displayName: 'DEF-5678',
                        type:        'issue',
                    }]);

                    expect(report.issues).to.deep.equal([
                        'ABC-1234',
                        'DEF-5678',
                    ]);

                    expect(report.additionalIssues).to.deep.equal([
                        'ABC-1234',
                        'DEF-5678',
                    ]);
                });
            });

            describe('@arbitrary-tag', () => {

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {ArbitraryTag}
                 */
                it('can be tagged with an arbitrary tag', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new ArbitraryTag('regression')),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.tags).to.deep.include.members([{
                        name:        'regression',
                        displayName: 'regression',
                        type:        'tag',
                    }]);
                });
            });

            describe('to indicate that a scenario', () => {

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {FeatureTag}
                 */
                it('belongs to a feature', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new FeatureTag('Checkout')),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.tags).to.deep.include.members([{
                        name: 'Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    }]);

                    expect(report.featureTag).to.deep.equal({
                        name: 'Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    });
                });

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {FeatureTag}
                 * @test {CapabilityTag}
                 */
                it('belongs to a capability', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new CapabilityTag('E-Commerce')),
                        new SceneTagged(defaultCardScenario, new FeatureTag('Checkout')),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.tags).to.deep.include.members([{
                        name: 'E-Commerce',
                        type: 'capability',
                        displayName: 'E-Commerce',
                    }, {
                        name: 'E-Commerce/Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    }]);

                    expect(report.featureTag).to.deep.equal({
                        name: 'E-Commerce/Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    });
                });

                /**
                 * @test {SerenityBDDReporter}
                 * @test {SceneStarts}
                 * @test {SceneFinished}
                 * @test {SceneTagged}
                 * @test {TestRunFinishes}
                 * @test {ExecutionSuccessful}
                 * @test {FeatureTag}
                 * @test {CapabilityTag}
                 * @test {ThemeTag}
                 */
                it('belongs to a theme', () => {
                    given(reporter).isNotifiedOfFollowingEvents(
                        new SceneTagged(defaultCardScenario, new ThemeTag('Digital')),
                        new SceneTagged(defaultCardScenario, new CapabilityTag('E-Commerce')),
                        new SceneTagged(defaultCardScenario, new FeatureTag('Checkout')),
                        new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                        new TestRunFinishes(),
                    );

                    report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                    expect(report.tags).to.deep.include.members([{
                        name: 'Digital',
                        type: 'theme',
                        displayName: 'Digital',
                    }, {
                        name: 'Digital/E-Commerce',
                        type: 'capability',
                        displayName: 'E-Commerce',
                    }, {
                        name: 'E-Commerce/Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    }]);

                    expect(report.featureTag).to.deep.equal({
                        name: 'E-Commerce/Checkout',
                        type: 'feature',
                        displayName: 'Checkout',
                    });
                });

                describe('is executed in the context and', () => {

                    /**
                     * @test {SerenityBDDReporter}
                     * @test {SceneStarts}
                     * @test {SceneFinished}
                     * @test {SceneTagged}
                     * @test {TestRunFinishes}
                     * @test {ExecutionSuccessful}
                     * @test {BrowserTag}
                     */
                    it('indicates the web browser where the test was executed', () => {
                        given(reporter).isNotifiedOfFollowingEvents(
                            new SceneTagged(defaultCardScenario, new BrowserTag('chrome', '80.0.3987.87')),
                            new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                            new TestRunFinishes(),
                        );

                        report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                        expect(report.context).to.equal('chrome');

                        expect(report.tags).to.deep.include.members([{
                            browserName: 'chrome',
                            browserVersion: '80.0.3987.87',
                            name: 'chrome 80.0.3987.87',
                            displayName: 'chrome 80.0.3987.87',
                            type: 'browser',
                        }]);
                    });

                    /**
                     * @test {SerenityBDDReporter}
                     * @test {SceneStarts}
                     * @test {SceneFinished}
                     * @test {SceneTagged}
                     * @test {TestRunFinishes}
                     * @test {ExecutionSuccessful}
                     * @test {ContextTag}
                     */
                    it('indicates the operating system where the test was executed', () => {
                        given(reporter).isNotifiedOfFollowingEvents(
                            new SceneTagged(defaultCardScenario, new ContextTag('iphone')),
                            new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                            new TestRunFinishes(),
                        );

                        report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                        expect(report.context).to.equal('iphone');

                        expect(report.tags).to.deep.include.members([{
                            name: 'iphone',
                            displayName: 'iphone',
                            type: 'context',
                        }]);
                    });

                    /**
                     * @test {SerenityBDDReporter}
                     * @test {SceneStarts}
                     * @test {SceneFinished}
                     * @test {SceneTagged}
                     * @test {TestRunFinishes}
                     * @test {ExecutionSuccessful}
                     * @test {BrowserTag}
                     * @test {ContextTag}
                     */
                    it('ensures that the user-specified context takes precedence over browser context', () => {
                        given(reporter).isNotifiedOfFollowingEvents(
                            new SceneTagged(defaultCardScenario, new BrowserTag('safari', '13.0.5')),
                            new SceneTagged(defaultCardScenario, new ContextTag('iphone')),
                            new SceneFinished(defaultCardScenario, new ExecutionSuccessful()),
                            new TestRunFinishes(),
                        );

                        report = stageManager.notifyOf.firstCall.lastArg.artifact.map(_ => _);

                        expect(report.context).to.equal('iphone');

                        expect(report.tags).to.deep.include.members([{
                            name: 'safari 13.0.5',
                            displayName: 'safari 13.0.5',
                            type: 'browser',
                            browserName: 'safari',
                            browserVersion: '13.0.5',
                        }, {
                            name: 'iphone',
                            displayName: 'iphone',
                            type: 'context',
                        }]);
                    });
                });
            });
        });
    });
});

