import { ConfigurationError } from '@serenity-js/core';
import { Config, FileFinder, FileSystem, ModuleLoader, Path, TestRunnerAdapter } from '@serenity-js/core/lib/io';
import { WebdriverIO } from '@wdio/types/build/Options';

import { WebdriverIOConfig } from './WebdriverIOConfig';

/**
 * @package
 */
export class TestRunnerLoader {
    private readonly fileSystem: FileSystem;
    private readonly finder: FileFinder;

    constructor(
        private readonly loader: ModuleLoader,
        private readonly cwd: Path,
        private readonly cid: string,
    ) {
        this.fileSystem = new FileSystem(cwd);
        this.finder     = new FileFinder(cwd);
    }

    runnerAdapterFor(config: WebdriverIOConfig): TestRunnerAdapter {
        switch (config?.serenity?.runner) {
            case 'cucumber':
                return this.cucumberAdapter(config?.cucumberOpts);

            case 'jasmine':
                return this.jasmineAdapter(config?.jasmineOpts);

            case 'mocha':
            case undefined:
                return this.mochaAdapter(config?.mochaOpts);

            default:
                throw new ConfigurationError(`"${ config?.serenity?.runner }" is not a supported test runner. Please use "mocha", "jasmine", or "cucumber"`);
        }
    }

    private cucumberAdapter(cucumberOptions?: WebdriverIO.CucumberOpts): TestRunnerAdapter {
        const { CucumberCLIAdapter, CucumberFormat, StandardOutput, TempFileOutput } = this.loader.require('@serenity-js/cucumber/lib/cli');

        delete cucumberOptions?.timeout;   // todo: support setting a timeout via config?
        delete cucumberOptions?.parallel;  // WebdriverIO handles that already

        const cleanedCucumberOptions = new Config(cucumberOptions)
            .where('require', requires =>
                this.finder.filesMatching(requires).map(p => p.value)
            )
            .where('format', values =>
                [].concat(values).map(value => {
                    const format = new CucumberFormat(value);

                    if (format.output === '') {
                        return format.value;
                    }

                    const basename = Path.from(format.output).basename();
                    const filenameParts = basename.split('.');

                    if (filenameParts[0] === basename) {
                        return `${ format.formatter }:${ format.output }.${ this.cid }`;
                    }

                    filenameParts.splice(-1, 0, `${ this.cid }`);

                    return `${ format.formatter }:${ format.output.replace(basename, filenameParts.join('.')) }`;
                })
            ).object();

        // check if we need to free up stdout for any native reporters
        const output = cleanedCucumberOptions?.format?.some(format => new CucumberFormat(format).output === '')
            ? new TempFileOutput(this.fileSystem)
            : new StandardOutput();

        return new CucumberCLIAdapter(cleanedCucumberOptions, this.loader, this.fileSystem, output);
    }

    private jasmineAdapter(jasmineOptions: WebdriverIO.JasmineOpts): TestRunnerAdapter {
        const { JasmineAdapter } = this.loader.require('@serenity-js/jasmine/lib/adapter')
        return new JasmineAdapter(jasmineOptions, this.loader);
    }

    private mochaAdapter(mochaOptions: WebdriverIO.MochaOpts): TestRunnerAdapter {
        const { MochaAdapter } = this.loader.require('@serenity-js/mocha/lib/adapter')
        return new MochaAdapter(mochaOptions, this.loader);
    }
}

import { SerenityConfig } from '@serenity-js/core';
import type { Options } from '@wdio/types';

/**
 * @desc
 *  [WebdriverIO configuration object](https://webdriver.io/docs/configurationfile/)
 *  with Serenity/JS-specific additions.
 *
 * @see {@link @serenity-js/cucumber/lib/cli~CucumberConfig}
 * @see {@link @serenity-js/jasmine/lib/adapter~JasmineConfig}
 * @see {@link @serenity-js/mocha/lib/adapter~MochaConfig}
 *
 * @see https://webdriver.io/docs/configurationfile/
 * @see {@link @wdio/types~Options.TestRunner}
 *
 * @public
 *
 * @extends {@wdio/types~Options.TestRunner}
 *
 * @example <caption>WebdriverIO with Serenity/JS and Cucumber</caption>
 *
 *  import { ConsoleReporter } from '@serenity-js/console-reporter';
 *  import { ArtifactArchiver } from '@serenity-js/core';
 *  import { SerenityBDDReporter } from '@serenity-js/serenity-bdd';
 *  import { WebdriverIOConfig } from '@serenity-js/webdriverio';
 *
 *  export const config: WebdriverIOConfig = {
 *
 *    framework: '@serenity-js/webdriverio',
 *
 *    serenity: {
 *        runner: 'cucumber',
 *        crew: [
 *            ConsoleReporter.forDarkTerminals(),
 *            new SerenityBDDReporter(),
 *            ArtifactArchiver.storingArtifactsAt('./target/site/serenity'),
 *        ]
 *    },
 *
 *    cucumberOpts: {
 *        // ...
 *    },
 *
 *    specs: [
 *        './features/*.feature',
 *    ],
 * };
 *
 * @example <caption>WebdriverIO with Serenity/JS and Mocha</caption>
 *
 *  import { ConsoleReporter } from '@serenity-js/console-reporter';
 *  import { ArtifactArchiver } from '@serenity-js/core';
 *  import { SerenityBDDReporter } from '@serenity-js/serenity-bdd';
 *  import { WebdriverIOConfig } from '@serenity-js/webdriverio';
 *
 *  export const config: WebdriverIOConfig = {
 *
 *    framework: '@serenity-js/webdriverio',
 *
 *    serenity: {
 *        runner: 'mocha',
 *        crew: [
 *            ConsoleReporter.forDarkTerminals(),
 *            new SerenityBDDReporter(),
 *            ArtifactArchiver.storingArtifactsAt('./target/site/serenity'),
 *        ]
 *    },
 *
 *    mochaOpts: {
 *        // ...
 *    },
 *
 *    specs: [
 *        './spec/*.spec.*',
 *    ],
 * };
 *
 * @example <caption>WebdriverIO with Serenity/JS and Jasmine</caption>
 *
 *  import { ConsoleReporter } from '@serenity-js/console-reporter';
 *  import { ArtifactArchiver } from '@serenity-js/core';
 *  import { SerenityBDDReporter } from '@serenity-js/serenity-bdd';
 *  import { WebdriverIOConfig } from '@serenity-js/webdriverio';
 *
 *  export const config: WebdriverIOConfig = {
 *
 *    framework: '@serenity-js/webdriverio',
 *
 *    serenity: {
 *        runner: 'jasmine',
 *        crew: [
 *            ConsoleReporter.forDarkTerminals(),
 *            new SerenityBDDReporter(),
 *            ArtifactArchiver.storingArtifactsAt('./target/site/serenity'),
 *        ]
 *    },
 *
 *    jasmineOpts: {
 *        // ...
 *    },
 *
 *    specs: [
 *        './spec/*.spec.*',
 *    ],
 * };
 */
export interface WebdriverIOConfig extends Options.Testrunner {

    /**
     * @desc
     *  Serenity/JS configuration with an additional `runner` entry
     *  allowing to specify the test runner, such as `cucumber`, `mocha`, or `jasmine`.
     *
     * @see {@link @serenity-js/cucumber/lib/cli~CucumberConfig}
     * @see {@link @serenity-js/jasmine/lib/adapter~JasmineConfig}
     * @see {@link @serenity-js/mocha/lib/adapter~MochaConfig}
     *
     * @type {@serenity-js/core~SerenityConfig}
     * @public
     */
    serenity?: SerenityConfig & { runner?: string };
}

import { ArtifactArchiver, Serenity } from '@serenity-js/core';
import { ModuleLoader, Path, TestRunnerAdapter } from '@serenity-js/core/lib/io';
import type { Capabilities } from '@wdio/types';
import type { EventEmitter } from 'events';
import { isPlainObject } from 'is-plain-object';

import { BrowserCapabilitiesReporter, InitialisesReporters, OutputStreamBuffer, ProvidesWriteStream } from './reporter';
import { OutputStreamBufferPrinter } from './reporter/OutputStreamBufferPrinter';
import { TestRunnerLoader } from './TestRunnerLoader';
import { WebdriverIOConfig } from './WebdriverIOConfig';
import { WebdriverIONotifier } from './WebdriverIONotifier';
import deepmerge = require('deepmerge');

/**
 * @package
 */
export class WebdriverIOFrameworkAdapter {

    private adapter: TestRunnerAdapter;
    private notifier: WebdriverIONotifier;

    constructor(
        private readonly serenity: Serenity,
        private readonly loader: ModuleLoader,
        private readonly cwd: Path,
        private readonly cid: string,
        webdriverIOConfig: WebdriverIOConfig,
        private readonly specs: string[],
        private readonly capabilities: Capabilities.RemoteCapability,
        reporter: EventEmitter & ProvidesWriteStream & InitialisesReporters
    ) {
        const config = deepmerge<WebdriverIOConfig>(this.defaultConfig(), webdriverIOConfig, {
            isMergeableObject: isPlainObject,
        });

        this.adapter = new TestRunnerLoader(this.loader, this.cwd, this.cid)
            .runnerAdapterFor(config);

        // This is the only (hacky) way to register a fake reporter programmatically (as of @wdio/reporter 7.4.2)
        //  - https://github.com/webdriverio/webdriverio/blob/365fb0ad79fcf4471f21f23e18afa6818986dbdb/packages/wdio-runner/src/index.ts#L147-L181
        //  - https://github.com/webdriverio/webdriverio/blob/365fb0ad79fcf4471f21f23e18afa6818986dbdb/packages/wdio-runner/src/reporter.ts#L24
        (reporter as any)._reporters.push(reporter.initReporter([
            BrowserCapabilitiesReporter, { serenity: this.serenity },
        ]));

        this.notifier = new WebdriverIONotifier(
            reporter,
            this.adapter.successThreshold(),
            cid,
            this.specs,
        );

        const outputStreamBuffer = new OutputStreamBuffer(
            `[${this.cid}]`,
        );

        const outputStreamBufferPrinter = new OutputStreamBufferPrinter(
            outputStreamBuffer,
            reporter.getWriteStreamObject('@serenity-js/webdriverio')
        );

        this.serenity.configure({
            outputStream:   outputStreamBuffer,
            cueTimeout:     config.serenity.cueTimeout,
            actors:         config.serenity.actors,
            crew: [
                ...config.serenity.crew,
                this.notifier,
                outputStreamBufferPrinter,
            ]
        });
    }

    async init(): Promise<WebdriverIOFrameworkAdapter> {

        await this.adapter.load(this.specs);

        return this;
    }

    hasTests(): boolean {
        return this.adapter.scenarioCount() > 0;
    }

    run(): Promise<number> {
        return this.adapter.run().then(() =>
            this.notifier.failureCount()
        );
    }

    private defaultConfig(): Partial<WebdriverIOConfig> {
        return {
            serenity: {
                crew: [
                    ArtifactArchiver.storingArtifactsAt(this.cwd.value, 'target/site/serenity'),
                ]
            }
        }
    }
}

import { Serenity } from '@serenity-js/core';
import { ModuleLoader, Path } from '@serenity-js/core/lib/io';
import type { Capabilities } from '@wdio/types';
import type { EventEmitter } from 'events';

import { InitialisesReporters, ProvidesWriteStream } from './reporter';
import { WebdriverIOConfig } from './WebdriverIOConfig';
import { WebdriverIOFrameworkAdapter } from './WebdriverIOFrameworkAdapter';

/**
 * @package
 */
export class WebdriverIOFrameworkAdapterFactory {
    constructor(
        private readonly serenity: Serenity,
        private readonly loader: ModuleLoader,
        private readonly cwd: Path,
    ) {
    }

    public init(
        cid: string,
        config: WebdriverIOConfig,
        specs: string[],
        capabilities: Capabilities.RemoteCapability,
        reporter: EventEmitter & ProvidesWriteStream & InitialisesReporters
    ): Promise<WebdriverIOFrameworkAdapter> {
        return new WebdriverIOFrameworkAdapter(
            this.serenity,
            this.loader,
            this.cwd,
            cid,
            config,
            specs,
            capabilities,
            reporter,
        ).init()
    }
}

import { LogicError, Stage, StageCrewMember } from '@serenity-js/core';
import { DomainEvent, SceneFinished, SceneStarts, TestSuiteFinished, TestSuiteStarts } from '@serenity-js/core/lib/events';
import {
    CorrelationId,
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionIgnored,
    ExecutionSkipped,
    ImplementationPending,
    Outcome,
    ProblemIndication,
    TestSuiteDetails,
} from '@serenity-js/core/lib/model';
import { Suite } from '@wdio/reporter/build/stats/suite';
import { Test } from '@wdio/reporter/build/stats/test';
import type { EventEmitter } from 'events';
import { match } from 'tiny-types';

/**
 * @package
 */
export class WebdriverIONotifier implements StageCrewMember {

    private readonly events = new EventLog();
    private readonly suites: TestSuiteDetails[] = [];

    constructor(
        private readonly reporter: EventEmitter,
        private readonly successThreshold: Outcome | { Code: number },
        private readonly cid: string,
        private readonly specs: string[],
        private failures: number = 0,
        private stage?: Stage,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        this.stage = stage;
        return this;
    }

    notifyOf(event: DomainEvent): void {
        return match<DomainEvent, void>(event)
            .when(TestSuiteStarts,          WebdriverIONotifier.prototype.onTestSuiteStarts.bind(this))
            .when(TestSuiteFinished,        WebdriverIONotifier.prototype.onTestSuiteFinished.bind(this))
            .when(SceneStarts,              WebdriverIONotifier.prototype.onSceneStarts.bind(this))
            .when(SceneFinished,            WebdriverIONotifier.prototype.onSceneFinished.bind(this))
            .else(() => void 0);
    }

    failureCount(): number {
        return this.failures;
    }

    private onTestSuiteStarts(started: TestSuiteStarts) {
        this.events.record(started.details.correlationId, started);
        this.reporter.emit('suite:start', this.suiteStartEventFrom(started));

        this.suites.push(started.details);
    }

    private onTestSuiteFinished(finished: TestSuiteFinished) {
        this.suites.pop();

        const started = this.events.getByCorrelationId<TestSuiteStarts>(finished.details.correlationId);
        this.reporter.emit('suite:end', this.suiteEndEventFrom(started, finished));
    }

    private suiteStartEventFrom(started: TestSuiteStarts): Suite {
        return {
            type:       'suite:start',
            uid:        started.details.correlationId.value,
            cid:        this.cid,
            title:      started.details.name.value,
            fullTitle:  this.suiteNamesConcatenatedWith(started.details.name.value),
            parent:     this.parentSuiteName(),
            file:       started.details.location.path.value,
            specs:      this.specs,
            pending:    false,
        }
    }

    private suiteNamesConcatenatedWith(name: string): string {
        return this.suites.map(suite => suite.name.value).concat(name).join(' ');
    }

    private suiteEndEventFrom(started: TestSuiteStarts, finished: TestSuiteFinished): Suite {
        return {
            ...this.suiteStartEventFrom(started),
            type:       'suite:end',
            duration:   finished.timestamp.diff(started.timestamp).inMilliseconds()
        }
    }

    private onSceneStarts(started: SceneStarts) {
        const test = this.testStartEventFrom(started);

        this.events.record(started.sceneId, started);

        this.reporter.emit(test.type, test);
    }

    private onSceneFinished(finished: SceneFinished) {

        if (finished.outcome.isWorseThan(this.successThreshold)) {
            this.failures++;
        }

        const started     = this.events.getByCorrelationId<SceneStarts>(finished.sceneId);

        if (this.willBeRetried(finished.outcome)) {
            const testResult  = this.testEndEventFrom(started, finished);

            const type = 'test:retry';

            this.reporter.emit(type, {
                ...testResult,
                type,
                error:  this.errorFrom(finished.outcome),
            });

        } else {

            const testResult  = this.testResultEventFrom(started, finished);
            this.reporter.emit(testResult.type, testResult);

            const testEnd     = this.testEndEventFrom(started, finished);
            this.reporter.emit(testEnd.type, testEnd);
        }
    }

    private willBeRetried(outcome: Outcome): outcome is ExecutionIgnored {
        return outcome instanceof ExecutionIgnored;
    }

    private testStartEventFrom(started: SceneStarts): Test {
        const title = started.details.name.value
            .replace(new RegExp(`^.*?(${ this.parentSuiteName() })`), '')
            .trim();

        return {
            type:       'test:start',
            title,
            fullTitle:  this.suiteNamesConcatenatedWith(title),
            parent:     this.parentSuiteName(),
            file:       started.details.location.path.value,
            pending:    false,
            cid:        this.cid,
            uid:        started.sceneId.value,
            specs:      this.specs,
        }
    }

    private parentSuiteName() {
        return this.suites[this.suites.length - 1]?.name.value || '';
    }

    private testEndEventFrom(started: SceneStarts, finished: SceneFinished): Test {
        const duration = finished.timestamp.diff(started.timestamp).inMilliseconds();
        return {
            ...this.testStartEventFrom(started),
            type: 'test:end',
            duration
        };
    }

    private testResultEventFrom(started: SceneStarts, finished: SceneFinished): Test {
        const test = this.testEndEventFrom(started, finished)

        const unlessSuccessful = (outcome: Outcome, type: Test['type']) =>
            ! outcome.isWorseThan(this.successThreshold) && (outcome instanceof ProblemIndication)
                ? 'test:pass'
                : type;

        return match<Outcome, Test>(finished.outcome)
            .when(ExecutionCompromised, (outcome: ExecutionCompromised) => ({
                ...test,
                type:   unlessSuccessful(outcome, 'test:fail'),
                error:  this.errorFrom(outcome),
            }))
            .when(ExecutionFailedWithError, (outcome: ExecutionFailedWithError) => ({
                ...test,
                type:   unlessSuccessful(outcome, 'test:fail'),
                error:  this.errorFrom(outcome),
            }))
            .when(ExecutionFailedWithAssertionError, (outcome: ExecutionFailedWithAssertionError) => ({
                ...test,
                type:   unlessSuccessful(outcome, 'test:fail'),
                error:  this.errorFrom(outcome),
            }))
            .when(ImplementationPending, (outcome: ImplementationPending) => ({
                ...test,
                type:           unlessSuccessful(outcome, 'test:pending'),
                error:          this.errorFrom(outcome),
                pending:        true,
                pendingReason:  outcome.error.message
            }))
            .when(ExecutionIgnored, (outcome: ExecutionIgnored) => ({
                ...test,
                // In WebdriverIO, skipped == pending == ignored
                // https://github.com/webdriverio/webdriverio/blob/a1830046f367be7737af2c00561796c3ae5dd85b/packages/wdio-reporter/src/index.ts#L162
                type:           unlessSuccessful(outcome, 'test:pending'),
                error:          this.errorFrom(outcome),
                pending:        true,
                pendingReason:  outcome.error.message
            }))
            .when(ExecutionSkipped, (outcome: ExecutionSkipped) => ({
                ...test,
                // In WebdriverIO, skipped == pending == ignored
                // https://github.com/webdriverio/webdriverio/blob/a1830046f367be7737af2c00561796c3ae5dd85b/packages/wdio-reporter/src/index.ts#L162
                type:           unlessSuccessful(outcome, 'test:pending'),
                pending:        true,
            }))
            .else(() => ({
                ...test,
                type: 'test:pass',
            }));
    }

    private errorFrom(outcome: ProblemIndication) {
        const error: Error & { type?: string, expected?: string, actual?: string } = outcome.error;

        // https://github.com/webdriverio/webdriverio/blob/7ec2c60a7623de431d60bb3605957e6e4bdf057b/packages/wdio-mocha-framework/src/index.ts#L233
        return {
            name:       error.name,
            message:    error.message,
            stack:      error.stack,
            type:       error.type || error.name,
            expected:   error.expected,
            actual:     error.actual
        }
    }
}

class EventLog {
    private readonly events = new Map<string, DomainEvent>();

    record(correlationId: CorrelationId, event: DomainEvent) {
        this.events.set(correlationId.value, event)
    }

    getByCorrelationId<T extends DomainEvent>(correlationId: CorrelationId): T {
        if (! this.events.has(correlationId.value)) {
            throw new LogicError(`Event with correlation id ${ correlationId } has never been recorded`);
        }

        return this.events.get(correlationId.value) as T;
    }
}

export * from './TestRunnerLoader';
export * from './WebdriverIOConfig';
export * from './WebdriverIOFrameworkAdapterFactory';

import { Serenity } from '@serenity-js/core';
import { SceneTagged } from '@serenity-js/core/lib/events';
import { Tag } from '@serenity-js/core/lib/model';
import Reporter from '@wdio/reporter';
import { Capabilities, Options, Reporters } from '@wdio/types';

import { TagPrinter } from './TagPrinter';

/**
 * @package
 */
export class BrowserCapabilitiesReporter extends Reporter {

    private readonly tagPrinter = new TagPrinter();
    private readonly serenity: Serenity;

    private readonly tags: Tag[] = [];

    constructor (options: Partial<Reporters.Options> & { serenity: Serenity }) {
        super({ ...options, stdout: false });

        this.serenity = options.serenity;

        this.on('runner:start', BrowserCapabilitiesReporter.prototype.recordBrowserAndPlatformTags.bind(this));
        this.on('test:start',   BrowserCapabilitiesReporter.prototype.emitRecordedTags.bind(this));
    }

    private recordBrowserAndPlatformTags(event: Options.RunnerStart) {
        const tags = event.isMultiremote
            ? this.tagsForAll(event.capabilities as unknown as Record<string, Capabilities.DesiredCapabilities | Capabilities.W3CCapabilities>)  // fixme: WDIO MultiremoteCapabilities seem to have incorrect definition?
            : this.tagPrinter.tagsFor(event.capabilities)

        this.tags.push(...tags);
    }

    private tagsForAll(capabilities: Record<string, Capabilities.DesiredCapabilities | Capabilities.W3CCapabilities>): Tag[] {
        return Object.keys(capabilities)
            .reduce((existingTags, entryName) => {

                const newTags       = this.tagPrinter.tagsFor(capabilities[entryName]);
                const uniqueNewTags = newTags.filter(tag => ! existingTags.some((existingTag: Tag) => existingTag.equals(tag)));

                return [
                    ...existingTags,
                    // todo: maybe add some additional tag to indicate the custom capability name,
                    //  or the fact that it's a multi-remote scenario?
                    ...uniqueNewTags,
                ];
            }, []);
    }

    private emitRecordedTags() {
        this.tags.forEach(tag => {
            this.serenity.announce(
                new SceneTagged(
                    this.serenity.currentSceneId(),
                    tag,
                    this.serenity.currentTime(),
                )
            )
        })
    }
}

import { Reporters } from '@wdio/types';

/**
 * @package
 */
export interface InitialisesReporters {
    initReporter(reporter: Reporters.ReporterEntry): void;
}

import { OutputStream } from '@serenity-js/core/lib/io';

/**
 * @package
 */
export class OutputStreamBuffer implements OutputStream {
    private buffer = '';

    constructor(private readonly prefix: string) {
    }

    write(content: string): void {
        this.buffer += content;
    }

    hasContent(): boolean {
        return this.buffer !== '';
    }

    flush(): string {
        const prefixedContent = this.buffer
            .split('\n')
            .map(line => `${ this.prefix } ${ line }\n`)
            .join('');

        this.buffer = '';

        return prefixedContent;
    }
}

import { Stage, StageCrewMember } from '@serenity-js/core';
import { DomainEvent, SceneFinished } from '@serenity-js/core/lib/events';
import { OutputStream } from '@serenity-js/core/lib/io';

import { OutputStreamBuffer } from './OutputStreamBuffer';

/**
 * @package
 */
export class OutputStreamBufferPrinter implements StageCrewMember {
    constructor(
        private readonly buffer: OutputStreamBuffer,
        private readonly outputStream: OutputStream,
        private stage?: Stage,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        this.stage = stage;

        return this;
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneFinished && this.buffer.hasContent()) {
            this.outputStream.write(this.buffer.flush());
        }
    }
}

import { OutputStream } from '@serenity-js/core/lib/io';

/**
 * @package
 */
export interface ProvidesWriteStream {
    getWriteStreamObject(reporter: string): OutputStream;
}

import { BrowserTag, PlatformTag, Tag } from '@serenity-js/core/lib/model';
import { Capabilities } from '@wdio/types';

/**
 * @package
 */
export class TagPrinter {
    tagsFor(capability: Capabilities.DesiredCapabilities | Capabilities.W3CCapabilities): Tag[] {
        const desiredCapabilities = this.desired(capability);

        return [
            this.browserTagFor(desiredCapabilities),
            this.platformTagFor(desiredCapabilities),
        ];
    }

    private browserTagFor(capabilities: Capabilities.DesiredCapabilities): Tag {
        return new BrowserTag(
            this.browserNameFrom(capabilities),
            this.browserVersionFrom(capabilities),
        );
    }

    private platformTagFor(capabilities: Capabilities.DesiredCapabilities): Tag {
        return new PlatformTag(
            this.platformNameFrom(capabilities),
            this.platformVersionFrom(capabilities),
        );
    }

    private browserNameFrom(capabilities: Capabilities.DesiredCapabilities): string {
        return capabilities.browserName
            || capabilities.browser
            || (capabilities.app && capabilities.app.replace('sauce-storage:', ''))
            || 'unknown';
    }

    private browserVersionFrom(capabilities: Capabilities.DesiredCapabilities): string | undefined {
        return capabilities.deviceName          // mobile web
            || capabilities.browserVersion      // W3C format
            || capabilities.version             // JSONWP format
            || capabilities.browser_version;    // BrowserStack
    }

    private platformNameFrom(capabilities: Capabilities.DesiredCapabilities): string {
        return capabilities.platformName
            || capabilities.platform
            || capabilities.os
            || 'unknown';
    }

    private platformVersionFrom(capabilities: Capabilities.DesiredCapabilities): string | undefined {
        return capabilities.platformVersion
            || capabilities.os_version;
    }

    private desired(capabilities: Capabilities.RemoteCapability): Capabilities.DesiredCapabilities {
        return this.isW3C(capabilities)
            ? capabilities.alwaysMatch
            : capabilities;
    }

    private isW3C(capabilities: Capabilities.RemoteCapability): capabilities is Capabilities.W3CCapabilities {
        return !!(capabilities as Capabilities.W3CCapabilities).alwaysMatch;
    }
}

