type MultiremoteCapabilities = Record<string, Capabilities.DesiredCapabilities | Capabilities.W3CCapabilities>;

/**
 * Fixme: it looks like WDIO RunnerStart 7.4.2 implementation incorrectly defines MultiremoteCapabilities
 *  below is the correct representation of what the reporter actually receives
 */
type RunnerStartEvent = RunnerStart & { capabilities: Capabilities.DesiredCapabilities | Capabilities.W3CCapabilities | MultiremoteCapabilities }

function runnerStart(overrides: Partial<RunnerStartEvent>): RunnerStartEvent {
    return {
        cid: '0-0',
        isMultiremote: false,
        sessionId: 'fake-session-id',
        specs: [ '/path/to/fake.spec.ts' ],
        instanceOptions: undefined,
        capabilities: undefined,
        config: undefined,
        ...overrides
    };
}

import 'mocha';

import { expect } from '@integration/testing-tools';

import { OutputStreamBuffer } from '../../../src/adapter/reporter';

describe('OutputStreamBuffer', () => {

    let buffer: OutputStreamBuffer;

    beforeEach(() => {
        buffer  = new OutputStreamBuffer('[prefix]');
    });

    it(`prefixes a line of content written to it`, () => {
        buffer.write('Hello');
        buffer.write(' ');
        buffer.write('World');

        expect(buffer.flush()).to.equal('[prefix] Hello World\n');
    });

    it(`prefixes multi-line content`, () => {
        buffer.write('first\nsecond\nthird');

        expect(buffer.flush()).to.equal(`[prefix] first\n[prefix] second\n[prefix] third\n`);
    });

    it(`clears the buffer when flushed`, () => {
        buffer.write('Hello World!');
        expect(buffer.flush()).to.equal('[prefix] Hello World!\n');
        expect(buffer.flush()).to.equal('[prefix] \n');
    });

    it(`knows when content has been written to it`, () => {
        expect(buffer.hasContent()).to.equal(false);

        buffer.write('');
        expect(buffer.hasContent()).to.equal(false);

        buffer.write('hello!');
        expect(buffer.hasContent()).to.equal(true);
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { BrowserTag, PlatformTag } from '@serenity-js/core/lib/model';

import { TagPrinter } from '../../../src/adapter/reporter';

describe('TagPrinter', () => {

    let tagPrinter: TagPrinter;
    beforeEach(() => {
        tagPrinter = new TagPrinter();
    });

    describe('when working with a mobile device', () => {

        it('should tag browser and platform for mobile web browser', () => {
            const tags = tagPrinter.tagsFor({
                deviceName:         'iPhone 6 Plus',
                platformVersion:    '9.2',
                platformName:       'iOS',
                browserName:        'Safari'
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('Safari', 'iPhone 6 Plus'));
            expect(tags[1]).to.equal(new PlatformTag('iOS', '9.2'));
        });

        it('should mark browser as "unknown" when information is missing', () => {
            const tags = tagPrinter.tagsFor({
                deviceName:         'iPhone 6 Plus',
                platformVersion:    '9.2',
                platformName:       'iOS'
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('unknown', 'iPhone 6 Plus'));
            expect(tags[1]).to.equal(new PlatformTag('iOS', '9.2'));
        });

        it('should tag the native app as "browser"', () => {
            const tags = tagPrinter.tagsFor({
                deviceName:         'iPhone 6 Plus',
                platformVersion:    '9.2',
                platformName:       'iOS',
                app:                'sauce-storage:myApp.app'
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('myApp.app', 'iPhone 6 Plus'));
            expect(tags[1]).to.equal(new PlatformTag('iOS', '9.2'));
        });

        it('should tag a simulator', () => {
            // https://appium.io/docs/en/writing-running-appium/caps/
            const tags = tagPrinter.tagsFor({
                automationName: 'XCUITest',
                browserName: 'Safari',
                deviceName: 'iPhone Simulator',
                platformName: 'iOS',
                platformVersion: undefined,
                udid: undefined,
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('Safari', 'iPhone Simulator'));
            expect(tags[1]).to.equal(new PlatformTag('iOS'));
        });
    })

    describe('when working with a single browser', () => {

        it('should tag "platform" as unknown where the information is missing', () => {
            const tags = tagPrinter.tagsFor({
                browserName:    'chrome',
                version:        '50',
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('chrome', '50'));
            expect(tags[1]).to.equal(new PlatformTag('unknown'));
        });

        it('should tag both browser and platform, where platformVersion is not available', () => {
            const tags = tagPrinter.tagsFor({
                browserName:    'chrome',
                version:        '50',
                platform:       'Windows 8.1'
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('chrome', '50'));
            expect(tags[1]).to.equal(new PlatformTag('Windows 8.1'));
        });

        it('should tag both browser and platform, where platformVersion is available', () => {
            const tags = tagPrinter.tagsFor({
                browserName:        'Chrome Headless',
                browserVersion:     '90.0.4430.212',
                platformName:       'darwin',
                platformVersion:    '20.4.0',
            });

            expect(tags).have.lengthOf(2);
            expect(tags[0]).to.equal(new BrowserTag('Chrome Headless', '90.0.4430.212'));
            expect(tags[1]).to.equal(new PlatformTag('darwin', '20.4.0'));
        });

        describe('running on BrowserStack', () => {

            it('should correctly describe desired capabilities when "os" is given', () => {
                const tags = tagPrinter.tagsFor({
                    browser:            'Chrome',
                    browser_version:    '50',
                    os:                 'Windows',
                    os_version:         '10'
                });

                expect(tags).have.lengthOf(2);
                expect(tags[0]).to.equal(new BrowserTag('Chrome', '50'));
                expect(tags[1]).to.equal(new PlatformTag('Windows', '10'));
            });

            it('should tag "platform" as unknown where "os" is missing', () => {
                const tags = tagPrinter.tagsFor({
                    browser:            'Chrome',
                    browser_version:    '50',
                });

                expect(tags).have.lengthOf(2);
                expect(tags[0]).to.equal(new BrowserTag('Chrome', '50'));
                expect(tags[1]).to.equal(new PlatformTag('unknown'));
            });

            it('should tag "platform" even when "os_version" is missing', () => {
                const tags = tagPrinter.tagsFor({
                    browser:            'Chrome',
                    browser_version:    '50',
                    os:                 'Windows',
                });

                expect(tags).have.lengthOf(2);
                expect(tags[0]).to.equal(new BrowserTag('Chrome', '50'));
                expect(tags[1]).to.equal(new PlatformTag('Windows'));
            });
        });

    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { Ensure, not } from '@serenity-js/assertions';
import { actorCalled, AssertionError } from '@serenity-js/core';

import { by, Click, isActive, Navigate, Target, Wait } from '../../src';

describe('isActive', function () {

    const Page = {
        activeInput:       Target.the('active input').located(by.id('active')),
        inactiveInput:     Target.the('inactive input').located(by.id('inactive')),
    };

    beforeEach(() =>
        actorCalled('Wendy').attemptsTo(
            Navigate.to('/expectations/is-active/active_inactive_inputs.html'),
        ));

    /** @test {isActive} */
    it('allows the actor flow to continue when the element is active', () =>
        expect(actorCalled('Wendy').attemptsTo(
            Wait.until(Page.activeInput, not(isActive())),
            Ensure.that(Page.activeInput, not(isActive())),
            Click.on(Page.activeInput),
            Wait.until(Page.activeInput, isActive()),
            Ensure.that(Page.activeInput, isActive()),
        )).to.be.fulfilled);

    /** @test {isActive} */
    it('breaks the actor flow when element is inactive', () =>
        expect(actorCalled('Wendy').attemptsTo(
            Ensure.that(Page.inactiveInput, isActive()),
        )).to.be.rejectedWith(AssertionError, `Expected the inactive input to become active`));

    /** @test {isActive} */
    it('contributes to a human-readable description of an assertion', () => {
        expect(Ensure.that(Page.activeInput, isActive()).toString())
            .to.equal(`#actor ensures that the active input does become active`);
    });

    /** @test {isActive} */
    it('contributes to a human-readable description of a wait', () => {
        expect(Wait.until(Page.activeInput, isActive()).toString())
            .to.equal(`#actor waits up to 5s until the active input does become active`);
    });
});

